INSERT INTO articles (article_id, anotation, title, url) VALUES (1, 'Tragická nehoda autobusu se třemi mrtvými se stala na rovném úseku, který podle Horoměřických svádí k rychlosti a předjíždění. Horizont však přijde dřív, než by řidič čekal. Příčinu pátečního neštěstí policie zatím nezná, bude čekat na závěry znaleckých posudků. Nyní žádá svědky, aby jeho průběh pomohli objasnit.', 'Autobus u Horoměřic boural na zrádné rovince, která svádí k rychlosti', 'https://zpravy.idnes.cz/nehoda-autobus-horomerice-dcg-/krimi.aspx?c=A180113_100532_krimi_mav');
INSERT INTO articles (article_id, anotation, title, url) VALUES (6, 'Autobus se v pátek odpoledne na pomezí Prahy a Středočeského kraje nedaleko Horoměřic srazil s autem a následně narazil do stromu. Podle záchranné služby tři lidé zemřeli, zraněných je 45, z toho pět je ve vážném stavu. Mezi zraněnými jsou i děti.', 'U Prahy narazil autobus do stromu. Tři lidé zahynuli, mezi zraněnými jsou děti', 'https://www.novinky.cz/krimi/460383-u-prahy-narazil-autobus-do-stromu-tri-lide-zahynuli-mezi-zranenymi-jsou-deti.html');
