INSERT INTO articles(article_id, title, anotation, url) VALUES (2, 'Babiš bude jednat s Hamáčkem o vládě, svoji neúčast v ní však odmítá', 'Premiér v demisi Andrej Babiš (ANO) se chce sejít s nově zvoleným předsedou ČSSD Janem Hamáčkem a jednat s ním o tom, za jakých podmínek by sociální demokracie mohla vstoupit do vlády nebo kabinet tolerovat. Řekl to novinářům po večeři s prezidentem Milošem Zemanem.', 'https://zpravy.idnes.cz/babis-se-sejde-s-hamackem-odmita-neucast-ve-vlade-ftn-/domaci.aspx?c=A180218_205542_domaci_lre');
INSERT INTO articles(article_id, title, anotation, url) VALUES (3, 'Lavina zabila dva lyžaře ve Francii, na Slovensku připravila o život horolezce', 'Dva lyžaři nepřežili pád laviny v oblasti francouzského střediska Val-d''Isère. Záchranáři našli otce s holčičkou po hodině, pomoci jim ovšem nedokázali. Další život si lavina vyžádala v Tatrách, kde zasypala dva polské horolezce. Pětatřicetiletý muž zahynul, o pět let mladší žena byla v kritickém stavu přepravena do nemocnice.', 'https://zpravy.idnes.cz/svycarsko-lavina-valais-francie-daz-/zahranicni.aspx?c=A180218_172055_zahranicni_aha');
INSERT INTO articles(article_id, title, anotation, url) VALUES (4, 'Hamáček vede ČSSD, která považuje za zásadní problém obviněného ve vládě', 'Novým předsedou ČSSD je dosavadní místopředseda Jan Hamáček, který je ochoten jednat o vládě s ANO. Schválit ji musí členové strany ve vnitrostranickém referendu. Strana přijala na Hamáčkův návrh usnesení, že účast trestně stíhaného politika ve vládě považuje ČSSD za „zásadní problém“. Hamáčkovým zástupcem je jihočeský exhejtman Jiří Zimola.', 'https://zpravy.idnes.cz/volba-predsedy-cssd-projevy-kandidatu-jan-hamacek-jiri-zimola-put-/domaci.aspx?c=A180218_150848_domaci_kop');
INSERT INTO articles(article_id, title, anotation, url) VALUES (5, 'Lety hlídali bachaři, zlobivé děti zavírali do psí boudy, říká syn přeživší', 'Koncentrační tábor v Letech byl oplocený a hlídali ho ozbrojení bachaři, reaguje Jan Hauer, jehož maminka táborem prošla, na výroky Tomia Okamury. Na šéfa SPD podal trestní oznámení, jeho vyjádření podle něj není možné odpustit. V Letech Hauerovi zahynula malá sestra, další část jeho rodiny zemřela v Osvětimi.', 'https://zpravy.idnes.cz/jan-hauer-koncentracni-tabor-lety-sintove-rozhovor-fh7-/domaci.aspx?c=A180215_114422_domaci_mpl');
INSERT INTO articles(article_id, title, anotation, url) VALUES (6, 'V íránském pohoří spadlo letadlo s 65 lidmi, katastrofu nikdo nepřežil', 'Při nehodě íránského dopravního letadla zahynulo všech 65 lidí na palubě, uvedl mluvčí letecké společnosti Iran Aseman Airlines. Letadlo se za mlhy zřítilo v horách na jihozápadě země, příčinu nehody zatím úřady zjišťují. Záchranné týmy budou pátrat po tělech obětí a troskách letounu ve špatně přístupných zasněžených horách celou noc.', 'https://zpravy.idnes.cz/iranske-dopravni-letadlo-50-lidi-zriceni-iran-fmo-/zahranicni.aspx?c=A180218_085428_zahranicni_ale');
INSERT INTO articles(article_id, title, anotation, url) VALUES (7, 'Rakousko, popelník Evropy? Lékaři bojují proti zrušení zákazu kouření', 'Nová rakouská vláda opět zruší zákaz kouření v hospodách a restauracích, který měl platit od letošního května. Za návrhem stojí Svobodná strana Rakouska (FPÖ), která vládne ve spolupráci s lidovci. Reakce hospodských a štamgastů jsou podle BBC smíšené, lékaři ostře protestují.', 'https://zpravy.idnes.cz/rakousko-zakaz-koureni-fpo-ddw-/zahranicni.aspx?c=A180218_143833_zahranicni_aha');
INSERT INTO articles(article_id, title, anotation, url) VALUES (8, 'ZPRÁVY: Nový předseda ČSSD, bronzová jízda Erbanové a zpěv Paris Hilton', 'Podívejte se na Zprávy iDNES.tv, mimo jiné uvidíte volbu předsedy ČSSD, prezidenta Zemana, jak nabádá sociální demokraty, aby se necpali do vlády, ale spolupracovali s hnutím ANO. Kateřinu Erbanovou, která získala bronzovou medaili na olympijské pětistovce i dědičku hotelového impéria Paris Hilton. Přestože o jejím zpěvu mají odborníci pochyby, nevzdává se a nazpívala novou píseň.', 'https://zpravy.idnes.cz/zpravy-idnes-tv-18-2-2018-0r6-/domaci.aspx?c=A180218_162059_domaci_pist');
INSERT INTO articles(article_id, title, anotation, url) VALUES (9, 'Jan Hamáček', 'Jan Hamáček byl v letech 2013 až 2017 předseda Poslanecké sněmovny, po volbách 2017 byl zvolen jejím místopředsedou. V únoru 2018 se stal předsedou České strany sociálně demokratické. Hamáček je polyglot. Mluví plynule anglicky, španělsky, německy a francouzsky, ovládá také základy ruštiny a polštiny. ', 'https://wiki.idnes.cz/jan-hamacek-c80-/politici.aspx?klic=455139');
INSERT INTO articles(article_id, title, anotation, url) VALUES (10, 'TÝDEN OBRAZEM: Velbloud na Sibiři, obrazy manželů Obamových a rok psa', 'Modelka během příprav před vystoupením na molu, velbloud vezoucí sáně v mínus sedmadvaceti stupni Celsia na jihu Sibiře nebo malé dítě prodávající v Indii lásku. Podívejte se na výběr nejlepších snímků zahraničních agentur za tento týden.', 'https://zpravy.idnes.cz/tyden-obrazem-vyber-nejlepsi-fotek-behem-tydne-fln-/zahranicni.aspx?c=A180218_171433_zahranicni_lre');
INSERT INTO articles(article_id, title, anotation, url) VALUES (11, 'Rusy straší tajemné video: Jděte volit, nebo vám do bytu nastrčí gaye', 'Měsíc před ruskými prezidentskými volbami se na internetu objevilo humorné video, které Rusy nabádá, aby šli volit. Pokud se totiž na svoji občanskou povinnost vykašlou, mohli by se probudit ve světě homosexuálů a černochů. Co nejvyšší volební účast je jedním z hlavních cílů kampaně Vladimira Putina.', 'https://zpravy.idnes.cz/rusko-volby-prezidentske-homosexual-gay-vladimir-putin-pk6-/zahranicni.aspx?c=A180218_165324_zahranicni_aha');
INSERT INTO articles(article_id, title, anotation, url) VALUES (12, 'Pozor, teď vás vyfotíme. Ochrana osobních údajů se dotkne i skiareálů', 'Skiareály budou muset začít lyžaře informovat o tom, že je snímá kamera, a jasně uvádět, jak s osobními údaji zákazníků zacházejí. I jich se totiž dotkne nařízení EU o ochraně osobních údajů (GDPR), které začne platit v květnu. Zároveň skiareály řeší jiný problém - kvůli přeprodeji nevyužitých skipasů tratí až miliony korun ročně.', 'https://zpravy.idnes.cz/preprodej-skipasy-vleky-permanentky-lyzarske-arealy-pdh-/domaci.aspx?c=A180207_104737_domaci_nub');
INSERT INTO articles(article_id, title, anotation, url) VALUES (13, 'Dienstbier na sjezdu ČSSD sepsul Chovance, kritice čelil nepřítomný Sobotka', 'Sociální demokraté řeší na svém sjezdu volební propadák, ale jedna klíčová osoba chybí. Bývalý premiér a dlouholetý šéf strany Bohuslav Sobotka. „I po porážce je třeba skládat účty, patří to k životu,“ kritizoval to končící místopředseda strany a volební lídr ČSSD Lubomír Zaorálek. Exministr Jiří Dienstbier tvrdě zkritizoval úřadujícího šéfa Milana Chovance.', 'https://zpravy.idnes.cz/sjezd-cssd-lubomir-zaoralek-michal-hasek-bohuslav-sobotka-p3h-/domaci.aspx?c=A180218_101447_domaci_kop');
INSERT INTO articles(article_id, title, anotation, url) VALUES (14, 'Netanjahu mával kusem dronu. Írán je největší hrozbou, řekl v Mnichově', 'Írán je největší hrozbou pro dnešní svět a Izrael proti němu bude konat bez zaváhání, pokud to bude potřeba, řekl dnes na mnichovské bezpečnostní konferenci izraelský premiér Benjamin Netanjahu. Zároveň Teherán varoval, aby nepokoušel izraelské odhodlání.', 'https://zpravy.idnes.cz/benjamin-netanjahu-izrael-iran-dron-mnichov-bezpecnostni-konference-1jg-/zahranicni.aspx?c=A180218_111752_zahranicni_aha');
INSERT INTO articles(article_id, title, anotation, url) VALUES (15, 'Suchá je skýva opozice, ale chtějte jenom náměstky, radil ČSSD Zeman', 'Na sjezdu ČSSD vystoupil prezident Miloš Zeman. „Suchá je skýva opozice,“ řekl jim k jejich rozhodování, zda mají jít do vlády. „Chtějte náměstky ministrů, chtějte odborníky,“ prohlásil prezident. Když nebude mít strana vliv na vládu, může podle něj klesnout pod 5 procent a nikdo o ní nebude vědět. Doporučil zahájit s ANO Andreje Babiše jednání o vládě.', 'https://zpravy.idnes.cz/prezident-milos-zeman-volebni-sjezd-cssd-fpw-/domaci.aspx?c=A180218_110029_domaci_kop');
INSERT INTO articles(article_id, title, anotation, url) VALUES (16, 'Kyperští maronité vymírají. Na vině je turecká invaze i asimilace mladých', 'Na Kypru už od středověku žije komunita maronitů - křesťanů, kteří kdysi uprchli z Libanonu. Jejich počet se však rok od roku snižuje. Turecká invaze v 70. letech menšinu rozdělila a v severní části ostrova dnes zůstává už jen poslední stovka starousedlíků. Těm nejmladším je přes šedesát a maronité tak postupně vymírají.', 'https://zpravy.idnes.cz/maronite-kypr-turci-invaze-libanon-dus-/zahranicni.aspx?c=A180215_144305_zahranicni_kha');
INSERT INTO articles(article_id, title, anotation, url) VALUES (17, 'Polský premiér zmínil židovské viníky holokaustu, naštval tím Izraelce', 'Polský premiér Mateusz Morawiecki se v Mnichově snažil hájit nový polský zákon proti obviňování polského národa z holokaustu a vyvolal mezinárodní pobouření. V rozhovoru s izraelským novináře prohlásil, že odpovědnost nesli i někteří Židé. Izraelští politici ho obvinili z antisemitismu.', 'https://zpravy.idnes.cz/mateusz-morawiecki-polsko-holokaust-izrael-fmk-/zahranicni.aspx?c=A180218_104634_zahranicni_aha');
INSERT INTO articles(article_id, title, anotation, url) VALUES (18, 'MALOSTRANSKÝ ZÁPISNÍK: Těžká práce senátorů a Paroubek prezidentem', 'Česká politická scéna je nevyčerpatelným zdrojem řady situací, ze kterých občas zůstane rozum stát. Komentátor MF DNES Miroslav Korecký glosuje, co se v politice v uplynulém týdnu odehrálo.', 'https://zpravy.idnes.cz/miroslav-korecky-malostransky-zapisnik-politika-fvl-/domaci.aspx?c=A180217_115945_domaci_ale');
INSERT INTO articles(article_id, title, anotation, url) VALUES (19, 'ČSSD zvolí nového lídra, na sjezdu hovořil prezident Zeman', 'Nový směr hledá sociální demokracie. V Hradci Králové si zvolí předsedu - zájem o to mají tři ostřílení politici a pět méně známých tváří. Mají zájem vést dříve nejsilnější stranu, která spadla po volebním debaklu až k 7 procentům. Sjezd ČSSD také naznačí, jaká bude podoba vlády na příštích několik let. Přijel se na to podívat i prezident Miloš Zeman.', 'https://zpravy.idnes.cz/volebni-sjezd-cssd-vlada-zeman-hradec-kralove-fa1-/domaci.aspx?c=A180217_132153_domaci_kop');
INSERT INTO articles(article_id, title, anotation, url) VALUES (20, 'Ochranná služba nakoupí 18 limuzín BMW, premiér Babiš to chce prošetřit', 'Ministři, které chrání a vozí policie, přesednou po letech z audi do BMW. Policejní ochranná služba kupuje osmnáct limuzín za celkem 50 milionů korun. MF DNES dílčí smlouvu z letošního 18. ledna na dodání prvních pěti limuzín BMW 750Li xDrive našla v registru smluv. Auta budou mít černou barvu a mají i pohon všech kol.', 'https://zpravy.idnes.cz/politik-vlada-novy-auto-bmw-limuzina-ds4-/domaci.aspx?c=A180215_204056_domaci_vitm');
INSERT INTO articles(article_id, title, anotation, url) VALUES (21, 'Khmerská dívka u soudu řekla, že ji Čech zadržovaný v Kambodži nezneužil', 'Václav Pavlát se cítí jako na houpačce. V jeho případu došlo k zásadnímu průlomu a od tajemné návštěvy dokonce dostal nabídku, že za patnáct tisíc dolarů může být do konce týdne na svobodě, přesto si civilní šaty ještě nechystá.', 'https://zpravy.idnes.cz/kambodza-cech-vaclav-pavlat-vezeni-divka-zneuzivani-fyt-/zahranicni.aspx?c=A180216_383356_zahranicni_hm1');
INSERT INTO articles(article_id, title, anotation, url) VALUES (22, 'Britská UKIP odvolala předsedu, jeho milenka urazila snoubenku prince', 'Členové britské protievropské Strany nezávislosti Spojeného království (UKIP) v sobotu na mimořádném sjezdu odvolali předsedu Henryho Boltona, který ztratil jejich důvěru kvůli rasistickým výrokům své milenky. Ta urazila snoubenku prince Harryho. Podle Boltona straně ubližuje nejednota a sobotní rozhodnutí ji poškodí na dlouhé měsíce.', 'https://zpravy.idnes.cz/britanie-ukip-protievropska-strana-henry-bolton-milenka-urazka-meghan-132-/zahranicni.aspx?c=A180218_072057_zahranicni_ale');
INSERT INTO articles(article_id, title, anotation, url) VALUES (23, 'Úřady Cruze vyšetřovaly už dříve. Nepředstavuje riziko, rozhodly tehdy', 'Americká policie měla již předloni informace o neobvyklém chování Nikolase Cruze, který ve středu postřílel ve floridské střední škole 17 lidí. Úřady tehdy vyhodnotily, že nepředstavuje velké riziko a není jej třeba monitorovat. Zatímco FBI čelí tlaku, že hrozbu podcenila, příbuzní zastřelených dětí dnes v Parklandu pokračují v pohřbech svých potomků.', 'https://zpravy.idnes.cz/policie-floridskeho-strelce-cruze-vysetrovala-jiz-predloni-florida-strelba-stredni-skola-parkland-vi-i7w-/zahranicni.aspx?c=A180217_212034_zahranicni_lre');
INSERT INTO articles(article_id, title, anotation, url) VALUES (24, 'Opilý Rus zmlátil hasiče, který ho zachránil. Vadilo mu, že ho budí', 'Ruští hasiči někdy musí zápasit nejen s plameny, ale i s lidmi, které z nich mají zachránit. Své o tom ví hasič z Jekatěrinburgu, kterého při zásahu v hořícím domě zbil opilec. Vadilo mu, že ho probudil.', 'https://zpravy.idnes.cz/opily-rus-zmlatil-hasice-ktery-ho-zachranil-vadilo-mu-ze-ho-budi-pw4-/zahranicni.aspx?c=A180217_180101_zahranicni_aha');
INSERT INTO articles(article_id, title, anotation, url) VALUES (25, 'Kosovo zatím nedokázalo, že je životaschopným státem, říká politolog', 'Nejmladší evropský stát slaví desáté narozeniny. Kosovo ale moc důvodů k oslavám nemá. Na mezinárodním poli stále nedosáhlo plného uznání a z hlediska ekonomiky patří mezi nejhorší evropské země. „Spory mezi kosovskými Albánci a srbskou minoritou se stále nepodařilo překonat,“ říká v rozhovoru pro iDNES.cz politolog Přemysl Rosůlek ze Západočeské univerzity v Plzni.', 'https://zpravy.idnes.cz/kosovo-vyroci-nezavislosti-rozhovor-premysl-rosulek-p1v-/zahranicni.aspx?c=A180217_163423_zahranicni_aha');
INSERT INTO articles(article_id, title, anotation, url) VALUES (26, 'ANALÝZA: Do boje o křeslo předsedy ČSSD promlouvá i vyjednávání o vládě', 'Několik hodin před začátkem nedělního sjezdu ČSSD v Hradci Králové se do bojů o vedoucí posty razantně vmísila poziční válka o případnou účast ve vládě Andreje Babiše a jeho hnutí ANO. V posledních dnech tento motiv nabral na dynamice.', 'https://zpravy.idnes.cz/cssd-volba-noveho-predsedy-vliv-vyjednavani-o-vlade-analyza-plc-/domaci.aspx?c=A180217_145746_domaci_lre');
INSERT INTO articles(article_id, title, anotation, url) VALUES (27, 'ZPRÁVY: Šok pro svět i Ledeckou, migranti v kamionu na D5 a Trumpova nevěra', 'Podívejte se na zprávy iDNES.tv, zlatá medailistka z olympijského super-G Ester Ledecká prozradí první dojem po vítězném dojezdu, řekneme vám o zadržených migrantech na D5, dalším vlaku, jehož strojvedoucí projel návěstidlo a řítil se proti expresu s cestujícími i nejnovějších problémech Donalda Trumpa se zveřejněnou nevěrou.', 'https://zpravy.idnes.cz/zpravy-idnes-tv-17-2-2018-0ru-/domaci.aspx?c=A180217_162725_domaci_pist');
INSERT INTO articles(article_id, title, anotation, url) VALUES (28, 'Po zemětřesení v Mexiku spadl vrtulník s ministrem, zemřelo 13 lidí', 'Mohutné zemětřesení o síle 7,2 zasáhlo jižní a centrální části Mexika. Při pokusu o přistání havaroval vojenský vrtulník s mexickým ministrem vnitra a guvernérem státu Oaxaca na palubě. Oba politici pád přežili, havárie si však vyžádala 13 obětí na životech. Mezi mrtvými byly tři děti.', 'https://zpravy.idnes.cz/mexiko-zemetreseni-7-2-stupne-di1-/zahranicni.aspx?c=A180217_072256_zahranicni_ale');
INSERT INTO articles(article_id, title, anotation, url) VALUES (29, 'Ruce k nebi a výraz bolesti. Hrdina slavného snímku z Vietnamu promluvil', 'Během války ve Vietnamu vznikly desetitisíce fotografií. Jen zlomek z nich si však vysloužil kultovní status. Snímek Arta Greenspona zachytil jednotku 101. výsadkové divize krátce po útoku Vietkongu. Jeden z hlavních hrdinů ikonického záběru po mnoha letech mlčení popsal, co vzniku fotografie předcházelo.', 'https://zpravy.idnes.cz/vietnam-slavna-fotografie-dallas-brown-vzpominka-fv1-/zahranicni.aspx?c=A180212_153700_zahranicni_ert');
INSERT INTO articles(article_id, title, anotation, url) VALUES (30, 'VIDEA TÝDNE: Pád ruského letounu, otravní taxikáři a nešikovní lupiči', 'Autentické video z letecká havárie, při které se zřítilo letadlo, otřásla Ruskem. A trápení řidiče tramvaje, který zažil vybržďování taxikářem během protestů v Praze. Právě tato videa byla diváky videoobsahu iDNES.cz nejsledovanější během tohoto týdne. Mezi ty další úspěšné patří také snímek, na kterém jsou dva zloději, kteří se vlastní vinou k loupeži ani nedostali.', 'https://zpravy.idnes.cz/letoun-pad-taxikari-protesty-nesikovni-lupici-fls-/domaci.aspx?c=A180217_163814_domaci_stan');
INSERT INTO articles(article_id, title, anotation, url) VALUES (31, 'KOMENTÁŘ: Lze si v Česku opravdu objednat něčí trestní stíhání?', 'Premiér Andrej Babiš nedávno šokoval slovy, že si v Česku můžete objednat trestní oznámení a někoho dostat za mříže. Zní to děsivě. Existují však některé případy, které se řešily velmi podivně, uvádí advokátka a bývalá politička Hana Marvanová.', 'https://zpravy.idnes.cz/trestni-stihani-andrej-babis-hana-marvanova-komentar-analyza-pt3-/domaci.aspx?c=A180213_382625_domaci_hm1');
INSERT INTO articles(article_id, title, anotation, url) VALUES (32, 'KOORDINÁTORKA SVATEB: Čím ztřeštěnější, tím lepší. Striptýz z rozluček mizí', 'K zařizování svateb se Veronika Šedivá dostala poté, co s kamarádkami nad vínem přemýšlely, jak využít tvůrčí přetlak. Jejich veselky jsou netypické, přírodní a vílí. A umí je uspořádat třeba i v továrně. „Baví nás, když lidé mají originální nápady,“ říká koordinátorka v rozhovoru pro seriál Lidé Česka.', 'https://zpravy.idnes.cz/veronika-sediva-svatebni-koordinatorka-lide-ceska-fsw-/lide-ceska.aspx?c=A180210_214337_lide-ceska_amu');
INSERT INTO articles(article_id, title, anotation, url) VALUES (33, 'Na Svitavsku zemřel řidič auta. Nezvládl řízení a narazil do stromu', 'V sobotu odpoledne mladý řidič nezvládl řízení na Svitavsku a s autem narazil do stromu. Muž na místě zemřel. Obdobná nehoda se stala i dopoledne na Orlickoústecku, tam žena vyvázla se zraněním. U ní policisté naměřili přes půl promile alkoholu.', 'https://pardubice.idnes.cz/nehoda-auto-strom-smrt-brezova-dm2-/pardubice-zpravy.aspx?c=A180217_144455_pardubice-zpravy_bse');
INSERT INTO articles(article_id, title, anotation, url) VALUES (34, 'Merkelová žádá státy EU o solidaritu, Juncker chce rychlejší rozhodování', 'Německá kancléřka Angela Merkelová vyzvala státy Evropské unie k pokroku v dohodě o azylovém systému a varovala, že státy očekávající finanční podporu unie musejí v řešení migračního problému projevit solidaritu. Podle šéfa Komise Jeana-Claudea Junckera musí EU rozhodovat rychleji a jednodušeji, aby mohla dělat světovou politiku.', 'https://zpravy.idnes.cz/merkelova-chce-solidarita-migracni-problem-vyzvala-staty-eu-pra-/zahranicni.aspx?c=A180217_152013_zahranicni_lre');
INSERT INTO articles(article_id, title, anotation, url) VALUES (35, 'Psa se zbavte, řekli jí na úřadě. Dům pro seniory přitom zvířata nezakázal', 'Po dvanácti letech čekání se Evě Janků podařilo získat místo místo v domě s pečovatelskou službou. Nicméně radost ze zprávy vzápětí vystřídalo zoufalství ze ztráty milovaného psa. Na městském úřadě jí řekli, že si s sebou Harika vzít nemůže. Vedení města ale trvá na tom, že jí nikdo psa nezakázal.', 'https://zpravy.idnes.cz/senior-dum-s-pecovatelskou-peci-hluboky-lidsky-pribeh-zraneni-odluka-zvire-1br-/domaci.aspx?c=A180212_103952_domaci_bja');
INSERT INTO articles(article_id, title, anotation, url) VALUES (36, 'Jaro ještě nepřijde. Jihozápad zasype sníh, do konce února bude mrznout', 'V Česku začne v sobotu odpoledne od jihozápadu sněžit. V jižní polovině území může do nedělního rána napadnout místy až deset centimetrů sněhu, na Šumavě až 15 centimetrů. Meteorologové také vydali měsíční výhled počasí na následující čtyři týdny, budou teplotně i srážkově průměrné až slabě podprůměrné.', 'https://zpravy.idnes.cz/pocasi-predpoved-vystraha-snezeni-snih-fc6-/domaci.aspx?c=A180217_115119_domaci_hm1');
INSERT INTO articles(article_id, title, anotation, url) VALUES (37, 'Trollí farma a žoldáci v Sýrii. „Putinův kuchař“ se špinavé práce neštítí', 'Třináct Rusů obviněných v USA z vměšování do prezidentských voleb pracovalo v ruské Agentuře pro výzkum internetu, která provozuje nechvalně známou petrohradskou trollí farmu. Její ústřední postavou je bývalý prodavač hot dogů Jevgenij Prigožin, který zbohatl díky kontaktům s Vladimirem Putinem.', 'https://zpravy.idnes.cz/jevgenij-prigozin-putinuv-kuchar-rusko-usa-trrolli-farma-p36-/zahranicni.aspx?c=A180217_111933_zahranicni_aha');
INSERT INTO articles(article_id, title, anotation, url) VALUES (38, 'OBRAZEM: Francouz zavrhl moderní technologie, pole zase orá s voly', 'Francouzský farmář Jean-Bernard Huon už před padesáti lety zavrhl nastupující moderní technologie a na svém hospodářství používá tradiční techniky zemědělců z dob jeho dědy. V Evropě tak je raritou. „Jsem šťastný outsider,“ říká muž, který si však na stará kolena přece jen dovolil pár úlev. Koně například vyměnil za voly a koupil si dva traktory.', 'https://zpravy.idnes.cz/francie-farmar-obrazem-moderni-technologie-navrat-k-tradicim-p5l-/zahranicni.aspx?c=A180216_095606_zahranicni_kha');
INSERT INTO articles(article_id, title, anotation, url) VALUES (39, 'Muž spadl z druhého patra nákupního centra v Praze, v nemocnici zemřel', 'Pražští záchranáři v pátek večer oživovali mladého muže, který bez cizího zavinění spadl z druhého patra v nákupním centru na Flóře. Podle informací iDNES.cz muž později mnohačetným zraněním v nemocnici podlehl.', 'https://praha.idnes.cz/pad-muze-z-vysky-flora-praha-nakupni-centrum-f3k-/praha-zpravy.aspx?c=A180217_103656_praha-zpravy_bse');
INSERT INTO articles(article_id, title, anotation, url) VALUES (40, 'Rentgen zubů má pomoci identifikovat utonulou ženu nalezenou v Pardubicích', 'Policisté stále neznají totožnost utonulé ženy, kterou téměř před rokem nalezli pod zdymadlem na Labi v Pardubicích. Již dříve kriminalisté uvedli, že neznámá dobře pečovala o svůj chrup. Nyní zveřejnili rentgenový snímek jejích zubů a obrací se především na zubaře, zda nepoznají svojí práci a tím by pomohli ztotožnit mrtvou ženu.', 'https://pardubice.idnes.cz/utonula-zena-pardubice-rentgen-zubu-d7q-/pardubice-zpravy.aspx?c=A180217_112902_pardubice-zpravy_bse');
INSERT INTO articles(article_id, title, anotation, url) VALUES (41, 'Nádor oka prozradí fotka s bleskem. Nemocí trpí nejmladší děti', 'Onemocnět nádorem oka mohou i několikaměsíční miminka. Jedná se o retinoblastom, který se projevuje šilháním, ale hlavně je dobře poznat na snímcích vyfocených bleskem, kde se zornička zbarví do bíla. Na tento typ nádoru upozorňuje letošní Den dětské onkologie.', 'https://zpravy.idnes.cz/nador-oko-retinoblastom-amelie-den-detske-onkologie-fy3-/domaci.aspx?c=A180216_142840_domaci_ale');
INSERT INTO articles(article_id, title, anotation, url) VALUES (42, 'Máme věřit činům, nebo tweetům? Gabriel se ostře navezl do Trumpa', 'Němci si už nejsou jistí, jestli při pohledu přes Atlantik ještě poznávají svoji Ameriku. Na bezpečnostní konferenci v Mnichově to prohlásil německý ministr zahraničí Sigmar Gabriel. Theresa Mayová premiérka ujišťovala, že Británie i po brexitu bude s bezpečnostními složkami států EU sdílet zpravodajské informace.', 'https://zpravy.idnes.cz/sigmar-gabriel-donald-trump-mnichov-konference-fr0-/zahranicni.aspx?c=A180217_100736_zahranicni_aha');
INSERT INTO articles(article_id, title, anotation, url) VALUES (43, 'PŘEHLEDNĚ: Bývalý „špion“, knihovník i známé tváře. Kdo chce šéfovat ČSSD?', 'Sociální demokraté si v neděli zvolí nového předsedu. A zatímco u jiných stran byla v poslední době volba šéfa formalitou s předem známým koncem, u ČSSD se čeká napínavější souboj. Restartovat jednu z nejstarších českých stran po podzimním volebním debaklu chtějí jak známé tváře, tak neokoukaní straníci z komunální politiky.', 'https://zpravy.idnes.cz/politika-kandidati-predseda-cssd-sjezd-dyl-/domaci.aspx?c=A180212_084734_domaci_jkk');
INSERT INTO articles(article_id, title, anotation, url) VALUES (44, 'PEREME SE S ČEŠTINOU: Psaní i/y po souhláskách obojetných (1. díl)', 'Český pravopis je obtížný, ale není nenaučitelný. Připravili jsme pro vás proto seriál, ve kterém probereme klíčové jevy. Kurz je určen jak žákům, které na jaře čekají přijímací zkoušky, tak i komukoli jinému, kdo si chce ověřit své znalosti.', 'https://zpravy.idnes.cz/cestina-pereme-se-s-cestinou-pravopis-diktat-test-vyjmenovana-slova-1iu-/domaci.aspx?c=A171123_120354_domaci_mbb');
INSERT INTO articles(article_id, title, anotation, url) VALUES (45, 'Hlásí se další Trumpovy milenky. Playmate prozradila, jak tutlal zálety', 'Po bývalé pornoherečce Stephanii Cliffordové se začínají ozývat další ženy tvrdící, že měly sexuální styk s Donaldem Trumpem. Karen McDougalová magazínu The New Yorker popsala systém, díky němuž Trump údajně úspěšně ukrýval svou nevěru. Prezident podle svého mluvčího odmítl, že by s někdejší modelkou časopisu Playboy cokoli měl.', 'https://zpravy.idnes.cz/donald-trump-prezident-stephanie-cliffordova-karen-mcdougalova-p8o-/zahranicni.aspx?c=A180217_092109_zahranicni_aha');
INSERT INTO articles(article_id, title, anotation, url) VALUES (46, 'KOMENTÁŘ ministra Vojtěcha: Němeček prostě špatně hospodařil', 'Exministr zdravotnictví Svatopluk Němeček, jehož jsem odvolal z čela Fakultní nemocnice Ostrava, si zase vymýšlí. Nejde samozřejmě z mé strany o žádnou politickou mstu a odvolání mu domů osobně přinesli zaměstnanci ministerstva, takže o něm neví z televize.', 'https://zpravy.idnes.cz/adam-vojtech-svatopluk-nemecek-diskuze-nemocnice-ostrava-odvolani-1d9-/domaci.aspx?c=A180217_082130_domaci_ale');
INSERT INTO articles(article_id, title, anotation, url) VALUES (47, 'Policie zbrojí na teroristy: chybí výcvik, výstroj i náboje, zlobí se kritici', 'Na případné setkání s teroristou či aktivním střelcem jsme připraveni, hlásí vedení policejního prezidia. Kritici z policie i mimo ni ovšem namítají, že v krizové situaci by se mohl projevit nedostatečný výcvik. Poukazují také na problémy s cvičnou municí či výstrojí.', 'https://zpravy.idnes.cz/policie-finance-nakupy-munice-vycvik-boj-proti-terorismu-pmr-/domaci.aspx?c=A180213_090708_domaci_mav');
INSERT INTO articles(article_id, title, anotation, url) VALUES (48, 'Kábulský smog trápí vojáky a zhoršuje viditelnost na letišti', 'Je to jako rána smradlavým hadrem přes obličej. Přichází náhle, ale pro personál na letišti v afghánském Kábulu už ne nečekaně. Během několika desítek minut zahalí celou rušnou oblast letiště nepříjemná smogová vlna z rozvíjející se metropole. Omezuje lety a trápí místní obyvatele i zahraniční vojáky, včetně těch českých.', 'https://zpravy.idnes.cz/kabul-smog-afghanistan-vojak-nemoc-dychaci-meteo-fua-/zpr_nato.aspx?c=A180216_180554_zpr_nato_inc');
INSERT INTO articles(article_id, title, anotation, url) VALUES (49, 'Kosovská Nezávislost slaví 10. narozeniny. Se Srby si nehraju, říká dívka', 'Psal se 17. únor 2008, když parlament v Prištině jednohlasně podpořil deklaraci o vyhlášení nezávislosti na Srbsku. Téhož dne se v Kosovu narodilo dítě. Holčičce dali jméno Pavaresia, což albánsky znamená Nezávislost. V neděli oslaví desáté narozeniny, píše agentura AFP.', 'https://zpravy.idnes.cz/kosovo-nezavislost-desate-vyroci-dite-dyl-/zahranicni.aspx?c=A180215_134425_zahranicni_aha');
INSERT INTO articles(article_id, title, anotation, url) VALUES (50, 'Tyran z Krupky bude ve vězení o půl roku déle kvůli napadení přítelkyně', 'Nikolas Bady známý jako „tyran z Krupky“ si vyslechl další rozsudek. Dvaadvacetiletý násilník, jenž si v současné době odpykává tříletý trest za oloupení dvou mladíků, bude ve vězení o půl roku déle. Pobyt za mřížemi si prodlouží kvůli napadení bývalé přítelkyně. Verdikt je pravomocný.', 'https://usti.idnes.cz/soud-nikolas-bady-usti-nad-labem-napadeni-zeny-tyran-z-krupky-ptz-/usti-zpravy.aspx?c=A180216_130502_usti-zpravy_vac2');
INSERT INTO articles(article_id, title, anotation, url) VALUES (51, 'Tři mrtví v tichém domě. Rodina ze Zlína měla podivné zvyky, říkají místní', 'Jak zemřeli tři členové jedné rodiny nalezení v pronajatém domě ve Zlíně, je stále nejasné. Sousedé mluví o rodině podivínů, kteří se stranili lidí, a někteří se domnívají, že mohlo jít o členy sekty, kteří spáchali hromadnou sebevraždu.', 'https://zpravy.idnes.cz/zlin-tri-mrtvi-vrazda-sebevrazda-dqd-/krimi.aspx?c=A180216_172719_krimi_ane');
INSERT INTO articles(article_id, title, anotation, url) VALUES (52, 'USA obvinily Rusy z vměšování se do voleb, Trump se od nich distancuje', 'Spojené státy obvinily třináct Rusů a tři ruské subjekty z ovlivňování amerických prezidentských voleb v roce 2016, oznámil šéf vyšetřovatelů Robert Mueller. Trump uznal, že se Rusko vměšovalo do americké politiky před prezidentskými volbami, dodal však, že tým vedoucí jeho kampaň s tím nemá nic společného.', 'https://zpravy.idnes.cz/usa-obvineni-rusko-vmesovani-do-americkych-prezidentskych-voleb-soud-1z0-/zahranicni.aspx?c=A180216_192016_zahranicni_dtt');
