INSERT INTO comments VALUES (265, 'Tomáš Brabec', 'Aspoň se nauč česky Koblihu jsi už dnes měl?', 2);
INSERT INTO comments VALUES (266, 'Pavel Ján Buvala', 'Babiš nebyl z dělnické třídy, neměl rukodělný původ. Postaral se o své dvě rodiny, vybudoval slušné firmy. Nebil žádný troškař, restituent ani parazit lidské společnosti . Uměl, umí a bude umět postarat se i o stát. Lidi to vědí a proto ANO pořád více a více jich volí a bude volit! Kdo umí - umí; kdo neumí - čumí anebo jako mnozí obvykle ale kecááááááááá!', 2);
INSERT INTO comments VALUES (267, 'Ivo F. Cilich', 'V roce 1946 ještě byla ... a ten postup ANO je nyní docela podobný', 2);
INSERT INTO comments VALUES (268, 'Jaroslav Kubín', 'Ať je mi prominuto, ale nezdá se Vám titulek článku na Forum 24 "Nemocný premiér povečeřel s nemocným prezidentem. Na to jsou zdraví dost" naprosto debilní?? Kam až ta zloba a nenávist některých novinářů dojde daleko? Někteří páni novináři a dámy novinářky, chtělo by to obyčejnou lidskou slušnost a nadhled k tomu, že v demokratické společnosti může mít někdo i jiné názory, než o kterých si ve své zalepenosti myslíte, že jen ty Vaše jsou nejsprávnější!', 2);
INSERT INTO comments VALUES (269, 'Daniel Novák', '"Čapí hnízdo" je z roku 2008. ANO 2011 ve vláde 2013 - 2017.', 2);
