INSERT INTO comments VALUES (1045, 'Martin Nevrla', 'Jako bývalý volič ČSSD pláču. Proboha pošlete už Zemana do ... pryč. Bude tam furt vrtat a vrtat až vás docela rozvrtá a ještě se u toho chechtá.', 4);
INSERT INTO comments VALUES (1046, 'Jakub Kučera', 'Solidarita je jedna z důležitých věcí demokracie, jinak většinou nefunguje.', 4);
INSERT INTO comments VALUES (1047, 'Jaroslav Bedrna', ':D', 4);
INSERT INTO comments VALUES (1048, 'Alex Slavíček', 'mate neco mene omleteho?', 4);
INSERT INTO comments VALUES (1049, 'Jiří Horký', 'Vám fakt nestačilo, jak s váma teď andrej vy...al? Vy mu půjdete dělat poskoka znovu? No jo, socani.', 4);
INSERT INTO comments VALUES (1050, 'Alex Slavíček', 'ja jen nevolil ale nikdy bych nic takoveho nepriznaval.', 4);
INSERT INTO comments VALUES (1051, 'Jarda Hofmann', 'oni zaplatili dluhy ?', 4);
INSERT INTO comments VALUES (1052, 'Petr Martíšek', 'Mě fascinuje, jak si někdo může myslet, že když se jinému daří a pracuje, tak je povinen odvádět ještě vyšší daně a ten, kdo nesáhne na práci, tak tomu nasypeme ze společného zdarma. Jak toto vůbec může někdo prosazovat, nepochopím..', 4);
INSERT INTO comments VALUES (1053, 'Jarda Hofmann', 'že dluhy se opravdu platí', 4);
INSERT INTO comments VALUES (1054, 'Alex Slavíček', 'zasadni omyl je vymezovat se vuci ANO andreje. nepochopitelne', 4);
INSERT INTO comments VALUES (1055, 'Josef Klat', 'Co platím pane?Za každé procento ve volbách dostávají nemalé peníze ze “státního”.My si tykáme?', 4);
INSERT INTO comments VALUES (1056, 'Ola Nováková', '..konečně se probrali???', 4);
INSERT INTO comments VALUES (1057, 'Jiří Krutil', 'tedy přesně 602 000 000 miliónů.', 4);
