INSERT INTO comments VALUES (1060, 'Jakub Kučera', 'Hehe.', 4);
INSERT INTO comments VALUES (1061, 'Karel Jedlička', 'Ano, že někdo neukradl nic, se dá definovat i tak, že nakradl nejmíň', 4);
INSERT INTO comments VALUES (1062, 'Jakub Kučera', 'Nikde jsem nenapsal, že za 14 dnů budou ok, ale že se za 14 dní uvidí. Fakt čteš správně?', 4);
INSERT INTO comments VALUES (1063, 'Ola Nováková', 'Do háje jděte Vy', 4);
