INSERT INTO comments VALUES (4128, 'František Welek', 'Ještě se omlozvám. První Libanonská válka nebyla kvúli Hisballahu, který vznikl až někdy v roce 82, ale kvůli Organizaci pro osvobozeni Palestiny (OOP)', 14);
INSERT INTO comments VALUES (4129, 'Eva Zavacka', '', 14);
