INSERT INTO comments VALUES (4642, 'Josef Němeček', 'Snad jen, že byl Kalousek někde v řece.', 15);
INSERT INTO comments VALUES (4643, 'Jan Hažmuka', 'Celý NY mu tleskal vestoje.', 15);
INSERT INTO comments VALUES (4644, 'Robert Máslo', 'Pro mě už bohužel ne .', 15);
INSERT INTO comments VALUES (4645, 'Jan Hora', '', 15);
INSERT INTO comments VALUES (4646, 'Jan Hažmuka', 'Va mete va mete -', 15);
INSERT INTO comments VALUES (4647, 'Oldřich Kaděra', 'Babiš byl pravomocně označen za vědomého agenta StB. A všichni dělají, jako by nic !!! Pro mne je to docela divné.', 15);
INSERT INTO comments VALUES (4648, 'Jan Hora', 'Jiří Langweile, mohl bys odpovědět za co bude dle tebe souzen Kalousek ? Když řekneš A měl bys také říci B.... Pro připomenutí tvůj příspěvek: "Kalousek bude jednou stejně souzen jako jsou dnes komunističtí pafuňáři ... pozdě ,ale řece..."', 15);
INSERT INTO comments VALUES (4649, 'Martin Kříž', 'Je to hodně devótní...', 15);
INSERT INTO comments VALUES (4650, 'Robert Máslo', 'Tak voliči ČSL překousli i 40 let trvající koalici s komunisty.', 15);
INSERT INTO comments VALUES (4651, 'Josef Němeček', 'Potíž je v tom, že ve světě Zeman nikoho nezajímá. Tedy...občas Putina.', 15);
INSERT INTO comments VALUES (4652, 'Rostislav Caha', 'Va mete velmi omezeny rozhled... Mimochodem, jeden, oznacovany za jeden z nejlepsich, jsme uz slyseli v Praze. Ten co je pronesl, za nej vzapeti dostal Nobelovu cenu miru. A nasledne se ukazalo, ze to taky byly jen plky....', 15);
INSERT INTO comments VALUES (4653, 'Martin Kříž', 'Pozice Jakesova proslovu z Červeného Hrádku se otřásá. :-)', 15);
INSERT INTO comments VALUES (4654, 'Miloš Dvořáček', 'Prezident sjednocovac. Tak to nam jeste tak chybelo.', 15);
INSERT INTO comments VALUES (4655, 'Karel Stix', 'Co na něm konkrétně bylo tak skvělého, že dle Vás překonává i Roznerovy promluvy?', 15);
INSERT INTO comments VALUES (4656, 'Pavel Řežábek', 'To je naprosto příšerný....Je jasný, že Zeman chce dotáhnout ČSSD do vlády, aby mohl tahat nitkami na jednotlivých ministerstvech...Jasný pakt, Babiš podpoří Zemana při volbě prezidenta, Zeman mu to oplatí, že mu dá velký prostor pro sestavení vlády, aby se stihl tento sjezd ČSSD a mohl Zeman dotlačit ČSSD do vlády společně s ANO Fakt hnus', 15);
INSERT INTO comments VALUES (4657, 'Pavel Sobotka', 'Konec starých stran - nová generace má nové lídry a nové strany, které volí', 15);
INSERT INTO comments VALUES (4658, 'Zuzana Erbenová', 'Morální dno udělovalo rady. Zeman je je nejodpornější figura české politiky. Podporovatel estébáků, lhář a ...', 15);
INSERT INTO comments VALUES (4659, 'Jakub Dušánek', 'Oproti Gottwaldovi a Stalinovi slabé.', 15);
INSERT INTO comments VALUES (4660, 'Jan Hažmuka', 'Jeden z nejlepších politických projevů světové politiky.', 15);
INSERT INTO comments VALUES (4661, 'Jakub Dušánek', 'Lhář ví, že lže, posluchači také, ale tváří se, že to nevědí, což lhář samozřejmě ví. Je to taková ta klasická zkouška, zda se h... udrží na hlavě. A pokud iniciační obřad dopadne dobře, ke korýtku se dostanou všichni.', 15);
INSERT INTO comments VALUES (4662, 'Radim Sládek', 'To nevymyslíš,největší podrazák a bodač do zad, vyzývá ostatní, aby se nechovali jako on. To si ho tam pozvali jako odstrašující příklad?', 15);
INSERT INTO comments VALUES (4663, 'Jan Hora', 'Bohužel, ČR žádného pana prezidenta nemá, tudíž tam nikdo takový nemluvil. Mluvil tam však Zeman, který v současnosti vykonává funkci prezidenta a mlel tam kecy přímo úměrné jeho povaze...', 15);
INSERT INTO comments VALUES (4664, 'Karel Pivrnec', 'jo, je to v takovém plazícím se Ovčáčkově duchu', 15);
INSERT INTO comments VALUES (4665, 'Jan Zenkl', 'Obnova vlivu? Vykopejte duch Fierlingera, vzpomeňte slovanské vzájemnosti, připomeňte, že před rokem 1989 nebylo všechno špatné a zaútočte na drobné podnikatele, původce všeho zla. Vždyť hájíte zájmy obyčejných a poctivých lidí', 15);
INSERT INTO comments VALUES (4666, 'Karel Stix', 'I přesto by měli být scopní říct, co na tom projevu bylo tak skvělého', 15);
INSERT INTO comments VALUES (4667, 'Rostislav Caha', 'to, ze opakuje ohrane trapne vtipy, kterym se smeje uz jen Ovcacek, Vam nevadi? To, ze si vymysli vic, nez kdokoliv jiny, snad jen Babis mu muze konkurovat, taky nevadi?', 15);
INSERT INTO comments VALUES (4668, 'Petra Potůčková', 'Tak jsou to jediné dvě demokratické a také tradiční strany. Byla by škoda vládu ANO nekontrolovat. Nechcete snad ODS nebo TOP?', 15);
INSERT INTO comments VALUES (4669, 'Petra Potůčková', 'Nebyl to spánek?', 15);
INSERT INTO comments VALUES (4670, 'Rostislav Caha', 'ono je jich tam vic', 15);
INSERT INTO comments VALUES (4671, 'Martin Kříž', 'Ovčáček hacknul Burešův twitter!', 15);
INSERT INTO comments VALUES (4673, 'Jaroslav Kyselák', 'Alespoň u Miloše', 15);
INSERT INTO comments VALUES (4674, 'Vladimír Koudelka', 'Právě jsem se vrátil... ze zimního běhu, tak nevím: jmenoval pan prezident ve svém projevu také pana Okamuru?', 15);
INSERT INTO comments VALUES (4675, 'Petr Petr', 'Jojo, zmoudřeli a nechtějí mít nic společného s postbolševickými stranami...a proto si na hrad zvolili člověka, který ty strany po revoluci vedl a může za jedno z nejtemnějších období po revoluci (období opoziční smlouvy) to je logika fakt', 15);
INSERT INTO comments VALUES (4676, 'Jaroslav Kyselák', 'Vaš je mrtev, ať žije Langweil.', 15);
INSERT INTO comments VALUES (4677, 'Petra Potůčková', 'ČSSD a KDU se do vlády nakonec nějak vecpe.', 15);
INSERT INTO comments VALUES (4678, 'Rostislav Caha', 'A kdyz zvoli Zimolu, tak taky', 15);
INSERT INTO comments VALUES (4679, 'Rostislav Caha', 'Kalousek je uz 5. rokem v opozici a ani Babis na nej nenasel nic, za co by ho mohli stihat', 15);
INSERT INTO comments VALUES (4680, 'Lucie Součková', '... a teď radí', 15);
INSERT INTO comments VALUES (4681, 'Jaromír Procházka', 'Je opravdu sranda,jak tu v diskuzi na pravicovém idnesu radí čssd a kritizují prezidenta.Pokud si nezvolí Zimolu tak to můžou sbalit.', 15);
INSERT INTO comments VALUES (4682, 'Martin Kříž', 'Strůjce legendární lánské schůzky nyní nabádá ty, proti kterým intrikoval, aby nebyli podrazáky. To nevymyslíš.', 15);
INSERT INTO comments VALUES (4683, 'Jan Hora', 'Když říká Zeman, že je ČSSD stranou podrazáků, je to vlastně jako když zloděj křičí, chyťte zloděje....', 15);
INSERT INTO comments VALUES (4684, 'Rostislav Caha', 'Co si vychoval, to v CSSD bylo', 15);
INSERT INTO comments VALUES (4685, 'Josef Babka', 'Asi to byl projev pro ně ne pro binky', 15);
INSERT INTO comments VALUES (4686, 'Rostislav Caha', 'retor, nebo zvanil? Byt dobry retor znamena mimojine to, ze si nevymyslim.', 15);
INSERT INTO comments VALUES (4687, 'Aleš Kožíšek', 'Napr.dokaze nefrazovite popsat aktualni stav politiky,nerika floskule jako vas Sobotka nebo Kalousek:)', 15);
INSERT INTO comments VALUES (4688, 'Jaroslav Kyselák', 'Takovému podvodu může naletět jen naprostý ale naprostý "slepec". On ten projev, nebo jeho hlavní body měl napsané na papíře, které byly na pultíku vlevo dole. Přesně pod tím jeho zase tikajícím okem, Langweile. Dobré bylo vidět, že se dnes smál těm jeho ubohým bonmotům jen on sám. Jeho naučené fráze mohli nahrát na magnetofon a ušetřit si pohled na hodně nemocného člověka. Jen to, jakým způsobem se snažil "tlačit" do vedení strany svého oddaného poskoka Zimolu, ukazuje jak podceňuje inteligenci občanů této země. Na štěstí alespoň čas pracuje na nápravě.', 15);
INSERT INTO comments VALUES (4689, 'Tomáš Vlasák', 'Ten tweet Burešovi snad nadiktoval sám viceprezident Ovec, ne? :-)', 15);
INSERT INTO comments VALUES (4690, 'Jan Hora', 'To bude nějaká forma babištiny...', 15);
INSERT INTO comments VALUES (4691, 'Marek Valiček', 'Už jen oliznout rit a s maškarní poděkovat. To vše na kolenou. Je to fakt sajrajt.', 15);
INSERT INTO comments VALUES (4692, 'Petra Potůčková', 'Hodně suchá, žádná osobnost, snad krom Pirátů tam nevidím nic, co by stálo za řeč.', 15);
INSERT INTO comments VALUES (4693, 'Jakub Dušánek', 'Pardon, mělo být mínus. Zeman byl vždycky egomaniak, rád se poslouchal. Jeho projevy jsou sérií bonmotů, ale fakticky skoro nikdy nic neřekne. Vše bývá překroucené a řečené je jen to, co se mu hodí. Kvůli své povaze taky nikdy neměl skutečné přátele.', 15);
INSERT INTO comments VALUES (4694, 'Lucie Součková', 'S tímhle kádrovým složením je těžké nebýt stranou podrazáků, nikdo jinej tam nezbyl', 15);
INSERT INTO comments VALUES (4695, 'Rostislav Caha', 'ano "sucha je skyva opozice", zadne tunelovani statu, zadne trafiky... Milos o tom neco musi vedet', 15);
INSERT INTO comments VALUES (4696, 'Otakar Vlasák', 'Jediné podrazy, který si Miloš Zeman totiž pamatuje, jsou ty na něj - a hlavně ten, kdy jeho vlastní strana dala při volbě prezidenta přednost Klausovi. Jeho vlastní podrazy, všechny ty Olova, Bambergy, povolební spiklenecké schůzky v Lánech, to nebyly podrazy, ale jeho pokusy jak spasit ČSSD, kterou vždycky jednou za čas proklel a zavrhnul, aby jí pak rozdělil a znovu začal oživovat pod svým prstenem moci.', 15);
INSERT INTO comments VALUES (4697, 'Martin Svoboda', 'Nevím, jestli ten Babisuv příspěvek na Twittru není nějaká humorná vsuvka.', 15);
INSERT INTO comments VALUES (4698, 'Bohuslav Binka', 'Zajímavé je, že podporovatelé Zemana nedokážou říct v čem konkrétně byl ten projev "skvělý", "inspirativní", "úžasný", "poučný", pouze, že byl "skvělý" atd. Chápu, že ho mají rádi, ale když se o jeho úžasnosti chtějí podělit, měli by je alespoň naznačit či vypsat. Jenže to jde dost těžko co? Pro mě to byl projev člověka co se ani po 15 letech nedokáže přenést přes (ať už domělou či skutečnou) zradu a co na sjezdu jedné strany kope za tu konkurenční. A to mi ani inspirativní, ani poučné moc nepřijde.', 15);
INSERT INTO comments VALUES (4699, 'Jan Hora', 'Které komunisty máš na mysli? A za co dle tebe bude Kalousek souzen, za to, že s ním ty a tobě podobní nesouhlasíte, za to že říká to co se vám nelíbí?', 15);
INSERT INTO comments VALUES (4700, 'Marek Valiček', 'Pravdou pro zaslepene ovce', 15);
INSERT INTO comments VALUES (4701, 'Jakub Dušánek', 'Připomněl jste mi ten vtip, jak přijde pacient k lékaři se zápachem z úst a ten doporučí pro začátek třikrát denně kloktat ho... Asi tak je to s tou vaší pochoutkou.', 15);
INSERT INTO comments VALUES (4702, 'Marek Valiček', 'Mladý, plný síly, vtipný a navíc sjednotitel. Další díl do Starých pověstí českých..', 15);
INSERT INTO comments VALUES (4703, 'Jan Hora', 'Osobně bych neviděl smrtící úder pro ČSSD jako něco s čím bych nesouhlasil. Ale v této době, kdy jsou na vzestupu netradiční formy politiky, bych to neviděl jako účelné. Proto říkám bůh chraň ČSSD před Zemanem....', 15);
INSERT INTO comments VALUES (4704, 'Otakar Fuka', 'To není odpověď, ani tip vám nevyšel, ale ta velice zúžená logika leccos napovídá', 15);
INSERT INTO comments VALUES (4705, 'René Burkoň', 'Nebudte stranou podrazaku, rekl Zeman. A Chovanec chytil barvu prave vytazeneho raka z vrouci vody', 15);
INSERT INTO comments VALUES (4706, 'Martin Kříž', 'ČSSD se musí od Zemana definitivně odstřihnout. Jeho ješitnost a pomstychtivost stranu vytrvale sráží, ale někteří jsou zřejmě masochisti a chtějí zas a znova.', 15);
INSERT INTO comments VALUES (4707, 'Šárka Staňková', 'Zeman má naučených pár lží které neustále opakuje do kola! Stokrát opakovaná lež se stává pravdou!', 15);
INSERT INTO comments VALUES (4708, 'Miroslav Kašpar', 'Jistě, až co řekne předseda', 15);
INSERT INTO comments VALUES (4709, 'Jiří Langweil', 'Kalousek bude jednou stejně souzen jako jsou dnes komunističtí pafuňáři ... pozdě ,ale řece...', 15);
INSERT INTO comments VALUES (4710, 'Pavel Sobotka', 'To víte, Češi...', 15);
INSERT INTO comments VALUES (4711, 'Jiří Langweil', 'Jsem překvapen kdo všechno nesouhlasí s tím , že na Zemanovi je znát jeho věk . Tvrdé jádro antizeman asi mínusy nepochopilo hlavní myšlenku mého příspěvku nebo fungoval jen rudý hadr Zeman ?', 15);
INSERT INTO comments VALUES (4712, 'Miroslav Kašpar', 'Koalice ANO s ČSSD s tím že by socani dostali místa náměstků ministrů, tedy menšinová vláda ANO. Z pozice náměstka sice nic neprosadí ale korejtka. Od Zemana je to pro ČSSD jednoznačně polibek smrti, inu "nadstranický" prezident', 15);
INSERT INTO comments VALUES (4713, 'Jakub Dušánek', 'To je česky?', 15);
INSERT INTO comments VALUES (4714, 'Jan Hora', 'Jj, zvláště když použije jako vzor odpudivého předsedy Kalouska, který již není předseda, Kdepak za vším hledej Kalouska, to už je stihomama....', 15);
INSERT INTO comments VALUES (4715, 'Vojtěch Růžek', 'Další vládní angažmá je právě to, co by ČSSD zasadilo poslední, smrtící úder. Zvláště kdyby žádný reálný vliv neměli. Andrej by si ale našel způsob, jak na ČSSD shodit všechny chyby. Potřebují pár let sebereflexe a rozjímání v opozici, jako předtím ODS.', 15);
INSERT INTO comments VALUES (4716, 'Jan Hora', 'PAN je víc než pan?', 15);
INSERT INTO comments VALUES (4717, 'Martin Kříž', 'Nejlepší retori jsou v SPD.', 15);
INSERT INTO comments VALUES (4718, 'Marie Zelená', 'Jestli on se náhodou nepozval sám, jak to dělá při svých návštěvách krajů...', 15);
INSERT INTO comments VALUES (4719, 'Jiří Langweil', 'Zeman je stále nejlepší rétor v této zemičce a jeho pověstné projevy zpaměti jsou pro nezaujatého posluchače pochoutkou . Přes to je znát jeho věk .', 15);
INSERT INTO comments VALUES (4720, 'Jan Hora', 'Jistě', 15);
INSERT INTO comments VALUES (4721, 'Martin Kříž', 'Zatím. Trošku.', 15);
INSERT INTO comments VALUES (4722, 'Petr Petr', 'Sorry, Babiše jsem nevolil a raději zloděje, který v politice ještě nebyl a nevíme, co tam bude dělat než lidi, kteří v politice jsou už roky a máme 100% jistotu, že tam budou jenom krást. Sice jsem naivní ale pořád je Babiš pro spoustu lidí větší naděje než ti, kteří tam doteď byli a nedělali nic.', 15);
INSERT INTO comments VALUES (4723, 'Ola Nováková', 'Nic lepšího nevymyslí ?? Už je to ohrané a hloupé!', 15);
INSERT INTO comments VALUES (4724, 'Antonín Katzer', 'Však on to posléze zelinář pohřbil a jedou dál ve stranických kolejích.Tak že se sebereflexe podrazáků nekoná a jedou starou,tvrdou,podrazáckou linii!!!Co taky čekat od podrazáka a korytáře!!', 15);
INSERT INTO comments VALUES (4725, 'René Šrůtek', 'Já myslel, že tam už je Ovčáček', 15);
INSERT INTO comments VALUES (4726, 'Radim Sládek', 'Babiš zaměstnává armádičku bývalých členů bis a jiných bezpečnostních složek určitě na to, aby mu jezdili kombajnem, nebo pekli v pekárně', 15);
INSERT INTO comments VALUES (4727, 'Martin Kříž', 'Bureš na to kašle a ta podpora se sama nevyjedná.', 15);
INSERT INTO comments VALUES (4728, 'Miloš Dvořáček', 'Ajta, M. Zeman jedna jako PAN prezident. Ale netreba se mu bat. Nejaka ta hulka na jeho ''prezidentsky hrbet'' se vzdy najde.', 15);
INSERT INTO comments VALUES (4729, 'Petr Dvořák', 'Třeba jak podrazil Šloufa, budiž mu země těžká.', 15);
INSERT INTO comments VALUES (4730, 'Jirka Kocman', 'Ti socani se buď zničí navzájem nebo je ve vládě Babiš zašlape pod hranici volitelnosti.', 15);
INSERT INTO comments VALUES (4731, 'Martin Kříž', 'Do hrobu? Od doby, co byl Klaus zvolen prezidentem.', 15);
INSERT INTO comments VALUES (4732, 'Jaroslav Křížek', 'Od kdy Miloš pomáhá ČSSD ???', 15);
INSERT INTO comments VALUES (4733, 'Petr Tyčka', 'Nesmyl. Ten už ani nevylézá. Takže tam nemůže lézt.', 15);
INSERT INTO comments VALUES (4734, 'Michal Melen', 'Fuj tajbl, to se ani neda docist', 15);
INSERT INTO comments VALUES (4735, 'Karel Novotny', '„Nebuďte stranou podrazáků,“ prohlásil obrpodrazák Zeman', 15);
INSERT INTO comments VALUES (4736, 'Miroslav Buček', 'Být prezidentem něco žádá ! Třeba ovládat i profesi - Pekař - Gumař ! Jidášsky je třeba pro Bureše něco upéci + Mít gumovo ohebnou páteř ! Že Miloši, že ano ...', 15);
INSERT INTO comments VALUES (4737, 'Šimon Martynek', 'Překvapivé to není, jen jsem to nečekal tak okaté.', 15);
INSERT INTO comments VALUES (4738, 'Martin Sýkora', 'Žijé, žijé, on žijé, žijé', 15);
INSERT INTO comments VALUES (4739, 'Tomáš Pak', 'Nebudte stranou podrazaku rika nekdo kdo krale vsech poddazaku jmanoval premierem bez duvery na dobu neurcitou...', 15);
INSERT INTO comments VALUES (4740, 'Martin Kříž', 'Ne, že by to bylo překvapivé.', 15);
INSERT INTO comments VALUES (4741, 'Martin Kříž', 'Třeba ne.', 15);
INSERT INTO comments VALUES (4742, 'Šimon Martynek', 'Bezva jak Zeman vyjednává podporu pro Babišovu vládu.', 15);
INSERT INTO comments VALUES (4743, 'Karel Pivrnec', 'Babiš právě vlezl svým příspěvkem na Twitteru Zemanovi až tam, kde slunce nesvítí', 15);
INSERT INTO comments VALUES (4744, 'Jaromír Vaništa', 'prostě máte těžkýho HOSIPA, je to tak, to neokecáte', 15);
INSERT INTO comments VALUES (4745, 'Šárka Staňková', 'Pomstychtivý Zeman si hraje na spasitele ČSSD? Přitom je to on kdykoliv to šlo tak si do ČSSD kopl!', 15);
INSERT INTO comments VALUES (4746, 'Aleš Kožíšek', 'Stejne je to parada,ze zele je uz na smetisti dejin:)', 15);
INSERT INTO comments VALUES (4747, 'Pavel Sobotka', 'Dyť píšu,že jsem je vynechal omylem - viz níže, tam jsem uvedl. Těch reliktů minulosti, hlavně komerčních stran na jedno použití, je tu dlouhý výčet....', 15);
INSERT INTO comments VALUES (4748, 'Ruda Hlousek', 'To říká ten pravý...!!!', 15);
INSERT INTO comments VALUES (4749, 'Stanislav Honěk', 'Fakt obdivuji hlupáky co se smějí vlastní hlouposti.', 15);
INSERT INTO comments VALUES (4750, 'Karel Pivrnec', 'nepošlou ani omylem, zvolí si Chovance i Zimolu a pak se budou dále poplácávat, jak dobře tu stranu řídí', 15);
INSERT INTO comments VALUES (4751, 'Martin Kříž', 'Zeman chce ČSSD definitivně potopit od doby, co byl zvolen prezidentem Klaus a ne Zeman.', 15);
INSERT INTO comments VALUES (4752, 'Michal Holek', ':D', 15);
INSERT INTO comments VALUES (4753, 'Pavel Sobotka', 'Podle posledních zpráv ze sjezdových kulóárů už v příštích volvách ČSSD kandidovat nebude, protože se odstěhuje za lepším do Polska', 15);
INSERT INTO comments VALUES (4754, 'Jaromír Vaništa', '"...s velive krátkou pamětí". Vy nám něco vykládejte o paměti, když na KSČM jste, jak se zdá, sám zapomněl.', 15);
INSERT INTO comments VALUES (4755, 'Petr Tyčka', 'Hmmm....tak Zeman chce zda se ČSSD definitivně potopit. Jako, že mají jít do vlády, ale mají chtít jenom náměstky? Zeman mluví jak zaměstnanec Agrofertu.', 15);
INSERT INTO comments VALUES (4756, 'Pavel Dvořák', 'Podrazák radí podrazákům, aby nebyli podrazáci. K popukání.', 15);
INSERT INTO comments VALUES (4757, 'Pavel Sobotka', 'jejda, na KSČM jsme úplně zapomněl, ti jsou taky jedlí', 15);
INSERT INTO comments VALUES (4758, 'Petr Bačavčík', 'Kde TOP nadává, tam dobře.', 15);
INSERT INTO comments VALUES (4759, 'Marek Kroupa', 'Krásný nadstranický projev prezidenta ve prospěch ANO a Babišovo vlády.', 15);
INSERT INTO comments VALUES (4760, 'Karel Pivrnec', 'protože tento stařík je kdykoliv připraven nastavit vlastní tělo obrovské lavině dvanácti imigrantů přece', 15);
INSERT INTO comments VALUES (4761, 'Radovan Sýkora', 'Zeman tam přijel v roli obchodního zástupce ANO. Pokud to nevidíte, jste slepý.', 15);
INSERT INTO comments VALUES (4762, 'Martin Kříž', 'Ještě zbývají Piráti.', 15);
INSERT INTO comments VALUES (4763, 'Pavel Sobotka', 'Tak nevolte ANO - máte tu na výběr několik nových stran', 15);
INSERT INTO comments VALUES (4764, 'Petr Burda', 'Jedině, že by mu nebylo jasné, jestli na velkou, nebo na malou stranu.', 15);
INSERT INTO comments VALUES (4765, 'Bohumir Malý', 'Je to zde samý Pižďuch.', 15);
INSERT INTO comments VALUES (4766, 'Jindřich Lněnička', 'To už je lepší ANO, vedené exbolševikem agentem Burešem, případně KSČM vedená bolševikem agentem Falmerem, případně politický podnikatel a jeden z nejdražších konzultantů Okamura.', 15);
INSERT INTO comments VALUES (4767, 'Martin Kříž', 'A taky dlužno dodat, že především kvůli Zemanovi.', 15);
INSERT INTO comments VALUES (4768, 'Josef Němeček', 'A vy volíte ANO s býv. komunistou v čele?', 15);
INSERT INTO comments VALUES (4769, 'Martin Kříž', 'A koho volí ti správňáci?', 15);
INSERT INTO comments VALUES (4770, 'Stanislav Drvota', 'Pravda je, socanům zvoní hrana.', 15);
INSERT INTO comments VALUES (4771, 'Martin Kříž', 'Tak jasně že Zeman bude lanařit svůj fanclub v ČSSD pro koalici s Burešem. Doufam, že si ČSSD zachová zbytek tváře a pošle Bureše i se Zemanem tam, kde slunce nesvítí.', 15);
INSERT INTO comments VALUES (4772, 'Pavel Sobotka', 'ČSSD, ODS, KDU-ČSL, TOP09, Zelení, Unie Svobody, ODA, Věci Veřejné,... tyhle postbolševické partaje může volit jedině člověk s velice krátkou pamětí', 15);
INSERT INTO comments VALUES (4773, 'Martin Pochman', 'Ja to proste nechapu, jak tuto bezpaterni kreauturu mohl vubec nekdo volit....', 15);
INSERT INTO comments VALUES (4774, 'Petr Burda', 'Proti Zemanovi jsou všichni Vámi jmenovaní kandidáti na svatořečení.', 15);
INSERT INTO comments VALUES (4775, 'Aleš Kožíšek', 'Dobre,Milos ;)', 15);
INSERT INTO comments VALUES (4776, 'Bohumil Procházka', 'Kde se zastaví obrátky českého politického bizáru?', 15);
INSERT INTO comments VALUES (4777, 'Josef Novak', 'Proč někdo volí ANO, to bych taky rád věděl.', 15);
INSERT INTO comments VALUES (4778, 'Jitka Kocánová', 'Nebuďte stranou podrazáků, zvolte si Chovance du bejt.', 15);
INSERT INTO comments VALUES (4779, 'Marek Valiček', 'Klasickej Zeman model.', 15);
INSERT INTO comments VALUES (4780, 'Petr Burda', '"Chtějte náměstky ministrů" - proč jen mám pocit, že Pospíšil od Kalouska má pravdu? "Nebuďte stranou podrazáků" - aneb nebuďte jako Zeman. ČSSD by si měla dát "bacha". Být filiálkou ANO znamená (podle mne) jistý zánik. ANOnimové budou volit ANO a demokraté nějakou demokratickou stranu.', 15);
INSERT INTO comments VALUES (4781, 'Marek Valiček', 'Musí dát andrejovi vratku za podporu před druhým kolem prez voleb. Chytrým to dojde, hloupým ne.', 15);
INSERT INTO comments VALUES (4782, 'Karel Zápotočný', 'moc tomu nerozumím... dle Zemana má ČSSD 15 ubohých poslanců... a dle něj by tito "ubožáci" měli jít do vlády s Babišem? proč? o tom, jak Chovanec otáčí, se nemá cenu vyjadřovat... ten copyright Zemana je fakt hodně nepovedený.', 15);
INSERT INTO comments VALUES (4783, 'Jindřich Lněnička', 'Ten Zeman má snad totální výpadek paměti, nebo nulovou sebereflexi. Vždyť to právě on udělal ze socanů stranu podrazáků a s podrazy neskončil dodnes. Začalo to pěkně kauzou Olovo, následně podrazil voliče oposmlouvou, potom se kvůli němu socani vzájemně podráželi při volbě prezidenta v r. 2003. Po volbách podrazil v roce 2013 spolu s Chovancem, Haškem, Zimolou a Tejcem Sobotku, do ČSSD kopal velkou část doby vládnutí a celé své podrazáctví odkopal v roce 2017 svými kreativními výklady Ústavy a jmenováním pana Andreše premiérem i bez 101 podpisů, které jinak tvrdošíjně vyžadoval.', 15);
INSERT INTO comments VALUES (4784, 'Josef Trnka', 'Havel nejezdil na sjezdy partají a neřešil s kým dělat nebo nedělat koalice... a taky nepredvadel ani šestinu toho co Zeman s Babišem', 15);
INSERT INTO comments VALUES (4785, 'Marek Sajda', 'Prijel tam agitovat za ANO. Jeho rady CSSD jsou zkratkou k zaniku - evidentne se tu stranu rozhodl znicit - net Zemana, net CSSD', 15);
INSERT INTO comments VALUES (4786, 'Marek Valiček', 'To je partička - stařec plnej zloby na sjezdu partaje, kterou chce dostat do vlády s Babišem. To by nedal dohromady ani F. Kafka, ani Arp!', 15);
INSERT INTO comments VALUES (4787, 'Josef Trnka', 'Já jen nechápu proč si ho tam ty exoti zvou?', 15);
INSERT INTO comments VALUES (4788, 'Jiří Souček', 'Havel snad byl nestranný, když jako prezident dal ostentativně najevo, že nebude jednat s komunisty? Klaus byl nestranný, když po celé první období zůstal funkcionářem ODS, byť formálním, a koncem druhého období vyjádřil podporu Jáklovi? Prezident si nanejvýš může hrát na nestranného, ale nikdy nemůže být nestranný, když je buďto vybírán členy politických stran (dříve), nebo politickými stranami vyroben či podporován (nyní).', 15);
INSERT INTO comments VALUES (4789, 'Jan Palát', 'Nebuddte v opozici, tam nebudete videt. Podporujte vladu Babise a chtejte namestky, kteri nejsou videt.', 15);
INSERT INTO comments VALUES (4790, 'Radovan Sýkora', 'Zkusím tip: Volič Anofertu nebo Okamurovec?', 15);
INSERT INTO comments VALUES (4791, 'Marek Sajda', 'Aneb Zeman vybidl k tomu tento sjezd preplanovat na sjezd slucovaci', 15);
INSERT INTO comments VALUES (4792, 'Jiří Dvořák', 'Ta reklama na odpuzovače krtků pod článkem celkem sedne.', 15);
INSERT INTO comments VALUES (4793, 'Jozef Záhumenský', 'suchá skyva opoziční smlouvy :) vy prostituce my drogy', 15);
INSERT INTO comments VALUES (4794, 'Pavel Sobotka', 'Tak proč volíte ANO? Máte tu na výběr SPD a Piráty.', 15);
INSERT INTO comments VALUES (4795, 'Jan Janotka', 'Co však dělat, když už stranou podrazaku dávno jsou?', 15);
INSERT INTO comments VALUES (4796, 'Luboš Marek', 'Takže jsi prohrál, že?', 15);
INSERT INTO comments VALUES (4797, 'Josef Trnka', 'Já pořad nechápu ten slovním .... volby OK, čili na vidlaky ale proč nemůže jednou mluvit normálně ?', 15);
INSERT INTO comments VALUES (4798, 'Martin Jurečka', 'Myslím si, že Zeman je spokojený a uspokojen, když se dějí věci podle něj, jestli v tom je pomsta nebo něco jiného si myslím není až tak předmětné.', 15);
INSERT INTO comments VALUES (4799, 'Jan Hora', 'To je vše, na víc nemáš? Popravdě ani jsem víc nečekal.', 15);
INSERT INTO comments VALUES (4800, 'Radovan Sýkora', 'Zeman: Jděte do vlády, protože v opozici nebudete vidět. Řekněte si o místa náměstků a odborníků. Zeman přijel na sjezd ČSSD jako obchodní zástupce ANO+SPD. To co radí ČSSD je nesmysl. Kdo zná náměstky nebo odborníky? Vidět je pouze premier, ministři a předsedové klubů a leadři včetně opozičních. Kdo zná odborné náměstky na ministerstvech? Závěr: Zemanovi nevěřím ani slovo. A už vůbec mu nevěřím, že mu jde o ČSSD.', 15);
INSERT INTO comments VALUES (4801, 'Radek Holík', '"Nebuďte stranou podrazáků.." řekl ten, který do ČSSD přivedl Grosse, Březinu, Sobotku.....křišťálově čisté....', 15);
INSERT INTO comments VALUES (4802, 'Rudolf Šlemer', 'Vše pro Bureše', 15);
INSERT INTO comments VALUES (4803, 'Jan Hora', 'Přesně tak, je to mstivý a křivý člověk a jde mu jen o to, aby Babiš dostal podporu....', 15);
INSERT INTO comments VALUES (4804, 'Vlada Vanek', 'Viditelně zdravý a živý zeman, opět nezklamal,hned v úvodu poučoval,a následně vyzýval členy ČSSD aby následovali jeho SPOZ na smetiště dějin.....', 15);
INSERT INTO comments VALUES (4805, 'Otakar Fuka', 'Zkusím tip - volič ODS?', 15);
INSERT INTO comments VALUES (4806, 'Martin Kral', 'Nebuďte skupinou podrazáků, radí strůjce lánského puče. Těším se, až tahle generace gerontů, co si nepamatují, co říkali včera, v politice nebude.', 15);
INSERT INTO comments VALUES (4807, 'Ondřej Šimíček', 'To je hodně velký masochismus.....pozvete si švába, který Vás posere, pozvrací, zadupe a poplive....a nakonec mu zatleskáte :D :D :D chudáci', 15);
INSERT INTO comments VALUES (4808, 'Jan Hora', 'Dobře míněné rady Zemana, teď jste mě pobavil....', 15);
INSERT INTO comments VALUES (4809, 'Luboš Marek', 'Když nejsi zvědav, tak nejsi zvědav. Proč tedy reaguješ?', 15);
INSERT INTO comments VALUES (4810, 'František Sobotka', 'Prezidentu Zemanovi nejde o nic jiného, než dokonat svoji pomstu za nezvolení v minulosti až do konce - jak se říká v jistých kruzích - "do hrdel a statků" Zadupat všechny tehdejší aktéry do země, denně zesměšňovat, znemožňovat, vymýšlet si jejich špatné skutky..... Jinak osud celé ČSSD ho nezajímá ani trochu, potřebuje jen pomoci Babišovi. Kdyby ten měl vládu již dnes sestavenou (třeba s ODS, tu by chtěli kvůli nálepce solidní pravicovosti) tak by ho na sjezd socanů nedostali ani ti největší kamarádi a pochlebovači.', 15);
INSERT INTO comments VALUES (4838, 'Luboš Marek', 'Zase někdo mluví za všechny. Tak hlavně mít poslední slovo, že?', 15);
INSERT INTO comments VALUES (4811, 'Jan Hora', 'Největší podrazák a křivák na naší politické scéně byl, je a bude Miloš Zeman. A pokud se něco socanům vymstí, bude to paktování s tímto exotem.', 15);
INSERT INTO comments VALUES (4812, 'Josef Novak', 'Hezky tomu stbakovi zobete výkaly z ruky.', 15);
INSERT INTO comments VALUES (4813, 'Ivan Marek', 'Takže už první řečník p.Chovanec neuposlechl dobře míněných rad M.Zemana a svým rádoby úderným projevem opět o něco prohloubil hrob své partaje.', 15);
INSERT INTO comments VALUES (4814, 'Josef Trnka', 'Nestranný prezident :)))', 15);
INSERT INTO comments VALUES (4815, 'Michal Holek', 'Socani jsou dilo :D jde videt ze jim na to sjezdu zavisi', 15);
INSERT INTO comments VALUES (4816, 'Bořek Mezník', 'Špína a hnus in Hradec Králové.', 15);
INSERT INTO comments VALUES (4817, 'Karel Kuna', 'Říká vyschlej eman', 15);
INSERT INTO comments VALUES (4818, 'Josef Novak', 'Z našeho pana prezidenta je mi opět nevolno.', 15);
INSERT INTO comments VALUES (4819, 'Josef Němeček', 'Babiš narozený v roce 1954 je skutečně mladou generací.....', 15);
INSERT INTO comments VALUES (4820, 'Luděk Jelínek', 'Už tenkrát věděl, co je Zeman zač, tak alespoň v tom měl koule a volil dle svého svědomí.', 15);
INSERT INTO comments VALUES (4821, 'Honza Novák', 'Jak praví klasik ,,Cherchez la Kalousek"', 15);
INSERT INTO comments VALUES (4822, 'Jan Hora', 'Možná byste byste mohl vyjádřit nějaký svůj názor, když už jste se rozhodl reagovat na můj příspěvek, nejlépe pak proč si myslíte, že je to hloupost. Na hloupé a zkratkovité odpovědi nejsem zvědav. Mimochodem pokud řeknu, že všichni zemánci se na to dívají stejně, tak vyjadřuji jen svůj názor a za nikoho nemluvím....', 15);
INSERT INTO comments VALUES (4823, 'Štefan Friča', 'V zásadě neřekl nic objevného. Chtějte koryta za každou cenu. Ovšem s tou podporou Chovance a Zimoly je vedle. To nejsou mordy, které by, až přijde čas, z bilboardů oslovily voliče.', 15);
INSERT INTO comments VALUES (4824, 'Slavomír Štěpán', 'Zdá se možného pomsta není dokonalá proto jim rádi aby jsi s Babišem tím se rozhodl ČSSD úplně dorazit protože z toho nic jiného nekouká', 15);
INSERT INTO comments VALUES (4825, 'Karel Jedlička', 'Do čela si strana podle Zemana má osobnost, ne nudnou úřednickou bytost Zdroj: https://zpravy.idnes.cz/zeman-volebni-sjezd-cssd-088-/domaci.aspx?c=A180218_110029_domaci_kop Nechybí tam slovíčko? A to "do královéhradeckého domě Střelnice se sjeli sociální demokraté" taky asi není úplně mluvnicky ono...', 15);
INSERT INTO comments VALUES (4826, 'Pavel Sobotka', 'Přeci byste nevolili socany,... Lidé už konečně zmoudřeli, a nechtějí mít nic společného s postbolševickými stranami, zodpovědnými za zpackanou rozprivatizaci, za zaostávání Česka, za to, že se z nás stala kolonie západních zemí a mzdy a životní úroveň ani náhodou nemůže dosáhnou slibovaného dorovnání s vyspělými zeměmi. ČSSD, KSČM, ODS, TOP09, STAN, KDU-ČSL,... dokonce i Zelení a KlausovoMachova SSO jddou do pekel a jsou nahrazováni novými stranami a novou, mladou politickou generací.', 15);
INSERT INTO comments VALUES (4827, 'Luděk Jelínek', 'Jj, soudruh Chovanec a gottwaldovec Zimola, to je dvojka, to teď budou vycházející hvězdy socanů. Snad i brzo zapadnou a s nimi ten zbytek preferencí socanů, aby ČSSD opět mohla začít od začátku (pravda, ani tak tuhle populistickou partaj volit nebudu, ale nějaká protiváha být vždy musí a socani jsou mi milejší než strana Stbáka či japonského "skialpinisty").', 15);
INSERT INTO comments VALUES (4828, 'Kamil Krbálek', 'Na rohu ulice vrah o morálce káže, před branou věznice se procházejí stráže', 15);
INSERT INTO comments VALUES (4829, 'Marie Zelená', 'Jinak řečeno - Moje milé věrné přítelkyně a moji milí věrní přátelé, rozhodně se nijak dlouho nerozmýšlejte, spojte se bez ohledu na své předvolební sliby s neestébákem Andrejem Babišem, skýva opozice je fakt suchá, takže se koukejte rychle přisát na nevydojitelný erární cecík a sofort se proderte ke korytům, je tam setsakramentsky dobrej veget, mohu vám to z vlastní zkušenosti vřele doporučit, váš vrchní podrazák.', 15);
INSERT INTO comments VALUES (4830, 'Petr Pidrmann', 'Pakárnu si dělá každý sám.', 15);
INSERT INTO comments VALUES (4831, 'Vladimír Bezděk', 'Chucpe', 15);
INSERT INTO comments VALUES (4832, 'Jiri Svarc', 'češi to dopustili,tolerují,ukázali to v obou volbách,nic z toho,co se kritizuje,jim nevadi.i ještě horší věci,co přijdou,to všechno většina čechu chce,schvaluje,toleruje.a demokracie jim to umožňuje.i mnohem větší absurdity,které se v česku vyskytují.s tím nikdo z nás nic neudělá,taky je fakt,že jde jenom o diskuzi nebo články v novinách,v realitě ti,kteří máme jiné názory,s tím,kam se sune česko,nic neudělá,pokud většina má jiný názor.', 15);
INSERT INTO comments VALUES (4833, 'Karel Novák', 'Vypadá přesně tak, jak to je. Největší chamraď si na svůj sjezd pozve svého krále.', 15);
INSERT INTO comments VALUES (4834, 'Miroslav Buček', 'Zhruba tak nějak si předsavuji proslov Jidáše - Miloše, v olivovém háji před 2.000 lety ! Nermakejte odspodu - nezískávejte důvěru zezdola ! Běžte na to shora ! Žádejte křesla - náměstků - ministrů - TAM VÁS POTŘEBUJI MÍIT . Šlouf mi umřel, Veleba získal ,už jenom 0.36% hlasů - Chlapci slepuji to, co jsem dříve rozbíjel ! Tak pravil, brouk Červotoč - Jasná slova našeho vůdce + frenetický potlesk ...', 15);
INSERT INTO comments VALUES (4835, 'Jaromír Procházka', 'Bojím se,že oni to stejně nepochopí,pokud zvolí Chovance tak je to výsměch,Hamáček je fajn,ale není to lídr.Jediná možná volba je Zimola-konečně někdo kdo má na to nahradit Zemana.Snad to pochopí i oni.', 15);
INSERT INTO comments VALUES (4836, 'Petr Novotný', 'Sucha je skyva opozice - to ma Milous pravdu, v opozici toho moc nekapne.', 15);
INSERT INTO comments VALUES (4837, 'Petr Čejka', 'Tohle je přesně mýtus. Ono to bylo naopak - ta schůzka byla tenkrát iniciována zevnitř ČSSD a já si vážně nemyslím, že je zas takovej problém, chtít odchod předsedy, pod jehož vedením strana dopadla mnohem hůř, než se tehdy čekalo..... Paroubek po takovém výsledku odstupoval sám.', 15);
INSERT INTO comments VALUES (4839, 'Marek Kroupa', 'Ano, podle výsledků posledních voleb volič miluje zrádce, podrazáky, lháře a podvodníky.', 15);
INSERT INTO comments VALUES (4840, 'Jiří Tepr', 'Ně věta "Nebuďte stranou podrazáků?" vyzněla jako: Nezůstávejte v ústraní podrazáků. ale spojte se s nimi (s ANO).', 15);
INSERT INTO comments VALUES (4841, 'Martina Vanková', 'Ostuda to monstrum Zeman a strana zoufalců...blíží se konečná, stačí se spojit s Babišem', 15);
INSERT INTO comments VALUES (4842, 'Luboš Marek', 'No, hlavně nemluv za všechny. To už tady bylo.', 15);
INSERT INTO comments VALUES (4843, 'Pavel Kovář', 'Stýská se vám po ní?', 15);
INSERT INTO comments VALUES (4844, 'Jan Hora', 'Všichni a přemýšlejte trochu....', 15);
INSERT INTO comments VALUES (4845, 'Jan Dostal', 'Nebuďte stranou podrazáků v době kdy jmenování premiérem už nebrání aktivní STB minulost ani trestní stíhání.', 15);
INSERT INTO comments VALUES (4846, 'Michal Holek', 'Myslim ze zradce zradcu Chovanec bude idealni kandidat', 15);
INSERT INTO comments VALUES (4847, 'Vašek Vašák', 'Nebuďte stranou podrazáků? To radil ten pravý těm pravým.', 15);
INSERT INTO comments VALUES (4848, 'Luboš Marek', 'Který?', 15);
INSERT INTO comments VALUES (4849, 'Marek Kroupa', 'Nebuďte stranou podrazáků, řekne Zeman v jedné větě a ve druhé podpoří Zimolu.', 15);
INSERT INTO comments VALUES (4850, 'Martin Valina', 'Upřímnou soustrast', 15);
INSERT INTO comments VALUES (4851, 'Jan Hora', 'Není, ale chápu, že zemánek to tak vidí....', 15);
INSERT INTO comments VALUES (4852, 'Jan Lukš', 'Milda byl opět na šrot. To musíte vidět, jinak neuvěříte.', 15);
INSERT INTO comments VALUES (4853, 'Luděk Jelínek', 'Ano, jak jde o koryta, tak Miloš ví, kam se přidat. Však ne nadarmo tak dlouho vedl socany, takže to musí dobře vědět, kde se točí prachy.', 15);
INSERT INTO comments VALUES (4854, 'Michal Lekeš', 'Prezidentský kandidát ANO radí, jak má postupovat jiná strana, kterou podrazil několikrát? Byli by hloupí, kdyby dali na jeho slova.', 15);
INSERT INTO comments VALUES (4855, 'Luboš Marek', 'Tak můj prezident to je.', 15);
INSERT INTO comments VALUES (4856, 'Eliška Kučerová', 'Už zase vylez?', 15);
INSERT INTO comments VALUES (4857, 'Luboš Marek', 'To je hloupost.', 15);
INSERT INTO comments VALUES (4858, 'Martin Valina', 'Zeman není president všech je to jen loutka Mynáře a spol.', 15);
INSERT INTO comments VALUES (4859, 'Luboš Marek', 'Socani byli a jsou stranou podrazáků. Nyní se podrážejí sami navzájem. Pokud jim Zeman radí změnu, radí jim dobře. Jedno z největších podrazáctví v posledním období byla společná vláda s ANO a následná hysterie okolo Babiše. To se jim vymklo z rukou a na to doplatili.', 15);
INSERT INTO comments VALUES (4860, 'Jan Hora', 'Lžeman vystupoval na sjezdu socanú jako by byl předsedou ANO, pokud se socani budou řídit tím, co Lžeman nastínil, bude to jejich cesta ven ze sněmovny....', 15);
INSERT INTO comments VALUES (4861, 'Milan Slimáček', 'Jak mnozí víme, tak Sobotka byl ten, kdo jako sociální demokrat při dávné, ještě nepřímé volbě prezidenta sociálního demokrata Zemana nepodpořil, stručně řečeno Sobotka si začal...', 15);
INSERT INTO comments VALUES (4862, 'Otakar Vlasák', 'Jaká náhoda, že prezidentovi favorité jsou Chovanec a Zimola :D Hlavně mít na srdci to (ne)podrazáctví :D "Prezident Miloš Zeman se po volbách dne 26. října 2013 setkal na zámku Lány s částí vedení ČSSD, a sice s Haškem, Tejcem, Škromachem, Chovancem a Zimolou, přitom nebyl přizván řádně zvolený a úřadující předseda strany Bohuslav Sobotka.[26] Výkonný výbor ČSSD pak jednal v Lidovém domě o výzvě k odstoupení Sobotky z čela ČSSD. Hašek i další účastníci několikrát v médiích popřeli, že by se schůzka vůbec konala,[27] i když její účastník Chovanec médiím svou účast potvrdil. Až tam prý zjistil, že schůzku dohodli Hašek a společníci. Hašek, Tejc, Škromach a Zimola nakonec pod mediálním tlakem schůzku potvrdili a posléze rezignovali na stranické funkce.[28] Zeman poté prohlásil, že považuje za komické utajovat jakoukoli schůzku a současně odmítl názor, že by šlo o pokus o převrat ve vedení ČSSD." Zdroj:Wiki', 15);
INSERT INTO comments VALUES (4863, 'Jiří Pokorný', 'přesně tak jsem si projev Miloše Zemana představoval....podrazáci?a co kauza olovo?jinak jen kopance do těch kteří neměli a nebo nemají stejný názor', 15);
INSERT INTO comments VALUES (4864, 'Miluše Welnová', 'Hlavně by měl už přestat rejpat,sednout si v Lánech na zahrádku a taky už konečně dát pokoj p.Kalouskovi.', 15);
INSERT INTO comments VALUES (4865, 'Marek Sajda', 'On na nej fakt nezapomnel?', 15);
INSERT INTO comments VALUES (4866, 'Pavel Chamrád', 'Poslouchám to od začátku a volil jsem v druhém kole Drahoše, ale absolutně souhlasím s dnešním projevem prezidenta i Fica, s Chovancem zatím ne, nevím jestli to myslí vážně a od srdce.', 15);
INSERT INTO comments VALUES (4867, 'Miluše Welnová', '', 15);
INSERT INTO comments VALUES (4868, 'Marek Tůma', 'Člověk, který protěžuje komunisty, stbáky, fízly a podvodníky, by neměl nic říkat o podrazácích...', 15);
INSERT INTO comments VALUES (4869, 'Marek Sajda', 'Uz je mi ho akorat lito...', 15);
INSERT INTO comments VALUES (4870, 'Jan Vacek', 'Ano, ĆSSD musí zaznít ve veřejné politice jako nositel nových šancí, defenzíva není řešením pro budoucnost.', 15);
INSERT INTO comments VALUES (4871, 'Pavel Šebesta', 'Tak Jezevec někomu radí, ať není podrazák... No to mi ho vyndej... Chybí tady blijící smajlík.', 15);
INSERT INTO comments VALUES (4872, 'Jaroslava Zimermannová', 'Nejdřív pochválí Chovance, a pak řekne Nebuďte stranou podrazákú...... Hmmmm, tak to je vážné. Chudák.', 15);
INSERT INTO comments VALUES (4873, 'Martin Fritz', 'Z úst tohoto člověka, to je absurdní výrok... Ale na sjezd CSSD se hodí!', 15);
INSERT INTO comments VALUES (4874, 'Karel Adámek', 'Stále více to připomíná projevy Milouše Jakešů...................', 15);
INSERT INTO comments VALUES (4875, 'Hana Ulrychová', 'Na Mírov?', 15);
INSERT INTO comments VALUES (4876, 'Radek Červenka', 'Tak i tak..', 15);
INSERT INTO comments VALUES (4877, 'Ondřej Hanslík', 'Ze by Zemana podrazili? Buzkova teda rozhodne ne, tu se pokusil odstranit Zeman. A zase si nemyslete, ze ON je vytahl nahoru. Mozna nekterym z nich nekdy lehce pomohl, ale dostali se tam hlavne svoji praci. Sobotku se pokusil Zeman s Haskem podrazit hned po volbach. Vzdyt i zalozil konkurecni stranu proti CSSD! ANO podrazelo nohy CSSD ve vlade, Zeman Sobotku nesnasi, takze mu skodil, kde to slo a ted jim radi, aby sli s ANO zase do vlady? To mi pripada jako past a ne jako dobra rada.', 15);
INSERT INTO comments VALUES (4878, 'Tomáš Hrubý', 'Tu českou vlajku si v souvislosti se Zemanem nechte a někde si stáhněte Ruskou.', 15);
INSERT INTO comments VALUES (4879, 'Andrea Šretrová', 'Zeman', 15);
INSERT INTO comments VALUES (4880, 'Roman Kozel', 'ČSSD naopak kdysi sebrala automatické podpory absolventům a také zavedla vzájemnou vyživovací povinnost - tedy že se musí nejprve postarat rodina.', 15);
INSERT INTO comments VALUES (4881, 'Zuzana Vinklerová', 'To je ale blbost. Kdyby něco takového existovalo, před volbami prezidenta by to bylo tisíckrát vytaženo našimi nestrannými, investigativními novináři.', 15);
INSERT INTO comments VALUES (4882, 'Karel Houska', 'největší z podrazáků tady bude plácat o podrazácích...neskutečné', 15);
INSERT INTO comments VALUES (4883, 'Vladislav Buryšek', 'spíš lidi doufají, že Babiš bude brát pracovitým ještě víc a lenochům, tedy lůze to bude dávat, jinak nevím proč ho zrovna lůza, která vždycky volila ČSSD, volila', 15);
INSERT INTO comments VALUES (4884, 'Hana Ulrychová', 'Tak je fakt, že on je v té době vytáhl na výsluní, ale to byl o několik hektolitrů alkoholu mladší.', 15);
INSERT INTO comments VALUES (4885, 'Tomáš Hrubý', 'Zřejmě jako Kim-Ir-Sen, i po smrti.', 15);
INSERT INTO comments VALUES (4886, 'Jan Novák', 'Vseho do casu pane prezidente.', 15);
INSERT INTO comments VALUES (4887, 'Otakar Vlasák', '"Nebuďte stranou podrazáků", řekl člověk, který si pozval po první volbě skupinu vybraných příznivců, aby jim pomohl svrhnout zvoleného předsedu této strany a premiéra, kterého sám jmenoval. Ten pán si nevidí do úst.', 15);
INSERT INTO comments VALUES (4888, 'Hana Ulrychová', 'Zeman někomu radí, aby nebyli stranou podrazáků? Vtipného máme prezidenta. A taky je dobrý, jak nabádá stranu, která by měla být stranou sociální a tedy zastáncem nižších příjmových skupin, aby opět vlezla do chomoutu vrchního oligarchy, kterému jde jen a jen o sebe.', 15);
INSERT INTO comments VALUES (4889, 'Jakub Hromádka', '.. a stále co ..?', 15);
INSERT INTO comments VALUES (4890, 'Jakub Chládek', '"Nebuďte stranou podrazáků" Bojí se konkurence?', 15);
INSERT INTO comments VALUES (4891, 'Jiří Krajíček', 'Podrazáků futrál, někomu radí, ať není podrazák. Český bizár v přímém přenosu.', 15);
INSERT INTO comments VALUES (4892, 'Jakub Hromádka', 'Zkušený podrazák ví o čem mluví. Z toho pohledu jsou to rozhodně cenné rady !!', 15);
INSERT INTO comments VALUES (4893, 'Petr Horák', 'Ale houby, ČSSD chudinské dávky nezvyšovala. Spíš přidávala matkám, důchodcům a zaměstnancům.', 15);
INSERT INTO comments VALUES (4894, 'Jiri Homolka', 'Chlapy...', 15);
INSERT INTO comments VALUES (4895, 'Marek Tůma', 'Podrazák a lhář zřejmě ví, o čem mluví...', 15);
INSERT INTO comments VALUES (4896, 'Jiri Homolka', 'Nebudte stranou podrazaku, volte rovne chlapi - Chovance, Zimolu, Haska.', 15);
INSERT INTO comments VALUES (4897, 'Otakar Vlasák', 'No hezky si Zeman kopnul během projevu do všech, kteří mu v minulosti nelíbali prsten dostatečně loajálně. I na Drahošovce se, samozřejmě bez posmívání, dostalo a velké finále ambasadora ANO v Andrejově stylu - za všechno může Kalúsek.', 15);
INSERT INTO comments VALUES (4898, 'Petr Koucký', 'Roky tvrdé práce v křivení charakteru, který se nakonec otiskne do tváře.', 15);
INSERT INTO comments VALUES (4899, 'Alena Eliášová', 'Těžký je život strany, která bere peníze pracovitým a rozdává je lenochům', 15);
INSERT INTO comments VALUES (4900, 'Jíří Novotný', 'Zeman na Hrad a stale', 15);
INSERT INTO comments VALUES (4901, 'Milan Mroczkowski', 'Nemyslím si, že ji Sobotka pohřbil, myslím si, že spíš ti co Sobotku včele ČSSD nechtěli ji pohřbyli. k tomu neměli náhradu.', 15);
INSERT INTO comments VALUES (4902, 'Jiri Homolka', 'Uz zaplatili ten dluh?', 15);
INSERT INTO comments VALUES (4903, 'Martin Macháň', 'A opět se kázala voda. A ve chvíli kdy se v projevu objevil ,z ničeho nic, mýtický Kalousek, jsem se musel smát.', 15);
INSERT INTO comments VALUES (4904, 'Radim Sládek', 'Dal bych ruku za to, nahlédnout do té složky, kterou má Babiš na Zemana. Pevně ho drží za koule , že tak poslušně podporuje a omlouvá Bureše.', 15);
INSERT INTO comments VALUES (4905, 'Mojmir Peřina', 'Prý "Nebuďte stranou podrazáků......." Přitom je to právě Zeman, který tuto podrazáckou kulturu do ČSSD zavedl a etabloval. Nikdo nikdy nepletichařil a nerozděloval více, jako Zeman sám, který by se mohl honosit titulem "Zasloužilý Podrazák".', 15);
INSERT INTO comments VALUES (4906, 'Ivan Marek', 'MZ.má naprostou pravdu.', 15);
INSERT INTO comments VALUES (4907, 'Petr Pidrmann', 'Zemanův polibek smrti?', 15);
INSERT INTO comments VALUES (4908, 'Václav Kureš', 'Lhář,podrazák, obhájce lumpů a STbáků radí. To jsme to dopracovali.', 15);
INSERT INTO comments VALUES (4909, 'Jiří Klíma', 'Zeman dal tímto projevem slovu "ubohost" obsah.', 15);
INSERT INTO comments VALUES (4943, 'Petr Černý', 'Morawiecki neřekl nic špatného.', 17);
INSERT INTO comments VALUES (4910, 'Petr Dostál', 'Projev prezidenta jsem sledoval. Způsob jakým se pokouší dehonestovat expremiéra Sobotku je mi z duše odporný. Prezident se nechová jako státník spíše mi připomíná zapšklého politikaře.', 15);
INSERT INTO comments VALUES (4911, 'Martin Valina', 'Jak může člověka dohnat jeho charakter', 15);
INSERT INTO comments VALUES (4912, 'Petr Fojta', 'To jsou ty pravá slova - nebuďte stranou podrazáků -> to se ukáže pokud nezvolí Chovance, Zimolu nebo Haška, ti se jako podrazáci už ukázali.', 15);
INSERT INTO comments VALUES (4913, 'Tomáš Horák', 'Je zvláštní pozvat si na sjezd hrobaře své strany.', 15);
INSERT INTO comments VALUES (4914, 'Jan Blana', 'Zeman z ČSSD udělal nejsilnější stranu a ti, které vytáhnul nahoru (Špidla, Sobotka, Gros, Buzková ......) ho následně podrazili a tohle moudří občané krásné ČR neodpouští, a proto je na tom ČSSD, tak jak je.', 15);
INSERT INTO comments VALUES (4915, 'Jindřich Obšil', 'Přeloženo: nejsi-li kolaborant, jsi podrazák.', 15);
INSERT INTO comments VALUES (4916, 'Radim Sládek', 'Takový opulentní sraz, být Altnerovými dědici, poslal bych tam exekutory', 15);
INSERT INTO comments VALUES (4917, 'Zuzana Erbenová', 'Stranou podrazáků je snad SPO, ne?', 15);
INSERT INTO comments VALUES (4918, 'Jiří Pospíšil', 'Od pohledu mladík plný sil!', 15);
INSERT INTO comments VALUES (4919, 'Jan Sedláček', 'Zeman si evidentně užívá, že se ČSSD propadla na stejné preference, jaké měla, než se v ní stal předsedou', 15);
INSERT INTO comments VALUES (4920, 'Jiří Smetana', 'Slova zlého, zapšklého muže.', 15);
INSERT INTO comments VALUES (4921, 'Olga Pavlíková', 'Buď poslechnete Zemana a vaše dny jsou sečteny, nebo půjdete do sebe, vyčistíte si chlív a po čase se zase zmátoříte', 15);
INSERT INTO comments VALUES (4922, 'Ivan Vašek', 'To dole,pane Pytelo,byl vtip.A asimilace původního obyvatelstva vůči novému,přišedšímu se většinou neřeší.Pokud se něco takové očekává,tak to očekává agresor.Asimilace nebo alespoň koexistence se řeší a očekává u těch,kteří nově přicházejí z důvodů žádosti o pomoc.A myslím,jestli jsem správně pochopil,tak se mluví o původním obyvatelstvu v oblasti napadené Tureckem,tedy muslimy.', 16);
INSERT INTO comments VALUES (4923, 'Kamil Pytela', 'Asimilace se ortodoxním nelíbí a tak je nejmladšímu šedesát a brzy vymřou.', 16);
INSERT INTO comments VALUES (4924, 'Marek Šimon', 'Tak to není nic zvláštního, Kypr je míchanec národů stejně jako Anglie.', 16);
INSERT INTO comments VALUES (4925, 'Ivan Vašek', 'Už to skoro zní,jakože ti dva jsou v čele těch islamistů.Jestli nejste už blízko pravdě.', 16);
INSERT INTO comments VALUES (4926, 'Denis Huml', 'Od 7. století? Není to čistě náhodou to století, kdy bylo vynalezeno jedno náboženství míru?', 16);
INSERT INTO comments VALUES (4927, 'Karel Stržanovský', 'Kdyby nezanikaly jednotlivé komunity, máme tu i my dodnes Lučany, Litoměřice, Charváty...', 16);
INSERT INTO comments VALUES (4928, 'Jiri Tichy', 'Ale my otevirame brany a vitame islamisty na celem "zapade", v cele s Merkelovou, Trudeau a dalsimi.', 16);
INSERT INTO comments VALUES (4929, 'Petr Macháček', 'Nebo zdrhnou.', 16);
INSERT INTO comments VALUES (4930, 'Petr Macháček', 'V křesťanských komunitách vyhlodalo díru, náboženství míru.', 16);
INSERT INTO comments VALUES (4931, 'Martina Sedlická', 'Všude, kam vstoupí muslimové v nadkritickém množství, jinověrci vymírají. Žádná záhada.', 16);
INSERT INTO comments VALUES (4932, 'Ivan Vašek', 'Blízkovýchodní křesťané na Kypr utekli v několika vlnách z dnešního Libanonu a Sýrie ...ve 13. století žilo na ostrově asi padesát tisíc maronitů, v dobách Osmanské říše jich však setrvale ubývalo Zdroj: https://zpravy.idnes.cz/maronite-kypr-turci-invaze-libanon-dus-/zahranicni.aspx?c=A180215_144305_zahranicni_kha Čí, ten úbytek?Díky ET nebo náboženství lásky a míru?', 16);
INSERT INTO comments VALUES (4933, 'Petr Munzar', 'No třeba i to, že třeba ten úžasný řečník Obama označil tyto tábory za polské a musel to dodatečně žehlit.', 17);
INSERT INTO comments VALUES (4934, 'Petr Munzar', 'Nějak váš příspěvek nechápu a hlavně nechápu jakou má souvislost tím, co sem napsal pan Zeman. Můžete to podrobněji vysvětlit?', 17);
INSERT INTO comments VALUES (4935, 'Petr Munzar', 'Že by dle množství tam žijících židů?', 17);
INSERT INTO comments VALUES (4936, 'Petr Munzar', 'Jenže o tom jste jako Žid rozhodně nevěděl.', 17);
INSERT INTO comments VALUES (4937, 'Ivan Kříž', 'A proč byly ty tábory na území býv. Polska a ne např. u nás? K.I', 17);
INSERT INTO comments VALUES (4938, 'Josef Hoffej', '-a dokonce dvě,jedna v Londýně,druhá v Moskvě.', 17);
INSERT INTO comments VALUES (4939, 'Jozef Bevilaqua', 'Nezmyselné je ale aj označiť vyhladzovacie koncentračné tábory ako "poľské"', 17);
INSERT INTO comments VALUES (4940, 'Miloš Boukal', '...jen ty souvislosti ti nějak unikají...', 17);
INSERT INTO comments VALUES (4941, 'Miloš Boukal', 'Poláci lítají od mantinelu k mantinelu... jako celou svoji pohnutou historii...', 17);
INSERT INTO comments VALUES (4942, 'Vladimír Rozehnal', 'Holokaust, antisemitismus, je zaklínadlo, které lze kdykoli použít. V každé komunitě, bez ohledu na rasu, národnost, nebo vyznání je určité % jedinců ochotných zradit, nebo kriminálních živlů. Výsadkaře, kteří spáchali atentát na H. udal Čech. Za protektorátu byli souzeni i Češi za ryze kriminální činy, vraždy, loupežné přepadení, znásilnění atd, která jsou trestná za každého režimu. Ale zkuste publikovat, že byla tehdy oprávněně odsouzena konkrétní osoba židovského vyznání za některý z výše uvedených trestných činů.......a jste antisemita a popírač holocaustu. Masové vraždění na základě nějaké zvrácené ideologie je hnus. To se netýká jen Židů, Cikánů a duševně nemocných. To čekalo i Slovany na našem území, pokud by Hitler vyhrál válku.', 17);
INSERT INTO comments VALUES (4944, 'Jozef Bevilaqua', 'Trochu ste mimo misu - čo má spoločné úspešnosť (nespochybniteľná) Izraela s tým, že sa snažia vnútiť svoj pohľad na dejiny úplne všetkým Teraz sa snažia zovšeobecniť vinu za holocaust na Poliakov....potom bude nasledovať ktoUkrajina Lotyšsko,Holandsko,Belgicko,Estónsko,Maďarsko,Slovensko...Česko', 17);
INSERT INTO comments VALUES (4945, 'Jozef Bevilaqua', 'Mosad dokáže všeličo...ale poľské zákony zmeniť nedokáže.', 17);
INSERT INTO comments VALUES (4946, 'Jaroslav Zajicek', 'Tak mu nerikali Nemci, to se traduje pouze v Cesku, ze mu tak Nemci rikali. Vyrokem "Böhmischer Gefreiter" chtel Hitlera v pocatcich jeho politickeho pusobeni zesmesnit (ve smyslu maly cesky vojacek) tehdejsi nemecky president Paul von Hindenburg, protoze cesky Braunau (dnes Broumov) povazoval za jeho rodiste, misto skutecneho rakouskeho Braunau am Inn. Stejne tak je v Cesku dosti rozsireno, ze Hitler byl malir pokoju, coz nikdy nebyl. Hitler mel nadani k umeleckemu malirstvi, ktere nebylo ale dostacujici na prijeti ke studiu na Akademii. Presto se zivil malovanim pohlednic a obrazu - ty stale dosahuji v aukcich slusnych cen.', 17);
INSERT INTO comments VALUES (4947, 'Jan Babička', 'Spíše české. Proto mu němci říkali ten zatracený český kaprál', 17);
INSERT INTO comments VALUES (4948, 'Jan Babička', 'A Hitler daroval židům celé město, Terezín, kde měli svoji samosprávu, vlastní peníze a policii. Prostě kdyby je židé k tomu nenutili, němci by židům nezkřivili ani vlásek na hlavě, vážený Zemane', 17);
INSERT INTO comments VALUES (4949, 'Ladislav Jaroš', 'Ale ten Hitler, to byl machr. 73 let po smrti a pořád rozhádává politiky napříč Evropou.', 17);
INSERT INTO comments VALUES (4950, 'Karel Zeman', 'V ghettech působila židovská policie, která vykonávala příkazy Němců proti vlastním lidem - tím je snad řečeno vše....', 17);
INSERT INTO comments VALUES (4951, 'Petr Koucký', 'Omlouvám se za plus. Překliknul jsem se. To neznamená, že mělo být mínus. To ze zásady nedávám nikomu. Jen jsem chtěl reagovat. Izrael nejen že kritizuje, ale i zakazuje. Prosadil do mnoha legislativ (včetně naší) zákon o popírání holokaustu. Aby bylo jasno, holokaust nezpochybňuji. Vadí mi však to, že vůbec existuje, co je v zákonu obsaženo a jakou výhodnou pozici pro Židy vytváří. O dennodenní mediální masírce už ani nemluvím.', 17);
INSERT INTO comments VALUES (4952, 'Jan Morava', 'Lžete - nikdo židovským rodinám nic neukradl a i kdyby, tak to po převratu 1989 dostávali s omluvou a opravené zpět. Nikdo by si nedovolil židům něco nevrátit, to by bylo řevu! A pokud máte na mysli něco jako vilu Tugendhat v Brně, která židům vrácena skutečně nebyla, pak vězte že to bylo proto, že Tugendhatovi aby ji za první republiky mohli odepisovat z daní, nedeklarovali ji jako rodinný dům, ale deklarovali ji jako "vloženou do firmy" a na to se restituce po 1989 nevztahovaly, protože ty záležitosti byly vyřízeny už v roce 1946 benešovým znárodněním kapitalistického majetku a dnes by to bylo to samé. Tugendhatovi se prostě rozhodli krátkodobě vychytrale, ale dlouhodobě špatně. A nebyli sami... Takže tak.', 17);
INSERT INTO comments VALUES (4953, 'Jan Morava', 'Ano, taky jsem slyšel o tom, že tzv. masakr v Jedwabnem nastal tak, že ta vesnice přešla z rukou Wehrmachtu do rukou Rudé armády, místní židi začali udávat Poláky a ti byli Rudou armádou zastřeleni, Když se zanedlouho vyměnila fronta a tu vesnici zase obsadil Wehrmacht, tak jim to Poláci vrátili. Považuji to za možné? Nevíte někdo jak to fakt bylo doopravdy? Fakt je ten, že i v 50. letech byli v Československu největší iniciátoři a vykonavatelé represí proti lidem židi - Slánský, Reicin, Šlink a další. Totéž ruské politbyro po převratu 1917 na které se teď tak nadává. To jsou nezpochybnitelná fakta - i když se to židům velice nelíbí a sami o tom hluboce mlčí. Zatím jsem od nich žádnou sebereflexi neslyšel. Podivné, což? Lidé bděme!', 17);
INSERT INTO comments VALUES (4954, 'Lukáš Cvrček', 'Počtem byla.', 17);
INSERT INTO comments VALUES (4955, 'Petr Koucký', 'Vážený pane, přečtěte si, prosím, co jsem zde všechno napsal. Potom se uklidněte a začněte uvažovat, co tu předvádíte.', 17);
INSERT INTO comments VALUES (4956, 'Lukáš Cvrček', 'Ale no tak. Chcete vážně tvrdit, že pogrom v Kielcích nařidila polská vláda a provedly ho polské úřady?', 17);
INSERT INTO comments VALUES (4957, 'Ivan Vašek', 'Ach tak.Teď je to jasné.', 17);
INSERT INTO comments VALUES (4958, 'Oto Paldus', 'Nevím co je na tom pravdy ale říká se, že i Hitler měl židovské předky.', 17);
INSERT INTO comments VALUES (4959, 'Ladislav Laker', 'Pane Čapku, zjevně nejste schopen porozumět jednoduchému psanému commentTextu. Poldký zákon je o něčem jiném.', 17);
INSERT INTO comments VALUES (4960, 'Veronika Skalová', 'Ma pravdu, treba george soros je priklad, je zid a sam zidum skodil, takze netanyahu zas nepripousti, ze nejaky zid by byl toho schopen?? Ten jen mimo realitu a zas si upravuje komentare po svem.', 17);
INSERT INTO comments VALUES (4961, 'Vlastimil Zastera', 'Kdo je bez viny ať hodí ten prvni kámen Polaci maji pravdu.Lepsi když každý zmlkne a nechaji Poláky na pokoji.', 17);
INSERT INTO comments VALUES (4962, 'Peter Franek', 'nechapu o co vam jde.. Zato o co jde polske vlade v jeji soucasne ofenzive bhzel chapu..', 17);
INSERT INTO comments VALUES (4963, 'Hampl Bretislav', 'To byla reakce na jednu zdejší paní, smazali ji, nicméně můj dotaz zůstal, sám nechápu....', 17);
INSERT INTO comments VALUES (4964, 'Jan Hosek', 'Ja nemam vubec nic proti kritickemu mysleni a studiu historie z ruznych uhlu, o coz se tady pokousite. Ale srovnavat vymysleni, naplanovani a castecne uspesne provedeni masove vrazdy s fci kapa, tedy snahou v ramci toho vrazdeni prezit, mi prijde opravdu trosku...ne, seru na to, tohle se slusne napsat neda. Vy jste normalni kus pracuraka. Jenom nevim, jestli od prirody, nebo vam za to nekdo plati.', 17);
INSERT INTO comments VALUES (4965, 'Ivan Vašek', 'Myslím,že to směšujete a je tady problematika kolektivní viny.Nikde se nemluví o holocaustu,který prováděl"německý národ".Mluví se o Němcích,kteří by měli být řádně dohledáni.Analogicky na "slovanský národ".A pogromy dělala celá středověká i "současná" Evropa,muslimský svět i antická doba.Opět se nemluví o národech.', 17);
INSERT INTO comments VALUES (5031, 'Ivan Vašek', 'Ano,ale je to selhání,nikoli státem organizovaná činnost.Měli bychom správně definovat.O to mi jde.', 17);
INSERT INTO comments VALUES (4966, 'Ivan Vašek', '1.Polsko nikdy nebylo vůči těmto zemím outsider. 2.Má s něma histori,kdy ho i šlapaly do země. 3.Pochybuji,že by za současného rozložení sil něco chtěli Poláci diktovat.Pouze neumějí ohýbat tak hřbet,jak nyní my.', 17);
INSERT INTO comments VALUES (4967, 'Petr Miller', 'Jenže jako Čech jste mohl projít jako Němec nebo dostat lístek za Ural (aspoň jako to Němci plánovali). Jako Žid byl váš osud předem rozhodnut, bez ohledu na spolupráci.', 17);
INSERT INTO comments VALUES (4968, 'Ivan Vašek', 'K té druhé části:Antisemitou snad vás nazvou židé.Myslím,že tato argumentace,obrana se používá v poslední době častěji,neboť se opět něco ve věci děje.Mám na mysli islamizaci Evropy.', 17);
INSERT INTO comments VALUES (4969, 'Petr Miller', 'A 90% z těchto lidí volilo Sudetsko-německou stranu. Jak historie ukázala, mít velkou menšinu obyvatelstva sousedního státu je velká chyba.', 17);
INSERT INTO comments VALUES (4970, 'Ivan Vašek', 'Nerozumím otázce.Ministr Gabriel nebo kdo?Citoval jsem citované. https://www.reflex.cz/clanek/zpravy/84842/za-smrt-milionu-zidu-muzou-nemci-par-kolaborantu-to-nemuze-zmenit-rekl-otevrene-nemecky-ministr-zahranici.html', 17);
INSERT INTO comments VALUES (4971, 'Ivan Vašek', 'Je otázkou,co je pozadí vzniku takového zákona.A vzhledem k obrovské energii okolo toho vynaložené,to může být důležité a přesvědčivé.Tato informace chybí.', 17);
INSERT INTO comments VALUES (4972, 'Jiří Horák', 'Protože chci a chtěl jsem být přesný, napsal jsem, že "během okupace v Polsku žádná polská vláda nebyla".', 17);
INSERT INTO comments VALUES (4973, 'Martin Kodat', 'Tvrdil to víceméně Netanjahu ;)', 17);
INSERT INTO comments VALUES (4974, 'Martin Kodat', 'Ano. Židé udávali jiné Židy, aby si koupili svobodu. Vzdělávat se Václave, vzdělávat se!! :))', 17);
INSERT INTO comments VALUES (4975, 'Milan Prikryl', 'pokud chcete byt presny, polska vlada v emigraci byla.', 17);
INSERT INTO comments VALUES (4976, 'Milan Prikryl', 'vim, ze na ceskem uzemi nebyly vyhlazovaci tabory, ale je to stejne, jako kdybychom chteli poslat do kriminalu na tri roky ty, co oznaci Terezin za cesky koncentrak! Neni to pravda, kazdy to vi,je to zbytecne.', 17);
INSERT INTO comments VALUES (4977, 'Hampl Bretislav', 'A copak vám při tom osobním setkání ten Žid udělal ?', 17);
INSERT INTO comments VALUES (4978, 'Ivan Vašek', 'Neexistuje nejmenších pochyb o tom, kdo je zodpovědný za vyhlazovací tábory, kdo je provozoval a kdo v nich zavraždil miliony evropských Židů: byli to Němci," uvedl v prohlášení ministr Gabriel. "Tato organizovaná masová vražda byla spáchána naší zemí a nikým jiným. Jednotliví kolaboranti na tom nic nemění," dodal Gabriel."Používání označení ''polský tábor smrti'' je chybné," dodal. A dále z commentTextu média:Podnětem ke vzniku zákona bylo mimo jiné používání geografického označení "polský" i v souvislosti s nacistickými koncentračními a vyhlazovacími tábory na území okupovaného Polska. A uznání min.Gabrielovi', 17);
INSERT INTO comments VALUES (4979, 'Ivan Vašek', 'Neexistuje nejmenších pochyb o tom, kdo je zodpovědný za vyhlazovací tábory, kdo je provozoval a kdo v nich zavraždil miliony evropských Židů: byli to Němci," uvedl v prohlášení ministr Gabriel. "Tato organizovaná masová vražda byla spáchána naší zemí a nikým jiným. Jednotliví kolaboranti na tom nic nemění," dodal Gabriel."Používání označení ''polský tábor smrti'' je chybné," dodal. A dále z commentTextu média:Podnětem ke vzniku zákona bylo mimo jiné používání geografického označení "polský" i v souvislosti s nacistickými koncentračními a vyhlazovacími tábory na území okupovaného Polska A uznání min. Gabrielovi.', 17);
INSERT INTO comments VALUES (4980, 'Ivan Vašek', 'Takže holocaust je německý produkt.Tak to bychom měli uzavřené a teď debata o antisemitismu ve světě.O to totiž v zákoně jde:Poslko a poslký národ.A teď dále polský občan.', 17);
INSERT INTO comments VALUES (4981, 'Milan Prikryl', 'Soucasny polsky premier v utajeni ( Kaczynski), neumi nic jineho nez vyvolavat konflikty, je to jeho osobni politika.Nepratele, konspirace, ohrozeni...', 17);
INSERT INTO comments VALUES (4982, 'Ivan Vašek', 'Postiženi by mohli...a to je spekulace,pokud to není v zákoně a mate to hodnocení tohoto zákona.', 17);
INSERT INTO comments VALUES (4983, 'Milan Prikryl', 'zatim', 17);
INSERT INTO comments VALUES (4984, 'Jiří Horák', 'Zvlástě když během okupace v Polsku žádná polská vláda nebyla! Ostatně i Polsko přejmenovali na Generální gouvermenent, kde guvernérem i jeho podřízenými byli jenom Němci.', 17);
INSERT INTO comments VALUES (4985, 'Bohumil Michal', 'Pogrom v Kielcích v roce 1946? https://en.wikipedia.org/wiki/Kielce_pogrom', 17);
INSERT INTO comments VALUES (4986, 'Jiří Krejča', 'Pokud bych chtěl srovnat pogromy na židy s německým holocakustem, pak by se to dalo přirovnat k rýmě a k rakovině.', 17);
INSERT INTO comments VALUES (4987, 'Jiří Krejča', 'V čem nemám pravdu, Habudo? Pravda o zrůdnosti a zvěrstvech Němců bolí, že ano? Opravdu neexistuje národ, který by se v tom Němcům vyrovnal...možná svého času Mongolové.', 17);
INSERT INTO comments VALUES (4988, 'Marián Pilc', 'Prakticky ve všem souhlasím, ale otázka je, zda-li skupinka polských udavačů byla opravdu marginální, nebo ne.', 17);
INSERT INTO comments VALUES (4989, 'Lukáš Cvrček', 'To není důkazem snahy Poláků těmto státům cokoliv diktovat.', 17);
INSERT INTO comments VALUES (4990, 'Ivan Vašek', 'https://www.denik.cz/ze_sveta/polsky-prezident-podepise-sporny-zakon-o-holocaustu-posle-ho-i-ustavnim-soudcum-20180206.html droj: https://www.denik.cz/ze_sveta/polsky-prezident-podepise-sporny-zakon-o-holocaustu-posle-ho-i-ustavnim-soudcum-20180206.html Za používání termínu „polský koncentrační tábor“ legislativa umožňuje udělit až tříletý trest vězení. V normě je zdůrazněno, že tábory smrti na území Polska stavělo nacistické Německo. Postiženi by mohli ale být i ti, kteří by jen naznačili, že polský stát s nacisty kolaboroval. Výjimkou jsou umělecká a vědecká díla. Zdroj: https://www.denik.cz/ze_sveta/polsky-prezident-podepise-sporny-zakon-o-holocaustu-posle-ho-i-ustavnim-soudcum-20180206.html Zdroj: https://www.denik.cz/ze_sveta/polsky-prezident-podepise-sporny-zakon-o-holocaustu-posle-ho-i-ustavnim-soudcum-20180206.html', 17);
INSERT INTO comments VALUES (4991, 'Ivan Vašek', 'A navíc:zákona, který trestá připisování jakékoli odpovědnosti za holokaust Polákům...nejde tady asi o to,že se nesmé poukázat,že ti a ti Poláci kolaborovali s Němci.', 17);
INSERT INTO comments VALUES (4992, 'Milan Prikryl', 'staci se podivat, s kym vsim jsou Polaci v konfliktu- Ukrajina, Rusko, Nemecko...', 17);
INSERT INTO comments VALUES (4993, 'Ivan Vašek', 'nebýt velkolepého německého plánu na "konečné řešení".Nešikovná formulka.', 17);
INSERT INTO comments VALUES (4994, 'Milan Prikryl', 'Mozna si neuvedomujeme, ze v Polsku bylo zilo daleko vice Zidu nez u nas. Takova Lodz, dnes mesto cca 900 tis. obyvatel, byla vylozene zidovska. Kdo chce znat polske realie, musel by tam zit, ja jsem tam zil 5 roku. Je to oproti nam velka zeme a treba nazory lidi jsou tam hodne odlisne podle toho, zda jde o puvodne polske nebo nemecke uzemi. Dokonce jsem se mnohokrat setkal i se sympatiemi k fasistum. A daleko casteji jsem se setkal s antipatiemi vuci Zidum. Sjednocuje je soucasny nacionalne- klerikalni rezim Kaczynskeho.', 17);
INSERT INTO comments VALUES (4995, 'Milan Prikryl', 'vzdy budu spise na strane Polaku, ale to, co tam provadi ted, je az zbytecne velke posilovani narodnostnich vasni, na coz logicky vzhledem k historii slysi.', 17);
INSERT INTO comments VALUES (4996, 'Jindřich Hrdlička', 'Prekrucujete. Zákon nijak nepostihuje tvrzení, že jednotliví Poláci se mohli na perzekuci podílet. Je namířen proti tvrzení, že vinni jsou Poláci jako společnost, Polsko jako stát. Holt někomu se šlápli do báboviček - mám na mysli úspěšně se rozšiřující demagogii typu ,,polských koncentračních táborů,, - takže se rozvíjí protipolska kampaň.', 17);
INSERT INTO comments VALUES (4997, 'Mario Trnka', 'Tak som si o tom nieco precital a ako je nacisticke Nemecko a Slovensky stat vzorom aj ekonomickym pre slovenskych nackov. Tak pravda bude niekde inde. Ked Hitlerovi doslo zidovske zlato tak sa vrhol na rakusko a potom nas aby mu ten vysnivany arijsky domcek z karat nespadol rovno na hlavu.', 17);
INSERT INTO comments VALUES (4998, 'Lukáš Cvrček', 'Tím, že srovnáváte Maďary s Poláky, dáváte na odiv jenom své ignorantství. Maďarský vládnoucí režim byl spojencem nacistického Německa a na perzekuci Židů se podílely oficiální maďarské úřady. Ukažte mi prosím něco podobného u Poláků.', 17);
INSERT INTO comments VALUES (4999, 'Martin Petvad', 'To nikdo nepopírá, a nikdo ani netvrdí nic jiného. Je jisté, že nebýt velkolepého německého plánu na "konečné řešení", ostatní Evropané by si i nadále vystačili jen s pohrdáním a občasnými pogromy. Je ale všeobecně známo, že tradiční antisemitské postoje v některých neněmeckých národech nacistům při vyhledávání židů a organizování jejich vyvlastňování a deportací do vyhlazovacích táborů vydatně přisluhovaly. K těm nejaktivnějším patřili nejen Poláci a Maďaři, ale např. i Slováci. Koneckonců protižidovské animozity byly po dlouhou řadu staletí přirozenou součástí křesťanské kultury, takže se není čemu divit.', 17);
INSERT INTO comments VALUES (5000, 'Jiří Krejča', 'Holocaust a genocidu židů vymysleli, organizovali a prováděli Němci...ti jediní jsou zodpovědni nejen za šest milionů pomordovaných židů, ale také za desítky vymordovaných občanů evropských zemí. Nebýt Němců a jejich vize Evropy nikdy by k masovému vyvražďování židů a dalších Evropanů nedošlo.', 17);
INSERT INTO comments VALUES (5001, 'Jiří Krejča', 'Když se Němcům podařilo vyvraždit desítky milionů evropských občanů, tak národy, které byly německou genocidou postiženy se vzpamatovávaly celá desetiletí.', 17);
INSERT INTO comments VALUES (5002, 'Milan Zich', 'pogrom a holokaust neni to same', 17);
INSERT INTO comments VALUES (5003, 'Martin Petvad', 'Polský premiér projevil hulvátskou drzost. Předmětem mezinárodní kritiky je zákon, který zakazuje spojovat Poláky s podílem na nacistické genocidě židů. - To, že na ní měli svůj podíl i představitelé jiných národů nebo snad dokonce i někteří židé, nemá s tím ostudným zákonem, který hovoří výhradně o Polácích, ani nejmenší souvislost, a v žádném případě to nemůže být argumentem proti kritikům. Ten polský zákon jednoduše lže: a nepochybuji, že to většina Poláků ví stejně dobře jako všichni ostatní. Skutečností je, že se Poláci na genocidě židů aktivně podíleli, podobně jako se na ní podíleli např. Maďaři (kteří mají aspoň tolik korektnosti, že to nepopírají).', 17);
INSERT INTO comments VALUES (5004, 'Jiří Krejča', 'Především je třeba neustále připomínat, že za holocaust a vyvraždění evropských židů mohou jen a jen Němci. Zrůdnost Němců vůči židům se nedá s žádným antisemitismem srovnat.', 17);
INSERT INTO comments VALUES (5005, 'Jiří Krejča', 'Pouze Němci povýšili antisemitismus na holocaust a za svůj národní cíl si vybrali vymordovat všechny evropské židy.', 17);
INSERT INTO comments VALUES (5006, 'Ivan Vašek', 'Je to hodně omezené,uznávám,ale já moc třesoucích se Němců nepotkal.Emancipovaní,jisti si svou sílou a financema,rachot a hluk.Já věřím,že jsou i Němci,kteří sedí a přemýšlí nad současností,budoucností,ale i nad minulostí.Že vědí,co udělali Němci,že jim to nikdo neodpáře a je potřeba s tím žít.Potřeba dobře a solidně s tím žít.A že vědí,pokud se objeví někdo,kdo tuto skutečnost chce odpárat,zapomenout,tak se ho mají bát,kam je chce vést.Chybami se člověk učí a kdo nezná svoji historii,nemůže očekávsat noc dobrého v budoucnu.', 17);
INSERT INTO comments VALUES (5007, 'Marek Słupik', 'Bohuzel, v Polsku a take v Izraele vlada ma pohled na svou historie jako to musi byt nejaka legenda. Ne egzistuje zadna strana na svete ktera ne musela by se stydit ze nejakego epizodu z minulosti... So take it easy and be realistic Poland and Israel!', 17);
INSERT INTO comments VALUES (5008, 'Walter Diviš', 'Za první půlku plus, ta druhá je nesmysl. Tam jste se dopustil úplně stejně hloupé generalizace. To, jak se chovají někteří Židé prostě nejde vztahovat na všechny a je jedno, na jakou to je stranu. Jinak Židé byli tak "populární" proto, protože se vždy vydělovali, měli pro okolí nepochopitelné zvyky a odmítali se plně podřídit a hlavně přes všechny ústrky nebyli nikdy úplně na dně. A prostě jim spousta lidí záviděla a závidí. I když není co.', 17);
INSERT INTO comments VALUES (5032, 'Vokoun Vladimir', 'Ale to je znma vec, i v Ceskoslovensku bylo za valky 20 000 000 partizanu co bojovali proti Risi. A po unoru 1948 zase to same mnozstvi antikomunistu. Staci dnes cist.', 17);
INSERT INTO comments VALUES (5009, 'Pavel Novotny', 'Morawiecky ma naprostou pravdu. Dokonce i mezi Zidy byli zradci a nekteri dokonce i byli v SS (aniz by tenkrat o jejich puvodu bylo Nemcum neco znamo). Dalsi pak spolupracovali s Nemci. To jsou proste fakta a faktem je i to, ze takovi lide se najdou v kazdem narode. Na co pan premier ovsem zapomel je to, ze Zid je v dnesni dobe nedotknutelny. Reknete neco co se jim nelibi nebo nehodi a jste hned antisemitista. Je tezke mit k nim kladny pomer, ale to jim je stejne jedno. Oni pouze chteji aby jsme byli zticha a delali to, co se hodi jim. Asi proto vzdy byli a jsou ve svete tak “popularni”.', 17);
INSERT INTO comments VALUES (5010, 'Jiri Tichy', 'Nacisticky plan zlikvidovani ceskeho naroda. Vypaleni 600 vesnic v Belorusku spolu s vyvravzdenim vsech obyvatel, vcetne zen a deti.', 17);
INSERT INTO comments VALUES (5011, 'Petr Koucký', 'To zkuste říct Romům. Poženou vás.', 17);
INSERT INTO comments VALUES (5012, 'Antonin Nemec', 'Antisemtismus byl nejen v Nemecku, ale hlavne i v Polsku a Rusku. Z Polska muswli emigrovat Zide i v roce 1968. Ani Cesi se zase tolik nelisili a radi zabrali zidovske majetky. Nakonec - i dnes mame nase stavebni pamatky, to jsou vily ukradene zidovskym rodinam', 17);
INSERT INTO comments VALUES (5013, 'Ivan Štípek', 'Taktika je pořád držet pod krkem, posilovat pocit viny a hanby, nedat vydechnout. Podívejte, jaký třesoucí se uzlíček nervů je z Němců. A to už je x-tá generace, která s tím nemá nic společného.', 17);
INSERT INTO comments VALUES (5014, 'Ivan Štípek', 'Už Polsko aktivovalo protiletadlové baterie?', 17);
INSERT INTO comments VALUES (5015, 'Štefan Bukovan', 'Samozrejme. V nacistami vytvorených getách to bolo bežné - skúste si naštudovať problematiku! Prečo napr. Čech udal čecha za to, že ukrýva parašutistov? Myslíte si, že medzi Židmi to bolo iné?', 17);
INSERT INTO comments VALUES (5016, 'Lukáš Cvrček', 'Jak si vysvětlujete přijetí tzv. Norimberských rasových zákonů už v roce 1935? Že by Hitler jasnovidně předvídal bankrot v roce 1938?', 17);
INSERT INTO comments VALUES (5017, 'Lukáš Adamec', 'Tak to snad ví každý nebo budeme opět přepisovat historii, aby se to hodilo vyvoleným? Jeden příklad za všechny Paul Moritz Warburg jako americký bankéř a zakladatel FEDu půjčuje ve velkém Německu. Jeho bracha Max Warburg sedí v představenstvu I.G. Farben tedy firmy, které vyrobila přes 90% výbušnin použitých německém po čas II.S.V. Především americké půjčky vedle nesmyslných reparací nadiktovaných Anglii a Francii po I.S.V. stály za tím, že Německo čelí roku 1938 faktickému státnímu bankrotu a řeší to nejdříve tak, že se v duchu nejlepších křesťanských tradic vrhnou na židy no a jelikož to nestačí tak volí mezi válkou a státním bankrotem. To je tvrdá realita, ostatní jsou max líbivé kecy, jelikož o peníze jde až v prvním případě již tisíce let.', 17);
INSERT INTO comments VALUES (5018, 'Štefan Bukovan', 'citujem Netanjahua: Výroky polského premiéra tady v Mnichově jsou pobuřující. Máme tu problém s neschopností chápat historii a nedostatkem ohleduplnosti k tragédii našeho lidu. A keďže poľský premiér sa vyjadril o "židovských vinníkoch", tak Netanjahu je POBÚRENÝ tým, že si niekto dovoľuje tvrdiť HISTORICKÝ FAKT, že aj medzi židmi existovali ľudia, čo kolaborovali s nacistami...', 17);
INSERT INTO comments VALUES (5019, 'Ivan Vašek', 'Sami sobě.A to se do vize federální EU nehodí.', 17);
INSERT INTO comments VALUES (5020, 'Ján Badiovský', 'Úplne s vámi souhlasím. Proto si myslím, ze onalepkovat 2.5 mio lidi a přemístit je do jiné země nebylo spravne.', 17);
INSERT INTO comments VALUES (5021, 'Lukáš Cvrček', 'Co a komu chtějí Poláci diktovat?', 17);
INSERT INTO comments VALUES (5022, 'Borek Tomis', 'Mám dojem, že se spolu s Morawieckým rodí nový problém v regionu. Polsko, stejně už problémové se začína vzpouzet všemu, co není polské a chtělo by diktovat. No uvidíme, ale začínám se bát průseru.', 17);
INSERT INTO comments VALUES (5023, 'Lukáš Cvrček', 'Vy ani nepotřebujete žádné fake news. Vy si jenom ve své hlavě naprosto převrátíte smysl přečteného commentTextu', 17);
INSERT INTO comments VALUES (5024, 'Jiří Bušek', 'Diskutovat se může skoro o všem , to ano , ale ne o Židech, to je totiž popírání holokaustu.', 17);
INSERT INTO comments VALUES (5025, 'Petr Macháček', 'Kolaboranti byli i mezi židovským obyvatelstvem. To nic nesnižuje zrůdnost holokaustu, ale byli.', 17);
INSERT INTO comments VALUES (5026, 'Lukáš Cvrček', 'Polsko během německé okupace neexistovalo a Poláci nemohli o své zemi svobodně rozhodovat. Připisovat proto Polsku podíl na holokaustu je zkrátka pitomost. Samozřejmě se mezi Poláky našli také bezcharakterní udavači, kteří pro svůj profit vydávali skrývající se Židy Němcům, nebo přesvědčení antisemité, kteří se podíleli na pogromech se stovkami obětí, Tito lidé se však dopouštěli z polského pohledu kriminálního jednání. Ne všichni z nich sice byli odhaleni a potrestáni, tak to však během válečných a čerstvě poválečných zmatků chodilo a chodí všude na světě. Na druhou stranu nevidím nic antisemitského na tom, když se někdo v koncommentTextu paušálního obviňování Poláků odváží připomenout, že někteří Židé v zájmu svého přežití přistoupili na spolupráci s Němci, která jim dost prospěla ve válečném úsilí nebo rasové perzekuci. Ať už to byli prominentní vysoce kvalifikovaní vězni (viz např. Ďáblova dílna) nebo různí kápové v lágrech, či představení židovských náboženských obcí, kteří byli přinuceni spolupracovat na organizaci transportů do koncentráků. Tito Židé jednali pod velkým tlakem a rozhodně by nebylo fér z nich dělat nějaké spolupachatele holokaustu. Stejně tak by však bylo nespravedlivé dělat to samé z Poláků, kteří by měli podle izraelských představ plošně pykat za marginální skupinku udavačů.', 17);
INSERT INTO comments VALUES (5027, 'Ivan Vašek', 'Tak antisemitů jsou kvanta.A já se už hodně ptal,a nikdo neodpověděl,proč.', 17);
INSERT INTO comments VALUES (5028, 'Jiří Nosek', 'jsem taky přesvědčen , že žádná tautologie to není', 17);
INSERT INTO comments VALUES (5029, 'Jindřich Hrdlička', 'Myšleno Polsko jako stát, Poláci jako národ. Což nevylučuje, že někteří Poláci se zločinu dopustili.', 17);
INSERT INTO comments VALUES (5030, 'Ivan Vašek', 'Ano,tam pan premiér protiútok významně přehnal.Ale zase:svoboda slova,pravda,informace,diskuze nebo klídek a nohy v teple?', 17);
INSERT INTO comments VALUES (5033, 'Ivan Vašek', 'To máchání je ale důsledkem nezapomínání.A katolická církev údajně tak činila,aby se nestali cílem aktivit Německa katolíci.Což ale nic nemění na tom,že to je politikaření a asi by s tím křesťané Nero(no?)va Říma nesouhlasili.A potom je zde ještě údajně napomáhání nacistickým zločincům post WWII.Ale to nevím už vůbec.', 17);
INSERT INTO comments VALUES (5034, 'Vokoun Vladimir', 'Takhle to holt vypada, kdyz komunisti prohlasi o sobe ze jsou pravicaky. Nastesti jsou Cesi o kousek rozumejsi nez Madari a Polaci.', 17);
INSERT INTO comments VALUES (5035, 'Marcel Pavlík', 'všetci poliaci boli v odboji, nik z nich (minimálne) nenapomáhal? je to rovnaká hlúposť ako zakázať pochybnosti o holokauste. pretože ten zákon zakazuje pochybnosť o číslach, ktoré rastú a rastú. a v tomto prípade zasa zákonom čísla klesnú na nulu, až prejdú do záporných hodnôt.', 17);
INSERT INTO comments VALUES (5036, 'Miroslav Kašpar', '„ Pachatelé nejsou oběti. Židovský stát nepřipustí, aby byli vraždění obviňováni ze své vlastní vraždy,“ To je pravda ale obviňovat Poláky z podílu na holokaustu je blbost, nakonec Poláci byli na řadě hned za Židy', 17);
INSERT INTO comments VALUES (5037, 'Ivan Vašek', 'Já bych si dovolil,paní Potůčková,požádat vás o názor.Díky.', 17);
INSERT INTO comments VALUES (5038, 'Marcel Pavlík', 'kto všetko počas vojny (zhruba sa kryje so spaľovaním) s nemeckou ríšou obchodoval, dodával jej (aj) vojenský materiál, prípadne materiál, ktorý na prevádzku koncentračných táborov NUTNE POTREBOVALI?', 17);
INSERT INTO comments VALUES (5039, 'Ivan Vašek', 'Pane Pavlíku,vždyť to je logické.S čím nesouhlasíte.Nebo Polsko vytvářelo nějaké lágry a tam likvidovali Židy?Hoolcaust byl státní akt,program Německa.', 17);
INSERT INTO comments VALUES (5040, 'Jindřich Hrdlička', 'Mluvíte asi z hladu. Kde jsem měl popírat, že protižidovské akce prováděli i Slované? Celkem se potvrzuje můj odhad vaší osobnosti, nedostatky ve faktografii i v logice musíte kompenzovat aroganci v bezpečí internetu.', 17);
INSERT INTO comments VALUES (5041, 'Frantisek Kofron', 'Mel jsem na mysli hromadne vyvrazdovani. Jinak se bohuzel vrazdi lidi vsech ras a vyznani i v roce 2011 prakticky vsude na svete. To je ale bezna kriminalita. Nikoliv statem organizovane masove vyvrazdovani.', 17);
INSERT INTO comments VALUES (5042, 'Marcel Pavlík', 'v poslednej dobe POSLEDNÉ poľsko na POSLEDNOM území.', 17);
INSERT INTO comments VALUES (5043, 'Ivan Vašek', 'Možná to,že Německo je mezinárodně pobouřeno,že se chtělo podělit o historický zápis WWII,jako vedoucí evropská síla,a Polák jim to zatrhl.', 17);
INSERT INTO comments VALUES (5044, 'Jindřich Hrdlička', 'Kde jste to vzal, proboha?', 17);
INSERT INTO comments VALUES (5045, 'Michal Darmovzal', 'Zvláštní ... tak jako za židovský holokaust mohou prý i někteří Židé, ale Poláci jsou zcela čistí..... ????', 17);
INSERT INTO comments VALUES (5046, 'Frantisek Kofron', 'Polsko nemohlo mit podil na holocaustu, nebot v po vetsinu doby, kdy holocaust probihal, tak Polsko neexistovalo. Bylo okupovano Nemeckem.', 17);
INSERT INTO comments VALUES (5047, 'Ivan Vašek', 'Wiki info.S kritickým a zvídavým myšlením je každá informace vhodná.', 17);
INSERT INTO comments VALUES (5048, 'Ivan Vašek', 'A jak se tedy vypořádat třeba s nacismem?To je také zavírání za názor a těžko může soudný člověk nesouhlasit.', 17);
INSERT INTO comments VALUES (5049, 'Ivan Vašek', 'Proč?Tento stát byl Němci napaden,zmasakrováno obyvatelstvo.Teď je zde otázka,proč tento zákon vzniká a jak ho uchopit.Pokud vzniká proto,že mají být Poláci diskreditování a jejich národní dědictví,odpor proti Němcům a nacistům potlačeno,znehodnoceno,tak je na místě(jako obrana).Pokud tento zákon zakazuje hovořit o části Poláků,kteří zradili svoji zem,tak je to v nepořádku.Ale o to asi nejde.', 17);
INSERT INTO comments VALUES (5050, 'Ladislav Dvořák', 'Zavíráním lidí za názor to začíná. Polsko se opravdu zbláznilo.', 17);
INSERT INTO comments VALUES (5051, 'Ivan Vašek', 'Myslím,že tuto činnost Němci rozšířili.Ale tam asi není spor.', 17);
INSERT INTO comments VALUES (5052, 'Jan Bílý', 'jo očividně. říká někdo koho uráží zmínka o zločinech slovanských národů a vyžaduje k tomu odkazy. pokud si ještě neslyšel o protižidovských pogromech v carském rusku, tak přestaň diskutovat zde a jdi si něco přečíst.', 17);
INSERT INTO comments VALUES (5053, 'Ivan Vašek', 'Ale to by byla vražda,nikoli holocaust.Tam musí být záměr,hromadění v zařízení a hromadné systematické vyvražďování v těchto.', 17);
INSERT INTO comments VALUES (5054, 'Jan Bílý', 'ne národy, jeden národ. židy.', 17);
INSERT INTO comments VALUES (5055, 'Jan Bílý', 'slovo holokaust se začalo používat jako synonymum pro genocidu na židech za druhé sv. války. je to takhle jednoduché', 17);
INSERT INTO comments VALUES (5056, 'Petra Potůčková', 'To, co poslední dobou předvádí polsko, to je nehoráznost.', 17);
INSERT INTO comments VALUES (5057, 'Ivan Vašek', 'Holocaust není o udávání.Holocaust je systém,který zavedli Němci,aby vyvraždili národy.', 17);
INSERT INTO comments VALUES (5058, 'Jindřich Hrdlička', 'Sesadit vás nechci.', 17);
INSERT INTO comments VALUES (5059, 'Kamil Krbálek', 'Když jsem byl malý, vždy se mluvilo jen i holokaustu Židů, vyvražďování Romů byla genocida. Najednou se mluví o Romském holokaustu. Podle mě je to nesprávně, ale budiž, nechť se tedy i genocida Romů nazývá holokaustem.', 17);
INSERT INTO comments VALUES (5060, 'Jindřich Hrdlička', 'Jo, zvyšujete si sebevědomí, v reálu to očividně nedokážete. Odkaz a konkrétní čísla se od vás čekat nedají.', 17);
INSERT INTO comments VALUES (5061, 'Jozef Záhumenský', 'To není pravda. V Topoľčanoch vraždili aj tých chudákov, čo sa vracali z lágrov.', 17);
INSERT INTO comments VALUES (5062, 'Jan Bílý', 'wiki moudrost ne, děkuji', 17);
INSERT INTO comments VALUES (5063, 'Kamil Krbálek', 'Predctete si to na Wikipedii', 17);
INSERT INTO comments VALUES (5065, 'Jan Bílý', 'slovo holokaust má jen jeden význam, to o druhém významu jste si vymyslel.', 17);
INSERT INTO comments VALUES (5066, 'Jan Bílý', 'sprostě obviňuje aspiruješ na hlavního šaška této diskuse', 17);
INSERT INTO comments VALUES (5067, 'Pavel Hauf', 'Proto Ti chytří a pracovití Židé se zmocnili majetku a vlády 1917 v Rusku...', 17);
INSERT INTO comments VALUES (5068, 'Kamil Krbálek', 'Zmatek pramení z toho, že slovo holokaustu má dva významy. V původním významu to bylo systematické vyvražďování Židů německým státem. Ale když se přihlásili i další skupiny, že byly také systematicky vyvražďovány, rozšířil se význam slova na všechny, koho chtěl německý stát vyvraždit. Židé pak to své vyvražďování začali označovat jako Událost Šoa. Jenže vyvražďování Židů nebylo jen záležitostí německého státu a po staletí probíhalo po celé Evropě, i v Polsku a teď ti vypadá, že někdo chce rozšířit význam slova holokaust i na tohle vraždění, jakože holokaust Židů probíhal v Evropě po staletí a můžou za něj všichni. Nemyslím si, že je to historicky správné.', 17);
INSERT INTO comments VALUES (5069, 'Jindřich Hrdlička', 'Pro chytré minusare, i v tom článku polsky představitel potvrdil, že se rozhodně nebude trestat za tvrzení, že i mezi polaky byli udavači.', 17);
INSERT INTO comments VALUES (5070, 'Jan Bílý', 'netykáme si, já ti tykám. zmínka o slovanech není účelová, vyplývá z historie protižidovských pogromů, které byly nejčastější právě u zmíněných národů.', 17);
INSERT INTO comments VALUES (5071, 'Frantisek Kofron', 'Ano, Polaci byli a mozna jeste porad jsou antisemite, ale nebyt Nemecke okupace, tak by Zidy nevyvrazdovali. To mohli delat pred okupaci i po okupaci a nedelali to.', 17);
INSERT INTO comments VALUES (5072, 'Pavel Hauf', 'Židovský pachatelé :) to si tedy dovolil až moc.', 17);
INSERT INTO comments VALUES (5073, 'Jindřich Hrdlička', 'Protože píše o nacistech, nikoliv o Němcích (oblíbený to trik) ale zároveň se sprostě obviňuje konkretne Polsko Polaci z takového antisemitismu, že to bylo údajně důvodem, že tam staly koncentráky.', 17);
INSERT INTO comments VALUES (5074, 'Jiří Horák', '"The Polish-Jewish historian and the Warsaw Ghetto archivist Emanuel Ringelblum has described the cruelty of the ghetto police as "at times greater than that of the Germans, the Ukrainians and the Latvians." https://en.wikipedia.org/wiki/Jewish_Ghetto_Police?oldid=435819677', 17);
INSERT INTO comments VALUES (5075, 'Marián Pilc', 'Říká vám něco matematická statistika? Vůbec, ani kousililínek si neumíte představit, že nějakého Žida štval jeho soused Žid a proto ho v počátku, kdy to jěště šlo, udal? Jsou známé případy kápov, kteří pomáhali nacistům udržovat pořádek v gethech, za kus chleba navíc byli schopni vyvraždit celou rodinu, i samotní přeživší z koncentráků je označovali za zločince. Byla to těžká doba, proto dělat černobílé závěry nepopisuje realitu. To je jako tvrdit, že žádný invalida nemůže být zločincem.', 17);
INSERT INTO comments VALUES (5076, 'Frantisek Kofron', 'V zadne zemi okupovane Nemeckem nebyli Zide vrazdeni pred okupaci ani po skonceni okupace. Z toho lze logicky dovodit zaver, ze za holocaust muze Nemecko. Samozrejme, ze treba zrovna v Polsku nebo i na Ukrajine nasli Nemci dost pomocniku, ale zase jen diky okupaci. Nechapu o co Izraeli jde. Asi o vztahy s Nemeckem. Rozhodne necitim vuci Zidum nenavist, ale nektere streotypy jsou proste platne.', 17);
INSERT INTO comments VALUES (5077, 'Jindřich Hrdlička', 'Neoslovujte tak, jak v reálu nazývají vás. Co to účelové obvinění z antisemitismu?', 17);
INSERT INTO comments VALUES (5078, 'Josef Babka', 'Ono je to těžké hledat jenom viníky a neustále je trestat a vracet se zpět. Nezapomínat je správné, ale nutné myslet vpřed. Vždyť i třeba katolická církev byla pravou rukou Hitlera a přesto jsme jí vrátili v církevních restitucích majetek. A tak to má v demokracii fungovat. Neustále máchání se v minulosti je pozůstatek totalitního myšlení, které škodí demokracii.', 17);
INSERT INTO comments VALUES (5079, 'Tomáš Tatíček', 'Moje slova.', 17);
INSERT INTO comments VALUES (5080, 'Jindřich Hrdlička', 'Netykáme si. Ano, vidím to tam, když se píše účelové jen o Slovanech.', 17);
INSERT INTO comments VALUES (5081, 'Jan Bílý', 'ty se urážíš sám svejma nesmyslama o izraelský snaze něco zakazovat.', 17);
INSERT INTO comments VALUES (5082, 'Jindřich Hrdlička', 'Pardon, četl jste alespoň dany článek?', 17);
INSERT INTO comments VALUES (5083, 'Jan Bílý', 'naprosto blbost, tisíckrát vyvrácená...', 17);
INSERT INTO comments VALUES (5084, 'Tomáš Tatíček', 'Běž urážet zrcadlo.', 17);
INSERT INTO comments VALUES (5085, 'Jan Bílý', 'ty joudo, jak konkrétně chce netanjahu něco zakazovat? navrhuje nějaký zákon? ne. ti, co chtěj zakazovat, jsou poláci. izraelci to kritizují, na což mají právo. nechtějí cokoli zakazovat, ani na to samozřejmě nemají právo. joudo', 17);
INSERT INTO comments VALUES (5086, 'Jan Bílý', 'ještě k těm pomocníkům přidejte maďary, rumuny a chorvaty', 17);
INSERT INTO comments VALUES (5087, 'Robert Máslo', 'A IBM dodala stroje na jejich sčítání...', 17);
INSERT INTO comments VALUES (5088, 'Martin Sedona', 'Zide to nikde nemeli jednoduche. Kam prisli s holym zadkem se za par let stali elitou, stali se brzy bohati (často se venovali lichve) - to moc lide unest nedokazou ani dnes. V obecne rovine lidi malo unesou ze jejich zemi ovlada někdo, kdo to tam nemá koreny (Slovaky z Uherskem parlament zastupovali jenom Zide). Pogromy proti Zidum byly i v zapadni Evrope, nejčastěji když kral moc dluzil a pogromem prestal dluh existovat, pripadne se rozdanim majetku Zidu získal sympatie mistnich lidi (Karlem IV podporovany pogram v Nemecku). I v literature je videt silna averze vuci Zidum (J. Verne, J. Neruda) - je to asi dano kulturou-odlisnosti, která mistnim lidem vadila.', 17);
INSERT INTO comments VALUES (5114, 'Robert Máslo', 'To není jen o holokaustu. Poláci se obecně zabývají hodně 2.ww. A třeba i v takových oblastech jako jsou PC hry. Je to dáno jejich historií - ztráty za 2.ww měli 16% z počtu obyvatel, takže každý z Poláků znal po válce spoustu lidí. kteří jim Němci zabili. Pro srovnání u nás byly ztráty 3% a i v Německu jen 10%.', 17);
INSERT INTO comments VALUES (5089, 'Miroslav Med', 'Já nevidím důvod se v tom pořád matlat a budit emoce (jistě - zapomínat by se nemělo, nicméně nezapomenout lze i bez neustálého okopávání, buzení antipatií a obviňování). Ten polský zákon považuji za poněkud (poněkud dost) kontraproduktivní, stejně jako to lpění Židů na tom, aby je všichni a povinně považovali za ty největší oběti na světě. Je to prostě tak, že žádný národ nedisponuje jen viníky a nebo jen obětmi. Já jako Čech mohu se smutkem a úctou vzpomínat na Čechy zavražděné během německé okupace, mohu být hrdý na ty, kteří přímo ve své vlasti a nebo na některé z front bojovali proti nacistickému Německu a stejně tak mi nezbývá než přijmout, že se našli i tací, kteří s nacisty různými způsoby aktivně spolupracovali. Obdobné je to u všech národů, kterých se válka týkala (každý měl své oběti, hrdiny i zrádce) a to včetně Židů. Není to až tak dlouho, co jsem četl o tom, že i za války byli v německé armádě Židé a o tom, že nějaký takovýto důstojník wehrmachtu dostal vyznamenání za boj na východní frontě (dost možná, že v okamžiku, kdy jeho blízcí umírali v koncentráku) a že i přímo Hitler měl nějakého oblíbence, kterého považoval za nepostradatelného a osobně mu vystavil potvrzení, že není Žid (ačkoliv jím byl). A jelikož tito lidé byli součástí nacistické mašinérie, byli i spoluviníky (nebylo jich asi mnoho, ale prostě byli). Jak už jsem ale psal - to neustálé vytahování kostlivců ze skříní a z toho plynoucí diplomatické přestřelky považuji za zbytečné, nic nepřinášející a nic neřešící.', 17);
INSERT INTO comments VALUES (5090, 'Tomáš Tatíček', 'Ne, polský premiér mě nezajímá, ten je v tomhle případě panák. Zajímá mě, co vadí Netanjahuovi. Ten chce zakázat vyřčení možnosti, že se nějaké špatnosti mohl dopustit i Žid.', 17);
INSERT INTO comments VALUES (5091, 'Josef Babka', 'A co si mám představovat pod takým pojmom "mezinárodní pobouření"', 17);
INSERT INTO comments VALUES (5092, 'Jan Bílý', 'slyšela jsem.. pravda o chazarech už vypátrána byla, jen vy jste o tom ještě neslyšela, vědkyně', 17);
INSERT INTO comments VALUES (5093, 'Rudolf Stary', 'Svycarsky fabrikant Buhrle byl zidovskeho puvodu a prodaval svoje vyborne kulomety po celou dobu valky nacistickemu Nemecku. Tim chci rict, ze thema je vytrzeno z konexu a nedoporucuji v teto diskuzi pokracovat. Je to thema pro historiky.', 17);
INSERT INTO comments VALUES (5094, 'Marián Pilc', 'Nevím, kde v jeho příspěvku berete, že se snaží vyvinit Německo. Je pravda, že Lotyši, Ukrajinci, Slováci, Poláci, nebo taky ve Francii, nadšeně pomáhali Německu s holokaustem? Ano. Zorganizovali to celé Němci? Ano. Který národ má nejvíc lidí označovaných jako Spravedlivý mezi národy? Poláci. Je pravda, že Stalin chtěl vyvraždit všechny Židy v SSSR před svou smrtí a už to jen nestihl? Ano. Svět není černobílý. Za zlo i dobro můžou jednotlivci, i když jsou jich miliony, ne národy, národ je abstraktní pojem a národnost si každý člověk určuje sám, dle toho jak se cítí.', 17);
INSERT INTO comments VALUES (5095, 'Jan Bílý', 'nikdo nic takového netvrdí, na hlavu si upadl ty', 17);
INSERT INTO comments VALUES (5096, 'Petr Koucký', 'Diskuze s Vaňkem (o čemkoliv) nikam nevedou. Je to ztráta času a energie.', 17);
INSERT INTO comments VALUES (5097, 'Jindřich Hrdlička', 'Všichni???', 17);
INSERT INTO comments VALUES (5098, 'Jan Bílý', 'máš podobné problémy s pochopením historie jako polský premiér. a ta tvoje poslední věta je chorý výplod tvého mozku, nikdo nic takového netvrdí', 17);
INSERT INTO comments VALUES (5099, 'Miroslav Čapek', 'Primární podstata ale tkví v onom sporném zákon, který chce trestat za projevení svobodného názoru o tom, že i Poláci udávali. Zákonem se pravda nikdy nepopře. Jako kdyby chtěli, aby si lidi stále mysleli, že zeměkoule je placatá.', 17);
INSERT INTO comments VALUES (5100, 'Robert Máslo', 'Obecně se židy vždy nejvíce soucítili podrobené národy. Byly totiž ve stejné pozici a pronásledovanévu vždy nejspíše zase pomůže jiný pronásledovaný. Takže třeba v Čechách se měli židé (vzhledem k podrobení od Habsburků) celkem dobře.', 17);
INSERT INTO comments VALUES (5101, 'Jan Bílý', 'to v tom příspěvku není. nebo ty to tam vidíš?', 17);
INSERT INTO comments VALUES (5102, 'Jindřich Hrdlička', 'V roce 1919 zuřila v plné síle občanská válka v Rusku, především mezi Rusy.Za této situace dobývat Evropu je nemožné.', 17);
INSERT INTO comments VALUES (5103, 'Tomáš Tatíček', 'Reaguji na kritiku z izraelské strany. Polský premiér řekl, že záměrem zákona není krýt špatnosti spáchané z jakékoliv strany, a opovážil se vyřknout jedno slovo, že špatností se mohli dopouštět kromě Němců i Poláci - to by ještě bylo košér -, ale dokonce i Židé - a na to už se nesmí ani pomyslet. Je nereálné si představovat, že na světě existuje jeden národ sestávající z andělů.', 17);
INSERT INTO comments VALUES (5104, 'Jindřich Hrdlička', 'Jiste, politicky musíte myslet....', 17);
INSERT INTO comments VALUES (5105, 'Jindřich Hrdlička', 'Zato jinde to byla idylka,že...?', 17);
INSERT INTO comments VALUES (5106, 'Jindřich Hrdlička', 'Copak, oblíbená relativizace?', 17);
INSERT INTO comments VALUES (5107, 'Jindřich Hrdlička', 'Protože tam zili miliony Židů. Je hezké, jak se snažíte nenápadně vyvinut Německo...', 17);
INSERT INTO comments VALUES (5108, 'Jindřich Hrdlička', 'Reakce na p. Kováře.', 17);
INSERT INTO comments VALUES (5109, 'Jindřich Hrdlička', 'Protože se čím dál více začal používat termín ,,polské koncentráky,,.', 17);
INSERT INTO comments VALUES (5110, 'Ondřej Horák', 'To tvrdíš leda ty. A nemusíš to sem psát, můžeš se pohádat doma sám se sebou.', 17);
INSERT INTO comments VALUES (5111, 'Tomáš Tatíček', 'Ti slabí a leniví by to nedokázali zorganizovat. Jednalo se o státem řízenou dispozici s majetkem a pracovními silami.', 17);
INSERT INTO comments VALUES (5112, 'Jindřich Hrdlička', ',,jako celek,, jen sprostá urážka od vás.', 17);
INSERT INTO comments VALUES (5113, 'Jindřich Hrdlička', 'Mylíte se (mírně řečeno). Místa byla vybrána dle miry hustoty židovského osídlení. A ti tajemní nacisté byli převážně jaké národnosti?', 17);
INSERT INTO comments VALUES (5115, 'Jiří Horák', 'A Němci to dovedli k dokonalosti.', 17);
INSERT INTO comments VALUES (5116, 'Petr Bačavčík', 'Židé to se Slovany nikdy neměli jednoduché. Poláci, Ukrajinci, Rusové - klasika.', 17);
INSERT INTO comments VALUES (5117, 'Alice Horáková', 'To byla dnes již vyvrácená hypotéza.', 17);
INSERT INTO comments VALUES (5118, 'Jan Prichystal', 'Z uplne stejnych duvodu jako kdyz Cesi udavali Cechy. Proc by muselo jit jen o majetek? Zavist, pomsta, nevrazivost, likvidace konkurenta.', 17);
INSERT INTO comments VALUES (5119, 'Tomáš Tatíček', 'Sakra, kde to jsme? Bude uzákoněno, že Žid nemůže za žádných okolností spáchat něco protizákonného či zlého atd.?', 17);
INSERT INTO comments VALUES (5120, 'Marek Klufa', 'Nebyl z nacistických špiček sám. Z části byli Židy i Himmler, Goebbels, dokonce třeba i Eva Braun.', 17);
INSERT INTO comments VALUES (5121, 'Ondřej Škoch', 'To je oblibena argumentace zidobijcu. Jo my nic proto "pravym Zidum" ale tohle je podvodny agresivni narod na Zidy si hrajici" dokonce pro to vytvorili mnozstvi sikovnych połopravd ne zadni Vyhlazovani Zide nebyli "Chazary"', 17);
INSERT INTO comments VALUES (5122, 'Václav Vaněk', 'Jste kompletně vedle.', 17);
INSERT INTO comments VALUES (5123, 'Petr Koucký', 'Ano. KoncommentText je důležitý. Takže 20 miliónů Rusů mohou být jak oběti tak ale i viníci. Zato 6 miliónů Židů je zákonem chráněných obětí. Hlavně se v tom nehrabat a nezpochybňovat to. Opět zajímavý historický pohled.', 17);
INSERT INTO comments VALUES (5124, 'Jan Prichystal', 'Hitlera podporoval i znamy americky prumyslnik Henry Ford, ktery za to take v roce 1938 dostal jako prvni American (a jako ctvrty clovek vubec) "Grosskreuz des Deutschen Adlerordens" tehdy nejvyssi nacisticke vyznamenani, ktere mohl cizinec dostat. Stejny vyznamenani obdrzel Mussolini ve stejnem roce.', 17);
INSERT INTO comments VALUES (5125, 'Tomáš Chrastný', 'Hm, co pak byl Judenrat? https://en.m.wikipedia.org/wiki/Judenrat', 17);
INSERT INTO comments VALUES (5126, 'Václav Vaněk', 'Antisemitická byla celá Evropa a Rusko. Ovšem pouze Němci z toho udělali zásadní složku jejich politiky se známými důsledky. A faktem je, že jim zbytek podrobené Evropy s tím více méně nadšeně pomáhal, ale teď máme na koho to svést. Ostudy plný pytel a nejen v Polsku.', 17);
INSERT INTO comments VALUES (5127, 'Peter Franek', 'Fakticky mate pravdu, ale koncommentText je dulezitej', 17);
INSERT INTO comments VALUES (5128, 'Petr Golich', 'https://www.youtube.com/watch?v=3LLA4cMuXOc', 17);
INSERT INTO comments VALUES (5129, 'Petr Koucký', 'Pojem msta je Židům cizí?', 17);
INSERT INTO comments VALUES (5130, 'Věra Petrů', 'Polaci byli pred valkou polofasisticka zeme..mysleli si o sobe ze jdou velmoc a vetsina polaku byli antisemiti uz pred tim nez byl Hitler vubec zvolen kanclerem.Pogromy zidu na jejich uzemi jsou dostatecne zname..to seme o Ukrajincich a pohranicnich oblastech, tam se vlastne bojovalo mezi valkama prakticky neustale.Nacisti moc dobe vedeli proc na jeich uzemi delaj vyhlazovaci tabory...', 17);
INSERT INTO comments VALUES (5131, 'Alois Slámečka', 'Přece Palestinci, ne? Bez práce mají koláče.', 17);
INSERT INTO comments VALUES (5132, 'Václav Vaněk', 'Pouze to dokazuje jak dalece se populismus v Polsku - a jinde zprofanoval. A ten ministr se v Mnichově nabodl na vlastní meč.', 17);
INSERT INTO comments VALUES (5133, 'Václav Vaněk', 'Žid udal žida za to, že je žid? Ten pád na hlavu se asi udál.', 17);
INSERT INTO comments VALUES (5134, 'Pavel Houska', 'Napadli Rusko? To se vám to nějak plete, v roce 1919 rusové napadli Polsko, aby dobyli celou Evropu a mohli v ní zavést komunismus.', 17);
INSERT INTO comments VALUES (5135, 'Řehoř Vlasák', 'Nejste náhodou z Ruska? Tam jsou konspirační teorie o chazarech velmi oblíbené...', 17);
INSERT INTO comments VALUES (5136, 'Tomáš Chrastný', 'Organizace Judenrat a Jüdischer Ordnungsdiens?', 17);
INSERT INTO comments VALUES (5137, 'Řehoř Vlasák', 'Myslím, že z jejich strany je to spíš reakce na to, kam se debata o holokaustu dnes stáčí. To máte jako u nás s táborem v Letech.', 17);
INSERT INTO comments VALUES (5138, 'Petr Koucký', 'Něco mi to připomíná. Někdo je slabý duchem a líný a druhý pracovitý a chytrý. Počkejte, že by to bylo o nadřazenosti a podřadných lidech?', 17);
INSERT INTO comments VALUES (5139, 'Tomáš Chrastný', 'To nam zakazou brzy Pianistu, oskarovy mezinarodne uznavany film, kdyz tematika Polaku a Zidu a toho jak lidi byli tehdy “horsi nez vepri” bude cenzurovana. https://en.m.wikipedia.org/wiki/Jewish_Ghetto_Police', 17);
INSERT INTO comments VALUES (5140, 'Luboš Smělý', '"neznám, přesto jsem přesvědčen..." To je tedy ukázková "tautologie".', 17);
INSERT INTO comments VALUES (5141, 'Martin Kodat', 'Vždyť má pravdu. Jestli někdo tvrdí, že žádný Žid neudal jiného tak takový člověk zřejmě spadl na hlavu.', 17);
INSERT INTO comments VALUES (5142, 'Tomáš Kuchař', 'Jsou prostě témata, která se v dnešní prozápadní demokratické společnosti neotvírají. Za žádnou cenu. Ono se toho od středověku až tak moc nezměnilo, jen ta tabu témata jsou nová, protože máme jiné mutukulturní mutirasové multisexuální náboženství.', 17);
INSERT INTO comments VALUES (5143, 'Michal Krásný', 'Máte pravdu. I hodně Němců, kteří nebyli židé skončili v koncentráku.', 17);
INSERT INTO comments VALUES (5144, 'Radek Červenka', 'No to je blábol. I když co od pana Babky očekávat, že', 17);
INSERT INTO comments VALUES (5145, 'Olga Pavlíková', 'Slyšela jsem něco o Chazarech, amerických židech, kteří údajně Hitlera podporovali, neboť údajně mezi židovstvem panuje stejná rivalita mezi kmeny, jako mezi muslimy. Z tohoto pohledu by pak nebylo marné se historicky dopátrat, jak to s těmi Chazary skutečně bylo a je.', 17);
INSERT INTO comments VALUES (5146, 'Radim Svoboda', 'ja jen nechapu, proc to polaci vytahli 73 po valce', 17);
INSERT INTO comments VALUES (5228, 'František Klíma', 'Zeman se jel akorát popást na mrtvole.', 19);
INSERT INTO comments VALUES (5147, 'Řehoř Vlasák', 'Zbytečné dohadování. Fakticky jsou dnes Polsko a Izrael přirození spojenci a partneři, takže izraelští politici by raději měli investovat čas do boje o svou věc v západevropských zemích, které pod vlivem masového mohamedanského přistěhovalectví sklouzávají k tvrdě protižidovským postojům.', 17);
INSERT INTO comments VALUES (5148, 'Tomáš Chrastný', 'Nebyl Heydrich nahodou taky rasovy Zid dle Norimberskych Zakonu?', 17);
INSERT INTO comments VALUES (5149, 'Milan Mroczkowski', 'S tím souhlasím i s tou částí co prohlásil. Izraelská reakce byla nepřiměřená. Ale to nemění na faktu, že zákon je špatný. Jen mluvčí, je dobrý. ale zákon stále špatný. Něco jiného je mluvit a ve skutečnosti prosazovat.', 17);
INSERT INTO comments VALUES (5150, 'Zuzana Vass', 'Páchatelia ako aj obete boli z rôznych národov a etník. Na všetkých môžete ukázať prstom, len v prípade Židov ide o antisemitizmus. Židia sú nekritizovateľní?', 17);
INSERT INTO comments VALUES (5151, 'Miroslav Med', 'Třeba ze strachu? A nebo ve snaze se zavděčit a doufat, že díky určité "spolupráci" bude ušetřen?', 17);
INSERT INTO comments VALUES (5152, 'Michal Urban', 'A Jezis uplne.', 17);
INSERT INTO comments VALUES (5153, 'Michal Urban', 'Ano, to je to jeden z duvodu. Dalsim byla hitlerova osobni nenavist k Zidum. Dalsim potreba vytvorit pro cely nemecky narod spolecneho nepritele. Takze mate pravdu, ale zduraznil bych to, ze neslo jen o ten majetek.', 17);
INSERT INTO comments VALUES (5154, 'Kamil Vrba', 'Polák má pravdu, vždyť i Hitler byl z části Žid...', 17);
INSERT INTO comments VALUES (5155, 'Michal Urban', 'Nemam nic proti Zidum, Polakum ani treba Nemcum - ale tvrdit, ze za WW2 byli naprosto vsichni Nemci spatni, nebo ze mezi Zidy nebylo ani jeden zkazeny, je naprosto scestne. Jak pise nize pan Sturm (lol): "Obětmi holakaustu byli lidé mnoha národností a etnik. A jejich vrazi a lidé nápomocní tomu byli také lidé mnoha národností a etnik."', 17);
INSERT INTO comments VALUES (5156, 'Imrich Sucharský', 'Těch případů bylo hodně např. ve Varšavském ghetu, kde figurovali židé, kteří dělali kapo gestapu a své sousedy nejen udávali, ale naháněli obuškama k transportu.', 17);
INSERT INTO comments VALUES (5157, 'Michal Urban', '"Poláci byli vždycky antisemité. Když v r. 1920 napadli Rusko, tak to tam kostili hlava nehlava - Židy, Bolševiky, ruské carské důstojníky, obyčejné lidi... " Coze? To, ze kosili kazdeho, z nich dela antisemity?', 17);
INSERT INTO comments VALUES (5158, 'Petr Koucký', 'Jestli věříte tomu, že existují případy, kdy Čech udal Čecha, Němec udal Němce, Polák udal Poláka, Rus udal Rusa, tak je statisticky téměř vyloučeno, že by se nevyskytl případ, že by Žid udal Žida.', 17);
INSERT INTO comments VALUES (5159, 'Michal Urban', 'Ne, ale statisticky je podle mne nemozne, aby se to aspon jednou nestalo.', 17);
INSERT INTO comments VALUES (5160, 'Jiří Nosek', 'neznám...přesto jsem přesvědčen, že výrok je pravdivý...', 17);
INSERT INTO comments VALUES (5161, 'Alena Eliášová', 'Třeba znáte aspoň jeden příklad?', 17);
INSERT INTO comments VALUES (5162, 'Jiří Nosek', 'při vší úctě pan premiér má pravdu....je prostě statisticky nemožné, aby nikdo z Židů vinu nenesl...nebyli to jen oběti....ten výrok je pravdivý', 17);
INSERT INTO comments VALUES (5163, 'Alena Eliášová', 'Dopouštěli se pronásledování židů a domnívají se, že útokem nejlépe zakryjí svou ničemnost', 17);
INSERT INTO comments VALUES (5164, 'Alena Eliášová', 'Znáte případ, že by žid udal žida?', 17);
INSERT INTO comments VALUES (5165, 'Alena Eliášová', 'Proč by židi udávali židy? Jejich majetku se stejně nemohli zmocnit.', 17);
INSERT INTO comments VALUES (5166, 'Alena Eliášová', 'Za holokaustem byla především závist lidí duchem slabých a lenivějších vůči pracovitým a chytrým Židům. Jak jinak se zmocnit majetku těch úspěšnějších?', 17);
INSERT INTO comments VALUES (5167, 'Petr Fojta', 'Aha, mezi udavači byli jen poláci, židi nikdy. To je dobře, že je to tak jasné.', 17);
INSERT INTO comments VALUES (5168, 'Adam Farinic', 'prave naopak , porozumeni s mensinami je ze ty nazory rict muzete ale premyslite jak to ktera strana vezme...jinak receno nemyslite jenom na svuj cil ideologii....jeho vyrok byl nestastny prave kuli nedomysleni jak nektere skupiny to mohou zobrat ...fakticky jeho vyrok moh byt ok ale chybelo mu zohledneni pomeru ucasti jednotlivych stran ktere si podle vyroku mohly dosadit ruzne skupiny ruzne, a ubezpeceni ze to neni zadny vyrok na snizeni utrpeni ktere bylo zidum zpusobeno.', 17);
INSERT INTO comments VALUES (5169, 'Jarek Jakubček', 'Cim vic do toho hrabou tim vice to smrdi. Kdyby se ted Polaci nesnazili trapne cenzurovat, malokdo by se o tom tematu bavil.', 17);
INSERT INTO comments VALUES (5170, 'Petr Koucký', 'Zakopaný pes je v této pasáži. Jeden z reportérů se zeptal, zda by byl v Polsku podle nového zákona potrestán za vyprávění příběhu o svých rodičích, které udali Gestapu polští sousedé. „Samozřejmě, že nebude trestné a nebude považováno za zločin říkat, že byli polští pachatelé, stejně jako byli židovští pachatelé, jako byli ruští pachatelé, jako byli ukrajinští pachatelé, ne pouze němečtí pachatelé,“ odpověděl Morawiecki. Pachatelé mohou být příslušníci všech národů s výjimkou židovských pachatelů. To jste potom antisemita. Vcelku zajímavý přístup.', 17);
INSERT INTO comments VALUES (5171, 'Pavel Kovář', 'Poláci v podstatě jako celek, "šli po Židech" které ještě zatím Němci nestačili odstranit, - jako cvičení ohaři sledovači se zájmem o jejich majetek a podvědomě také proto, "že jim Židé, zabili Ježíška". Inu, zbožní to křesťané, ti Poláci.', 17);
INSERT INTO comments VALUES (5172, 'Jarek Jakubček', 'Tak povidejte. Ktery z jejich sousedu je uspesnejsi?', 17);
INSERT INTO comments VALUES (5196, 'Jana Mášová', 'My, co jsme pracovali celý život, máme poslouchat toto o pracovní morálce v Senátu, a ve sněmovně to není lepší. Jen tam sedět a brát peníze. Za to, že navrhne nějakou blbost o kouření? Jen si stále zvedají platy, necudné.', 18);
INSERT INTO comments VALUES (5227, 'Bořek Mezník', 'V tomto koníčku nemá konkurenci v celé střední Evropě.', 19);
INSERT INTO comments VALUES (5173, 'Lukáš Pavlásek', 'Poláci byli vždycky antisemité. Když v r. 1920 napadli Rusko, tak to tam kostili hlava nehlava - Židy, Bolševiky, ruské carské důstojníky, obyčejné lidi... minimálně 60-80 tisíc obětí. Té války se zúčastnil jako politruk i Stalin a pak jim to dal sežrat u Katyně. Nechci prohlašovat, že si to Poláci zasloužili, ale přijde mi dost trapné furt mluvit o Katyni a zapomínat, že Poláci předtím zavraždili podstatně víc Rusů. Přičemž většina mrtvých ruských zajatců zemřela hladem či žízní, což je asi o dost horší než kulka do hlavy. Asi mě za to omínusujete, ale pouze cituji historická fakta. Ostatně, i Čechům a Slovákům Poláci zatápěli, když obsazovali na základě polsko-německého paktu o neútočení Těšínsko, Spiš a Oravu.', 17);
INSERT INTO comments VALUES (5174, 'Luboš Marek', 'Nechápu, proč se Poláci neustále zabývají holokaustem. Trochu mi to připomíná "Babišování" u nás.', 17);
INSERT INTO comments VALUES (5175, 'Radim Zeher', 'Úspěšný? Celej život se dívat přes rameno a stále se klepat, kdy zase začne válka? Já si úspěšný stát představuju jinak.', 17);
INSERT INTO comments VALUES (5176, 'František Sturm', 'To nálepkování, Žid = oběť, Polák = hrdina, je nesmyslné. Obětmi holakaustu byli lidé mnoha národností a etnik. A jejich vrazi a lidé nápomocní tomu byli také lidé mnoha národností a etnik. Tvrdit, že žádný Polák nebo Žid za války nic nespáchal je nesmysl. Toto nálepkování vychází z nacistického pohledu na svět, který lidi odlišťuje tím, že je z lidských bytostí redukuje jen na skupiny Němce, Židy, komunisty, kteří jsou buď dobří nebo špatní.', 17);
INSERT INTO comments VALUES (5177, 'Jan Bílý', 'vypadá to, že pláčeš spíš ty. izrael je úspěšný stát a skutečný antisemiti to nedokážou rozdejchat', 17);
INSERT INTO comments VALUES (5178, 'Josef Babka', 'Mosad', 17);
INSERT INTO comments VALUES (5179, 'Josef Babka', 'Celé je to o falešnosti demokracie, která fakticky vyžaduje jenom to "správné hodnocení historie", ale bojí se zatím potrestat každého, kdo si dovolí jiný názor, bez toho aby tu byl zákon zakazující jiné myšlenky a jiné názory', 17);
INSERT INTO comments VALUES (5180, 'Jiří Horák', 'Zároveň ti hnusní antisemité mají nejvíc vyznamenaných v rámci Spravedlivých mezi národy, na pomoc Židům existovala celá organizace (Žegota), katolík Jan Karski zázračně přežil, aby na Západě podal svědectví o koncentračních táborech (které tam ale nikoho nezajímalo), Armia Krajowa vydávala rozsudky smrti za udávání židů. Není to tak jednoduché.', 17);
INSERT INTO comments VALUES (5181, 'Ondřej Škoch', 'Zbytečné popichování. Poláci byli za 2. sv války antisemité jak prase a nijak se není fo divit že si Polsko nacisti vybrali jako ideální místo pro zřízení vyhlazovacích táborů. Měl by přemýšlet co říká.', 17);
INSERT INTO comments VALUES (5182, 'Jozef Bevilaqua', '"...se ohradili také další izraelští politici..." - to je tak všetko, čo môžu urobiť.A ešte ísť vyplakávať za veľkú mláku.', 17);
INSERT INTO comments VALUES (5183, 'Milan Soušek', '...a i to by bylo moc, tyhle tlachy snad jednou člověka unuděj. Občas si to přijdu přečíst v naději, že se to nějak zlepšilo, nicméně podobný pocit ve mně nezůstal.', 18);
INSERT INTO comments VALUES (5184, 'Zdeňka Veselá', 'Naprostá většina jich tam kompetentních je, jenže ti pracují v pohodě a není o nich tolik slyšet. Že tam ale lidi zvolí někoho typu Čuba a podobně, to je chyba voličů. A je to dáno i tím, že spousta z nich bere volby do Senátu jako nějaké druhořadé, protože často slyší, jaká zbytečná instituce to údajně je. To by bylo nezbytné změnit.', 18);
INSERT INTO comments VALUES (5185, 'Miluše Welnová', 'V senátu to tak jednoduché není,je to dřina.Senátoři se musí šetřit,bylo by škoda,kdyby se nám roznemocněli,unavili a pak,nedej Bůh,tam přez rok nebyli!No to nevím co by s náma bylo.', 18);
INSERT INTO comments VALUES (5186, 'Arnold Lička', 'Článek mi udělal radost - tím, že má chuť občan Miroslav Korecký se vyjadřovat pouze jednou za týden. - To naše správcová to ráda dělá také, musel jsem ji vysvětlit, že mě malostranské drby o druhých nezajímají. Pravděpodobně existují profese, ke kterým toto maloměšťanské vyjadřování jaksi patří.', 18);
INSERT INTO comments VALUES (5187, 'Antonín Babáček', 'Průřez národem', 18);
INSERT INTO comments VALUES (5188, 'Antonín Babáček', 'K životu potřebujete jediné...neumřít', 18);
INSERT INTO comments VALUES (5189, 'Antonín Babáček', 'býval ten Korecký lepší, když byl v Týdnu. Teď je takový...bezzubý', 18);
INSERT INTO comments VALUES (5190, 'Hana Ulrychová', 'Ta pasáž o Senátu tak nějak přesně splňuje zadání. Nebo snad Babiš už nechce Senát zrušit?', 18);
INSERT INTO comments VALUES (5191, 'Karel Kuttelwascher', 'Pane Korecký proč jste "zahájil" mediální "demontáž" Senátu?To Vám nařídil Mára Prchal?Vy jste opravdu, ale opravdu jen slouha Babiše.To není V Praze neskonale více problémů, jež se dotýkají jeho obyvatel?Neschopné vedení magistrátu Vám nevadí?Proč "pomocí" Malostranského zápisníku, tedy navýsost pražského fenoménu, se pouštíte do celostátních záležitostí?', 18);
INSERT INTO comments VALUES (5192, 'František Pokorný', 'Pani Veselá,souhlasím s Vámi,ale to by tam museli být LIDI KOMPETETNÍ a ne tam být jen jako "FIGURA !!!', 18);
INSERT INTO comments VALUES (5193, 'František Pokorný', 'Sledoval jsem spolek senátorů a jejich jednání ze dne února .Schůzi řídil s.Kubera ,účast 25 max.30 senátorů ,"koktala" tam senátorka v modrých šatech , kterou Kubera pořád musel usměrňovat ,vůbec nevěděla o čem mluví....Zkrátka velký BĚS a ještě větší UBOHOST za ty nýmandy,které tam jsou !!!', 18);
INSERT INTO comments VALUES (5194, 'Miloš Setík', 'Hezké no, ale co s tím jako? Všechno zůstane při starém. A to je tam vše říkáno velmi, velmi opatrně.', 18);
INSERT INTO comments VALUES (5195, 'Zdeňka Veselá', 'Nepřála bych této zemi, aby se v plné síle ukázalo, proč je Senát zapotřebí... Kdo chce zrušit Senát, ten v podstatě útočí na základní principy demokracie (tím nemyslím vás, ale ty politiky, kteří by tuto "neužitečnou" komoru Parlamentu chtěli odstranit).', 18);
INSERT INTO comments VALUES (5226, 'Robert Máslo', 'Na rozdíl od včerejška tento sjezd žádné zlato nepřinese.', 19);
INSERT INTO comments VALUES (5197, 'Jiří Nosek', 'docela vtipný....ale řekl bych opatrně vtipný....takové suché vtipné konstatování....být panem Koreckým zkusil bych to trochu zostra....národ to má rád (já taky)', 18);
INSERT INTO comments VALUES (5198, 'Alena Eliášová', 'Možná někdo potřebuje ke životu senát. Já nikoli', 18);
INSERT INTO comments VALUES (5199, 'Vlada Vanek', 'To vi každý, kdo v té době žil,a lidé jako Bureš udávali,aby mohli jezdit na západ ..', 19);
INSERT INTO comments VALUES (5200, 'Tomáš Cibulec', 'Jak víte jaké jsou metody StB?', 19);
INSERT INTO comments VALUES (5201, 'Vlada Vanek', 'Je mi líto vaší duše.... myslíte ze ČSSD skončí na smetišti dějin jako zemanova SPOZ...', 19);
INSERT INTO comments VALUES (5202, 'Vlada Vanek', 'Bohužel stále pravdivé... .... tím že se pomocí StB metod bude zamlčovat pravda, pomůže Pouze lidem jako vy ...', 19);
INSERT INTO comments VALUES (5203, 'Vlastimil Kocurek', 'Přesto všechno, že jsem si tím i ublížili, museli dát voliči najevo, že nám není jedno, jak ty předchozí vlády zacházely s republikou a lidmi. Jejich nepracovitost a neschopnost byla do nebe zvolající. Jen papalášovali a politikařili. Výsledkem jsou nedostavěné dálnice ( už měly být podle plánu postavené, ale neudou ani v roce 2050) a žel. koridory. Volič nemůže říct, to nevadí, dělejte to tak dále, protože takový postup by nás úplně vyčlenil od vyspěých zemí, stali bychom se jen muzeem. Tím v podstatě dnes už jsme.', 19);
INSERT INTO comments VALUES (5204, 'Jaromír Vít', 'Těmi jeho pravicovými řečmi o nemakačernkách tomu akorát dal korunu.', 19);
INSERT INTO comments VALUES (5205, 'Jiří Babinský', 'bohužel to není osobnost která by čssd mohla nějak pomoci', 19);
INSERT INTO comments VALUES (5206, 'Marta Doubravová', 'Jsem chronický nevolič ČSSD,ale když už, tak jediný volitelný v tom panoptiku je právě hovořící Zimola. Ostatní mi připomínali sjezd bývalého výboru KSČ.', 19);
INSERT INTO comments VALUES (5207, 'Jiří Babinský', 'Chovanej je tam asi tak jedinej kdo ví o čem mluví , jinak mi to přijde jako schůze komunistů před revolucí , také milion slov o ničem , mluví mluví a nic neřeknou , neřeknou že čtyři roky cucaly ze státního koryta , neřeknou že jakmile si plácly s Kalouskem začal jejich sešup protože se věnovaly pouze těžbě mamonu přesně jak Kalous potřeboval , odepsaly se a ted jen žvaní a žvaní', 19);
INSERT INTO comments VALUES (5208, 'Věra Trnková', 'Úžasný pan prezident, mluvil mi z duše', 19);
INSERT INTO comments VALUES (5209, 'Martin Sedláček', 'Ten Chovanec je neskutečně odpornej...', 19);
INSERT INTO comments VALUES (5210, 'Martin Sedláček', '"Babiš řekl, že jsme nepotřební" - no to řekl, Chovanče, naprosto přesně. Vy můžete jen po čtyřech přilézt a pěkně poprosit, jinak končíte. Takže asi tak...', 19);
INSERT INTO comments VALUES (5211, 'Martin Sedláček', 'Asi nhějaká socanská logika - všichni přijímají odpovědnost za debakl, ale všichni kandidují na předsedu...', 19);
INSERT INTO comments VALUES (5212, 'Michal Zelený', 'Ja to cetl, ze "strana je v cloudu"... Uz mi z toho taky sibe :-)', 19);
INSERT INTO comments VALUES (5213, 'Michal Zelený', 'Hamacek je stejny bezbarvy bambula, jako Boza Sobotka. Jestli si ho zvoli, tak po ristich volbach uz bude vymalovano. Na druhou stranu, dluzi Altnerovym dedicum spoustu penez. Leccos by se zanikem CSSD vyresilo.', 19);
INSERT INTO comments VALUES (5214, 'Tomáš Jouský', 'Tyhle přenosy ze stranického sjezdu socdem jsou skvělou ukázkou, proč ČSSD patří minulosti.', 19);
INSERT INTO comments VALUES (5215, 'Robert Máslo', 'jj budu zklamaný .', 19);
INSERT INTO comments VALUES (5216, 'Alex Slavíček', 'dinstbir nekandiduje nebo jo?', 19);
INSERT INTO comments VALUES (5217, 'Alex Slavíček', 'obavam se ze tato strana je v coudu. proflakle obliceje kam se clovek podiva a zadny program, aujal. to i ti komunisti si drzi svoje', 19);
INSERT INTO comments VALUES (5218, 'Robert Máslo', 'Taky. Ale pokud to nebude Dienstbier nebo Škromach tak budu zklamaný .', 19);
INSERT INTO comments VALUES (5219, 'Jiří Veselý', 'Chatař Zimola, pučista z Lán, je přece očištěn - milionové výdělky podřízených je jen malá lapálie. Má rád Babiše se všim všudy a to je jeho největší plus.', 19);
INSERT INTO comments VALUES (5220, 'Robert Máslo', 'Chápu Vás, ale nedočkáte se. Ono je totiž nesmyslné zkoušet dělat nějakou pravou nebo levou politiku, když už teď je to řízeno z EU a budoucnu bude ještě víc. Samozřejmě, že si můžete dát do programu třeba, že věci pro kojence budou v nižší sazbě DPH (levicový bod), ale EU Vás to stejně nepovolí. I kdybyste získal přes 50%. Nebo si tam můžete dát třeba nějaké pravicové body (jako svoboda mít zbraň) a dopadnete úplně stejně. Budoucí politika bude jednoznačně o vztahu k EU a teprve po případném vystoupení by se dala dělat nějaká pravo-levá politika. Strany, které zůstanou v tradičním pravo-levém schématu časem skončí - až si všichni voliči tuto novou situaci danou EU uvědomí.', 19);
INSERT INTO comments VALUES (5221, 'Mirek Tureček', 'Ja se na toho noveho lidra ČSSD ukrutne těšim...', 19);
INSERT INTO comments VALUES (5222, 'Filip Šlesinger', 'No, tak se jen dohádají a půjdou do prde... však vy víte kam.', 19);
INSERT INTO comments VALUES (5223, 'Alex Slavíček', 'pravice a levice jsou uz jako politicke smery prekonany. v zasade jde o demokracii, a efektivni rizeni penez, ktere my vsichni statu odevzdavame. nepochybne i o zahranicni politiku, ktera je vsak celkem dane tim, kde se CR nachazi. takze je tady balik stran, ktere jsou v pruseciku, a pak 2 extremiste - asocialni ods a xenofobni spd. Topka bude mit naslapnuto hned jakmile se zbavi kalhouska. chtelo by to vsechno nechat projit sitem, refreshnout a pak si muze Andrej jet uzivat.', 19);
INSERT INTO comments VALUES (5224, 'Alex Slavíček', 'dinstbir - to je rodily mluvka a jak dokaze urazit v jedne vete asi pet svych spolubojovniku', 19);
INSERT INTO comments VALUES (5225, 'Robert Máslo', 'Také bych si přál aby vyhrálo křídlo Valachové a Dienstbiera . Pak už by od ČSSD byl klid.', 19);
INSERT INTO comments VALUES (5229, 'Jaromír Vít', 'Oni se už asi nezmění. Místo zastavení privatizace obecních bytů chtějí novou výstavbu a tím likvidovat zeleň. Proti zvyšování důchodů taky podivné řeči. Publikum se chovají jako náctiletí, neposlouchají přednášející a neustále ruší. Atd...', 19);
INSERT INTO comments VALUES (5230, 'Marta Doubravová', 'P.P.S.: Moderátorka je tam musí tišit jako malé děti. To je vizitka...', 19);
INSERT INTO comments VALUES (5231, 'Bořek Mezník', 'Jaké jsou sázky?', 19);
INSERT INTO comments VALUES (5232, 'Marta Doubravová', 'Nemohu si pomoct, ale jak ted mluví Zaorálek, připomněl mi Pitkina. P.S.: a ten hluk v publiku, to je děs běs. Proč tam tedy lezou, když nechtějí naslouchat?', 19);
INSERT INTO comments VALUES (5233, 'Martin Kukol', 'Hybeš, Šmeral, Němec, Hampl a další významní představitelé sociální demokracie se musí obracet v hrobě, kam strana došla...', 19);
INSERT INTO comments VALUES (5234, 'Radek Zlámal', 'Můžete jmenovat ty poctivé strany? Já tam nevidím ani jednu. Snad piráti, ale ti se ještě nevymezili a s ničím konstruktivním doposud nepřišli. Dále si díky politickým funkcím nenahrabali lidi z ANO a komunisti s okamurovci, jenže ti zatím nedostali příležitost. Ale ani jednu z mnou zmíněných, předpokládám, jste na mysli neměl, že?', 19);
INSERT INTO comments VALUES (5235, 'Petr Přikryl', 'K.Valachova: lyžařské kurzy zdarma a mohli jsme mít hlasy rodičů a prarodičů... To je politička nejvyššího levelu', 19);
INSERT INTO comments VALUES (5236, 'Martin Kral', 'Socani musí být úplně v rozkladu. Na sjezd si pozvou svého hrobaře a megazloděje že Slovenska, který mafiánský kapitalismus dovedl k dokonalosti. A k tomu mají přitažlivý program okrádat úspěšně pracující na progresivní dani. Teď si ještě zvolí podrazáka a už fakt nevím, kdo tohle ještě může volit. Ta impotence vyprodukovat socdem leadera je impozantní.', 19);
INSERT INTO comments VALUES (5237, 'Filip Šlesinger', 'Já to tak po očku sleduji a něketří diskutující, všeobecná nálada a kulisy okolo mi neustále připomínají geniální dílo Miloše Formana Hoří, má panenko.', 19);
INSERT INTO comments VALUES (5238, 'Marie Zelená', 'Nezklamal, opět narvanej jak taška z Lidlu...', 19);
INSERT INTO comments VALUES (5239, 'Vladimír Smolík', 'Nejsem volič ČSSD, ale stav soc. dem. mne nenechává klidným, neboť každá země, chceteli zdravý politický systém potřebuje silnou levicovou demokratickou stranu. Bohučel Zeman tuto stranu pomohl ze msty docela efektivně destruovat. Pomohl vyniknou lidem s odporným charakterem a snaží se o to neustále. Těžko říct zda v soc. dem. zvítězí poctivá demohratická politika, osobně si myslím, že ne. Voličstvo je momentálně nakloněno silám, pro které je demokracie spíš přítěž, což v podstatě potvrdila prezidentská volba. Byl bych rád za dvě silné tradiční pol. strany napravo/nalevo, které dělají poctivou politiku. Ta situace tady již byla, ale nepodařilo se to zvládnout. Proč? To je na jinou diskusi. Já jen doufám, že se funkční politický systém v budoucnu podaří docílit. Bude to však chtít více vzdělaných a poctivých lidí v politice.', 19);
INSERT INTO comments VALUES (5240, 'Jaromír Hlubek', 'ČSSD by se už konečně měla dostat do stínu. Svou činností za předchozích 27 let jen dokázali, že demokratickou politiku neumí. Sám tomu nevěřím, ale nejlepší premiér z řad ČSSD byl Jyrka Paroubek. Labutě prý zpívají až těsně před smrtí, tak zpívá i naše "slavná" ČSSD.', 19);
INSERT INTO comments VALUES (5241, 'Vlada Vanek', 'Bylo dojemné,jak si zeman dal s ficem dvojitého brežněva... pak už ale Fico moc nebodoval když pochválil ČT za objektivitu.... zřejmě chudák netuší , že zeman může plácat pouze v TV Barrandov...', 19);
INSERT INTO comments VALUES (5242, 'Vlada Vanek', '... Škoda že ten " moudrý" neporadil své SPOZ..... A nyní radí ČSSD aby se dostala na stejná procenta...', 19);
INSERT INTO comments VALUES (5243, 'Jan Blana', 'A to jste doma v kuchyni byli plni očekávání?', 19);
INSERT INTO comments VALUES (5244, 'Vlada Vanek', 'Četl jste v mém některém příspěvku nějakou vulgaritu! zeman přivedl úctu k nejvyššímu úřadu na samé dno...Jeho SPOZ propadla u všech voleb,i s teamem kterým se obklopil! Kancléř čeká 5 let !!! Na prověrku... a dalších 5 bude čekat', 19);
INSERT INTO comments VALUES (5245, 'Jan Blana', 'Moudrý prezident Zeman, zvolený moudrými občany krásné ČR pronesl moudrá slova a záleží na ČSSD jak s nimi naloží. Sobotka nikdy neměl být předsedou ČSSD.', 19);
INSERT INTO comments VALUES (5246, 'Václav Růžek', 'Prezidentovo vystoupení bylo takové, jak jsme očekávali. nenávistné ke kritikům, urážlivé k demokratům a naprosto oddané Andreji Babišovi.', 19);
INSERT INTO comments VALUES (5247, 'Radovan Sýkora', 'Já myslel, že 10 let mrtvý Havel', 19);
INSERT INTO comments VALUES (5248, 'Radek Zlámal', 'Tak kdy se dozvíme, kterej lump bude těm vyžírkům předsedat?', 19);
INSERT INTO comments VALUES (5249, 'Jan Lukš', 'Milda opět na šrot.', 19);
INSERT INTO comments VALUES (5250, 'Václav Růžek', 'Je docela vtipné, když Zeman mluví o zradě. Zeman, který si založil truc stranu Strana přátel Miloše Zemana. Stranu, která přes obrovskou kampaň skončila v politickém septiku se ziskem v řádu promile. A ještě vtipnější je, když podporuje Zimolu a Chovance a zároveň vyzývá socany, aby nebyli stranou podrazáků...)))', 19);
INSERT INTO comments VALUES (5251, 'Pavel Zajíček', 'a jeste jim doporuci at nejdou do vlady ale vymeni podporu stbaka za nejake namestkovske korytko... ten clovek je neskutecny.', 19);
INSERT INTO comments VALUES (5252, 'Václav Kureš', 'Nadstranický prezident, burcuje pro spojení s STbákem a dotačním podvodníkem. Kde jsou tvé předvolební sliby lháři!!!!!', 19);
INSERT INTO comments VALUES (5253, 'Libor Walichiewicz', 'Však brzo bude výročí.', 19);
INSERT INTO comments VALUES (5254, 'Pavel Zajíček', 'Dnesni dobu bych nazval verejnym utokem komunistu na cr. Kdysi tahali v zakulisi za nite babis, zemanuv nohsled slouf a td... Ted uz ta ruda banda nepokryte hazi vidle do demokracie a dela si co chce jako nekde v rusku. Lide za slib zvance udelaji cokoliv... ten moralni dno mnoha ceskych obcanu je odpudive.', 19);
INSERT INTO comments VALUES (5255, 'Jan Lukš', 'To je jeden grázl vedle druhého. Mildovo proslov byl taky výživný.', 19);
INSERT INTO comments VALUES (5256, 'Martin Kukol', 'Aby byl Dolejš předseda ČSSD, musel by být slučovací sjezd s KSČM.', 19);
INSERT INTO comments VALUES (5257, 'Martin Sedláček', 'Jen aby se to kvůli němu nezačalo prát...', 19);
INSERT INTO comments VALUES (5258, 'Martin Janda', 'jako každý, kdo ji podstoupil', 19);
INSERT INTO comments VALUES (5259, 'Otta Zabilanský', 'Socani se plácli přes kapsu a objednali si baviče a lidového vypravěče Zemana. Menšík už je po smrti, tak holt musí brát co je. Eman je jako bavič jistě levnější - jako odměna mu bohatě postačí tlačenka s cibulí -hojně politá octem a paňák podřadné kořalky ....Jinak sranda s ním jistě je, to zase jo...', 19);
INSERT INTO comments VALUES (5260, 'Bořík Pulkrábek', 'Jiří Dolejš', 19);
INSERT INTO comments VALUES (5261, 'Marek Kroupa', 'Myslím, že socanům by k úspěchu pomohl někdo jako Středula. Někdo s ostrou levicovou rétorikou. Z ČSSD se stala strana papalášů a to nezmění ani Hamáček ani Chovanec.', 19);
INSERT INTO comments VALUES (5262, 'Martin Sedláček', 'Já vám to vysvětlím - nesnáším Zemana a z ČSSD mám už jenom srandu, a to náramnou...', 19);
INSERT INTO comments VALUES (5263, 'Tomáš Cibulec', 'Už je to zbytečné, jste zaseknutá gramofonová deska.', 19);
INSERT INTO comments VALUES (5264, 'Jiří Dvořák', 'Tak končí (aspoň doufám !) naše komédie. Zlo prohrává a dobro žije. Vám, paní, vidím, se slzy v oku blyští, však chodí to tak pouze na jevišti. V životě našem opáčně to bývá. Tam dobrák úpí, vrch má duše křivá. Jak ale říkám na každičké štaci: Svět bude lepší, dáme-li si práci. Je marné vzlykat, modliti se zdrávas, vše závisí jen na vás, na nás, ale hlavně teda tady vlevo na vás !', 19);
INSERT INTO comments VALUES (5265, 'Radim Černý', 'Fico, v první řadě vyměň sebe, nedávej zde rozumy', 19);
INSERT INTO comments VALUES (5266, 'Dalibor Zverbík', 'Ty vole omylem kliknu a ze skla mě vyhrožuje Fico že chce zase vyhrát v SK volby. Všichni mu plácají a MZák je zase na šutr.', 19);
INSERT INTO comments VALUES (5267, 'Martin Sedláček', 'Já myslím, že potvrdil, že je Č...', 19);
INSERT INTO comments VALUES (5268, 'Stanislav Přibyl', 'To je dilema. Zdejší inteligenti nesnáší ČSSD a stejně tak i Zemana. Jak to jen propojit. Když bude někdo kritizovat socany, budou rádi, ale když je to Zeman, tak je to hulvát, co si to k nim dovoluje. Ale ono je to vlastně ani nezajímá - jdou si jen sprostě zanadávat.', 19);
INSERT INTO comments VALUES (5269, 'Petr Ševčík', 'Jestli poslechnou svého úhlavního hrobaře Zemana a připojí se k druhému hrobařovi Babišovi, bude s nimi konec....', 19);
INSERT INTO comments VALUES (5270, 'Martin Sedláček', 'A bude na závěr "My jsme ten sprááávnej tým" nebo jiná kolektivní hymna? Bez toho to nemá cenu. Jo, nejsou peníze, aha...', 19);
INSERT INTO comments VALUES (5271, 'Jan Kovařík', 'Chovanec přežral i bandáž žaludku', 19);
INSERT INTO comments VALUES (5272, 'Libor Walichiewicz', 'O jéje, už se zase rozdává.', 19);
INSERT INTO comments VALUES (5273, 'Josef Němeček', 'Státní to on je...', 19);
INSERT INTO comments VALUES (5274, 'René Burkoň', 'To prostredi sjezdu zcela jasne odpovida situaci CSSD.....cekam uz jen kapelu o jednom cloveku s klavesy, vlastnim ozvucenim a honorarem 3tis.', 19);
INSERT INTO comments VALUES (5275, 'Ivan Růžička', 'ano, v přímém přenosu je to zvlášť odporné...', 19);
INSERT INTO comments VALUES (5276, 'Josef Němeček', 'Smuteční.', 19);
INSERT INTO comments VALUES (5277, 'Pavel Černý', 'Socani založili svou politiku na slibování a nepočítali s tím, že objeví Babiš který bude slibovat ještě víc.', 19);
INSERT INTO comments VALUES (5278, 'Roman Kozel', 'životní minimum, od kterého se odvíjí peníze těm lenochům, zvýšila naposledy ministryně za TOP 09 a v braní peněz pracujícím, respektive zvyšování daní..tak na to byli jiní přeborníci.', 19);
INSERT INTO comments VALUES (5279, 'Jakub Rous', 'No tak jen abyste se stejnou vervou informovali i o sjezdech stran jako třeba ODS, SPD, nebo třeba KSČM :-)', 19);
INSERT INTO comments VALUES (5280, 'Karel Adamus', '...zajíma to vůbec ještě někoho? Myslím dění v ČSSD?Každý ví,že ČSSD dluží 500 mil. Kč z toho 330 mil. Kč JuDR. Altnerovi resp. jeho dědicům,pak 100Mil. Kč za volby atd. a tito rádobi politici chtějí ČR vládnout? Ne...jde jím jen a jen o koryto,plné koryto,kde budou u něj .... až z toho budou říhat. Holá zkutečnost.', 19);
INSERT INTO comments VALUES (5281, 'Čestmír Poštolka', 'Zeman ve svém projevu opět potvrdil, že je státní s velkým S. Bravo.', 19);
INSERT INTO comments VALUES (5282, 'Vlada Vanek', 'To je gól! Fico chválí ČT24, prý nedoporučuje občanům Slovenské republiky pozorovat jejich média! Zřejmě netuší že nejoblíbenější TV Zemana je Barrandov, Hnojomet....', 19);
INSERT INTO comments VALUES (5283, 'Stanislav Přibyl', 'a kdo je tvůj prezident - inteligente', 19);
INSERT INTO comments VALUES (5284, 'Ivan Růžička', 'drahé vilky', 19);
INSERT INTO comments VALUES (5285, 'Stanislav Přibyl', 'Tak to máte docela slabou vůli, že se kvůli tomu chováte hůř než on.', 19);
