INSERT INTO comments VALUES (5327, 'Bořík Pulkrábek', 'Jiří Dolejš - ideální kandidát na předsedu ČSSD, jedinej sociální demokrat, kterýho v politice vidím, když tak koukám na podiu na Středulu, kterej si tak trochu říká o kandidaturu...', 19);
