INSERT INTO comments VALUES (5330, 'Martin Janda', 'hele Vaňku, nehádal ses tady ještě nedávno do roztrhání těla, že Zeman nevyhraje?', 19);
INSERT INTO comments VALUES (5331, 'Martin Janda', 'Těžko, já jsem byl rozhodnutý volit Zemana od té doby, kdy lidem cpali před objektivy kamer červené karty a na Zemana létala vajíčka.', 19);
INSERT INTO comments VALUES (5332, 'Vlada Vanek', 'Zeman nezklamal, plácal jako vždy, škoda že neporadil své SPOZ.. Teď mají v ČSSD šanci díky jeho radám je následovat..', 19);
INSERT INTO comments VALUES (5333, 'Martin Sedláček', 'Já myslím, že Babišovi jsou naprosto ukradení...', 19);
INSERT INTO comments VALUES (5334, 'Zdeněk Wernhart', 'Ještě lepší by bylo se se ctí rozpustit a začít znova, s jinými lidmi a s úplně jiným programem..', 19);
INSERT INTO comments VALUES (5335, 'Ivan Růžička', 'Neber si Boha do úst, darebáku pekelnej....', 19);
INSERT INTO comments VALUES (5336, 'Karel Halberštádt', 'Piráti hodně leží v žaludku Zemanovi i Babišovi. Jsem za to rád.', 19);
INSERT INTO comments VALUES (5337, 'Tomas Krejzek', 'V tobe', 19);
INSERT INTO comments VALUES (5338, 'Martin Sedláček', 'Ale pobavil jste...', 19);
INSERT INTO comments VALUES (5339, 'Jakub Musil', 'Pokud si chce ČSSD znovu získat důvěru voličů, měli by si její členové zvolit nové vedení složené z neznámých tváří i za cenu velkého risku. Její známé osobnosti jako jsou Chovanec, Zimola nebo Hamáček by znamenaly další propad podle mě.', 19);
INSERT INTO comments VALUES (5340, 'Zdeněk Wernhart', 'Úžasné. Sociální demokraté, řiďte se výzvou prezidenta. Pomůžete tím sobě i státu... :-D', 19);
INSERT INTO comments VALUES (5341, 'Martin Janda', 'Zeman měl mít touhle dobou podle antizemanovců před sebou poslední dva týdny na Hradě. Kde se stala chyba?', 19);
INSERT INTO comments VALUES (5342, 'Lukáš Adamec', 'Půjdou light oramžový komunisté do vlády s radikálními komunisty a STBáky z ANOferu nebo nepůjdou s obviněnými zločinci do vlády?', 19);
INSERT INTO comments VALUES (5343, 'Ivan Růžička', 'Ne, to byl příprava na "vtipný" bonmot - Husák - Štěpán - aby co nejvíce znectil hejtmany, kteří ho nevolili, kterým to samozřejmě "vůbec nevyčítá" Zeman je ... - doplňte si cenzurovatelný výraz dle svého gusta strašně smutný - tohle Česko', 19);
INSERT INTO comments VALUES (5344, 'Ladislav Polák', 'Pro Chovance a Hamáčka je příliš nebezpečný.', 19);
INSERT INTO comments VALUES (5345, 'Martin Sedláček', 'Tak dědek nezklamal - krystalická esence pokrytectví a ubohosti. Tak si ho, "přátelé", hezky užijte...', 19);
INSERT INTO comments VALUES (5346, 'Otomar Thoma', 'ale chybí - kapitál, vyšší mzdy, poctiví politici s jasnou vizí ...', 19);
INSERT INTO comments VALUES (5347, 'Martin Kukol', 'Zeman nějak není v kondici - jeho proslov býval vždy plynný, nyní se ale dost seká a zapomíná...', 19);
INSERT INTO comments VALUES (5348, 'Ivan Růžička', 'Mstivá, zlá kreatura...', 19);
INSERT INTO comments VALUES (5349, 'Kamila Kalenská', 'Pardon "Zdiskreditovali"', 19);
INSERT INTO comments VALUES (5350, 'Ladislav Polák', 'Ještě jsem na těchto diskusích nezaznamenal člověka prolezlého nenávistí, žlučí a ubohostí jako jste Vy. Musíte být hodně nešťastný člověk!', 19);
INSERT INTO comments VALUES (5351, 'Mojmír Přívara', 'Zeman už účtuje', 19);
INSERT INTO comments VALUES (5352, 'Kamila Kalenská', 'ČSSD sice není pro mě volitelná strana, nicméně je lepší když v politice působí takováto tradiční strana, než pochybné projekty různých populistů a nacionalistů (ANO, SPD, Věci veřejné apod.) Měli by si do čela zvolit paní Žateckou. Mohla by straně přinést obrodu narozdíl od ostatních korytářů a rozdělovačů (Zimola, Sobotka, Hašek, CHovanec, Hamáček, Dientsbier,) co ji zdiskreditovaly.', 19);
INSERT INTO comments VALUES (5353, 'Jenda Benda', 'hasičskej bál z Hoří má panenko je proti tomu hadr', 19);
INSERT INTO comments VALUES (5354, 'Alena Eliášová', 'Proč by muselo něco chybět? Měli jsme prostě vyšší ambice.', 19);
INSERT INTO comments VALUES (5355, 'Tomáš Cibulec', 'Nikdo nechce tolerovat výsledek voleb, pokud se budeme zaměřovat na jednu osobu, tak tento stát se nikam neposune. Sakra lidi pojďme makat!!! To že někdo dělá obstrukce a nechce pracovat na zvelebování státu je jeho problém a nemyslím si, že by bylo zásadní, řešit jednu osobu. Je tu spoustu jiných věcí které by se měly řešit a odbornická vláda je tomu důkazem, i když nemají důvěru makají a makat chcou protože jsou to lidé co opravdu chtějí změnu a ne jenom žvanit o tom jak to umí Kalousek a podobní....', 19);
INSERT INTO comments VALUES (5356, 'Alena Eliášová', 'Strana, která bere penize těm pracovitým a rozdává je lenochům, nemůže nikdy patřit mezi ty, které volím', 19);
INSERT INTO comments VALUES (5357, 'Vlada Vanek', 'Tím doufám chcete říct že Zeman pouze plácal.... Je přece zdravý a silný! Také ho vždy obdivuji když vynáší dva členy ochranky po schodech nahoru...', 19);
INSERT INTO comments VALUES (5358, 'Ladislav Polák', 'Prezidentské volby už skončily, už byste měl skončit s tím plácáním taky!', 19);
INSERT INTO comments VALUES (5359, 'Mojmír Přívara', 'mi to příjde, jak kdyby šmejdi prodávali vysavače někdě v kulturáku', 19);
INSERT INTO comments VALUES (5360, 'Vlada Vanek', 'Myšlenka ANO, zaujala......ale přešla ve zklamání , neschopnost , personální bídu ! Skoro půl roku po volbách není schopná sestavit vládu 70% zúčastněných voličů odmítá mít za premiéra zloděje, lháře,.. který není schopen doložit původ svého majetku,a nyní majetek ještě násobí! Česká šlechta " neshromáždila" takový majetek za 400 let.....Dej si koblihu, pošli účtenku, třeba vyhraješ..', 19);
INSERT INTO comments VALUES (5361, 'Tomáš Novák', '30 let po revoluci a nic, furt se tu vsichni bavi o tom kdo bude predseda, kdo kde bude v jaky funkci, to je uplne jedno ne, at tu zacne neco fungovat. CSSD je u toho tech 30 let at uz tahnou k certu.', 19);
INSERT INTO comments VALUES (5362, 'Ladislav Polák', 'Laciné.', 19);
INSERT INTO comments VALUES (5363, 'Jaromír Petlach', 'Jo, ještě v exekuci přijít o Lihač a nepohrne se tam ani takový cvičitel a zoufalec, jako Paroubek...', 19);
INSERT INTO comments VALUES (5364, 'Ladislav Polák', 'Jsme tam, kde jsme - chválabohu, že jsme tam kde jsme. Nejvyšší životní úroveň v historii, nízká nezaměstnanost atd. Co Vám chybí?', 19);
INSERT INTO comments VALUES (5365, 'Jaromír Petlach', 'Bude tam Zeman zase kouřit Pohanku?', 19);
INSERT INTO comments VALUES (5366, 'Lada Novak', 'no to je vtipný... Profesionální sport..., tzn. politika, prachy, podvody, drogy... Nevim co je lepší.. Je komické, jak málo stačí, aby měl národ sportovce o kterém prakticky neví vůbec nic za poloboha..', 19);
INSERT INTO comments VALUES (5367, 'Tomáš Novák', 'Tuhle stranu uz by nemel nikdo volit, se tam naparujou, dulezity sou jak nikdo. jeste vymre jedna generace a CSSD a komunisti se nedostanou ani do snelovny, mlady lidi je volit nebudou, doufam', 19);
INSERT INTO comments VALUES (5368, 'Jiří Krutil', 'jestli dluží půl miliardy tak nějaký milión nehraje roli.', 19);
INSERT INTO comments VALUES (5369, 'Tomáš Cibulec', 'Ano u nás jsou lidé, kteří makají, u nás v zastupitelstvu s vládou ANO město vzkvétá, takže nevím kde berete tu jistotu, že jsme sekta. Vím ono je těžké proměnit kecy v činy, ale u nás v ANO se tomu daří, protože se bavíme mezi sebou i s ostatními.', 19);
INSERT INTO comments VALUES (5370, 'Vlada Vanek', 'Jakou porážku ? ..zeman je přece mladý silný, zdravý....jen ho 4 hodinové debaty vyčerpaly..... že o něm není slyšet..... žádná ostuda! Dnes mu lékaři dají léky aby vydržel chvíli plácat nesmysly, a ČSSD se dostala na procenta jeho SPOZ..', 19);
INSERT INTO comments VALUES (5371, 'Jiří Klíma', 'Proč používají staré logo??', 19);
INSERT INTO comments VALUES (5372, 'Martin Kukol', 'Už ta vnitřní revoluce začíná - chtějí odvolat grémium + za lithium a nečinnost. Inu, bude to zajímavé.', 19);
INSERT INTO comments VALUES (5373, 'Vlada Vanek', 'Jako Okamůrovci v Brně...', 19);
INSERT INTO comments VALUES (5374, 'Vlada Vanek', 'Většině také jde pouze o oběd a diety...... Kde na to nejzadluženější Česká strana bere to nechápu', 19);
INSERT INTO comments VALUES (5375, 'Pavel Nocar', 'Šašci se neumí domluvit na banalitách a to ti zoufalci řídili stát Se nedivím že jsme tam kde jsme', 19);
INSERT INTO comments VALUES (5376, 'Vlada Vanek', 'Pokud vám imponuje ÁNO sekta, lhaní podvody, neschopnost doložit původ majetků.... volíte správně,a není vám pomoci.. Veď Fšeci kradnů..... Ale proč není rodina Bureše schopná doložit účty k čapímu hnízdu, které prý "poztrácela" a přitom Bureš požaduje odeslání účtenky na Ministerstvo za každé rohlík mi asi nevysvětlíte..... Stejně jako proč není sekta schopná nominovat lidi kteří nejsou obvinění z trestných činů nebo spolupráce s StB, a Ministry dělají lidé bez jakýchkoliv zkušenosti, stačí když si čtou 14 dní na internetu...... Nekecáme makáme odborníci', 19);
INSERT INTO comments VALUES (5377, 'Jaroslav Baran', '6 medvědů s cibulkou', 19);
INSERT INTO comments VALUES (5378, 'Martin Kukol', 'To je chaos - nejsou se schopni domluvit ani na banalitě, a to teprve řeší organizační otázky.', 19);
INSERT INTO comments VALUES (5379, 'Tomáš Cibulec', 'Pořád to je ta Vaše jedna strana Zkuste vyslyšet druhou stranu. Pokud nevidíte nic jiného, než to co jste mi tu popsal, tak jste jednostranný radikál. Já jsem pro politiku středu, ale jde o to aby se obě strany spolu bavili a neházeli na sebe špínu.', 19);
INSERT INTO comments VALUES (5380, 'Jindřich Obšil', 'Dnes nezvolí ani náhodou Teprve se slézají, pak bude řečňovat Eman, pak obídek, nějaká debata, hosté (Fico) a kandiduje skoro každej delegát...vsadím boty, že dnes ne. Harmonogram zcela mimo mísu.', 19);
INSERT INTO comments VALUES (5381, 'Jiří Krutil', 'Jsem pamětník a za komančů pokud soudruh vykazoval známky neschopnosti byl povýšen, pokud se ukázal jako naprosto nepoužitelný strana soudruha poslala do Prahy. Historie se neustále opakuje. Tady jdou a hovoří dva naprosto nepoužitelní kandidáti na předsedu.', 19);
INSERT INTO comments VALUES (5382, 'Vlada Vanek', 'Zkuste psát něco k věci,', 19);
INSERT INTO comments VALUES (5383, 'Luboš Marek', 'Tak hlavně se nepoperte.', 19);
INSERT INTO comments VALUES (5384, 'Vlada Vanek', 'Ta druhá strana, to myslíte třeba dotace které byly určené na rozvoj regionu a malého podnikání, a Bureš tyto dotace čerpá , a vyvádí z regionu pryč takže se tam zvyšuje nezaměstnanost?! Třeba v Rakousku jsou dotace omezený pouze na určitou výměru, a větší firmy již nemohou čerpat takové částky! Myslíte že zájmem Bureše je aby toto opatření platilo i u nás?! Aby se do malých vesnických regionu opět vrátil život, zemědělská výroba která je skutečně potřebná místo řepky jejíž chemie devastuje spodní vody, což jste zřejmě z dokumentů také nepochopil! Možná vás zaujalo zaměstnávání cizinců kteří pracují bez zdravotních průkazů 12 hodin denně za 8000 ..... Určitě si s chutí pochutnáváte na potravinách do kterých se přimíchá vají separáty určené pro výrobu zvířecích konzerv....Jestli Toto je podle vás ta odvrácená druhá strana tak je mi vás pouze líto', 19);
INSERT INTO comments VALUES (5385, 'Jana Kozáková Volrábová', 'Souhlas. Měli by ho ukazovat všem dámám chtivým podstoupit plikaci žaludku a následné uříznutí nadměrné volné kůže. Tolik bolesti, tolik peněz to stojí veřejné pojištění (od BMI tuším 30 to jde na pojišťovnu), tak šílený výsledek po pár letech! On tu operaci měl tuším před 4-6 roky a už se výrazově blíží zase tam, kde předtím byl.', 19);
INSERT INTO comments VALUES (5386, 'Radek Zlámal', 'To udělal velmi šikovně. Konečně někdo řekl Sobotkovi pravdu. A to u toho ani nepromluvil :))', 19);
INSERT INTO comments VALUES (5387, 'Robert Máslo', 'No zlatá z něj určitě nebude .', 19);
INSERT INTO comments VALUES (5388, 'Pavel Jeřábek', 'Chlapi se taky sjeli. A nakonec dokázali vyřešit zda Krušovický je lepší Radegastu. Nějaký sjezd socanů je v takovém případě podružná záležitost.', 19);
INSERT INTO comments VALUES (5389, 'Libor Petráš', 'no on ten hamáček taky není žádná hitparáda . takovej Bohuš 2 .', 19);
INSERT INTO comments VALUES (5390, 'Jiří Nosek', 'vítám vás zpět pane Vanek....už jste tu porážku rozchodil a v plné síle zpět....gratulace....opět tu máme souboj zlý stařec Miloš x divoký spravedlivý Vlado', 19);
INSERT INTO comments VALUES (5391, 'Robert Máslo', 'Včerejší sjezd byl mnohem zajímavější .', 19);
INSERT INTO comments VALUES (5392, 'Jiří Nosek', 'Altnerovým', 19);
INSERT INTO comments VALUES (5393, 'Ladislav Frydryšek', 'Že v české politice chybí liberálové, socialisté je pravdou, zatím se v oblasti politického českého remízku vyskytují náznaky těchto základních kamenů demokracie.', 19);
INSERT INTO comments VALUES (5394, 'Tomáš Cibulec', 'Sám mám zkušenost s tím, že mě svědci potopili u výpovědi, byl jsem až tak důvěřivej ve spravedlnost, že jsem se dokonce sám přišel udat a vysvětlit situaci, ale bylo mi to prd platný, potopili mě v tom co mě živilo, takže pokud si nechcete vyslechnout druhou stranu a přijímáte pouze jednu stranu mince, tak se nemáme o čem bavit.', 19);
INSERT INTO comments VALUES (5395, 'František Dolský', '*promena', 19);
INSERT INTO comments VALUES (5396, 'František Dolský', 'Neni to tak davno, co Chovanec mel celkem prumernou postavu. Nyni vypada jako vyhazovac na vesnicky diskotece. Nechci se navazet do niciho vzhledu, jen me zaujala ta premena za poslednich pet let.', 19);
INSERT INTO comments VALUES (5397, 'Robert Máslo', 'Doufejme, zasloužili by si to.', 19);
INSERT INTO comments VALUES (5398, 'Petr Šťastný', 'Bububu Babiš, bububu ANO, Zprofanovaní lídři zavedených pol.stran, které tady od sametu akorát krmili kmotry a s lidma si vytírali zadek vykřikuji a tupé stádo je stále voli a stále volí.', 19);
