INSERT INTO comments VALUES (5401, 'Miloš Poledno', 'pokud fakta a výpovědi lidí, kteří mají s Babišem co dočinění, tak se nemáte nad čím zamýšlet. Prostě ten člověk může s vaší podporou krást donekonečna. Vlastně ne, až se Vás to dotkne osobně,ale to asi nehrozí,že.', 19);
INSERT INTO comments VALUES (5402, 'Robert Máslo', 'A už zaplatili Altnerovo dědicům?', 19);
INSERT INTO comments VALUES (5403, 'Martin Kukol', 'Bude iDnes vysílat online? Taková komedie se jen tak nevidí, takže bych to uvítal.', 19);
INSERT INTO comments VALUES (5404, 'Jaroslav Reznik', 'Voliči vám utekli k ano a spd.......co s tím?', 19);
INSERT INTO comments VALUES (5405, 'Ivo Bouše', 'Jedině Zimola, toho podřízení mají tak rádi, že mu staví vilky.', 19);
INSERT INTO comments VALUES (5406, 'Robert Máslo', 'Souhlas, jestli se jim povede odstranit Babiše (rozhodně nejsem jeho fanda) tak přijde čas na to, aby se do parlamentu dostal Řád národa a Dělnická strana. Představa, že se lidé vrátí k stranám typu TOP09 je naprosto nereálná.', 19);
