INSERT INTO comments VALUES (5410, 'Michael Holy', 'Pan prezident už CSSD místo ukázal.... https://zpravy.idnes.cz/foto.aspx?r=domaci&c=A170504_154700_domaci_kop&foto=JB6b1555_55.jpg', 19);
INSERT INTO comments VALUES (5411, 'Karel Káš', 'Že by překlep?', 19);
INSERT INTO comments VALUES (5412, 'Ladislav Frydryšek', 'Káši, co je to za slovo ublížiíli?', 19);
INSERT INTO comments VALUES (5413, 'Pavel Chamrád', 'Učení od Zemana.', 19);
INSERT INTO comments VALUES (5414, 'Karel Káš', 'To ony "třešničky" asi ani nečekají. Vždyť ani do vlády nechtějí. Jim stačí těch pár teplých flíčků.', 19);
