INSERT INTO comments VALUES (6585, 'Jaroslav Vacek', 'Vždy se najdou blbci', 25);
INSERT INTO comments VALUES (6586, 'Milan Chvojka', 'Melete nesmysly a srovnáváte nesrovnatelné, jste demagog.', 25);
INSERT INTO comments VALUES (6587, 'Milan Chvojka', 'Ten začátek jste ovšem přepísknul....', 25);
INSERT INTO comments VALUES (6588, 'Jaroslav Vacek', 'Kosovo patří k Srbsku! To co se odehrálo v Kosovu je ostuda demokratického světa.', 25);
INSERT INTO comments VALUES (6589, 'Milan Chvojka', 'Na rozdíl od Rusů, kteří ukradli Krym, USA a NATO nic neukradlo, jen se postarali o vznik státu v rámci ukončení jugoslávsé války. Autonomní oblast Kosovo by již nemohla být spojena se Srbske, protože by obě strany pokračovaly ve válce.', 25);
INSERT INTO comments VALUES (6590, 'Pavel Sobotka', 'Kosovo, Tibet, Katalánsko, Morava, Slezsko,... ma plné právo na samostatný stát. Asmozřejmě začátky nejsou snadné, stačí se podívat na Izrael s jakými se dodnes potýká problémy, ale určitě osamostatnění má smysl, především pro budoucí generace.', 25);
INSERT INTO comments VALUES (6591, 'Radek Janata', 'Jediný důvod, proč to udělali, byl ten, že za Srbskem stálo Rusko. Chtěli si s ním poměřit síly.', 25);
INSERT INTO comments VALUES (6592, 'Richard Tomeček', 'Je zbytečné pořád psát o vojenské základně USA zřízené pro udržování Evropy v poslušnosti a servilitě.', 25);
INSERT INTO comments VALUES (6593, 'Václav Lada', 'Největší exportér muslimských teroristů je Rusko.', 25);
INSERT INTO comments VALUES (6594, 'Miroslav Kašpar', '"Američané v těch dalších jednáních už nehráli hlavní roli, protože přišlo 11. září a oni začali mít jiné starosti" Nemá politolpg v letopočtech trochu guláš.', 25);
INSERT INTO comments VALUES (6595, 'Jiří Schmidt', 'Principiálně nikoli.', 25);
INSERT INTO comments VALUES (6596, 'Veronika Skalová', 'veci se umele vytvorit nedaji, kosovo se statem jako takovym nikdy nestane, je to uzemi rizeno cizinci slouzici cizim zajmum hlavne tem americkym, jeste aby usa neuznalo kosovo jako stat, kdyz ho tolik potrebuji. Chudaci lide, na se se nikdo neptal.', 25);
INSERT INTO comments VALUES (6597, 'Jan Sláma', 'nikdy nic není jisté', 25);
INSERT INTO comments VALUES (6598, 'Jaroslav Reznik', 'Holt USA a NATO dosáhli svého...mají Kosovo...děkujeme', 25);
INSERT INTO comments VALUES (6599, 'Václav Lada', 'To je jako kdybych napsal že Němci odjakživa bránili Evropu před Židy a Evropa se jim odvděčila bombardováním.', 25);
INSERT INTO comments VALUES (6600, 'Jiří Schmidt', 'Lidé jako vy jsou největším nebezpečím pro demokracii a lidská práva. Rychlá a efektivní řešení měli Hitler, Stalin, Pol Pot, z poslední doby třeba styl boje proti drogám Duterteho.', 25);
