INSERT INTO comments VALUES (7590, 'Ivan Minář', 'Posedlost Merkelové - zničit Evropu i její společnost - parazitickým islámem, je již nyní možné označit .. za zločin.', 34);
INSERT INTO comments VALUES (7591, 'Milan Prikryl', 'Czexit je blbost', 34);
INSERT INTO comments VALUES (7592, 'Erika Slunéčková', 'A stále kvoká, stále kvoká.....', 34);
