INSERT INTO comments VALUES (7604, 'Richard Tomeček', 'Žádost se zamítá.', 34);
INSERT INTO comments VALUES (7605, 'Jana Mala', 'Myslete trochu hlavou a ne jen peněženkou...', 34);
INSERT INTO comments VALUES (7606, 'Aleš Szabó', 'nestrašte', 34);
INSERT INTO comments VALUES (7607, 'Ivan Vašek', 'Takže za mrzkou zlatku?', 34);
INSERT INTO comments VALUES (7608, 'Michal Svašek', 'Národní hosty, velkoryse pposkytnuté Německem, už jsme na našem území za 2. války měli. Opakovat netřeba.', 34);
INSERT INTO comments VALUES (7609, 'Václav Vaněk', 'Musíte se zeptat rodičů jak to bylo předtím.', 34);
INSERT INTO comments VALUES (7610, 'Jana Fuszteiová', 'Myslíte jako že bych se radikalizovala a šla vyhodit do luftu s cílem povraždit co nejvíce evropanů....tak nebojte , to ti vaši muslimští kamarádi.', 34);
INSERT INTO comments VALUES (7612, 'Andrea Potůčková', 'jde o princip. Pokud někdo sám rozhodně o otevření hranic všem vč. kriminálních a teroristických živlů, tak nemůže být o solidaritě absolutně řeč. Merkelová není vládkyně všech zemí EU chápete? Takže je dobře, že jsme se postavili na zadní, jinak i ta východní Evropa by už byla plná muslimů. A je dobře, že jsme vnímáni jako nepřátele migrace, aspoň nejsme alespoň prozatím jejich vytouženým cílem', 34);
INSERT INTO comments VALUES (7613, 'Aleš Szabó', 'hordy?', 34);
INSERT INTO comments VALUES (7614, 'Petr Truhlařík', 'V tom grafu to vypada docela realisticky', 34);
INSERT INTO comments VALUES (7615, 'Jana Mala', 'Nevzpomínám si že bysme v 90.letech stáli fronty na maso , nebo že by např.v Bosně měli nedostatek....', 34);
INSERT INTO comments VALUES (7616, 'Jaroslava Božíková', 'Jen nestrašte. Až se nebudou stavět rozhledny na rovině - běžte se podívat k obci Kadlín, nebudete věřit vlastním očím, na co šly dotace - tak se peníze konečně nasměrují správným směrem, kam už dávno měly směřovat. Takhle se kvůli vyčerpání dotací, kde se prostě musely dát i nějaké české peníze, jen plýtvalo. Dotace na příšerné zelené balisety v památkové zóně, dotace na čáry na asfaltu, které se vydávají za cyklostezku, vždyť je to všechno bohapusté plýtvání a skutečně se bez těch zhůvěřilostí obejdeme.', 34);
INSERT INTO comments VALUES (7617, 'Václav Vaněk', 'Zajímavé. A proto se k nim nadále hrnou hordy Čechů aby si vydělali na podobný život jako mají Němci?', 34);
INSERT INTO comments VALUES (7618, 'Ivan Vašek', 'Krize je to pouze pro Evropu,kdy se islámské masy daly na pochod do nového životního prostoru.Takže,jak si od nich pomoci.Ne,jak jim pomoci.', 34);
INSERT INTO comments VALUES (7619, 'Petr Truhlařík', 'Hrach, zed atakdale', 34);
INSERT INTO comments VALUES (7620, 'Pavel Fógel', 'Bude asi důležité si udržet suverenitu než sem dosadit protektora. :-) Dnešní krize je krizí národní nenávistí, neláskou Němců vůči nim samotným. A tak doufám, že v této zkoušce snad obstojí, ale cesta nevede určitě s Merklovou.', 34);
INSERT INTO comments VALUES (7621, 'Jana Fuszteiová', 'Véíe já mám svou rodinu zabezpečenou co se týče financí, jen má strach z idiotů co jsou schopni sem natahat muslimské okupanty a skončit s kulturním chováním.', 34);
INSERT INTO comments VALUES (7622, 'Václav Vaněk', 'Koukám, že se nám vážená radikalizuje. Co takhle přidat ještě nějakou teorii konce světa.', 34);
INSERT INTO comments VALUES (7623, 'Štefan Friča', 'To je diktát, zrada. Pak nezbývá, než Czexit.', 34);
INSERT INTO comments VALUES (7624, 'Rudolf Strnad', 'Myslím, že jste úplně mimo realitu.', 34);
INSERT INTO comments VALUES (7625, 'Jana Mala', 'Němci už normálně nežijí , mám syna kousek od Frakfurtu nad Mohanem , v Mainzu a plný penzion Němců a věřte že už nežijí normálně...', 34);
INSERT INTO comments VALUES (7626, 'Jirka Kožich', 'Ta baba je jak kafemlejnek, pořád dokola to stejný. Je to marný, je to marný, je to marný.....', 34);
INSERT INTO comments VALUES (7627, 'Petr Truhlařík', '', 34);
INSERT INTO comments VALUES (7628, 'Aleš Szabó', 'na dotacích připlouvá cca 2% HDP a odplouvá cca 6-8%', 34);
INSERT INTO comments VALUES (7629, 'Rudolf Strnad', 'Předpokládám, že jste již vyklidil své obydlí, postavil stany a vývařovny, poučil své okolí, že se nemá bránit ani v případě znásilnění, že když sem tam bouchne uprchlík, tak se mají všichni semknout atd?', 34);
INSERT INTO comments VALUES (7630, 'Michal Slavík', 'tak nejdříve kecy jak to zvládne a když vidí, že to je asi problém, tak začne vyhrožovat to se nedá slušně komentovat', 34);
INSERT INTO comments VALUES (7631, 'Václav Vaněk', 'Já se mám stydět? Za co? Že jsem tady jeden z mála co umí trochu dát věci do souvislostí?', 34);
INSERT INTO comments VALUES (7632, 'Jana Fuszteiová', 'Aspoň, že máme mít z koho srandu, v hnusných dobách normalizace nás to drželo pohromadě si dělat srandu z pitomečků propagujících zhovadilosti.', 34);
INSERT INTO comments VALUES (7633, 'Jan Cibulka', 'Jak? Nemají doklady a ověření věku je za hranou nějakých těch lidských práv, či co. A to ostatní jim musíme věřit. A pak i to, kdo všechno jsou příbuzní, které jim sem sami přivezeme v rámci slučování rodin.', 34);
INSERT INTO comments VALUES (7634, 'Václav Vaněk', 'Jenže vy nejdřív musíte zjistit kdo je kdo předtím než někoho deportujete.', 34);
INSERT INTO comments VALUES (7635, 'Ivan Vašek', 'A bez obdivu je absence argumentu.', 34);
INSERT INTO comments VALUES (7636, 'Jana Mala', 'Těch "mrzkých" dva a půl tisíce je jen začátek a gesto že jsme se ohnuli. Žili jsme před EU zvládneme to i po ní , bez dotací a za své..zvedá se mě žaludek z lidí ktří za peníze jsou ochotni ohýbat záda , styďte se', 34);
INSERT INTO comments VALUES (7637, 'Pavel Pilát', 'A nedají si říct a nedají...', 34);
INSERT INTO comments VALUES (7638, 'Václav Vaněk', 'Holt se bojte. Ostatní budou místo vás normálně žít.', 34);
INSERT INTO comments VALUES (7639, 'Milan Kadlec', 'Chtěl jsem napsat vlastní příspěvek, ale rád navážu na Váš. Přesně první dvě věty vystihují i můj názor. A vybrat si jednu z nich? Raději ven z EU. Věřte, my Češi jsme sice "švejci", ale vždy si poradíme. Věřte! Naši předkové v 15 století zachránili Evropu, a Evropa nás potom několikrát poslala do kolen. Ale ČR je tu pořád a bude !!!!', 34);
INSERT INTO comments VALUES (7640, 'Petr Truhlařík', 'Budem stavet huhulaka', 34);
INSERT INTO comments VALUES (7641, 'Jan Cibulka', 'Nemáme snad už dostatečně silnou zkušenost s pojmy jako DIKTÁT, DOČASNĚ, SOLIDARITA?', 34);
INSERT INTO comments VALUES (7642, 'Václav Vaněk', 'Levná pracovní síla pochází z řízené migrace. Tady jde o uprchlickou krizi, což je obrovský rozdíl. Pochopitelně se do toho zamíchá spousta lidí co odnikud neprchají, ovšem je na nás to zjistit.', 34);
INSERT INTO comments VALUES (7643, 'Jana Fuszteiová', 'S tímto neuspějete, budete označen za xenofoba, možná i nácka a odstraněn ze ,,slušné" společnosti......', 34);
INSERT INTO comments VALUES (7644, 'Václav Vaněk', 'Hm, a co stát ve frontě na maso abyste měla něco pro ty děti k večeři?', 34);
INSERT INTO comments VALUES (7645, 'Tomáš Zemánek', 'Solidaritu bych chápal v případě nějakého neštěstí, ale běženci měli být přínosem v podobě levné pracovní síly a měli přinést samá pozitiva.', 34);
INSERT INTO comments VALUES (7646, 'Václav Vaněk', 'Chápu, že Němcům vadí platit Čechům miliardy navíc, které by určitě našly lepší místo doma a dostanou za to nazpět jen nadávky a rady typu - co jste si uvařili si snězte. Tak budou vařit. Ze svého a za své. Jenže nám bude scházet třicet až padesát miliard z dotací, které by postavily infrastrukturu, co by nám umožnila lépe konkurovat ostatním státům. To je přesně to co nás čeká, jen kvůli tomu, že nejsme schopni a ochotni převzít mrzkých dva a půl tisíce lidí k vyhodnocení jejich přihlášky na azyl. Co nás čeká to nás nemine a možná, že si jednou uvědomíme, že solidarita je byznys - jednou dal, pak dostal a my holt nedostaneme nic.', 34);
INSERT INTO comments VALUES (7647, 'Jana Fuszteiová', '́Tu ťu ťu ťu ťu a kolik je chlapečkovi? Ále ještě mu není ani osmadvacet ......', 34);
INSERT INTO comments VALUES (7648, 'Jana Mala', 'Opravdové štěstí není luxusní dům ,auto slavné značky a pomocí dotací z EU nacpaná konta. Štěstí je rodina , děti , život v bezpečí a svobodě , které nám zaručuje NATO a ne EU ,proto se raději uskromním a místo zvyšování plotu kolem domu a neskutečného strachu o své blízké ,raději dám k vánocům svým blízkým třeba vlastní rukou vyrobenou drobnost než luxus vykoupený neustálým strachem z nekulturních barbarů nacpaných nám sem za cenu umělé prosperity a peněz z EU', 34);
INSERT INTO comments VALUES (7649, 'Vendula Faiferová', 'Právě, proč Merkel údajně mluví o neochotě dočasně pomoci a přitom žádná jasná pravidla dočasné pomoci neuvedla a pravděpodobně to ani není na pořadu dne. Vše jen plané řeči s cílem dosáhnout stavu, kdy bude možné ohledně migrace nadiktovat cokoli, co si Brusel zamane.', 34);
INSERT INTO comments VALUES (7650, 'Kryštof Laník', 'EU tvori 28 zemi, ale mne se zda, ze to všechno ridi nebo chce ridit Nemecko.', 34);
INSERT INTO comments VALUES (7651, 'Jaromír Král', 'Jste si jistý, že odpovídáte pod správným příspěvkem ? Já totiž říkám prakticky totéž, co Vy. Tedy že Němci mají z toho víc, než nám dávají. No a samozřejmě na tom vyděláme i my.', 34);
INSERT INTO comments VALUES (7652, 'Ivan Vašek', 'Kdy ji nevidíš.', 34);
INSERT INTO comments VALUES (7653, 'Jana Fuszteiová', 'Co si soudružka Merkel představuje pod pojmem solidarita? Že bude diktovat ostatním co mohou, nemohou a co musí?', 34);
INSERT INTO comments VALUES (7654, 'Andrea Potůčková', 'přesně tak. Dublin IV - nový výmysl EU. Říkám honem pryč z EU než bude pozdě, až se u nás uhnízdí, tak bude pozdě', 34);
INSERT INTO comments VALUES (7655, 'Lenka Pokorna', 'To snad je jasne, ale v kazdem pripade tady odvadime dobrou praci. Nemusi nas zivit z davek. Spis jsme pro ne levna pracovni sila. Lepsi nez Rumuni.', 34);
INSERT INTO comments VALUES (7656, 'Ivan Vašek', 'No ti maj jít k nám.', 34);
INSERT INTO comments VALUES (7657, 'Ondřej Šindler', 'Cože je v podstatě to, co píši. Vy se mnou nějak nesouhlasíte nebo potvrzujete?', 34);
INSERT INTO comments VALUES (7658, 'Pavel Fógel', 'Bohužel otázka uprchlíků nemizí a je to, kde Angelu velice tlačí bota. Teď Se ji kvuli tomu blbě skládá vláda. A tak začíná být zlá nejen na své “fašistické” spoluobčany tak i na “fašistické” sousedy. (Slovem “fašista” se označují v současném mediálním prostoru ty pomílence, kteří by náhodou chtěli uprchlíky odmítat).', 34);
INSERT INTO comments VALUES (7659, 'Kryštof Laník', 'není nutne vystupovat z EU, staci odmitnout dotace, ne?', 34);
INSERT INTO comments VALUES (7660, 'Ondřej Šindler', 'Tak ono se lhalo už předtím, že každý, kdo nedostane azyl bude deportovaný. Pak se ukázalo, jak každý s trojciferným IQ tušil, že deportovat 25 letého eritrejce, co tvrdí že je 17tiletý syřan vůbec není jednoduchá věc.', 34);
INSERT INTO comments VALUES (7661, 'Slavomír Štěpán', 'Vystoupit je špatně EU je třeba změnit omezit Brusel a vrátit pravomoci národním vládám', 34);
INSERT INTO comments VALUES (7662, 'Andrea Potůčková', 'pokud zůstaneme v EU, tak to stejně vrátíme a to formou spolupodílení na úhradě neudržitelných dluhů Řecka a dalších jihoevropských států.', 34);
INSERT INTO comments VALUES (7663, 'Zuzana Vinklerová', 'Tady jde o zdanění těch dividend. Kdyby zisky reinvestovali v ČR, bylo by to něco jiného. Když platí u nás třetinové mzdy a zisky z ČR kryjí ztráty v jiných zemích( kde jsou mimochodem podstatně více zdaněny), je to vysávání a kolonialismus.', 34);
INSERT INTO comments VALUES (7664, 'Veronika Exnerová', 'Budou chtít až jim budeme vyplácet podporu jako v Německu', 34);
INSERT INTO comments VALUES (7665, 'Jaromír Král', 'No právě. Ale pak ať nežádají solidaritu.', 34);
INSERT INTO comments VALUES (7666, 'Aleš Adam', 'Blbost. K nám nikdo nechce a kvóty nikdo nepřijme.', 34);
INSERT INTO comments VALUES (7667, 'Tomáš Zemánek', 'Počkejte před referendem. Články o tom, jak pro odchod hlasují ti z venkova a s nižším vzděláním, případně jinou zavrženíhodnou vlastností', 34);
INSERT INTO comments VALUES (7668, 'Ivan Vašek', 'Fuj', 34);
INSERT INTO comments VALUES (7669, 'Vendula Faiferová', '"... řešení otázky dočasného domova pro přistěhovalce...." No a nechtělo by to tedy nejprve představit plán a stanovit pravidla pro to "dočasně"? Zatím se západní státy v čele s Merkel vyjadřovaly a chovaly tak, že svým vítačstvím lákaly k ilegální ekonomické migraci, v hlavách migrantů trvalé.', 34);
INSERT INTO comments VALUES (7670, 'Veronika Exnerová', 'Odtrhnout se jednotně se všemi státy V4', 34);
INSERT INTO comments VALUES (10007, 'Tomáš Tatíček', 'Aha, omlouvám se.', 49);
INSERT INTO comments VALUES (7671, 'Slavomír Štěpán', 'Zase povinná solidarita??? To jsme už myslím zažili.RVHP byla proti EU klub kapitalistů. To o čem snily byvalé komunistické režimy jako o zářivé budoucnosti EU dávno překonala', 34);
INSERT INTO comments VALUES (7672, 'Petr Mareček', 'A proč bychom měli nějakého nutně vidět? Nestačí ty zpráva o tom co páchají všude na světě? A teď by měl přijít váš protiargument o tom že při dopravních nehodách zahyne mnohem více lidí než povraždí muslimové.', 34);
INSERT INTO comments VALUES (7673, 'Veronika Exnerová', 'Migranti k nám nově chtít budou, protože jim budeme musel platit podporu jako Němci', 34);
INSERT INTO comments VALUES (7674, 'Ondřej Šindler', 'Ono vystupit z eu taky není zrovna sranda. Tím spíš je to škoda, že EU samotná je dobrý nápad. Jen ta současná podoba je strašlivá. Zajímalo mě jestli by výledově bylo možné se domlouvit alespoň s 2-3 dalšími zeměmi a vypovědět Lisabonskou smlouvu a pak sledovat co se stane-', 34);
INSERT INTO comments VALUES (7675, 'Andrea Potůčková', 'řešení je vystoupit co nejrychleji z EU. Problémy s migrací jsou nesrovnatelně větší a nebezpečnější než být mimo EU. Mimo EU jsme byli i předtím, a taky jsme žili. I kdybych se měla hodně uskromnit, tak je to lepší varianta než přeplněná muslimská ghetta nezintegrovatelných fanatických vyznavačů Allaha.', 34);
INSERT INTO comments VALUES (7676, 'Frantisek Kofron', 'Posilame je tam jako nase predstavitele. Neposilame je jim a to ke velky rozdil. Ano, 2000 migrantu nevytrhne ani nas, ani Nemce, ale taky u toho nejspis nemusi skoncit. A gesto? Ano mate pravdu bylo by to gesto souhlasu s vitaci politikou a ja osobne bych nekomu nedaval souhlasne gesto, kdyz s necim absolutne nesoulasim a vetsina lidi v CR absolutne nesouhlasi s vitaci politikou Nemecka.', 34);
INSERT INTO comments VALUES (7677, 'Ivan Vašek', 'Au,au,au.Přistoupit na tuto solidaritu je pomoci šířit infekci.', 34);
INSERT INTO comments VALUES (7678, 'Petr Mareček', 'Vy jste asi fakt hodně naivní ..', 34);
INSERT INTO comments VALUES (7679, 'Lenka Pokorna', 'Ti Arabove, co se umi uspesne prosadit, neemigruji. Ti jsou napr ve spojenych ar. emiratech.', 34);
INSERT INTO comments VALUES (7680, 'Pavel Fógel', 'Czexit je prý ekonomická sebevražda. Zůstat za současného vedení EU je zase určitě sebevražda politická. Pakliže zůstane v Německu u moci královna Merkel, která přijímá rozhodnutí o Evropě podle svého momentálního psychického rozpoložení, a nebude opět zvykem dospět k politickému konsensu na základě dialogu jak uvnitř Německa, kde měla zatím většinu, tak i vně Německa, tak je další setrvávání otázkou ztráty národních suverenit.', 34);
INSERT INTO comments VALUES (7681, 'Marek Netolický', 'A furt dokola. Protože nechcete migranty, keří k vám stejně nechtějí, nedostanete peníze, které nakonec stejně vrátíte.', 34);
INSERT INTO comments VALUES (7682, 'Jaroslav Novák', 'Si je pozve a ted otravuje. At si je tam necha jsou to jeji hoste. Ja taky neotravuju sousedy.', 34);
INSERT INTO comments VALUES (7683, 'Veronika Exnerová', 'Chtějí nás prostě dokopat k czexitu. Ještě bych chvíli počkala, až bude situace trochu víc gradovat a pak by bylo dobré to referendum', 34);
INSERT INTO comments VALUES (7684, 'Roman Pittl', 'Dodejme, že není daleko doba, kdy tito lidé stanou před soudem.', 34);
INSERT INTO comments VALUES (7685, 'Petr Horák', 'Další přípustná varianta onoho slova pro naše vystoupení z EU.', 34);
INSERT INTO comments VALUES (7686, 'Petr Sojka', 'Nikdo jí nenutil. Já bych je nepustil ani na pláž,', 34);
INSERT INTO comments VALUES (7687, 'Zdeněk Nutz', 'No jo Jean-Claude Van Damme. Ta podoba..... a fotí se s nějakou babkou z roští....', 34);
INSERT INTO comments VALUES (7688, 'David Staněk', 'To pozvání frau Merkel přišlo sice až ve chvíli, kdy už tu ten problém byl (tisice migrantů v Maďarsku), ale svým pozváním vše ještě zhoršila a dala naději bůhví jak velké mase lidí, že mohou všichni do Německa. Navíc zde už dávno nejde o nějakou dočasnou ochranu, ale o trvalé přesídlení. Nad důvody toho celého zůstává rozum stát.', 34);
INSERT INTO comments VALUES (7689, 'Frantisek Kofron', 'Myslim, ze neni nutne vystupovat z EU. Migranty nebrat a v EU zustat. Pokud nam za nebrani migraku daji pokutu, tak ji zaplatit a brat to jako obranou investici.', 34);
INSERT INTO comments VALUES (7690, 'Petr Veselý', '1) nepřizpůsobiví a nesolidární budou eliminováno. 2) začne občanská válka 3) zdecimované ”původní” obyvatelstvo si podrobí “kočovníci”.', 34);
INSERT INTO comments VALUES (7691, 'Tomáš Zemánek', 'Pro prášek do Německa se jezdí i teď. Protože jsme se stali popelnicí Západu.', 34);
INSERT INTO comments VALUES (7692, 'Karel Šrejbr', 'Ona je fakt blbá.', 34);
INSERT INTO comments VALUES (7693, 'Tomáš Pak', '2000 lidí jako hrozba? směšné....', 34);
INSERT INTO comments VALUES (7694, 'Erik Sanetrik', 'Som za voľný pohyb občanov EU a voľný pohyb tovarov. Ale teraz keď sa mení zloženie obyvateľstva na západe možno začnem byť aj proti voľnému pohybu osôb.', 34);
INSERT INTO comments VALUES (7695, 'Tomáš Pak', 'koupili jen stín fabriky, kterou dnes je. ano budovy mohou prodat, technologii odvést...', 34);
INSERT INTO comments VALUES (7696, 'Zdeněk Nutz', 'Tak jsem si trochu zavyhrožovala a dem na pivo!', 34);
INSERT INTO comments VALUES (7697, 'Jarek Jakubček', 'Souhlas, proto jsem zminil EU :-)', 34);
INSERT INTO comments VALUES (7698, 'Tomáš Pak', 'a zřejmě budete nejméně vysokoškolský profesor s IQ 150 a skvělým rozhledem, když mne označujete za blba, že?', 34);
INSERT INTO comments VALUES (7699, 'Pavel Podešva', 'My křiklouni v východu jsme už tak 10 let říkali, že něco dělají špatně. A byli jsme pořád ti, co do toho nemají právo žvanit. No a teď nemáme právo žvanit ani do toho, jestli budou nosit žhavé uhlí do naší stodoly, co furt ještě stojí.', 34);
INSERT INTO comments VALUES (7700, 'Frantisek Kofron', 'A narkomanovi pomuzu tak, ze taky zacnu brat drogy? Tim prokazu svoji solidaritu? To mi totiz prijde jako spravny primer.', 34);
INSERT INTO comments VALUES (7701, 'Martin Blahník', 'No a Škodovku snad koupili, ne? Když se jim znelíbí, tak ji prodají. Nicméně já mám problém s tím Vaším konceptem stavět něco co už dávno funguje a koupím to, protože chci zisky', 34);
INSERT INTO comments VALUES (7702, 'Tomáš Pak', 'ano to máte pravdu, ale bez jednoho Česka to klidně zvládne, naopak to neplatí... 2000 migrantů rozhodně Německo nevytrhne, ani to nepozná, jde o gesto, jde o solidaritu naše hlavní představitele samozřejmě do EU posíláme', 34);
INSERT INTO comments VALUES (7703, 'Petr Veselý', 'Tohleto prostě nedopadne dobře.', 34);
INSERT INTO comments VALUES (7704, 'Ivan Vašek', 'Máte pravdu.Ale,obávám se,že Czechcíp.', 34);
INSERT INTO comments VALUES (7705, 'Milan Prikryl', 'Svemu sousedovi, kteremu vyhori byt, bude potrebovat pujcit vrtacku nebo cokoliv jineho rad pomohu, ubytuji ho, dam mu penize. Sousedovi, ktery si domu pozve deset lidi a pak je nezvlada a mluvi o solidarite, ze bych si jich mel pulku vzit domu, ukazu vite co...', 34);
INSERT INTO comments VALUES (7706, 'Jarda Hofmann', 'co znamená to poslední slovo ?', 34);
INSERT INTO comments VALUES (7707, 'Josef Staňura', 'Z takových lidí má Putin radost. Děkuji, nechci.', 34);
INSERT INTO comments VALUES (7708, 'Marcel Vlna', 'Solidaritu ochotne poskytujeme v prípade živelných katastrof. Tam sa na kultúru nepozerá a pomôcť treba. Migrácia nekompatibilných jedincov zo skolabovaných štátov do tejto kategórie nepatrí. Takže Merkel by mala vážiť slová. Je osobne zodpovedná za zníženie kvality života Európanov na desiatky rokov dopredu. Jej jediné šťastie je, že bezdetná zomrie skôr ako ju za to súčasné nemecké deti a ich deti zoberú na zodpovednosť.', 34);
INSERT INTO comments VALUES (7709, 'Martina Moudrá', 'Merkelová věří, že solidarita nemůže být jednosměrná. Jakou reciproční solidaritu si od přijatých imigrantů představuje?', 34);
INSERT INTO comments VALUES (7710, 'David Novotny', 'Arogance je na blbstvi nejlepsi. O tech argumentech jsem se zminoval, jsem ochoten vam pomoci ...', 34);
INSERT INTO comments VALUES (7711, 'Dominik Jaeger', 'Takhle jednají psychicky labilní lidé, kteří vás o něco poprosí a protože je to prosba, tak si myslí, že musí být automaticky přijata...ne.nemusí a myšlení si opaku je náznakem duševní nevyrovanosti a odtržení od reality', 34);
INSERT INTO comments VALUES (7712, 'Petr Horák', 'Za těch pár miliard nebudeme ze sebe dělat voly. Ty miliardy, co nám přijdou, si tady německé firmy násobně vyberou nazpět. Czechzit.', 34);
INSERT INTO comments VALUES (7713, 'Petr Pavlata', 'Myslím, že země V4 jsou ochotné se plně zapojit do řešení migračné problematiky efektivní a vyzkoušenou cestou - po vzoru Austrálie.', 34);
INSERT INTO comments VALUES (7714, 'Frantisek Kofron', 'A kdo ma? Kdo se prisel ze sousedni valkou zasazene zeme? Nikdo. I Syrane meli pozadat o azyl v sousednich zemich a ne v EU a to nemluvim o Afghancich nebo Nigerijcich.', 34);
INSERT INTO comments VALUES (7715, 'Petr Truhlařík', 'Preslapy, ktere nechce videt uz nejsou preslapy, ale ignorantsvi skutecnosti.', 34);
INSERT INTO comments VALUES (7716, 'Dominik Jaeger', 'Já bych to pochopil, kdyby jistá kancléřka nepronesla větu ve smyslu ,,přijďte všichni, postaráme se o Vás,, Sorry jako, to se na mě nezlobte, pozvala je bez probrání toho problému sama, jako by mluvila za celou Evropu a teď žádá po celé Evropě solidaritu, pěkný nic...když byla suverenní tehdy, ať je i teď a sem nám.migranty necpe, my je tu nechtěli ani tehdy, ani teď a žvásty o solidaritě jsou prostě jen lži a emoční nátlak, opepřený o výhružky', 34);
INSERT INTO comments VALUES (7717, 'Jaromír Hlubek', 'Musím reagovat něčím nepublikovatelným.', 34);
INSERT INTO comments VALUES (7718, 'Jana Procházková', '', 34);
INSERT INTO comments VALUES (7719, 'Petr Pavlata', 'Ale to je přesně cíl, likvidace našeho způsobu života. Neomarxisté se tím ani moc netají.', 34);
INSERT INTO comments VALUES (7720, 'Frantisek Kofron', 'Czexit by ty firmy urcite poskodil. Tak jako nas. Ja jsem pro zustav EU, ale nelegalni migranty nebrat. Radeji platit pokuty nebo nemit dotace a brat to jako investici do obrany. Klidne bych ty pokuty platil z rozpoctu Ministerstva obrany.', 34);
INSERT INTO comments VALUES (7721, 'Petr Pavlata', 'Řešit cizí přešlep je jedna věc, kopírovat ho jiná. Tohle je jako ponoci kolegovi, kterého nahání exekutor tím, že si převezmete část jeho dluhů. Uděláte to?', 34);
INSERT INTO comments VALUES (7722, 'Ondřej Šindler', 'Sousedovi pomůžu rád když si zlomí nohu a bude potřebovat odvýzt do nemocnice. Když bude stát v 6tym patře u okna připravenej skočit a bude chtít abych se k němu přivázal provazem tak ho pošlu někam. Tady nejde o nějaký "přešlap". Tady jde o prosazování naprosto sebevražedně šílené politky a EU po nás chce abychom jim vypsali bianco šek na něco, o čem vlastně nevíme skoro nic. Pokud by se po ČR chtělo aby finančně a materiálně přispěla na řešení problému v africe, tam odkud ti lidé pochází a na zvýšenou ochranu hranic, tak jsem všemi deseti pro. Pokud se chce abychom se mlčky podíleli na tom, co může potenciálně znamenat destrukci toho, co činí euroamerickou civilizaci hodnotnou a tu nejlepší, co kdy byla, tak rozhodně ne.', 34);
INSERT INTO comments VALUES (7723, 'Petr Truhlařík', 'To je neco jako ten slib Goringa jak zmakne zasobovat 700t kazdy den ty uvizle u Stalingradu.', 34);
INSERT INTO comments VALUES (7724, 'Milan Prikryl', 'ze si zahranicni matky vyplaceji dividendy, je asi v poradku. Znamena to, ze tady tvori hodnoty, zamestnavaji lidi, plati dane. Loupezniky bych z nich nedelal.', 34);
INSERT INTO comments VALUES (7725, 'Frantisek Kofron', 'Navic bych rekl, ze za dotace Nemcum nikdo nenadava. Je to neefektivni vynakladani zdroju a pokud jsem si dobre vsiml, tak naopak Nemci vyhrozuji zastavenim dotaci. Podle me by ztratu dotaci bylo mozno brat jako vybornou investici do obrany. Bude mene cKlostesek, ale taky mene teroristickych cinu a budeme mit dal k obcanske valce, nez treba ve Francii nebo Nemecku. Ze me lepsi varianta.', 34);
INSERT INTO comments VALUES (7781, 'Radim Simunek', 'vy to furt nechápete.. to jsou naše vymyšlená práva a svobody, to je relativní.. pro ně jsou práva a svobody v koránu...', 34);
INSERT INTO comments VALUES (7726, 'Roman Klazar', 'A ještě jednou píchnu do vosího hnízda. Je naprosto zřejmé, že neochota pomoct sousedům se svými problémy se nám jednou vymstí. Když pominu skutečnost, že největší křiklouni, kteří nyní řvou, že nemáme za povinnost řešit cizí přešlapy, by v opačném případě řvali úplně naopak. A tedy, že sousedi mají za povinnost pomoct nám. A k této situaci dojde, tím si budte jistí. Pokud si myslíte, že tohle se nám stát nemůže, tak jste také úplně mimo. A všichni děláte, jako kdybyste byli bez životních zkušeností. V životě to totiž funguje úplně stejně a málokomu se poštěstí proplout životem bez pomoci druhých, kteří možná brblají, jací jste idio..., ale pomůžou Vám. Až budete v loji a kolega v práci se na Vás vybodne, že si za to můžete sami, tak at si to vyřešíte, tak ho pochopíte? To sotva. Pro Vás jako člověk zkončil a bud s ním už nepromluvíte, nebo budete nepřátelé. A řešení průšvihů puberťáků také většinou nenecháte na nich, ale snažíte se jim do menší, nebo větší míry pomoct. Pokud tedy nedělá stejné průšvihy opakovaně, což ale není tento případ.', 34);
INSERT INTO comments VALUES (7727, 'Ondřej Šindler', 'Problémová třetina je SAKRA hodně...', 34);
INSERT INTO comments VALUES (7728, 'Frantisek Kofron', 'Napr. Nikoho jsme nezadali, aby nam zajistil pohodlny zivot. EU melo a ma zajem na rozsirovani, nebot se tim zvetsuje trh i ekonomicka sila, ale kdyz mate jedno ekonomicke uskupeni, tak je nutne zajistit i ekonomickou konvergenci. Ze EU zvolila formu dotaci misto napr. povoleni danovych ulev je druha vec. Je to rozhodnuti EU a ne nase. Pro nas by byla vyhodnejsi ta danova politika. To nam ale EU nedovoli. Ze se dotace spatne utraci je castecne nase vec, ale castecne vec EU. Spise vice EU. Proc napr. Nedovoli pouzit dotace na snizeni dani? To by bylo velmi efektivni a nikdo by pritom nemohl krast. Lide z neziskovek zhorsuji problem s migraci. Pokud myslite, ze to problem neni, tak proc po nas chce Merkel solidaritu? Dale nikoho neposilame do Nemecka, jak pisete, atd, atd. Temer s kazdou vasi vetou nemohu souhlasit, ale nechce se mi to cele na mobilu vypisovat.', 34);
INSERT INTO comments VALUES (7729, 'Mirek Tureček', 'S tim co nam ta jůnyje predvadi nekolik let ji nemohu ani citit.', 34);
INSERT INTO comments VALUES (7730, 'Miroslav Vykoupil', 'Dotace dostává jen někdo (většinou kdo je nepotřebuje protože je za vodou), ale náklady na uprchlíky z Afriky bude platit i důchodce z Horní Dolní. Takže NE ! Strčte si dotace a Afričany do řiti. Zaplaťte důchodcům, kteří budovali Evropu slušně, aby nemuseli živořit !!!!!', 34);
INSERT INTO comments VALUES (7731, 'Ondřej Šindler', 'Víte já s jedním Syřanem co tu už přes deset let žije dokonce občas chodím na pivo. Nikdo koho znám nemá tak radikálně odmítavý postoj k migraci z afriky jako on. Je to totiž teplej ateista a vyžral si tam "doma" hodně a přesně ví jaká mentalita se sem importuje.', 34);
INSERT INTO comments VALUES (7732, 'Milan Prikryl', 'budeme roky vyjednavat s temi staty, odkud prisli, aby je vzali zpatky, posilat jim prachy, ale stejne se bude jednat o zlomek z nich', 34);
INSERT INTO comments VALUES (7733, 'Zuzana Vinklerová', 'Zahraniční společnosti vyvádějí ročně z ČR více než 600 miliard, čistý příjem z dotací skončí v roce 2020. Poté odvedeme do pokladny více, než z ní dostaneme. Co je na tom negramotného?', 34);
INSERT INTO comments VALUES (7734, 'Tomáš Pak', 'škoda, že pan Hejna ne...', 34);
INSERT INTO comments VALUES (7735, 'Pavel Podešva', 'Merkelová žádá a u toho vyhrožuje... No s tím fingerem si počkám, až bude prosit.', 34);
INSERT INTO comments VALUES (7736, 'Jan Cibulka', 'Tomáš fuk...', 34);
INSERT INTO comments VALUES (7737, 'Ondřej Šindler', 'Svobodní jsou tradiční strana? Co já se tady dneska ještě nedozvím. A nezlobte se na mě, ale nazývat se komunistou a hlásit se tak k dědictví půlstoletí teroru, masového vraždení a útlaku, to už opravdu cce buď hodně silný žaludek.', 34);
INSERT INTO comments VALUES (7738, 'Tomáš Pak', 'jo "český trh to vytrhne"... tak to jo...', 34);
INSERT INTO comments VALUES (7739, 'Vítězslav Pečinka', 'Frekvence článků, pořadů, rozhovorů odpovídá situaci tak 14 dní před referendem...', 34);
INSERT INTO comments VALUES (7740, 'Jiří Horák', 'Zatím ale EU směřuje k merkelismu-junckerismu. A pokud v ní hlavní slovo nebudou mít tihle dva (plus někteří další), budou tam jiní. Podobní. Já jsem v tomto skeptik, vývoj a směřování EU už hned tak jiné nebude.', 34);
INSERT INTO comments VALUES (7741, 'Jan Cibulka', 'Právě jste použil oxymóron.', 34);
INSERT INTO comments VALUES (7742, 'Tomáš Pak', 'oni to nechtějí? No podle mě spíš my sami ničíme vlastní budoucnost..', 34);
INSERT INTO comments VALUES (7743, 'Mirek Tureček', 'Sice pravda, ale tito rozhoduji a ostatni jsou tam bud'' šašci anebo nohsledove...', 34);
INSERT INTO comments VALUES (7744, 'Jaroslav Hejna', 'Pan Pak myslel?', 34);
INSERT INTO comments VALUES (7745, 'Milan Prikryl', 'ona mluvi o solidarite pri prechodnem ubytovani bezencu. Kazdy vi, ze o zadnou docasnost nejde, minimalne do doby, nez kvuli jeji politice prijdou zase nacisti a jsme tam, kde jsme byli', 34);
INSERT INTO comments VALUES (7746, 'Pavel Novotný', 'asi tak', 34);
INSERT INTO comments VALUES (7747, 'Tomáš Pak', 'polovina lidí pod tímto článkem na ně nadává.. Pokud nebudou opravdu se "nic neděje". Jen budeme mít horší silnice, nemocnice atd... "Nic" se přeci "nedělo" ani za komunistů...', 34);
INSERT INTO comments VALUES (7748, 'Zdeněk Trutnovský', 'Tak si volte "tradiční demokratické" strany... Vy morální pokřivenosti...', 34);
INSERT INTO comments VALUES (7749, 'Petr Pavlata', 'Vykopat neomarxisty, demontovat celou politickou unii a nechat pouze ekonomickou - obchodní. To je to, o co jsme stáli.', 34);
INSERT INTO comments VALUES (7750, 'Pavel Novotný', '', 34);
INSERT INTO comments VALUES (7782, 'Zdeněk Trutnovský', 'Solidarita s EU... migranti nejsou EU ani to nejsou občané EU.', 34);
INSERT INTO comments VALUES (7783, 'Vítězslav Pečinka', 'Ona ta mediální masáž o apokalypse v případě czexitu není pro nic za nic...', 34);
INSERT INTO comments VALUES (7751, 'František Schlichts', '>Vtip z diskuze z novinek k témuž tématu. Humoru není( dle autora) nikdy dost: > Na palubě letadla s vojáky Cizinecké legie > letící do Afganistánu bojovat s teroristy, říká seržant: > "Chlapi, za každý pár mohamedánských > uší dostane každej z vás 200 euro, > za hlavu s talibánským vousem pět stovek!" > Letadlo přistane a natěšení vojáci z něj okamžitě vyběhnou... >Za dvě hodiny se radostně vracejí s koši plnými hlav a uší. > Seržant to vidí a je celý bledý a zpocený: > "Ku.va, chlapi, to bylo jen mezipřistání v Berlíně!".', 34);
INSERT INTO comments VALUES (7752, 'Antonín Švihnos', 'Stále více mne přesvědčují, abych se stal rasistou.', 34);
INSERT INTO comments VALUES (7753, 'Radim Simunek', 'existuje na svět dost velkých arabských firem.. banky, aerolinky, rejdaři.. to neukočíruje diletant...', 34);
INSERT INTO comments VALUES (7754, 'Tomáš Pak', 'tak největší zisky mají ze Škodovky, že? můžete klidně porovnat před tím a teď. Patří jim s tím se smiřte...', 34);
INSERT INTO comments VALUES (7755, 'Zdeněk Trutnovský', 'Každý den aspoň jeden článek..', 34);
INSERT INTO comments VALUES (7756, 'Ladislav Stehlík', 'Takze se nam (nebo spise jejim volicum) snazi rici, ze za problemy s uprchliky muzeme my?', 34);
INSERT INTO comments VALUES (7757, 'Zdeněk Trutnovský', 'Kolikrát máme Africe a Afričanům pomáhat? Poprvé jsme jim pomáhali, když byli malí. Aby neměli hlad, aby mohli do škol. Teď vyrostli a pomoc chtějí zas. Aby mohli žít v Evropě... to už je druhá pomoc. A třetí bude jako ta první, ale už v Evropě...', 34);
INSERT INTO comments VALUES (7758, 'Martin Blahník', 'Deutschland über alles A neberte mě špatně, já jsem pro EU, ale bez vůdčí úlohy Německa v čele s frau Merkel a s Junckerem jako šéfem komise...', 34);
INSERT INTO comments VALUES (7759, 'Frantisek Kofron', 'Rada Arabu je schopnych a rade neschopnych. Tak jako u jinych ras. Problem je Islam, ktery je nekompatibilni s liberalni demokracii.', 34);
INSERT INTO comments VALUES (7760, 'Tomáš Pak', 'myslíte, že na nás nebudou vydělávat jako na nečlenech?', 34);
INSERT INTO comments VALUES (7761, 'Mirek Tureček', 'No ja bych to jeste urychlil. Junker poda navrh a kdyz Merkel souhlasi tak tradáá ! Druhy den se muze realizovat. Zejmena kolonie V4 se to pak muze dozvedet v mediich. To by odsypavalo...', 34);
INSERT INTO comments VALUES (7762, 'Ondřej Šindler', 'Já to chápu. A právě proto tvrdím, že existuje obrovský koeficient třecího odporu mezi kulturami který činí bezproblémovou MASOVOU migraci téměř nemožnou a její zastavení je proto v nejlepším zájmu nás všech. Pokud hledáte objektivnější měřítka, tak si dovoluji skromně tvrdit že kultura co postavila Saturn V a vypracovala kvantový model atomu je objektivně hodnotnější než ta, co se zabývá kodifikací soulože s domácími zvířaty...', 34);
INSERT INTO comments VALUES (7763, 'Tomáš Pak', '*leč (to abych vám ušetřil zbytečný komentář)', 34);
INSERT INTO comments VALUES (7764, 'Jiří Soldát', 'Toto je EU ne německá říše, aby německá kancléřka diktovala jiným státům jak se mají chovat', 34);
INSERT INTO comments VALUES (7765, 'Honza Karásek', 'Jsem již 15 let euroskeptik, přesto jsem nevolil (a ani volit nemohl, ze svého přesvědčení) SPD ani KSČM. Jak se říká - nic není černobílé.', 34);
INSERT INTO comments VALUES (7766, 'Marek Jan', 'Tady se spíš nadává na Merkel a Junkra podkud se nepletu.', 34);
INSERT INTO comments VALUES (7767, 'Jarda Hofmann', 'tvá finanční negramotnost je obdivuhodná', 34);
INSERT INTO comments VALUES (7768, 'Lenka Mey', 'Tak uz je to tady. Vetsinove rozhodovani se hodi pro federaci, ale ne pro spolek suverenich statu. Juncker se uz netaji tim, ze nas trvale obelhava a vyhlasuje evropskou federaci. To jsem zvedava, jak si s tim Babis poradi. Zrejme nas Brusel, Nemecko a Francie kvuli svym svetovym ambicim postavi pred hotovou vec. Tady mate dotace a budte hezky zticha.', 34);
INSERT INTO comments VALUES (7769, 'Frantisek Kofron', 'Taky k nim nemigrujeme nebo snad jo?', 34);
INSERT INTO comments VALUES (7770, 'Ondřej Šindler', 'Svobodní jsou autentická a euroskeptická strana. Komunisté jsou ideoví dědici největších zrůdností a masových vražd 20. století. Pokud by vás jen napadlo je volit tak si ani neumím představit úroveň vaši morální pokřivenosti. O SPD už se snad ani nemá cenu bavit....', 34);
INSERT INTO comments VALUES (7771, 'Jarda Hofmann', 'a pak nastane ráj v česku ?', 34);
INSERT INTO comments VALUES (7772, 'Petr Pavlata', 'A už babka vyřešila, jak vylifrovat z Evropy těch 300 000, které shledali zatím shledali za ekonomické vyžírky bez nároku na nic?', 34);
INSERT INTO comments VALUES (7773, 'Marek Jan', 'Hlavně by měla taky říci B. Skoda auto 19 miliard do Německa, Banky Letzte a další dalších 40 miliard. Takže těch peněz z EU zase tak moc není. Pravda že si je rozkrademe to je o něčem jiném.', 34);
INSERT INTO comments VALUES (7774, 'Frantisek Kofron', 'Ve stredoveku byli vyspelejsi, ale bohuzel tam tak ustrnuli.', 34);
INSERT INTO comments VALUES (7775, 'Tomáš Pak', 'arogance vám zdá se nechybí leš argument nemáte ani jeden...', 34);
INSERT INTO comments VALUES (7776, 'Radim Simunek', 'třetina zapadne, třetina zapadne s problémy a třetina bude problémová.. a 10% bude vysloveně problémových..', 34);
INSERT INTO comments VALUES (7777, 'Jarda Hofmann', 'kd e vidíš masáž ?', 34);
INSERT INTO comments VALUES (7778, 'Jaroslav Tínek', 'Oni jsou natolik vyspělí, že kdyby neměli ropu, kde by byli ? Znáte nějaký náročný technický výrobek " Made in Arabský stát " ?', 34);
INSERT INTO comments VALUES (7779, 'Martin Blahník', 'Z firem které u nás postavili? ehm? cože?', 34);
INSERT INTO comments VALUES (7780, 'Tomáš Pak', 'na Češích js specifické to, že si myslí, že všechno ví nejlépe. Všichni okolo jsou blbci jen my máme patent na moudrost... A to ačkoi 99% Čechů muslima nikdy nevidělo...', 34);
INSERT INTO comments VALUES (8073, 'Jaroslav Douša', 'Strčte si dotace i vaše hosty (migranty z arabského světa) někam.', 34);
INSERT INTO comments VALUES (7784, 'Pavel Sobotka', 'To chce klid - v říjnu budou předčasné volby, tak volte Okamuru nebo Komunisty, pokud získají většinu, vyhlásí referendum o vystoupení z EU a bude pokoj. Opravdu se vám všem divím, jak můžete na EU nadávat, a při tom dát svůj hlas některé z Evropskou Unií zkorumpovaných stran - ODS, TOP09, ČSSD , STAN,...tihle nikdy z EU nebudou chtít odejít SPD+KSČM je JEDINÁ SKUTEČNÁ CESTA K REFERENDU O VYSTOPENÍ', 34);
INSERT INTO comments VALUES (7785, 'Frantisek Kofron', 'Radeji neberme dotace a berme to jako penize investovane do vlastni obrany. Aspon splnime ten 2% podil vydaju na obranu a budou to pritom jedny z nejlepe investovanych penez do obrany vubec.', 34);
INSERT INTO comments VALUES (7786, 'Jaroslav Hejna', '"Podle šéfa Komise Jeana-Claudea Junckera by v otázkách zahraniční politiky neměla být v Evropské radě potřeba jednomyslnost." Ať žije čtvrtá říše!', 34);
INSERT INTO comments VALUES (7787, 'Zuzana Vinklerová', 'Zdanit více nadnárodní společnosti, zdanit vyváděné peníze, zastavit odvody do EU pokladny a výsledek bude čistý zisk i bez dotací.Migranty si nechte a multikulti obohacování také.', 34);
INSERT INTO comments VALUES (7788, 'Radim Simunek', 'a zase podceňování... ach jo.. například u nás jsou arabové vesměs úspěšní a bohatí lidé.. tedy co znám.. arabové se o sebe umějí postarat.. u nich je taky nikdo neživí, nebojte se o ně', 34);
INSERT INTO comments VALUES (7789, 'Daniel Panek', '"...a varovala, že státy očekávající finanční podporu unie musejí v řešení migračního problému projevit solidaritu. " Já myslel, že EU je o společném jednání a postupu a zatím to vypadá jako nadvláda Německa Prosím od kdy Merkelová rozhoduje o financích v EU?', 34);
INSERT INTO comments VALUES (7790, 'Ondřej Šindler', 'Já si nemyslím že každý jeden imigrant musí mít vymytý mozek. On může mít sám o sobě neškodné úmysly a skutečně tu jen plánovat tiše žit, jenže prostě tady existuje nějaký koeficient třecího odporu mezi kulturami a to se nezapře...', 34);
INSERT INTO comments VALUES (7791, 'Michal Wendl', 'Němci na našem členství vydělávájí. Najděte si seznam Německých firem, kteří u nás vydělávají nepředstavitelné částky. Našim odchodem by o to přisli. :) Zapoměnte, že firma jako Bosh si nechá něco takového líbit :). Když tu má bilionové investice. :)', 34);
INSERT INTO comments VALUES (7792, 'Martin Blahník', 'Češi migrují na západ a k nám migruje východ (velké komunity slováků, ukrajinců, vietnamců atd). Nemám problém podpořit naše sousedy nebo členy EU, ale otevřít hranice "potřebným" je otevřený bianco pro celý svět přijet se napakovat na naší práci - celé EU. Druhá světová byla způsobena poválečným uspořádáním první světové, ignorace Hitlera, frustrací německé generality, chudoby a v první i poslední řadě všechny své velké aktéry našla na západě. Jestli se chcete prodávat jako šlapka, to je Vaše volba, ale v demokracii a tím spíše na té supranacionální má jít o kolektivní rozhodování a ne výkřiky několika politických jednotlivců, které vláčí se zbytkem. Ať EU utne kohoutky (Němci příspívají do kotle nejvíce, ale netuším proč by jim i jejich poslední euro mělo koupit "trůn), já jsem pro. Aspoň bude méně keců kolem...', 34);
INSERT INTO comments VALUES (7793, 'Jaroslav Tínek', 'Merkelová - pozvala jste si je ? Tak se starejte...', 34);
INSERT INTO comments VALUES (7794, 'Roman Kráčalík', 'Nemecko uz raz vitazilo na vsetkych frontach pre "Novu Europu" - a ako to dopadlo... Merkelovej ta lekcia evidentne nestacila...', 34);
INSERT INTO comments VALUES (7795, 'Jan Cibulka', 'Když to nechtějí, my toho nikdy nedosáhneme. Na to jsme příliš málo suverénní. A o to šlo.', 34);
INSERT INTO comments VALUES (7796, 'Ondřej Šindler', 'Evropa s tím fašismem experimentovala. Islám v zásadě fašistický je (fašismem v moderním smyslu rozumějme myšlenkový směr popírající zákaldnáí lidská práva a svobody)', 34);
INSERT INTO comments VALUES (7797, 'Vít Sklenář', 'Miss. Merkel', 34);
INSERT INTO comments VALUES (7798, 'Honza Karásek', '"Rychlejší rozhodování" = ukazuje se, jak se EU stala těžkopádným útvarem. Takovým, který aby "rychle rozhodoval", rozhoduje proti vůli části členských států. V předvolebních kalkulačkách byla mj. otázka, zda si myslím, že pozice ČR má být tzv. v jádru EU. Podle mě žádné jádro EU existovat nemá - společně řešeny mají být jen ty věci, u kterých si všichni členové přejí, aby byly řešeny společně.', 34);
INSERT INTO comments VALUES (7799, 'Dan Šída', 'Tak jste měl poslouchat to vyjádření odborníků. Jde o přístup na jednotlivé trhy, spolupráci, vzájemné obchodní vazby apod. Už nyní, když zpochybňujeme členství v EU, se různé firmy vyjadřují, že by od nás odešly, což by znamenalo ztrátu spousty pracovních míst apod.', 34);
INSERT INTO comments VALUES (7800, 'Jan Zahradníček', 'Extrémisté ve vedení EU a EK se chovají hůř než diktátoři.', 34);
INSERT INTO comments VALUES (7801, 'Tomáš Pak', 'hm myslíte, že Němci nemají firmy v Rusku, kterému ovšem žádné dotace nedávají? Na Ukrajině kde to je to samé? V Bělorusku? Myslíte, že je nemohou přesunout do Rumunska. My nejsme pupek světa, proberte se... Myslíte, že když opustíme EU vyženeme od nás německé firmy?', 34);
INSERT INTO comments VALUES (7802, 'David Novotny', 'I gramatiku muzem probrat', 34);
INSERT INTO comments VALUES (7803, 'Vít Sklenář', 'netvrdi ale manipuluje lidi stejne jako ten pan', 34);
INSERT INTO comments VALUES (7804, 'Lenka Pokorna', 'No oni hlavne nemaji co ztratit a mozky maji vymyte. Takze udelaji cokoliv. Jedine proc nas tu nechaji je to, ze by je nemel kdo zivit.', 34);
INSERT INTO comments VALUES (7805, 'David Novotny', 'Pokud mate zajem o lekce logickeho mysleni, neco historie a zorientovani se ve spolecenko-politicko-ekonomickych zalezitostech, muzem se domluvit na nejakych (placenych) konzultacich. Jinak nemam cas a naladu venovat se nejak podrobneji vasim blabolum.', 34);
INSERT INTO comments VALUES (7831, 'Radim Simunek', 'u muslimů si musíme uvědomit jedno, nejednáme s primitivy, jednáme s lidmi z vysoce kulturní oblasti staré tisíce let.. ještě ve středověku vyspělejší, než evropa.. my je ohromným způsobem podceňujeme, že my jsme ti západní chytří vyspělí a oni z nás spadnou na zadek.. nespadnou!', 34);
INSERT INTO comments VALUES (7806, 'Petr Kovařík', 'A kdo nadává za dotace? Část se hloupě promarnila, ale mnohem větší část se použila dobře - infrastruktura, ČOV a mnoho dalších. Nicméně to není to, co vidím jako výhodu členství v EU - pokud dotace nebudou, nic se neděje. Bude to těžší, ale o nic vážného nejde. Naopak vměšování se do kultury státu a vnucování nechtěné nekompatibilní a neintegrovatelné kultury vážná a nebezpečná věc skutečně je.', 34);
INSERT INTO comments VALUES (7807, 'Radim Simunek', 'evropa zase mezitím experimentovala s fašismem, komunismem... oni jsou jiní, my jsme jiní..', 34);
INSERT INTO comments VALUES (7808, 'Jan Nádvorník', 'Tak to jste mi to nandal.', 34);
INSERT INTO comments VALUES (7809, 'Roman Klazar', 'Ano, tohle je základní myšlenka sjednocené Evropy, pokud Vám to ušlo. Průšvihy ostatních řeší všichni. A můžu Vám říct, že dřív nebo později uděláme i my průšvih a takoví automatičtí křiklouni, jako Vy, pak budou zase křičet o tom, že EU nám má za povinnost pomoct.', 34);
INSERT INTO comments VALUES (7810, 'Jan Cibulka', 'Dva hlavní dealeři EU-tripu. No je jasný, že když už na tom frčíme a jsme na tom závislí, že přestat by bylo bolestivé. Ale nepřestat by bylo - jako u každé drogy - mnohem horší. Právě nám říkají, že kvalita drogy se bude nadále zhoršovat, zatímco cena bude stále vyšší... Je na nás, jestli budeme mít vůli, nebo jestli budeme věřit dealerům (včetně těch našich).', 34);
INSERT INTO comments VALUES (7811, 'Tomas Drajsajtl', 'On neni brepta, jen mu vypadlo pismenko ze jmena.', 34);
INSERT INTO comments VALUES (7812, 'Roman Kráčalík', 'Nepoznate "Vodcovsky princip"? Vodcovi predsa nemusi nikto davat mandat. Vedie a ochranuje ho prozretelnost,verte mi,Nemci to maju odskusane....', 34);
INSERT INTO comments VALUES (7813, 'Tomáš Pak', 'no ono to spíš vypadá, že pokud nedospějeme, nebudeme na západě...', 34);
INSERT INTO comments VALUES (7814, 'Dan Šída', 'Žije u nás už spoustu let asi 20 000 muslimů a nevšiml jsem si, že by nás likvidovali. Ale nemyslete si, že já chci, aby nás muslimové přečíslili nebo že bych byl pro právo šaría. V žádném případě, oni se musí přizpůsobit, pokud by u nás chtěli žít. A pokud vím, tak u nás žádné problémy v tomto směru nejsou.', 34);
INSERT INTO comments VALUES (7815, 'Jiří Hrbek', 'Ty socansky centrálně rozdávané peníze nazývané vznešeně "čerpání" ve skutečnosti jen ničí spravedlivý svobodný trh, nikdo si těch peněz neváží jak by měl a v důsledku se utrácí za voloviny, svádí ke korupci a vytváření zbytečné vrstvy "odborníků na lobbing a eu-formuláře". Nechte si své peníze i "krimiuprchlíky" a dejte nám šanci žít si jen za to, co si sami vyděláme', 34);
INSERT INTO comments VALUES (7816, 'Jiří Rohan', 'Solidarní znamená dobrovolné. To co nabíze Merkelová je skoro až vydírání.', 34);
INSERT INTO comments VALUES (7817, 'Michal Hruboš', 'Neviem čo berieš, ale vysaď to!', 34);
INSERT INTO comments VALUES (7818, 'Ondřej Šindler', 'Když už teda jste s tím začal. Zlomový bod pro obě civilizace byl když se začal řešit konflikt a neslučitelnost rozumu a víry. Zatímco euroamerická civilizace většinově touto zkouškou prošla a dospěli jsme k osvícenství, liberalismu a positivismu, islámský svět v této zkoušce naprosto selhal a proto se dodnes pohybuje mentálně někde ve středověku.', 34);
INSERT INTO comments VALUES (7819, 'Tomáš Pak', 'Němci nám ukládají povinnost brát si je do svých bytů? Vážně? Já mysle, že máme provést azylové řízení a kdo nemá na azyl nárok poslat ho domů... Tedy dodržovat naše zákony...', 34);
INSERT INTO comments VALUES (7820, 'Tomas Vrlik', 'Hlavne at si ten zadek nechaji u nich doma.', 34);
INSERT INTO comments VALUES (7821, 'Jaroslav Zeman', 'Viertes Reich um jeden Preis!!', 34);
INSERT INTO comments VALUES (7822, 'Tomas Drajsajtl', 'Co si Mrkalova nadrobila, at si taky vyzere.', 34);
INSERT INTO comments VALUES (7823, 'Zdeněk Janout', 'Vždycky to dělala, a starého psa novým kouskům nenaučíš (a pro starou fenu to platí jakbysmet)', 34);
INSERT INTO comments VALUES (7824, 'Petr Kovařík', 'Snažíte se splácat všechno možné do jedné koule. Tak to ale vůbec není. Mluvíte o lidech v nouzi, přitom jde ve většině o lidi bez nouze, kteří se pouze chtějí mít mnohem líp. Chcete po nás, abychom podporovali to, co nikdy nefungovalo, nefunguje a fungovat nebude, tj. integraci muslimů do evropské kultury. Vaše řeči o dotacích jsou pouze řeči - pokud tu dotace nebudou, tak nebudou. Nikomu to žíly trhat nebude, prostě se to vezme jako fakt, a půjde se dál bez dotací. Ale podmiňovat tím nefunkční projekt přijímání a integraci muslimů? To nemyslíte vážně.', 34);
INSERT INTO comments VALUES (7825, 'Tomáš Pak', 'ano je to přesné, blbě to utratíme a pak jim za to nadáváme, pěkné, že jste to potvrdil...', 34);
INSERT INTO comments VALUES (7826, 'Tomas Vrlik', 'Jakym pravem mluvi za EU nemecka politicka?', 34);
INSERT INTO comments VALUES (7827, 'Marek Jan', 'Aleši ne.Jen ať si to Merkel s Junkrem vyřeší a to i za cenu žádných dotací pro Česko. Pokud nám je sem nacpou tak ať laskavě Merkel a Junker šáhnou do peněz svých daňových poplatníků a za tyhle lidi kteří stejně utečou k nim nám hezky platí.', 34);
INSERT INTO comments VALUES (7828, 'Tomáš Pak', 'a když je nebudeme dostávat protože nebudeme v EU tak nebudou mít zisky z firem, které u nás postavili? vážně?', 34);
INSERT INTO comments VALUES (7829, 'Roman Klazar', 'To, co říkáte pouze jakási falešná hrdost. A kdybyste si sehnal informace, tak byste věděl, že v každém zákoně je maximálně pár evropských paragrafů ( a německé zákony tu neplatí vůbec pokud vím). To Vy jste mimo, když si myslíte, že někoho posloucháme, obzláště Němce. Víte vůbec, v čem přesně ty Němce posloucháme?', 34);
INSERT INTO comments VALUES (7830, 'Roman Kráčalík', 'V Berline sa svieti dlho do noci... To sudruzka Angela pracuje pre blaho vsetkych pracujucich vo velkej krajine,kde zajtra uz znamena vcera,vo velkej EU,kde zije sa radostnejsie,zije sa veselsie! Pod zastavou Merkelovej,Junckera,Schulza a Gabriela za lepsi zajtrajsok,budovat a branit vzdy pripraveni!', 34);
INSERT INTO comments VALUES (8104, 'Jaroslav Novák', 'a clech za dovoz zboží a další náklady spojené s naším členstvím v EU', 34);
INSERT INTO comments VALUES (7832, 'Aleš Chudý', 'Dvořáku, v té lebce toho asi moc nebude. Ty dotace potřebuje hlavně těch cca 35 tisíc zaměstnanců Agrofertu, ne Babiš. Ten di tě může už dnes koupit i hezkým ostrovem a na všechno se vysmo...(''o'')', 34);
INSERT INTO comments VALUES (7833, 'Dan Šída', 'Dovolte, to srovnání je úplně mimo. Adolf Hitler rozpoutal 2. světovou válku, byl členem NSDAP, byl to diktátor a jeden z největších zločinců 20. století, zatímco Angela Merkel je kancléřkou demokratického Německa.', 34);
INSERT INTO comments VALUES (7834, 'Petr Mareček', 'To je ale pěkná snůška nesmyslů co jste splácal. Kdyby o nás EU jako o trh nestála. Nikdy by nás nepřijala! To jen takoví jako vy se to snaží prezentovat jako nějaké milosrdenství. A lidé z integračních ziskovek by opravdu zasloužili něco jiného než urážky. Hádejte ovšem co!', 34);
INSERT INTO comments VALUES (7835, 'Martin Halilovič', 'Ano odborníci nás můžou strašit a možná i oprávněně ekonomickou sebevraždo v případě czexitu, nicméně přesně kvůli podobným nátlakovým akcícm Merkelové a spol. bude nakonec většina lidí hlasovat pro vystoupení. Včetně mě. Nějak bylo před vstupem do EU, nějak bude i po vystoupení z ní.', 34);
INSERT INTO comments VALUES (7836, 'Zdeněk Janout', 'Aha. A ty příspěvky do fondů EU za nás platí svatý Petr, že jo', 34);
INSERT INTO comments VALUES (7837, 'Ondřej Šindler', 'Tak to pozor. Občané ČR si zvolili členství v EU, Lisabonský systém jim byl naprosto antimorálně vnucen tehdejším parlamentem. Jinak jedna věc je se podílet i na problémech. Druhá věc je přistoupit na naprosto šíleně sebevražednou politiku kdy se migrace řeší jen na oko. Ti turečtí muslimové v německu volili nedávno totalizující antisekularistu Erdogana víc než lidi v turecku, tolik k úspěšnosti integrace za kterou jsou ty neziskovky tak dobře placené...', 34);
INSERT INTO comments VALUES (7838, 'Tomáš Pak', 'no a co není pravda byste uměl napsat?', 34);
INSERT INTO comments VALUES (7839, 'Martin Blahník', 'Opravím, ČR samozřejmě přispívá, ale více z EU dostává - je tam cca 80 miliard CZK rozdíl. EU není jen o penězích, ale taky vzdání se částečně suverenity v zahraniční politice, otevření trhu, volného pohybu osob atd. atd - je toho vlastně celá kupa věcí. Ten finanční rozdíl brzy skončí a já s tím osobně nemám problém, aby ČR příspívala finančně, ale nechat se vydírat za peníze mi nepřijde v pořádku. Navíc něčím takovým, jako je "solidarita" na základě německé politiky', 34);
INSERT INTO comments VALUES (7840, 'Lenka Pokorna', 'Mily pane, pokud nevidite to, co byste mel, tak pro pripomenuti. Ja tady pracuji a zivim sebe i sve deti. Ti co pracuji pro Nemce, se Nemcum svou praci dost vyplati, takze v tomto bode take nic nedluzime. Nemci tento stat behe svetove valky zdecimovali a zpustosili, takze dluzi oni nam.', 34);
INSERT INTO comments VALUES (7841, 'Jiří Krejča', 'Němci si tak nějak historicky myslí, že jsou nadlidé a ostatním by měli vládnout. Naštěstí se vždy najde někdo, kdo jim ukáže kde je jejich místo.', 34);
INSERT INTO comments VALUES (7842, 'Jan Cibulka', 'Západ nás nepotřebuje dospělé...', 34);
INSERT INTO comments VALUES (7843, 'Rudolf Strnad', 'Vemte si jednu, dvě rodiny uprchlíků domů a teprve pak se podělde o zkušenosti.', 34);
INSERT INTO comments VALUES (7844, 'Jaroslav Zeman', 'Já souhlasím s tím, že k právům se vážou i povinnosti. Ale ať jsou ty povinnosti rozhodnutím všech a ne jen jednoho státu. Teď je to tak že Uzenáryba chce rozhodovat za celou Evropu bez mandátu. Pokud se EU nevrátí k dobrémů základu (volný pohyb osob, zboží a služeb) a suverenitě jedntolivých států tak z toho spolku raději pryč. Každý stát má totiž úplně jiné zájmy a minulost a mezi jedntlivými státy je tolik pocitů nespravedlnosti z historie že USE (obdobu USA) se jim nepodaří nikdy vytvořit.', 34);
INSERT INTO comments VALUES (7845, 'Jiri Dvorak', 'Teď se ukáže, zda Babiš dá přednost EU dotacím nebo ochraně občanů ČR.', 34);
INSERT INTO comments VALUES (7846, 'Aleš Chudý', 'Váš názor, než se toho nějaký čičmunda chytne...', 34);
INSERT INTO comments VALUES (7847, 'Pavel Novák', 'Dobře, buďme tedy solidární, pokud tady chtějí žít uprchlíci, tak je přijmeme a klidně se můžeme pokusit o jejich integraci. ALE zároveň tedy chci, aby byla EU solidární s námi, chci platy jaké mají v Německu, chci sociální dávky (důchody) jako v Německu, chci stejně potraviny jako v Německu a za stejné ceny.', 34);
INSERT INTO comments VALUES (7848, 'Jaromír Král', 'Ti ostatní ne. Aspoň ne všichni.', 34);
INSERT INTO comments VALUES (7849, 'Pavel Sobotka', 'Tento prpoces již započal. Dvourychlostní evropa bude znamenat, že integrované jádro bude mít nárok na všechny výhody členství, zatímco země mimo hlavní proud budu na hraně výhodnosti a mnoho jich dobrovolně společnost, se kterou nesouhlasí, opustí.', 34);
INSERT INTO comments VALUES (7850, 'Václav Zelinka', 'A proč máme lízat nějaká negativa, když vznikají kvůli šlendriánu někoho jiného? Kdyby se respektovaly základní pilíře schengenské dohody (ochrana vnějších hranic a jednotná vízová a imigrační politika), tak žádný problém není. Frau Merkel a další vítači si s těmito důležitými ustanoveními vytřeli zadek a my to teď máme pomáhat hasit. Prosím račte, když chcete, ale za své. Je smutný paradox, že Maďarsko, které podle těchto ustanovení postupovalo, bylo považováno za černou ovci.', 34);
INSERT INTO comments VALUES (7851, 'Jan Prichystal', 'Coz o tom, hranicni zeme by sve hranice rady hlidaly. Nebo si snad myslite, ze Rekove a Italove jsou nadseni z toho jak jim barbari plundruji jejcih zemi a kulturne obohacuji jejich zeny a divky? Jenomze branit hranice jim zakazala EU, konkretne soud EU rozudkem, ktery zakazal dosavadni praxi okamziteho vraceni plavacku jeste na mori.', 34);
INSERT INTO comments VALUES (7852, 'Aleš Chudý', 'To je vám názor, který vám vůbec neberu.', 34);
INSERT INTO comments VALUES (7853, 'Jaromír Král', 'Bereme od Němců dotace a dávky ? Vážený pane, ty peníze se Němcům bohatě vracejí !', 34);
INSERT INTO comments VALUES (7854, 'Roman Klazar', 'Tak za jednu chybu maximálně se dvojkou. A přesto bych byl tipuju mnohem lepší, než většina populace.', 34);
INSERT INTO comments VALUES (7855, 'Jura Mazik', 'Kdo jí dal mandát?', 34);
INSERT INTO comments VALUES (7856, 'Jiří Krejča', 'Nechme eurokolchozu jejich prachy a žádné jim tam neposílejme. Ano nebudeme mít lavičky za 100 000, nebudeme stavět další akvaparky a rozhledny, bude méně cyklostezek a sosky z organizací přisátých na bruselský penězovod nebudou mít co žrát, ale nebude naše vlast zaplavena tím mohamedánským štěstím, kterého se chce Merkel zbavit.', 34);
INSERT INTO comments VALUES (7857, 'David Novotny', 'Ale jdi ty brepto', 34);
INSERT INTO comments VALUES (7858, 'Josef Chovanec', 'Ta baba neda pokoj, a neda.', 34);
INSERT INTO comments VALUES (7859, 'Roman Klazar', 'Je smutné, že si tohle myslíte. Bohužel každý, kdo tohle tvrdí má informace na úrovni vesnické hospody.', 34);
INSERT INTO comments VALUES (7860, 'Dan Šída', 'Jen málokdo u nás získá azyl.', 34);
INSERT INTO comments VALUES (7861, 'Jaromír Král', 'Nejdřív dělá kopance, ač ji na to ostatní upozorňují a teď chce od nich "solidaritu" ?', 34);
INSERT INTO comments VALUES (7862, 'Petr Mareček', 'O žádné Syřanky s dětmi nejde! A prověřit kohokoliv z těch končin prostě dost dobře není možné. To o co se tu pokoušíte. By se dalo nazvat umírněným sluníčkařením.', 34);
INSERT INTO comments VALUES (7863, 'Dan Šída', 'Proč bych toho měl nechávat?', 34);
INSERT INTO comments VALUES (7864, 'Jiří Krejča', 'V čem by byla pro nás katastrofa, kdybychom nedostávali z eurokolchozu žádné prachy a žádné bychom tam neposílali?', 34);
INSERT INTO comments VALUES (7865, 'Ondřej Tomek', 'Uboze mimo jsi ty Klazare. My nejsme povinni skákat tak jak Němec píská, zvlášť když píská falešně.', 34);
INSERT INTO comments VALUES (7866, 'Tomáš Pak', 'Merkel má naprostou pravdu. Neustále tu pomlouváte ti kdo s nataženou rukou nechtějí ni dělat a jen brát dávky... Neustále tu pomlouváte ekonomické migranty co podle vás přicházejí na západ nechtějí nic budovat a očekávají, že jim "zápaďaně" zajistí vysokou životní úroveň... a tady se začíná celý stát chovat přesně podle tohohle vzorce... žádali jsme, aby nás přijali do EU, abychom získali stejnou životní úroveň a teď odmítáme nést úděl bohatých států, který nesou už od druhé světové války, že totiž ti bohatí obvykle pomáhají lidem v nouzi... Okolní státy jim pomáhají, nám pomáhali když jsme byli v nouzi... jsme typičtí celostátní ekonomičtí migranti co chtějí od těch kdo nás přijali, aby nám zařídili pohodlný život a nechtěli po nás s čímkoli pomoct. bereme od Němců jejich daně jako dávky (dotace) protože jsme chudší a za to, že je blbě utrácíme jim nadáváme. To je myslím opravdu novum, které by nenapadlo ani naše nepřizpůsobivé, že? Nadáváme jejich představitelům, které si zvolili, protože mají jiný názor. Sami jim posíláme jako hlavní představitele naší země agenta STB, který pracoval v organizaci, která měla za cíl zničit každého kdo k západu projevoval náklonnost a jen tak mimochodem zcizil část peněz, které nám poslali.... Pak odsouzeného lháře, co pomluvil každého kdo v tomto státě stál na nezáviděníhodné těžké historické křižovatce v roce 68 a lísá se k těm kdo je do té situace dostali a teď hlasitě litují, že v roce 89 nepoužili tanky. Uráží Němcům jejich spoluobčany, ať už to jsou turečtí muslimové, kteří v Německu žijí, nebo lidé z neziskovek, kteří pomáhají s intergrací. Buď se 100 let od svého vzniku stane tento stát "dospělým" a přijme svou část zodpovědnosti za budoucí Evropu, protože kdo má výhody má i povinnosti. Nebo do konce svých zbývajících dějin svádět vše na své okolí a pak nadávat po hospodách v jednom velkém koncentráku obehnaném ostnatým drátem protože když ho stavěli naposled, také říkali, že to je ochrana před "vnějším nebezpečím"...', 34);
INSERT INTO comments VALUES (7867, 'Mirek Buchtík', 'To již delší dobu.', 34);
INSERT INTO comments VALUES (7868, 'Lenka Pokorna', 'No pravda je, ze par vystudovanych zdravotnich sester s praxi, par lekaru a potom lidi do vyroby by se nam sesli. Takze v nedeli na ubytovnu a v pondeli muzou zacit makat, pani Merkelova.', 34);
INSERT INTO comments VALUES (7869, 'Jiří Krejča', 'Výsledek je ale stejný...zavlečený druh likviduje ten domácí, že ano?', 34);
INSERT INTO comments VALUES (7870, 'Roman Kráčalík', 'No problem je v tom,ze ak si raz tie nebezpecne kobylky dovezieme k nam domov,uz sa ich nezbavime - naopak,este si dotiahnu dalsich v ramci "zlucovania rodin". Ucinne je len jedno - vôbec ich sem nepustit,ani docasne (oni totiz pojem docasne nepoznaju)', 34);
INSERT INTO comments VALUES (7871, 'Jura Mazik', 'Tak proč nám do všeho kecá', 34);
INSERT INTO comments VALUES (7872, 'Alois Slámečka', 'My uprchlíky přijímali a stále přijímáme. Ovšem z vlastní vůle, ne ty nakvótované. Mimo jiné zejména ty, o které "západ" nestojí.', 34);
INSERT INTO comments VALUES (7873, 'Pavel Sobotka', 'Česko nepřispívá, ale čerpá. A tento parasitismus velmi rychle skončí.', 34);
INSERT INTO comments VALUES (7874, 'Jiří Žamboch', 'Solidární můžeme být jen s rozhodnutími, na kterých jsme se podíleli. Jednostranné a dokonce protiprávní kroky členských států je třeba odsoudit a dokonce potrestat (k čemuž porušením Dublinu Německem nedošlo), Rozhodně si však důsledky musí nést sami', 34);
INSERT INTO comments VALUES (7875, 'Jan Cibulka', 'Dobře si je prohlédněte, ten napravo dále zpochybňuje základní principy tohodle nesmysu proklamacemi, že:"EU se musí rozhodovat rychleji, ne vždy je nutná shoda", a ta NALEVO vzkazuje: "Chcete peníze, musíte být solidární s uprchlíky" Jestli rychle neodejdou oni, my bychom neměli o moc déle váhat.', 34);
INSERT INTO comments VALUES (7876, 'Josef Pišvejc', '...dočasného domova pro přistěhovalce... - jestli ono to nebude i tím, že s tzv. ''dočasným'' pobytem máme bohaté zkušenosti :-/', 34);
INSERT INTO comments VALUES (7877, 'Martin Blahník', 'Hééj, tak čas vyhodit Španělsko, Itálii, Řecko, atd. VB už se vyprovodilo samo. EU by vlastně mohla být jen Francie a Německo, to by byla teprve ta pravá socialistická jednomyslnost!', 34);
INSERT INTO comments VALUES (7878, 'Dan Šída', 'Což by v dnešní době byla katastrofa, ke které se už odborníci vyjádřili. Ale tyto argumenty nechtějí odpúrci EU pochopit.', 34);
INSERT INTO comments VALUES (7879, 'Jura Mazik', 'Jako ein folk, ein reich, eine führerin? Na základě čeho tuto funkci má?', 34);
INSERT INTO comments VALUES (7880, 'Zdeněk Janout', 'Bundeskancléřka v demisi s velmi nejistou budoucností.', 34);
INSERT INTO comments VALUES (7881, 'Petr Mareček', 'Klid! Zatím stále dostáváme více než odvádíme. Tedy pokud pomineme kolik odsud vyvádějí západní firmy.', 34);
INSERT INTO comments VALUES (7882, 'Mirek Buchtík', 'Když jsme v EU ještě nebyli, tak jsme nefungovali? Jistě že ano a byli jsme v mnoha věcech soběstační. Je si vemte, kolik jsme loni importovali ze západu masa. A to jen kvůli tomu, že jsme museli na jejich rozhodnutí zlikvidovat chovy zvířat. A co cukr, drůbež atd.', 34);
INSERT INTO comments VALUES (7883, 'Jan Nádvorník', 'Tady už nám negativa začínají převažovat nad pozitiva.', 34);
INSERT INTO comments VALUES (7884, 'Aleš Chudý', 'Je u naps cca 120 tisíc Ukrajinců. Já potkám Ukrajince tak jednou za měsíc někde v nákupním centru. Tak pojďme příjmout 2 tisíce usmolených Syřanek s děckama, které my, jenom my samotní (ČR) si prověří a až je prověří, tak můžou přijít. Do půl roku jich u nás zůstane asi 40 (o to se náš občan postará), takže, tak malého čísla, v desetimilionové, poměrně bohaté zemi, bych se vůbec nebál. Tohle je jenom mediální masáž Japonska, které nám z nějakého důvodu nemůže odpustit, že jsme se trhli od císařství, když to Japonsko je taky císařství...', 34);
INSERT INTO comments VALUES (7915, 'Antonin Kadlcek', 'Jenom aby ta opona nemusela jit znovu navrch. Jenom je me lito Rakusanu a Svycaru, ze si je take nechali zahnizdit ve spolecnosti.', 34);
INSERT INTO comments VALUES (7916, 'Petr Mareček', 'Pokud jich ročně přijde do EU 1M. Je 100k při vší umírněnosti našeho podílu. Otázkou tak pěti let. Co vy na to?', 34);
INSERT INTO comments VALUES (7885, 'Martin Blahník', 'Čtvrtá říše v čele s Německem za každou cenu a ještě k tomu EU rozšiřovat až k Balkánu. Naopak opevnit vnější hranice a řešit vnitřní problémy - určitě ne tím, že omezíme princip v jednomyslnosti na úkor akce schopnosti. A propo, solidarita je logicky vždy jednosměrná, protože jinak už to je obchod něco za něco. No nevím jak Vy, ale já se nechci cejtit jako ku... šlapka - za peníze skákat jak Německo píská', 34);
INSERT INTO comments VALUES (7886, 'Zdeněk Janout', 'Souhlas. Česko přispívá víc než dost. Máte něco dalšího ?', 34);
INSERT INTO comments VALUES (7887, 'Dan Šída', 'Nemůžete přece srovnávat rostliny či živočichy s lidmi?????', 34);
INSERT INTO comments VALUES (7888, 'Zdeněk Janout', 'Abyste neZkončil s pětkou z češtiny', 34);
INSERT INTO comments VALUES (7889, 'Pavel Sobotka', 'Popkud Česko nechce ničím přispívat, ale chce jen čerpat výhody, nemá v EU co dělat.', 34);
INSERT INTO comments VALUES (7890, 'Honza Karásek', 'Ty jim nebudou proti srsti ani dál. Jen už by měli problém s vyplácením dotací. Hlavně - odvody do společného rozpočtu se platí tak jako tak; dotace jsou účelově vázané a jen na konkrétní projekt s nejistým výsledkem přidělení.', 34);
INSERT INTO comments VALUES (7891, 'Helena Vondráčková', 'Bába bláznivá.', 34);
INSERT INTO comments VALUES (7892, 'Ondřej Šindler', '"zrychlit a zjednodušit rozhodování" - jasný eufemismus pro centralizaci moci a její posouvání zase dál od voliče.', 34);
INSERT INTO comments VALUES (7893, 'Dan Šída', 'To v žádném případě já nechci. Ale když přijmeme nějaké uprchlíky před válkou, myslím tím skutečné, tak projevíme dobrou vůli a nebudeme za sobce, kteří umí jen kritizovat a nepomohou. Takový postoj jako země Visegrádu nemají žádné další země EU a to jsou tam třeba pobaltské státy, které patřily kdysi k SSSR.', 34);
INSERT INTO comments VALUES (7894, 'Luboš Mašín', 'Kdo si je pozval ať si je živí.', 34);
INSERT INTO comments VALUES (7895, 'Jiří Krejča', 'Pokud chceme být i nadále v eurokolchozu, nechme si mohamedány zaneřádit i naši vlast...to opravdu stojí za to.', 34);
INSERT INTO comments VALUES (7896, 'Zdeněk Janout', 'Makrelo, ty své prachy si teda strč do Brusele, žádné vetřelce přijímat nebudeme. Já chápu že seš nervózní - do vlády Ti odmítl jít Tvůj nohsled Schulz, navíc socdemáci Ti možná vládu shodí úplně a Ty půjdeš od válu. A k těm kvótám - proti nim už zdaleka není jen Visegrád, ale také třeba Rakousko a další, a po březnových volbách v Itálii padne s velkou pravděpodobností i tato "kvótařská" bašta...', 34);
INSERT INTO comments VALUES (7897, 'Petr Mareček', 'Á už se to blíží. Podle pí. Junckera bude na plno věcí stačit rozhodnutí evropské komise. Už se nebudou nikoho ptát na jeho názor. V takovém spolku nemá cenu dál setrvávat.', 34);
INSERT INTO comments VALUES (7898, 'Pavel David', 'nepozvala. dokola opakovany mytus.', 34);
INSERT INTO comments VALUES (7899, 'Marián Pilc', 'Tady jde o solidaritu s Itálií, Řeckem, Španělskem a částečně Francií, které mají plné pobřeží, jak to jen říct?, náplav a ne o solidaritu s Německem. To právě solidaritu ukázalo tím, že před pár lety všechnu náplavu z Maďarska pozvalo k sobě. Kdyby zavřelo hranice, jak původně chtělo, tak je náplava v Maďarsku, Řecku a Itálii.', 34);
INSERT INTO comments VALUES (7900, 'Zdeněk Vodák', 'To je jako říct že se Adolf Hitler nedopustil žádného trestného činu', 34);
INSERT INTO comments VALUES (7901, 'Radek Běhavý', 'Angelo, dneska nás nenase..š, dneska slavíme Ester', 34);
INSERT INTO comments VALUES (7902, 'Tomáš Tatíček', 'A ty si toho roztrubování mouder nenecháš?', 34);
INSERT INTO comments VALUES (7903, 'Pavel David', 'netvrdi', 34);
INSERT INTO comments VALUES (7904, 'Pavel David', 'nic takoveho netvrfi', 34);
INSERT INTO comments VALUES (7905, 'Mirek Buchtík', 'Navrhuji s okamžitou platností přestat té hydře (EU) platit desátky. Nebo snad dvacátky?', 34);
INSERT INTO comments VALUES (7906, 'Roman Klazar', 'No já myslím, že všichni odmítači EU stejně nikdy nepochopí, že Merkelová má pravdu. Pokud chceme být v Evropě, musíme ji pomoct lízat i ta hořká negativa, poněvadž ta pozitiva převažují o řád. Pokud si někdo myslí, že budeme zobat jen ta pozitiva a negativa ventilovat kamsi mimo náš obzor, ten má velmi úzké obzory, protože nic nefunguje jednoduše, jak si asi spousta lidí myslí. A víte, jaká je jediná cesta, jak Vás, tuhle většinu, přesvědčit, jak uboze jste mimo? Nechat Vás, at si něco velkého chvíli vedete sami. To se pak na Vás sesype tak rychle, že byste se divili i ušima. A dřív, nebo později byste to bud pochopili a změnili názor, anebo zkončili totálně zahořklí, jak velký to byl Váš výbuch.', 34);
INSERT INTO comments VALUES (7907, 'Jiří Krejča', 'Reichsfuehrerin', 34);
INSERT INTO comments VALUES (7908, 'Petr Klein', 'To je těžký. podle toho, co se jim právě hodí.', 34);
INSERT INTO comments VALUES (7909, 'Kateřina Slavíková', 'Každá dotace je pouze legální úplatek, a za každý úplatek následuje protislužba.', 34);
INSERT INTO comments VALUES (7910, 'Jura Mazik', 'Co je to Merkelová. Nějaká oficiální funkce by nebyla.', 34);
INSERT INTO comments VALUES (7911, 'Benedikt Václavík', 'Nechte si vaše dotace a my přestanem dotovat vás! Neumím si představit ten hunbuk, kdyby někdo použil větné spojení ve kterém by bylo: znárodnění a německé podniky.', 34);
INSERT INTO comments VALUES (7912, 'Rostislav Meisner', '...no podívejme se na bábinu,,--kdože to tvrdil že to zvládne?? vždyť si je pozvala, tak proč je najednou strká k sousedům? tolik doktorů a inženýrů,, jen ať si je všechny nechá,, budou ji obohacovat.', 34);
INSERT INTO comments VALUES (7913, 'Dan Šída', 'My jsme spolu pásli husy???? A Angela Merkel není žádná diktátorka.', 34);
INSERT INTO comments VALUES (7914, 'Jaroslav Krejčí', 'To mluví za Německo, nebo opět za EU, ačkoliv Německo není předsedajícím státem?', 34);
INSERT INTO comments VALUES (7917, 'Jiří Žamboch', 'Evropa zase zasypána německými výhrůžkami, tentokrát preventivně - to milujeme. Vojáci by řekli dělostřelecká příprava. Ejhle „ .. solidarita nemůže být jednosměrná,“ To tedy skutečně ne, pokud máme poskytovat arabům azyl či dočasný pobyt, nemohou do nás najíždět auty, bodat noži a střílet nás samopaly.', 34);
INSERT INTO comments VALUES (7918, 'Karel Hauptvogel', 'Jó, to on by chtěl a už maká - pro sebe. Naštěstí na větší "sousta" nemá dostatek hlasů.', 34);
INSERT INTO comments VALUES (7919, 'Kateřina Slavíková', 'je to jako nástup Hitlera v roce 1933.', 34);
INSERT INTO comments VALUES (7920, 'Dan Šída', 'Až na nepatrné výjimky teroristické činy nepáchali uprchlíci z migrační vlny po roce 2015. A nikdo po nás nechce, abychom přijímali neprověřené migranty.', 34);
INSERT INTO comments VALUES (7921, 'Mirek Bárta', '', 34);
INSERT INTO comments VALUES (7922, 'Honza Karásek', 'Euroskeptikem jsem se stal již někdy v 1. polovině roku 2003 (mj. již v době jednostranné masírky typu "vysvětlovací kampaň s heslem ANO" či "nejprve harmonizovat 25 přístupových kapitol A PAK se občanů v referendu zeptat, jestli tam vůbec chtějí vstoupit) a jsem jím dodnes. Jsem za to rád a nestydím se za to.', 34);
INSERT INTO comments VALUES (7923, 'Jaroslav Krejčí', 'A kvůli tomu musíme zadělává našim dětem a vnukům na katastrofu? Já tu nechci za 100 let žádný islámskou republiku.', 34);
INSERT INTO comments VALUES (7924, 'Kateřina Slavíková', 'Na jednu stranu Unie nařizuje likvidaci nepůvodních zavlečených přírodních druhů kvůli ochraně těch původních, na druhou dováží cizince z Afriky . Jak se v tom má člověk orientovat ?', 34);
INSERT INTO comments VALUES (7925, 'Jan Cibulka', 'Co se píše v Koránu o "Domu islámu"? Kam vkročí noha muslima... Proto nejdou "ke svým", tam už není co do Domu islámu připojovat. Za tím musí být něco velkého, velkého jako peníze samy. Ale od koho?', 34);
INSERT INTO comments VALUES (7926, 'Petr Mareček', 'Fajn. Si ty dotace nech nádhero jedna. Ale nikdo nám nebude diktovat s kým máme žít v naší zemi!', 34);
INSERT INTO comments VALUES (7927, 'Jiří Seidl', 'Ano, celých 1.300, to je brnkačkaTady jde spíše o desetitísíce ročně, respektive cca 3% z jakéhokoliv počtu.', 34);
INSERT INTO comments VALUES (7928, 'Mirek Bárta', 'V 89. jsme cinkali klíčema za svobodu a dočkali jsme se tohohle....', 34);
INSERT INTO comments VALUES (7929, 'Marián Pilc', 'Ehmehm Výskedek->Výsledek', 34);
INSERT INTO comments VALUES (7930, 'Vít Sklenář', 'sak ja uz to tu jednou v jedne diskuzi psal, ze mi pani Merkel pripomina jednoho pana s knirkem, ktery kdysi taky vladnul v nemecku,taky zmanypuloval lidi, a taky dostal nemecko do sr.č.k!!', 34);
INSERT INTO comments VALUES (7931, 'Jan Derka', 'Chtějí po nás solidaritu, kterou už z podstaty nelze vynucovat a do toho chtějí ještě odzbrojit legální držitele? Není toho už trošku příliš?', 34);
INSERT INTO comments VALUES (7932, 'Tomáš Tatíček', 'O právech a povinnostech byly uzavřeny smlouvy, které ta tvoje milovaná diktátorka porušuje. Raději už mlč.', 34);
INSERT INTO comments VALUES (7933, 'Marián Pilc', 'Výskedek referenda o Brexitu je důsledkem toho, že Britům vadí přistěhovalci z východní části EU, jako Poláci, Češi, Slováci, Rumuni,...', 34);
INSERT INTO comments VALUES (7934, 'Mirek Bárta', 'Také vidím výhody EU, ale kvůli nim se nemíním nechat postřílet, podřezat, nebo rozjezdit!', 34);
INSERT INTO comments VALUES (7935, 'Blanka Vágnerová', 'nechci solidaritu, migranti chtějí do Německa, Francie, Británie - tak ať si tam jsou, ne násilím u nás', 34);
INSERT INTO comments VALUES (7936, 'Tomáš Tatíček', 'Hele, sociální bojovníče, těch třináct set jich tam zůstalo z doby tažení "uprchlíků" balkánskou cestou, nikdo jim je neposlal z Německa nebo vodkuď. Tak nepřevracej - jako vždy - skutečnost.', 34);
INSERT INTO comments VALUES (7937, 'Jiří Klofera', 'tady nejde o žádnou solidaritu. Jde o pud sebezáchovy. A to je určující.', 34);
INSERT INTO comments VALUES (7938, 'Richard Tomeček', 'Ona tvrdí, že pobíráme peníze, já ne.', 34);
INSERT INTO comments VALUES (7939, 'Kateřina Slavíková', 'Nějak podobně si představuje vládu Babiš. Aby mu do toho nikdo nekecal a mohl makat', 34);
INSERT INTO comments VALUES (7940, 'Pavel Chamrád', 'Bez mandátu OSN (Rusko neschválí nic co pomůže EU) obsadit některé severní části Afriky, zřídit tam uprchlické tábory, domluvit přerozdělování ohrožených žen, dětí, starých a nemocných do všech zemí EU, ale jen dočasně. Domluvit NATO a Ruskou federaci (pak možná povolí) k zásahu ve všech místech Afriky, kde vládnou teroristické skupiny, bohužel tam zavést loutkové režimy (Rusko i NATO zvlášť) a dlouhodobě stabilizovat. Každého to bude stát hodně peněz a životní úrovně, ale máte lepší varianty?', 34);
INSERT INTO comments VALUES (7941, 'Jiří Seidl', 'Zeman leží pod kyslíkovým stanem a připravuje se na inauguraci.Na boj proti migrantům v příštích pěti letech už bohužel nemá čas.', 34);
INSERT INTO comments VALUES (7942, 'Mirek Bárta', 'Počkejte dva měsíce, kolik jich tam zbude...', 34);
INSERT INTO comments VALUES (7943, 'Tomáš Bašanda', 'Já ne, ale někteří naši politici na to vypadají. A v minulé vládě... co si budeme povídat.', 34);
INSERT INTO comments VALUES (7944, 'Dan Šída', 'Jsem obyčejný důchodce. A jsem velkým příznivcem EU, protože v členství vidím velké výhody. Ale když jsme v nějaké organizaci, tak pro nás platí práva i povinnosti. Zatímco já jsem příznivec EU, tak Vy jste naopak odpůrce. Můžete si podat ruce s oběma Klausy, Tomiem Okamurou a dalšími eurofoby.', 34);
INSERT INTO comments VALUES (7945, 'Aleš Chudý', 'Jsem. Já ale o koze a vy o voze...', 34);
INSERT INTO comments VALUES (7946, 'Jarda Sokol', 'O nás bez nás. Živili jste Rakousko Uhersko potom naši Říši pak SSSR no a teď nám pomůžete s našimi hosty kteří mají k uprchlíkům tak daleko jako je na Měsíc.', 34);
INSERT INTO comments VALUES (7949, 'Tomáš Tatíček', 'Pro všechny, kteří zůstali dodnes příznivci členství: Ne, s tím, co byla "Evropa" kdysi a do čeho ČR vstupovala, to nemá společného už naprosto nic. Dnes je to Berlínský diktát, Hitler hadra. Styďte se.', 34);
INSERT INTO comments VALUES (7950, 'Mirek Bárta', 'To by něco udělala s hranicemi....', 34);
INSERT INTO comments VALUES (7951, 'Pavel Podešva', 'Jdi už někam, nádhero!', 34);
INSERT INTO comments VALUES (7952, 'Mojmir Peřina', 'Maďarsko už dávno uprchlíky přijalo! Nejste v obraze!', 34);
INSERT INTO comments VALUES (7953, 'Martina Moudrá', 's prvními čtyřmi slovy souhlasím. Ta dohromady dávají smysl.', 34);
INSERT INTO comments VALUES (7954, 'Petr Veselý', 'Další německý kancléř, který chce diktovat celé Evropě.', 34);
INSERT INTO comments VALUES (7955, 'Kateřina Slavíková', 'Zakládajícím...', 34);
INSERT INTO comments VALUES (7956, 'Karel Hauptvogel', 'Tak proč uprchlíky nezvou a nepřijímají?', 34);
INSERT INTO comments VALUES (7957, 'Mojmir Peřina', 'Nesmysl. Jak jste na to číslo příšel. Z projevu Okamury?', 34);
INSERT INTO comments VALUES (7958, 'Tomáš Bašanda', 'Ono slovo DOTACE evokuje, že něco dostáváme zdarma, ale tak to není.', 34);
INSERT INTO comments VALUES (7959, 'Aleš Chudý', 'Zatím drží Střední Evropa pospolu. Tak úplně jako první nás zradí Slovensko. Chvilku po nich Polsko. Maďarsko vydrží nejdéle, ale nakonec i oni, zhruba 6 měsíců po Slovácích zradí také. Ona to vlastně zrada nebude. Bude to nejméně špatné řešení ze všech možných špatných řešení. Samozřejmě, my, jako národ, který si myslí, že je pupek světa, kolem kterého se točí vesmír si to uvědomíme, až po našem vystoupení z EU a až na naše zboží uvalí EU dovozní clo. Vyvážíme tam "pouze" 85% veškerých produktů, takže nezaměstnanost u nás naroste na 20% během dvou let, následně ještě trochu víše. On si to drahé zboží od nás totiž nikdo nekoupí, ani to Rusko, které nám slíbí bezmeznou pomoc a podporu. A díru po našem zboží v té odporné EU zaplní nejdříve Slovensko, chvilku po nich Polsko a Maďarsko, až jako posledních . Zhruba půl roku po Slovensku. Hezký zbytek víkendu přeji děcka...ježíš, čo to táram, vždyť my, ČR, rozhodujeme o tom, co je správné...', 34);
INSERT INTO comments VALUES (7960, 'Mojmir Peřina', 'Vy už jste?', 34);
INSERT INTO comments VALUES (7961, 'Jan Cibulka', 'To ne, ale mohli by jít ke svým, stejná kultura, náboženství, bezpečí, blahobyt. No jo, ale tam už není co zabírat...', 34);
INSERT INTO comments VALUES (7962, 'Karel Bláha', 'Nejste ale doma v Čechách.', 34);
INSERT INTO comments VALUES (7963, 'Vojtěch Toráč', 'Pojdme od nich pryc. Nedaji si pokoj, dokud jich sem nenakybluji 100.000.', 34);
INSERT INTO comments VALUES (7964, 'Mirek Bárta', 'Proč tlačí? Vždyť to tak je už dávno!', 34);
INSERT INTO comments VALUES (7965, 'Richard Tomeček', 'Pak už nezbude než CZexit. Na direktivy z ciziny máme alergii.', 34);
INSERT INTO comments VALUES (7966, 'Radovan Sýkora', 'Projevit solidaritu neznamená přebírat migranty. Země V4 preferují ochranu hranic a budou pomáhat materiálně i odborně v tomto směru. Na tahu je Německo. Pokud stále chtějí přijímat a řeší jestli 100.000 nebo 200.000 ročně, musejí si poradit sami.', 34);
INSERT INTO comments VALUES (7967, 'Mojmir Peřina', 'Kancléřka Merkel pochopitelně nemyslí solidaritu s Německem, které žádnou nepotřebuje. Solidarita je myšlena se státy, které nejvíce trpí pod utečenci, jako například Itálie, nebo Řecko.', 34);
INSERT INTO comments VALUES (7968, 'Radim Simunek', 'ohledně zahraniční politiky souhlasím, i když třeba mám jiný názor, ale nemá smysl, aby každá země hrála sama za sebe... ohledně té migrace je zapotřebí, aby česká republika jasně deklarovala nějaké stanovisko.. tj. migranti jsou lákáni některými státy na sociální benefity, ty lidi nejsou do evropy, ty lidi jdou do německa.. pokud se ustanoví funkční ochrana hranic, tak aby se nestalo, že ročně přijdou statisíce lidí, ale třeba proklouzne pár tisíc... pak může být řeč o nějakém solidárním přerozdělení toho mála, co proklouzne...', 34);
INSERT INTO comments VALUES (7969, 'Luděk Tondr', 'Merkelová s Junckerem budou tak dlouho otravovat, tak dlouho tlačit na pilu, až opravdu donutí Čechy vystoupit z EU.', 34);
INSERT INTO comments VALUES (7970, 'Honza Karásek', 'Má správnou myšlenku - o ty Syřany, Afghánce atd. by se přednostně měli postarat v Saudské Arábii, SAE atd. Sví ke svým.', 34);
INSERT INTO comments VALUES (7971, 'Alois Slámečka', 'Korespondenční hlasy?', 34);
INSERT INTO comments VALUES (7972, 'Josef Ulman', 'Jo, tyhle dva lidi kdyby Evropa neměla, tak by bylo mnohem líp.', 34);
INSERT INTO comments VALUES (7973, 'Tomáš Bašanda', 'To je fuk, prostě nám vyhrožují a přitom nás potřebují. Jen z nich nesmíme být podělaní.', 34);
INSERT INTO comments VALUES (7974, 'Petr Veselý', 'A to bude kdy?', 34);
INSERT INTO comments VALUES (7975, 'Alois Slámečka', 'Možná jen člověk, kterého už budoucnost až tak netrápí...', 34);
INSERT INTO comments VALUES (7976, 'Jan Cibulka', '...věřící?', 34);
INSERT INTO comments VALUES (7977, 'Mojmir Peřina', 'Máte trochu naivní představy o arabském světě. Z těch několika bohatých žádní uprchlíci nepřicházejí.', 34);
INSERT INTO comments VALUES (7978, 'Dan Šída', 'Za co by měla být podle Vás souzena? Nedopustila se žádného trestného činu.', 34);
INSERT INTO comments VALUES (7979, 'Mojmir Peřina', 'Až si na ně vyděláme!', 34);
INSERT INTO comments VALUES (7980, 'Kateřina Slavíková', 'Tlačí to na to, aby o důležitých otázkách rozhodovalo Německo a Francie.', 34);
INSERT INTO comments VALUES (7981, 'Mirek Bárta', 'Také nejsem příznivec, ale pokud se to nezmění, nebo třeba zhorší, nebudu proti!', 34);
INSERT INTO comments VALUES (10078, 'Václav Jonáš', 'V nedeli je ale 18.', 49);
INSERT INTO comments VALUES (7983, 'Ruda Hlousek', 'Arabský svět se honosí luxusem v podání mrakodrapů až do nebes, umělé ostrovy.. zlaté toalety ... ale postarat se o své soukmenovce či jim nabídnout azyl a jim blízké prostředí to se jim nechce !!', 34);
INSERT INTO comments VALUES (7984, 'Mojmir Peřina', 'I to Německo, Francie a ostatní, nás, menší ......', 34);
INSERT INTO comments VALUES (7985, 'Vlastimil Smoleja', 'Tak se chystejte na odchod do Horních Uher. Tam máte své jisté. Buta Tóth.', 34);
INSERT INTO comments VALUES (7986, 'Tomáš Tatíček', 'Zaprodanec? Ředitel neziskovky? Pobírač dotací?', 34);
INSERT INTO comments VALUES (7987, 'Richard Tomeček', 'On někdo v přístupových dohodách podmínil pobírání dotací příjmem uprchlíků? Nějak se už v tom ztrácím.....', 34);
INSERT INTO comments VALUES (7988, 'Martin Kodat', 'Podepsat ho musel, protože ÚS rozhodl, že není v rozporu s ústavou,', 34);
INSERT INTO comments VALUES (7989, 'Rudolf Strnad', 'Leda tak homo pride, tomu by věnovali pozornost', 34);
INSERT INTO comments VALUES (7990, 'Tomáš Bašanda', 'Podmiňovat dotace čímkoli jiným, např. solidaritou s přijímáním migrantů, je v naprostém rozporu se systémem fungování dotací jako takovým. My, ani ostatní země nedostáváme evropské peníze zdarma a za nic. Je to celý balík opatření, která jsme výměnou za dotace museli přijmout, např. kvóty na zemědělskou a potravinářskou produkci, legislativní opatření, importní a exportní politiku atd. Teď nám vyhrožují, ale je potřeba si uvědomit, že ta ekonomická závislost není jednostranná, jak se nám podsouvá. I to Německo, Francie a ostatní, nás, menší a okrajové státy EU potřebují. Z naší práce jim prostřednictvím jejich firem plynou miliardové zisky, my jsme ta levná pracovní síla, které oni využívají - ne nadarmo je 80% našeho exportu právě do zemí EU (hlavně do Německa).', 34);
INSERT INTO comments VALUES (7991, 'Dan Šída', 'V žádném případě!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!', 34);
INSERT INTO comments VALUES (7992, 'Petr Veselý', 'Ta je jak flašinet. Furt dokola. A co německé důchody? Budou? Kdy?', 34);
INSERT INTO comments VALUES (7993, 'Mirek Bárta', 'Třeba budou v Německu předčasné volby a Němci se vzpamatují...', 34);
INSERT INTO comments VALUES (7994, 'Honza Karásek', 'Přesně tak, po přijetí případných dalších států EU bude Česko o to méně brát, resp. po zvýšení své úrovně o to více platit. Navíc, do rozpočtu odvádí peníze tak jako tak, zatímco příjem peněz je vázán na "správné" dotační programy, "správné" požádání o ně atd.', 34);
INSERT INTO comments VALUES (7995, 'Tomáš Filipčík', 'Solidární jsme až až. Někdo přijímá ukrajince někdo afgance...', 34);
INSERT INTO comments VALUES (7996, 'Jan Cibulka', '...prachy!?', 34);
INSERT INTO comments VALUES (7997, 'Tomáš Tatíček', 'Czexit, czexit, czexit! Včera bylo pozdě!!', 34);
INSERT INTO comments VALUES (7998, 'Jan Cibulka', '...i naše veřejnoprávní média káží solidaritu s migranty, vědci podepisují manifesty za solidaritu, mladí katolíci bonzují na Duku, že je málo solidární, nadvládní organizace tu utrácí naše peníze za inkluzivní programy pro imigranty... ...a my to pořád ne a ne pochopit a ještě k tomu si naivně myslíme, že to třeba změníme ve volbách. Ha-ha-ha.', 34);
INSERT INTO comments VALUES (7999, 'Mirek Bárta', 'A nebude ji stačit, když se chytneme za ruce a uděláme průvod?', 34);
INSERT INTO comments VALUES (8000, 'Zbyšek Hlinka', 'Ještě, že jsem si dal k obědu 3 piva. Hned se mi ten pokažený den snáší o trošku lépe.', 34);
INSERT INTO comments VALUES (8001, 'Jan Prichystal', 'Je jen otazkou casu, kdy obcanum vysavanych zemi dojde trpelivost. Deindustrializace jizni Evropy se jiz neda zakryvat. Euro je vyhodne pro Nemecko a mozna pro Francii, ale nevyhodne pro vsechny ostatni.', 34);
INSERT INTO comments VALUES (8002, 'Honza Karásek', 'Angela mj. řekla, že toto opatření si představuje "při ochraně vnějších hranic". Takže vlastně se sem žádní ilegálové nedostanou? A jak při otevřených hranicích dosáhne toho, aby "utíkající před válkou" zůstali v té zemi, kde budou přerozděleni? Jestli jednotně vysokými dávkami, kdo to zaplatí? Jestli to zaplatí jednotlivé státy, pak přijdou o své peníze tak jako tak (přišly by o ně i v případě nesouhlasu s utečenci - tím, že by jim pak byly stopnuty eurodotace)...', 34);
INSERT INTO comments VALUES (8003, 'Vlastimil Smoleja', 'Ona husopaska když už neví jak díl, zkouší vydírání. Všechny nové státy, když lezly do tohoto spolku podepsaly přístupové dokumenty. S tím, že nějaký čas budou brát dotace, časem se změní na plátce. A mávnutím koštěte to Die Hexe zneplatní? To ať ná rovnou propuštěcí papíry a sbohem. Nebo celá EU je na tom, jak Makrelu chytne fantas?', 34);
INSERT INTO comments VALUES (8004, 'Mirek Bárta', 'Pardon, říkala...', 34);
INSERT INTO comments VALUES (8005, 'Pavel Svitil', 'NE !!', 34);
INSERT INTO comments VALUES (8006, 'Zbyšek Hlinka', 'Jenže tímto způsobem nezničíte Evropu a nerozpoutáte tak snadno občanskou válku. Tedy je jasné, že je třeba tlačit na přerozdělování. Němci už moc dlouho neválčili...', 34);
INSERT INTO comments VALUES (8007, 'Jiří Seidl', 'Zajaci, běž.', 34);
INSERT INTO comments VALUES (8008, 'Pavel Klíma', 'O co jí jde? Zamořit evropu? Doufám, že bude jednou souzena!', 34);
INSERT INTO comments VALUES (8009, 'Mirek Bárta', 'Přesně! Coby říkal, kdyby takhle mluvil Babiš?', 34);
INSERT INTO comments VALUES (8010, 'Jaroslav Novák', 'přesně tak, k rozšiřování unie by nedocházelo, kdyby nebylo jasné, že stávajícím členům se to vyplatí', 34);
INSERT INTO comments VALUES (8011, 'Zdeněk Šmerda', 'Ani vtipné to není. Natož racionální uvažování.', 34);
INSERT INTO comments VALUES (8012, 'Jiří Seidl', 'Kohezní fondy a migrační kvóty spolu nijak nesouvisí.', 34);
INSERT INTO comments VALUES (8013, 'Jan Prichystal', 'Protektorat uz nejakou dobu ma - Recko.', 34);
INSERT INTO comments VALUES (8014, 'Kamil Krbálek', 'Kde je Zeman? Je třeba aby zaletěl do Berlína a tenhle nesmysl Merklové rozmluvil. Přerozdělování migrantů není řešení, jediné řešení je vracet je domů a pomoci jim zlepšit situaci tam.', 34);
INSERT INTO comments VALUES (8015, 'Mirek Bárta', 'JJ Asi si ten vyhazov opravdu zasloužíme. Jestli budou takhle pokračovat, tak bohudík!', 34);
INSERT INTO comments VALUES (8016, 'Jan Prichystal', 'Ti nabizeji kulturni obohaceni.', 34);
INSERT INTO comments VALUES (8017, 'Julius Zajac', 'Já jsem doma v EU.', 34);
INSERT INTO comments VALUES (8018, 'Zdeněk Šmerda', 'Ať ti nevypadnou oči z důlků demagogu.', 34);
INSERT INTO comments VALUES (8019, 'Stanislav Novotný', 'Už jim otrnulo před volbami byl klid a už zase vystrkují růžky. Ani se nedivím, že VB odchází přece si taková velmoc nenechá poroučet koho přijmout a kolik přijmout. Už je na čase, aby se EU vzpamatovala jinak je potřeba toto diktátorské společenství opustit.', 34);
INSERT INTO comments VALUES (8020, 'Jaroslav Novák', 'to máte asi jako s Babišem, taky ho všichni chcou', 34);
INSERT INTO comments VALUES (8021, 'Julius Zajac', 'Honci, dej pozor na migranty.', 34);
INSERT INTO comments VALUES (8022, 'Tom Dvorak', 'Tak uz vyhlasila valku a protektorat?', 34);
INSERT INTO comments VALUES (8023, 'Miloš Urban', 'A kdo jí dal právo hrát si na vůdce evropy?', 34);
INSERT INTO comments VALUES (8024, 'Dita Mášová', '', 34);
INSERT INTO comments VALUES (8025, 'Pavel Chamrád', 'Pane Vašek, pravdu nezměníte a nevděk se zatím vždy každému vrátil. Máte snad jiné zkušenosti?', 34);
INSERT INTO comments VALUES (8026, 'Jan Prichystal', 'K dotacim nepotrebujete EU. I v USA maji dotace. Klidne si muzete vytvorit svuj vlastni system dotaci, abyste meli podporu tech, kteri z dotaci ziji. I v EU se to resi na urovni polytbyra, vdzyt tam donedavna meli i jednoho clena polytbyra sovetskeho. Dnes je v polytbyru "jen" sest komunistu, z nichz ctyri jsou mistopredsedove polytbyra. I Cesko melo donedavna jako sveho zastupce v polytbyru komunistu Telicku. V soucasnem polytbyru EU maji cleny-komunisty Italie, Francie, Slovensko, Estonsko, Bulharsko a Chorvatsko. Co se tyce tech tanku, tak proc si myslite, ze chce Juncker vytvorit ozbrojene slozky EU? K obrane hranic to nebude, k te by stacilo zrusit ten zakaz. Vsechny hranicni zeme maji natolik silne armady a namornictva, aby hranice dokazaly ochranit samy. Jenze nesmi.', 34);
INSERT INTO comments VALUES (8027, 'Tomáš Kuchař', '"Věříme, že solidarita nemůže být jednosměrná.." Vtomhle s Merkelovou souhlasím. Nejvyšší čas to posílat zpátky.', 34);
INSERT INTO comments VALUES (8028, 'Jirka Honců', 'Víš, Zajíci, ono je to složitější. Buď předáme tuto zemi svým dětem, nebo migrantům s totalitním smýšlením.', 34);
INSERT INTO comments VALUES (8029, 'Vlaďka Černajová', 'Umí Němec něco jiného než vyhrožovat? Takový hezký den to byl, než jsem koukla na ty dva na fotce.', 34);
INSERT INTO comments VALUES (8030, 'Zdeněk Vodák', 'A proč nám to tu Vy jako cizinec píšete. Proč nepíšete v diskuích u vás doma.', 34);
INSERT INTO comments VALUES (8031, 'Richard Tomeček', 'Tak podle jiných novin to řekla poněkud jinak "Chcete peníze, musíte být solidární s uprchlíky". To už zní jako rozkaz, vydírání.', 34);
INSERT INTO comments VALUES (8032, 'Aurel Babic', 'Ta německá ďáblice mě děsí', 34);
INSERT INTO comments VALUES (8033, 'Jiří Schmidt', 'Takové písně budou brzy opět v trezoru', 34);
INSERT INTO comments VALUES (8034, 'Jaromír Masák', 'přestaň fňukat, zvládnete to', 34);
INSERT INTO comments VALUES (8035, 'Michael Holy', 'Jen bacha aby cenzor slova Pana Kryla necenzuroval....', 34);
INSERT INTO comments VALUES (8036, 'Pavel Baroš', 'Bylo jasné od začátku, že to co Angela dělá není řešení problému, ale vytváření problému nového v EU. Furt jen "My to zvládneme" bla bla bla. Kdyby poslouchala taky lidi, možná by ten problém nenastal. Co sem viděl, tak v Německu se teď demonstruje proti migrantům o sto šest. Ta krá.a chybu nikdy nepřizná. Od ostatních ale očekává, že to lejno co si vytvořila teď utře do ostatních. To je fakt drzost katastrofolních rozměrů. Tak to ani náhodou. Tady každej věděl co dělá za blbost. Každý stát má právo si diktovat koho vpustí na uzemí a koho ne.', 34);
INSERT INTO comments VALUES (8037, 'Jiří Provazník', 'Německo,Francie a spol. porušují pravidla, zvou jednostranně na území EU ilegální ekonomické migranty a ještě mají tu drzost žádat solidaritu.Merkel a spol. jsou zločinci konající v rozporu se zájmy unieTakovou unii si strčíte do zadnice.', 34);
INSERT INTO comments VALUES (8038, 'Zdeněk Vodák', '"a varovala, že státy očekávající finanční podporu unie musejí v řešení migračního problému projevit solidaritu" Chce-li EU přežít, nechce-li se rozpadnout tak je nutné "odstranit" z vedení německé Říše A. Merkel', 34);
INSERT INTO comments VALUES (8039, 'Tomáš Hertig', 'Na projektu EU a otevřeném trhu nejvíce vydělalo právě Německo, které se dlouhodobě topí v obrovských přebytcích. Bláboly o čistém plátcovství a jednostranné solidaritě jsou absurdní a hloupé.', 34);
INSERT INTO comments VALUES (8040, 'Radek Zlámal', 'Němci jsou s imigrační politikou nespokení. Stačí si pročíst reakce na německých webech. Nějak mi tedy nejde na rozum, že mohla tato strana získat takový počet hlasů.', 34);
INSERT INTO comments VALUES (8041, 'Zdenek Šitina', 'Německo si přece zvolilo znovu Angelu. Žádné změny v EU se neočekávají. "Uprchlíky" by si měli rozdělit zejména bývalé koloniální mocnosti - Francie, Británie, Španělsko ....... Z kolotoče EU se nedá již za současných politických a ekonomických podmínek vystoupit. Stali jsme se vazaly mocností. Nám už zde v Česku nic nepatří.', 34);
INSERT INTO comments VALUES (8042, 'Jiří Schmidt', 'Bohužel takhle zpracovaných lidí, jako vy je u nás již hodně, je to výsledek dlouhodobé propagandy a strašení, že bez EU tu bude Putinovo.', 34);
INSERT INTO comments VALUES (8103, 'Aleš Kralovec', 'Musejí??? Já měl za to, že evropská demokracie znamená, že si každý může dělat co chce....', 34);
INSERT INTO comments VALUES (8043, 'Zdeněk Nutz', 'Kolik vám platějí za tenhle nápad? Táhněte raději s děvkama chrápat! Co mi to říkáte? Že šel bych zas? Rád? Odpověď čekáte? Nasrat, jo, nasrat!', 34);
INSERT INTO comments VALUES (8044, 'Julius Zajac', 'Vy si vyhazov z EU zasloužíte. Až v obchodech budete mít prázdný regály a pro prací prášek si budete jezdit zase do Německa, tak se vám bude celej svět smát. On se vám vlastně svět směje už teď. Zvolit si stbáka za premiéra, to jste se vyznamenali. A Okamura? Ten vám vypěstuje rýži až budete mít hlad.', 34);
INSERT INTO comments VALUES (8045, 'Martin Škořepa', 'Kde byla německá solidarita s ostatními zeměmi EU ve chvíli, kdy se Německo jednostranně rozhodlo migranty přijímat bez omezení? Jestli někoho, tak by ostatní země mohly v rámci solidarity přijímat občany Německa, kterým se díky Máme Merkelové tak moc zkomplikoval život. A ještě poznámka: vypadá to, že v ČR, která je součást EU ty migranty přeci jen máme.', 34);
INSERT INTO comments VALUES (8046, 'Martina Moudrá', 'Samozřejmě, kdyby byli v krizi. Podstatné je, že schopný politik by měl umět předvídat, dokázat odhadnout, jaké budou důsledky jeho rozhodnutí a zda na jejich sanaci nakonec bude mít nebo ne (a tomu své rozhodnutí podřídit)', 34);
INSERT INTO comments VALUES (8047, 'Jiří Fibiger', 'A pokud tedy musí být solidarita obousměrná, co nabídnou ty davy migrantů, za to že je Evropa přijme? Nebo jak se odvděčí země odkud utíkají, až tam jednou nastane mír?', 34);
INSERT INTO comments VALUES (8048, 'Marian Kral', 'CZEXIT', 34);
INSERT INTO comments VALUES (8049, 'Zdeněk Nutz', 'Ať to čtu, jak to čtu, tak je z toho jasně vidět, jak si v té Evropské unii jsme všichni rovní! A bez vyhrožování by to nešlo, paní Adolfová?', 34);
INSERT INTO comments VALUES (8050, 'Petr Novotný', 'Muj prostrednik je erektovan.', 34);
INSERT INTO comments VALUES (8051, 'Jan Novák', 'táhni ty krávo jedna', 34);
INSERT INTO comments VALUES (8052, 'Libor Ulrich', 'Zamenovat privatni penize ve forme dividend a verejne penize ve forme dotaci, to uz chce poradneho fistrona :)', 34);
INSERT INTO comments VALUES (8053, 'Ivan Vašek', 'Pane Chamráde,sebekritika je dobrá věc,ale nesmí to být blud.A tady je to i sebemrskačství.', 34);
INSERT INTO comments VALUES (8054, 'Tomáš Macejka', 'Aber die Deutsche haben andere Meinung.', 34);
INSERT INTO comments VALUES (8055, 'Martin Buneš', 'Copak se asi musi stat, aby tu hnusnou babu uz odstavili.', 34);
INSERT INTO comments VALUES (8056, 'Jana Liebová', 'Zazpívejte jim někdo poslední slova Krylovy "Písně neznámého vojína" v němčině.', 34);
INSERT INTO comments VALUES (8057, 'Michael Holy', 'A kdo je byl je a bude solidarni s kolonii CR??? Kdyz se ma soused hospodarsky lepe a nepomuze ze silidarnosti druhemu? Proc bychom meli byt silidarni my, kdyz uz tak akt zniceni Evropy nazyvame? Nevezmem ani jednoho.', 34);
INSERT INTO comments VALUES (8058, 'Matěj Škalda', 'Kolikrát tý bábě někdo musí říct NE aby to pochopila ?', 34);
INSERT INTO comments VALUES (8059, 'Jiří Schmidt', 'Demokracie pro německo-bruselsku, my vám řekneme co a jak máte dělat a zrychlíme to tím, že už o tom nebudeme diskutovat a posléze už ani hlasovat.', 34);
INSERT INTO comments VALUES (8060, 'Mirek Bárta', 'Ještě že tam není!', 34);
INSERT INTO comments VALUES (8061, 'Karel Janyška', 'Demarchie. Znovu a znovu se ukazuje jakým omylem je používaný model tzv. liberální většinové demokracie. Přivádí k moci i zločince a lidi všelijak nehodné. Nemohu v této souvislosti neuvést to, že Babiš měl v roce 2013 majetek 40 miliard a dnes, po 4 letém ministrování financí, má 88 miliard. Čert prostě kálí, až mi z té slušnosti je zle, na větší hromadu, jak se říká, když se mu dokonce pomůže, tak ještě víc.. Proto mistr Hayek navrhuje demarchii, která zařídí institucionálně a institucionální vládu jediného práva pravého, práva evolučního, soukromého, které řídí lidský svět ve skutečnosti a které máme všichni v myslích odmalička, kdy jsme ho nasáli od rodičů a okolí. Má výhradně zákazový charakter. Není pravda, že má platit - lid si to přeje, ať to má. Lid je totiž obří skupina v této věci nevzdělanců, která neví o soustavě používané demokracie nic, vůbec nic. Zato je plně řízen a to 24 hodin denně, právě právem soukromým. Demarchie je popsána plně v Hayekově světovém majstrštyku Právo, zákonodárství a svoboda a spočívá v tom, že Senát je navolený coby zástupce ročníků, ne politických stran. Ty by měly do Senátu zákaz vstupu stejně jako dnes ho mají jednotlivci do sněmovny. Dosáhlo by se tak potřebného mentálního obsazení, coby vyhledavače tzv. práva právníků v mnohdy až zločinných blafech, které mu pošle sněmovna. Senát by měl samozřejmě vyšší kompetence, než ona a systému by prakticky vládl. Ve spořádaném lidském světě totiž platí: Zatímco se nám zdá, že jsme ve svých životech vedeni svými zájmy, není tomu tak. Plně nás ovládá, pokud nejsme krimonogenní nebo mentálně postižení, mínění o tom co je správné. A tímto míněním důsledky z plnění našich zájmů nejprve testujeme a pak se teprve rozhodneme o jejich realizaci. A správa věcí veřejných, které dnes říkáme demokracie, musí této vazbě odpovída a být její analogií. Samozřejmě, tato 400 stránková publikace věc popisuje do takové hloubky a souvislostí, že je velmi složité se v ní vyznat.', 34);
INSERT INTO comments VALUES (8062, 'Julius Zajac', 'To si myslíte? Nebo viníte Němce z českých nízkých mezd?', 34);
INSERT INTO comments VALUES (8063, 'Jan Prichystal', 'Prvnim krokem je zruseni nebo aspon omezeni potravinove pomoci. Potravinova pomoc je pro jejich zemedelstvi katastrofou. Jak se ma africky zemedelec uzivit, kdyz potraviny tam cpou EU a USA zadarmo? S takovou cenou se neda dost dobre konkurovat, zvlast kdyz tam nemaji zadny system dotaci.', 34);
INSERT INTO comments VALUES (8064, 'Milan Vik', 'Já bych jim hodil (jen jednou) pod nohy něco jiného', 34);
INSERT INTO comments VALUES (8065, 'Jan Blana', '......... státy očekávající finanční podporu unie musejí v řešení migračního problému projevit solidaritu, ......... takovou podporu si strčte za klobouk, nenachystáme tu potomkům no-go zóny. ČESKOSLOVENSKO nebohatlo z kolonií jako západní státy ani nelikvidovalo funkční sekulární státy Libyi, Irák a Sýrii, takže nemá proč být solidární. V tom případě adié EU, islámskou ČR nechci.', 34);
INSERT INTO comments VALUES (8066, 'Petr Macháček', 'Způsob německé solidarity zdá se mi býti poněkud svérázným. Když Maďaři bránili hranice, nazývali Němci Orbána - patrně v rámci solidarity - xenofobem a náckem. A teď zas po Maďarsku chtějí, aby si od nich zase solidárně vzali ty dárečky, před kterými se tehdy Maďaři bránili, a za co kasírovali jednu kritiku z Německa za druhou. Tak z toho jsem trochu jelen.', 34);
INSERT INTO comments VALUES (8067, 'Vladislav Buryšek', 'volně přeloženo si obecně malé země na světě moc vyskakovat nemůžou, to ještě buďme rádi, že za námi stojí EU, nebýt toho, tak se s náma nevbaví už nikdo, jak by řekl Rus, tak by bylo', 34);
INSERT INTO comments VALUES (8068, 'Jaromír Hlubek', 'Dotace jsou největší zlo a ještě budeme litovat, že jsme je přijali. Psal jsem to již za Nečasovy vlády, kterou taky vydírali odejmutím dotací, důvod si už nepamatuji. Prostě se dotací zříct. Nečas však k tomu neměl koule, nemusili bychom dnes řešit ani Čapí hnízdo. Budou do nás bušit, brali jste a když máte něco dát, tak nechcete. Za sovětského svazu se to řešilo na úrovních politbyr komunistických stran a když to nešlo, tak tanky. Vzhledem k tomu, že EU se od sovětského svazu příliš neliší, můžeme se dočkat ještě mnoha nepříjemností, asi to nebudou tanky, ty EU nemá, ale bude to ekonomika a nemusí jít ani o exit.', 34);
INSERT INTO comments VALUES (8069, 'Jaroslav Port', 'Jak to říkal Drahoš Kvoty už neplatí ......', 34);
INSERT INTO comments VALUES (8070, 'Libor Ulrich', 'To by me zajimalo, proc Rokyta minusuje odkazy na zdroje. Nelibi se mu fakta, ktera mu nejdou dohromady s jeho svetonazorem?', 34);
INSERT INTO comments VALUES (8071, 'Mirek Bárta', 'Kdyby byli Němci v krizi a ne kvůli tomuhle, ta proč ne!', 34);
INSERT INTO comments VALUES (8074, 'Zdeněk Nutz', 'Já nevím, ale já bych ty rozhledny v údolí, úzké cyklostezky odnikud nikam, zvedací mosty co se nezvedají a kruhové objezdy s odbočkou do kravína za ty migranty neměnil!!! A peníze, které bychom do té černé díry neposlali, bychom mohli třeba začít používat s rozumem a bez těch hald papírů a nenažraných korupčníků.', 34);
INSERT INTO comments VALUES (8075, 'Honza Karásek', 'Připomenu slova ekonoma V. Pikory: v součtu je to šul-nul. Nepřijmeme ty mírumilovné utečence + stopnou nám dotace? Jo, budeme na tom finančně hůř. Přijmeme-li však ty mírumilovné utíkající před válkou (a "díky" tomu dotace budou dál putovat i k nám), taky na tom budeme finančně (a nejen finančně) hůř (vyšší nárok na sociální systémy).', 34);
INSERT INTO comments VALUES (8076, 'Pavel Chamrád', 'Je lepší mít v čele státu hulváta? To myslíte vážně?. Můj kandidát byl diplomat Fischer. A podle mého názoru je loutka Zeman.', 34);
INSERT INTO comments VALUES (8077, 'Karel Janyška', 'Solidarita ve zločinu... Dzp.', 34);
INSERT INTO comments VALUES (8078, 'Alois Slámečka', 'Merkel muss weg', 34);
INSERT INTO comments VALUES (8079, 'Karel Janyška', 'Na tom prd zíleží. To jsou drobné a korupční prachy a ukradené oklikou slabším státům... Dzp.', 34);
INSERT INTO comments VALUES (8080, 'Artur Novak', 'No tak asi to věděl, že nepodepsat když všichni řvali, že je to goood nedává smysl.', 34);
INSERT INTO comments VALUES (8081, 'Kamil Krbálek', '8% našeho HDP jde od nás evropským firmám jako zisk, který si doma daní. To, že se nám 1% vrátí jako dotace z EU naší solidaritu s Evropou nezmenšuje.', 34);
INSERT INTO comments VALUES (8082, 'Honza Karásek', 'V situaci, kdy zůstal již úplně posledním. Kdy byla mediální masírka typu "profesor práv na ČT říká, že Klaus to musí podepsat, jinak by měl rezignovat" atd.', 34);
INSERT INTO comments VALUES (8083, 'Tomáš Macejka', 'Když Němci chtějí vyšší platy, tak se organizují a stávkují, škoda, že totéž nedokáží i v případě tohoto, kdyby probíhaly opravdu masové demonstrace a stávky, tak by se snad začalo řešit i odsun ilegálů a ne jejich neustávající příchod a slučování rodin, které oni nemají vůbec malé. Já hlupák jsem zapomněl, nedávno měli velkou možnost se vyjádřit ve volbách...', 34);
INSERT INTO comments VALUES (8084, 'Martina Moudrá', 'Tak dlouho byla solidární, až nakonec sama žádá o solidaritu. Jak typické....', 34);
INSERT INTO comments VALUES (8085, 'Jiří Schmidt', 'Volně přeloženo nám menším tím chtějí říct "držte huby a krok".', 34);
INSERT INTO comments VALUES (8086, 'Karel Janyška', 'Já ho chci... Dzp.', 34);
INSERT INTO comments VALUES (8087, 'Jirka Honců', '', 34);
INSERT INTO comments VALUES (8088, 'Honza Karásek', 'Německo není jediným tzv. čistým plátcem do euro-rozpočtu. Logicky, někteří jsou převažujícími plátci a někteří převažujícími příjemci. Tzn. argumentační klam od Fr. Merkel. Někteří převažující plátci z této migrační vlny dosud nepřijali skoro žádné "utíkající před válkou".', 34);
INSERT INTO comments VALUES (8089, 'Karel Janyška', 'Bohužel, ho podepsal. Mohl být skutečně slavný. I když by ho místo něho podeposal jiný... Dzp.', 34);
INSERT INTO comments VALUES (8090, 'Miroslav Vlasák', 'Jestli chtějí náš Czexit, ať to řeknou rovnou.', 34);
INSERT INTO comments VALUES (8091, 'Ivan Vašek', 'Já žádám spíše solidaritu od Německa,aby přestalo zase Evropu dusit.', 34);
INSERT INTO comments VALUES (8092, 'Karel Janyška', 'Uprchlíky před hrůzami přijímat a pomoci jim je lidské Připrchlíky však ne. Od toho jsou hranice států... Dzp.', 34);
INSERT INTO comments VALUES (8093, 'Jirka Honců', 'A Klause jste chtěli lynčovat, když nechtěl Lisabon.', 34);
INSERT INTO comments VALUES (8094, 'Petr Tinka', 'Asi nebude problém poslat imigranty vydělávat 300,- za den v Babišových zpracovárnách kuřat. A snad jim ani nebude vadit 7st. pracovní teploty s jednou přestávkou na vyčůrání. Že je to 20% toho, co mají v Německu na dávkách, bez práce, nebude určitě uprchlíka před smrtí vůbec zajímat.', 34);
INSERT INTO comments VALUES (8095, 'Jana Liebová', 'Už jí někdo na plná ústa řekněte, kam si ty své dotace a migranty může strčit. Třeba do toho svého Renaulta.', 34);
INSERT INTO comments VALUES (8096, 'Karel Novák', 'Solidaritu? Německo snad postihla nenadálá přírodní nebo podobná katastrofa? Ne, takže žádají o solidaritu s hloupostí a nezodpovědností. Lituji, ne.', 34);
INSERT INTO comments VALUES (8097, 'Marcel Vlna', 'No moment... Veď doteraz som si podľa vašich kecov myslel, že migranti sú dar! A kto bude robiť na dôchodky, keď vám niektorých nigérijských, sudánskych alebo marockých stachanovcov zoberieme?', 34);
INSERT INTO comments VALUES (8098, 'Mirek Bárta', 'A to je ještě dobře, že soc.demáci tam dostali tak na pe... a je tam tahle!', 34);
INSERT INTO comments VALUES (8099, 'Radek Kvasnička', 'Hitler nejdřív taky "jenom" vyhrožoval!!', 34);
INSERT INTO comments VALUES (8100, 'Petr Jaroš', 'Projevit solidaritu. Zajimave, jak pekne se da rikat uplaceni. U nas take nekteri urednici projevuji solidaritu s nasimi podnikateli.', 34);
INSERT INTO comments VALUES (8101, 'Zdeněk Nutz', 'Přesně!! Jenže kdyby to někdo opravdu zveřejnil, jaké kraviny se z projektů vlastně budují, tak je druhá revoluce. A kolik peněz vlastně posíláme směrem tam??? Cyklostezky, které jsou delší než silnice a nebo tak úzké, že se po nich kolaři bojí. A tak stejně jezdí po silnici. A to je jen příklad. Kdy tu někdo řekne že ten král je vlastně úplně nahý???', 34);
INSERT INTO comments VALUES (8102, 'Stanislav Krejcar', 'Ale to není v kapitalismu věcí státu a režimu jako podnikatelů a fabrikantů. Pokud se vám nezdají platy v českých zahraničních montovnách přiměřené, máte možnost jít pracovat do mateřských montoven v Německu, Francii, Japonsku nebo Koreji. Pokud jste tedy schopným řemeslníkem , a nemusíte se za svoji vykonanou práci stydět.', 34);
INSERT INTO comments VALUES (8105, 'Vaclav Grepl', 'pane Chamrád, a vy jste nikdy nelhal? Pan Zeman na rozdíl od vás svým národem neopovrhuje. Už je načase, abyste se vzpamatoval, že v demokratických volbách nezvítězil váš kandidát. Je lepší mít v čele státu hulváta, než loutku, která na všechno z Bruselu kývá ano.', 34);
INSERT INTO comments VALUES (8106, 'Marek Pospíšil', 'Atˇ si trhne nohou!! Němci by chtěli abychom se starali,ale platit nechtějí. Mzdy německých firem v Česku jsou na 26% těch samých firem v Německu. Tak jakou se nám snaží vnutit solidaritu. Prostě Muslimy tady nechceme a hotovo. Ať už to konečně pochopí. A pokud ne a budou na nás sankce,tak bych byl za to aby jsme opustili ten klub který se snaží řídit jiné státy a životy jiných lidí v jiných státech.', 34);
INSERT INTO comments VALUES (8107, 'Mirek Bárta', 'Že se soudružka neptala ostatních při tom když je pozvala... Hlavně že Orbánovi nasadila psí hlavu!', 34);
INSERT INTO comments VALUES (8108, 'Zdeněk Janout', 'Makrelo, opakovaný vtip přestává být vtipem... Navíc socdemáci Ti tu Tvou vládu možná sejmou dříve než vznikne. Tak si nevyskakuj. Jo a peníze ? Ty si strč za klobouk, než abychom si zaplevelili zemi vetřelci.', 34);
INSERT INTO comments VALUES (8109, 'Mark Pokorny', 'Dočasný domov pro přistěhovalce znamená co? Upřesnit. 10,20,30.......let živení stále vzrůstajicího počtu kdovíkoho.', 34);
INSERT INTO comments VALUES (8110, 'Jiří Schmidt', 'Zapomínáte na to, že cílem je zaplavení Evropy nehomogenní masou, proč a kdo to chce přesně nevím, ale mám tušení.', 34);
INSERT INTO comments VALUES (8111, 'Libor Ulrich', 'Tady mate citaci na moje data. Bairoch 1976, pp. 286, table 6; 297, table 12; 301, table 14.', 34);
INSERT INTO comments VALUES (8112, 'Jan Cibulka', 'Jako když dva vlci a jedna ovce hlasují, co bude k večeři. Kdo nám nakukal, že nám s vlky bude lépe, než v ohrádce?', 34);
INSERT INTO comments VALUES (8113, 'Karel Janyška', 'Demokracie je závadným modelem správy věcí veřejných. Proto nositel Nobelovy ceny A.F. Hayek navrhuje demarchii, které odpovídá tomu, co se v lidském světě děje skutečně. Řídí ho evoluční soukromé právo, jediné právo pravé, ne vůle lidí... Dzp.', 34);
INSERT INTO comments VALUES (8114, 'Honza Karásek', 'Jde od ní o argumentační klam. Odvádění pozornosti.', 34);
INSERT INTO comments VALUES (8115, 'Eliška Kučerová', 'Já bych klidně přijala 100 000 uprchlíků. Ale za předpokladu, že budou otevřené hranice a nikdo jim nebude bránit v návštěvě příbuzných v Germoney.', 34);
INSERT INTO comments VALUES (8116, 'Frantisek Kofron', 'Proč? Něco jsme porušili, ze máme něco vracet?', 34);
INSERT INTO comments VALUES (8117, 'Jan Čadek', 'Německo je soused k nezaplacení. Jim nestačí smrt cca 50 milionů lidí, kterou způsobili jen v období 20. století? Chtějí ještě víc? Znovu zničit Evropu? Přestaňte nás otravovat a neste si sami následky své hlouposti a podvratného chování!', 34);
INSERT INTO comments VALUES (8118, 'Honza Karásek', 'DOČASNÉ přijetí utečenců? Hahaha. Ochrana vnějších hranic? To je vidět mezi Itálií a Libyí, resp. svého času též jak byli peskováni Maďaři za to, že plní svoji povinnost. Mmch. část utečenců "utíká před válkou" právě proto a právě do Německa, že Fr. Merkel je svého času "pozvala". Ale teď chce "solidaritu".', 34);
INSERT INTO comments VALUES (8119, 'Karel Charvát', 'Solidaritu si zaslouží ty státy, které dělají něco pro ochranu hrani, třeba Maďarsko, ne ty, které vetřelce do Evropy zvou. Kdyby náhodou potřebovaly více policistů, vojáků, nebo jim docházely náboje, ČR by určitě měla být solidární.', 34);
INSERT INTO comments VALUES (8120, 'Jaroslav Novák', 'udělá cokoliv, co jí v německu může přinést nějaké politické body, věřte tomu, že hodně němců, třeba i většina, by to nakonec považovalo za její vítězství a potvrzení správnosti jejích rozhodnutí', 34);
INSERT INTO comments VALUES (8121, 'Frantisek Kofron', 'Německo je vlastně bývalá Východofranská říše.', 34);
INSERT INTO comments VALUES (8122, 'Jan Cibulka', 'Já vám říkal, neberte od EU úplatky, neberte úplatky, neberte je... a bylo to co-? Marné! Pár lidí na našem členství výrazně profituje, někdo věří, že jim je dobře jen díky EU (aniž by měli možnost paralelního srovnání), náš obchod a průmysl jsme učinili na EU závislým, no a o ztráty se jako obvykle podělíme my, občané-dělníci. Jsme holt pořád moc drazí, je třeba nás naředit... do béžova.', 34);
INSERT INTO comments VALUES (8123, 'Jiří Schmidt', 'násobenou Audinou', 34);
INSERT INTO comments VALUES (8124, 'František Ratolístka', '', 34);
INSERT INTO comments VALUES (8125, 'Karel Janyška', 'Většinoivé hlasování bez testu jediným právem pravým, tedy právem soukromým, nebo právem veta, není možné. Změní se na totalitu většin. V tomto případě franěmeckého tandemu, podpořenéhu vždy některými koupenými proti ostatním. Nesmíme si ho nechat vnutit, naopak, to, co je už tak zařízeno, je nutno okamžitě zrušit. Jinak bude zle. Odpor narůstá a dosáhne potřebné síly. A může skončit krveprolitím. EU, pokud má být, a může, musí stát na negativní, tedy zákazové, smlouvě tak, jak je zařízen přirozený lidský svět a jeho spontánní řád. Ne příkazové. To se stát nesmí !!! Dzp.', 34);
INSERT INTO comments VALUES (8126, 'Kuba Vala', 'Dejte tu obludu pryč z titulní stránky, dělá se mi z ní špatně jen ji vidím.', 34);
INSERT INTO comments VALUES (8127, 'Marcel Vlna', 'To by bolo absolútne ideálne riešenie. Vyriešili by sme 2 problémy jednou ranou. Bez fondov by korupcia dostala poriadne na frak.', 34);
INSERT INTO comments VALUES (8128, 'Jiří Schmidt', 'Takže Merklová upekla vládu ze Schulzem a tak nelze očekávat, žádné změny v politice Německa, to pojede i nadále na destruktivní vlně. Junckera taky nikdo novým kouskům nenaučí a je ostuda, že člověk, který svými postoji má na svědomí brexit a nárůst euro skeptických nálad v unii je stále předsedou EK.', 34);
INSERT INTO comments VALUES (8129, 'Petr Bačavčík', 'Zní to hezky ale máte problém se zeměpisem.', 34);
INSERT INTO comments VALUES (8130, 'Jaroslav Novák', 'dva škůdci', 34);
INSERT INTO comments VALUES (8131, 'Vítězslav Klozík', 'Ona si pozve hosty a pak je cpe sousedům, to je systém jak svina.', 34);
INSERT INTO comments VALUES (8132, 'Jirka Honců', 'Nevím, jak je to možné, ale vzpomněl jsem si a automobilku Renault.', 34);
INSERT INTO comments VALUES (8133, 'Stanislav Krejcar', 'Ale může se to přeložit i takto - Pokud jste umožnili běžencům volný pohyb po vašem státním území tak mate a musíte být solidární Pokud by jste hned v prvopočátku si SVÉ státní a národní území chránili a bránili nemuselo by k evropským problémům s běženci vubec docházet', 34);
INSERT INTO comments VALUES (8134, 'Petr Kovařík', 'Ale bohužel ona neškemrá, ona vydírá.', 34);
INSERT INTO comments VALUES (8135, 'Petr Šrámek', 'Juncker: Jednomyslnost zdržuje Demokracie je složitý proces, takže zdržuje. Demokracie je tedy podle Junckerovo logiky přežitá a jen zdržuje.', 34);
INSERT INTO comments VALUES (8136, 'Ladislav Haška', 'A oni nám zase doplatí rozdíl ve mzdach u českých a německých poboček a budeme v plusu', 34);
INSERT INTO comments VALUES (8137, 'Jan Peka', 'Dotační systémy EU začaly pracovat dávno před aktuální migrační krizí, může mi někdo vysvětlit, proč se je na ni nyní snaží navázat?', 34);
INSERT INTO comments VALUES (8138, 'Stanislav Rosecký', 'Od dob První říše Karla Velikého se Němec se svou mocenskou politikou nezměnil.', 34);
INSERT INTO comments VALUES (8139, 'Petr Kovařík', '"varovala, že státy očekávající finanční podporu unie musejí v řešení migračního problému projevit solidaritu" 1. Slova "musejí" a "solidaritu" není možné dávat do jedné věty, navzájem se popírají. 2. Není možné stavět fungování EU na principu vydírání.', 34);
INSERT INTO comments VALUES (8140, 'Libor Ulrich', 'Tvrzeni, ktere tu postujete jsou z dilny jakehosi Karla Krize, ktereho se mi po hledani internetu nedari identifikovat jako nejakeho vyznamneho ekonoma, takze nas zna mozna podobny pocet lidi. Ja jsme narozdil od nich dodal presna cisla, pan Kriz toto rozhodne neudelal a data prekrucuje. Dokazal jsem vam, ze Rakousko v te dobe BYLO bohatsi nez Ceskoslovensko, pan tvrdi opak. Tvrzeni, ze zrovna ctyri zeme, ktere nejvice upadly byly ty vyse zminene je take naprosto absurdni.', 34);
INSERT INTO comments VALUES (8141, 'Julius Zajac', 'Tak rychle šup vyskočit z EU a vrátit všechny peníze, který sem poslala.', 34);
INSERT INTO comments VALUES (8142, 'Leoš Konečný', 'Už mě ti dva fakt s....u', 34);
INSERT INTO comments VALUES (8143, 'Pavel Chamrád', 'To těžko paní Merkelová, u nás, už se neví jak se má chovat křesťan a i to křesťanství se vlastně ohýbá k vlastnímu obrazu a zájmům. Čech za posledních 27 let dohnal západ alespoň co do množství jídla, aut před domy, zaměstnanosti a bazénu, ale dělit se? Raději si zvolí do čela lháře, demagoga co má hlášky jeskynního muže. Vyjednávat neumíme, aktivně se jen plácáme do zad a věřím, že pomoct se dá i jinak, nejen přijímáním uprchlíku.', 34);
INSERT INTO comments VALUES (8144, 'Martin Hlavaty', 'poprosil bych vsechny europoslance, kteri si vazi suverenity vlastniho statu (https://cs.wikipedia.org/wiki/Svrchovanost), aby hazeli temle dvema loutkam a ostatnim jim podobnym klacky pod nohy.', 34);
INSERT INTO comments VALUES (8145, 'Richard Tomeček', 'To je pohitlerovské trauma, které z Němců hned tak nevyprchá. Ale až jednou vyprchá bude ještě hůř.', 34);
INSERT INTO comments VALUES (8146, 'Radek Holík', '...tyhle dva stačí vidět na fotce a člověk má zkažený celý den...', 34);
INSERT INTO comments VALUES (8147, 'Lukáš Novák', 'Myslím si, že když jim propůjčíme pár letadel k deportaci uprchlíků, tak se to dá považovat za solidaritu ne?', 34);
INSERT INTO comments VALUES (8148, 'Jiří Schmidt', 'Takže přeložím do češtiny, potřebujeme si dělat co chceme a nemáme zájem, aby do toho pořád všichni kecali a solidarita znamená pouze nabrat k sobě migranty, finanční pomoc, pomoc při střežení hranic a pomoc v místech problémů se zřejmě nepočítá do solidarity v pojetí Merklové nebo spíše jde o zaplavení celé Evropy nehomogenní masou a solidarita je pouhá přikrývka pro realizaci tohoto cíle. Ač jsem zastáncem společné Evropy, tak stále více chápu hlasy těch, kteří chtějí referendum o vystoupení.', 34);
INSERT INTO comments VALUES (8149, 'Vladimír Horejš', 'Nechte si peníze a my si necháme náš postoj k migrantům. OK?', 34);
INSERT INTO comments VALUES (8150, 'Jaroslav Tichy', 'Osoba, ktera je zodpovedna za soucasny stav a kterou vsichni varovali ze to nedopadne dobre nema pravo skemrat o nejakou solidaritu. Kdyby tento babochlap mel nejakou sebereflexi tak stahne ocas a vsem se omluvi, ale neco takoveho nelze ocekavat od bezdetneho psychopata.', 34);
INSERT INTO comments VALUES (8151, 'Jiří Seidl', 'Solidarita opravdu není jednosměrná,nikdo z V4 nezpochybňuje, že se časem staneme čistými plátci a budeme pomáhat vyrovnávat např.úroveň infrastruktury v chudších zemích EU.Masová migrace je ale úplně jiná věc, jednak většina osob ,které v posledních letech přicházejí do EU nejsou uprchlíci, ale migranti , kteří naprosto nesdílejí základní hodnoty evropské společnosti a státy EU nejsou schopny ani zabránit vstupu nelegálních migrantů na své území, ani je deportovat zpět ani zajistit jejich asimilaci.Snaží se proto problém rozředit a předat do zemí, které tímto problémem nejsou zasaženy.Jakmile by se u nás vytvořily statisícové komunity muslimů nabylovali by se na ně další příchozí a situace by byla nevratná, tak jako v Německu, Francii, Švédsku ...', 34);
INSERT INTO comments VALUES (8152, 'Richard Tomeček', 'O solidaritu se nežádá, ta buď je nebo není. Žádá se třeba o pomoc.', 34);
INSERT INTO comments VALUES (8153, 'Petr Jansa', 'Merklová má vážné problémy s psychikou a měla by se léčit. Jestli si myslí, že musí pečovat o všechny, co se tváří, že mají nějaké problémy, a ještě to neurvale vnucuje jiným, tak to je velmi vážná indikace jeji choroby.', 34);
INSERT INTO comments VALUES (8154, 'Julius Zajac', 'Merkel pozvala uprchlíky a mikrovlnky zabíjejí', 34);
INSERT INTO comments VALUES (8155, 'Ladislav Haška', 'Měli byste být solidární....budete solidární...musíte být solidární...bundeswehr', 34);
INSERT INTO comments VALUES (10109, 'Karel Vachut', 'Josefe, vy jste neskutečné sluníčko!', 49);
INSERT INTO comments VALUES (8156, 'Jaroslav Port', 'Se s nima můžeš tak leda vyfotit , ale nech si je tam když sis je pozvala a nikam je necpi .', 34);
INSERT INTO comments VALUES (8157, 'Pavel Schelle', 'Kteremu pismenu ze slova NE tahle tupa soudruzka nerozumi? N nebo E? Mozna by uz bylo vhodne zacit pouzivat klasicky cesky argument, facku. Vyzdy kdyz tenhle bolsevicky odpad rekne "sloidarita" nebo "vyzva" tak ji jednu strihnout, ze bude mesic zrat kasicky. Treba by ji to konecne doslo...', 34);
INSERT INTO comments VALUES (8158, 'Frantisek Kofron', 'Nelegalni migranty nebrat!!!! Žádné dotace za to nestojí. Nechápu o jaké solidaritě Merkel mluví. Proč máme být solidarní s Nigérii nebo Afghánistánem¿ Pokud s nimi chtějí být solidarní Němci a lákat je na dávky, tak je to jejich věc, ale nechápu, proč je chtějí vnucovat ostatním.', 34);
INSERT INTO comments VALUES (8159, 'Jirka Honců', 'Hele, internacionální eurosocialismus. Pro lidi, kteří prožili socík, takové Déjà vu. Takže díky, ale ne.', 34);
INSERT INTO comments VALUES (8160, 'Vlaďka Černajová', 'Nemám absolutně nic proti tomu, když se někdo rozhodne nemít děti. Ale tím se taky rozhodl nemít pokračovatele. Takže nemá co kecat do budoucnosti dětí jiných lidí. Jenže přesně takoví lidé dnes vládnou Evropě. Nejde jim o nic. Za pár let tu nebudou. Naše děti ale ano. Takže vy dva, jděte už konečně do Brusele i se svými pro budoucnost Evropy destruktivními počiny!', 34);
INSERT INTO comments VALUES (8161, 'Libor Ulrich', 'Vazeny pane, jste s prominutim cvok. Prectete si tu definici jeste jednou. Odmitani referenda se rozhodne jako vlastizrada nekvalifikuje, mozna ve vasem svete fantazie.', 34);
INSERT INTO comments VALUES (8162, 'Josef Švec', 'Dokud tito dva budou u moci vcetne jejich prisluhovacu, cela EU bude mirit ke svemu rozpadu.', 34);
INSERT INTO comments VALUES (8163, 'Petr Bačavčík', 'Jakou solidaritu? Ale měl bych jinou představu. Co takhle místo všelijakých czexitů a V4exitů prosadit Bruselexit, Merkelexit a Junckerexit? Prostě EU bez byrokratů a politiků-korytářů.', 34);
INSERT INTO comments VALUES (8164, 'Radim Vladik', 'Tak ma to Milko,přestože máš dneska svátek,rovnou zapomeň.Našínejvyšší prioritou je toto nikdy nepřipustit.', 34);
INSERT INTO comments VALUES (8165, 'Slavomír Beneš', 'Víte, co mne fascinuje? Že až nějaký článek bude pojednávat o naší neochotě přijímat migranty, budou někteří pravidelní eurohujeři tady psát, jak to západní státy zvládají a jak to vůbec žádný problém není. Že my si myslíme, jaký to pro ně je problém, ale oni samotní to tak necítí a že my si ve své nabubřelosti myslíme, že tomu, co se děje v Německu, rozumíme lépe než samotní Němci. Pod tímto článkem to říkat nemohou, protože když ten článek je o žádosti o solidaritu, dost dobře nemohou psát, že to Němci zvládají sami, ale až jiný článek bude o postoji Čechů, tak nás budou zarytí eurohujeři poučovat o tom, že ten problém vidíme jen my. Pár jmen bych tady uvedl, ale obávám, se, že by to bylo smazáno jako trolling. Proto je neuvádím, ale možná někteří z Vás víte, kteří lidé tady opakovaně do diskusí a někdy i do blogů píší, že nás myšlenkově zaostalých Čechů se otázka migrantů netýká, ale problém v ní vidíme, zatímco myšlenkově vyspělých Západoevropanů se ta otázka týká, v ní problém nevidí. Přitom jakýpak :nevidí problém"? Proč by někdo chtěl po sousedech solidaritu v případě, že by příslušný problém neexistoval?', 34);
INSERT INTO comments VALUES (8166, 'Jaroslav Port', 'On jí to řekl jen Orbán , Zeman a Szidlová ostatní se hrbí a mlčí .', 34);
INSERT INTO comments VALUES (8167, 'Roman Maliha', 'Proč dnešní krásný sportovní den kazíte tímto odporným ksichtem', 34);
INSERT INTO comments VALUES (8168, 'David Chládek', 'Než začnete někoho přisprostle urážet a kolaborantsky hájit cizí zájmy, zjistěte si více. Zde máte zdroj (pokud jste chytřejší než pánové v uvedeném rozhovoru, jak je možné vás nikdo nezná?) https://echo24.cz/a/SzT4m/utika-odtud-moc-penez-300-miliard-jen-v-dividendach', 34);
INSERT INTO comments VALUES (8169, 'Michal Grešlik', 'Štáty vážiace si slobodu, by sa nemali nechávať podplatiť. To je veľká výzva pre našich ("asi" skorumpovaných) politikov.', 34);
INSERT INTO comments VALUES (8170, 'Marie Čermáková', 'Milá milko, každý si necháme své. Ty si tam nech ty své africké miláčky, když jsou tak vítaní, a taky si nech ty svoje dotace na rozhledny v údolí a my budeme žít taky za své, bez přikazování, za co můžeme utrácet.', 34);
INSERT INTO comments VALUES (8171, 'Mirek Gajer', 'Merklova vstoupi do historie jako ta, ktera zlikvidovala EUnii.', 34);
INSERT INTO comments VALUES (8172, 'Tomáš Macejka', 'Co ta baba nechápe na slovu NE??? Už jí to bylo řečeno asi 1000x ze všech možných stran a ona to není schopná pochopit a furt nedá pokoj.', 34);
INSERT INTO comments VALUES (8173, 'Miroslav Teissig', 'Kdo migrační problém vyvolal, způsobil, napomohl k jeho vyvolání se bude muset postarat i o jeho vyřešení. Staré české přísloví říká, že "Čím kdo zachází, tím také schází." Takže tak nějak, soudružko Führerko.', 34);
INSERT INTO comments VALUES (8174, 'Radovan Sýkora', 'Okamurovské bláboly.', 34);
INSERT INTO comments VALUES (8175, 'Luboš Deršák', 'Soudružky a soudruzi, to jsme to dopracovali:)', 34);
INSERT INTO comments VALUES (8176, 'Radovan Sýkora', 'Stačí aby se dohodnul Molotov a Ribbentrop.', 34);
INSERT INTO comments VALUES (8177, 'Ivo Hnilica', 'Německo, Francie a další země byly svého času koloniálními zeměmi po válce zase Německo v rámci průmyslového rozvoje přijalo, ne právě malý počet Turků. Jsou na cizince z jiných a diamentrálně se odlišujících kultur zvyklí tak ať v tom pokračují a země které nemají s tímto své zkušenosti nechají být! A to i za cenu zastavení dotací z EU. Roubování je dobré v sadařství o novodobém "stěhování" národů si tohle nemyslím.', 34);
INSERT INTO comments VALUES (8178, 'Mirek Gajer', 'Opet Merklova lze a tvrdi, ze nektere zeme odmitaji prijimat "uprchliky". Pritom odmitaji pouze ilegalni migranty. Tu p...u uz meli davno impeach.', 34);
INSERT INTO comments VALUES (8219, 'Tomáš Bílek', 'My je nezvali. To ona. At si je tedy nech a nebo odveze z EU.', 34);
INSERT INTO comments VALUES (10110, 'Jiří Chocholáč', 'jako fórek po ránu dobrý', 49);
INSERT INTO comments VALUES (8179, 'Milan Mroczkowski', 'Jsem zastánce EU a doufám že při dalším hlasování do Evropského parlamentu nepřibude euroskeptiků, ale lidi co doopravdy mají opačný postoj než Merkelová, Merkelová a Juncker EU škodí. Spíš by se měli zabývat zda neporušili pravidla ochrany hranic.', 34);
INSERT INTO comments VALUES (8180, 'Radek Zlámal', 'No jo paní Merkelová. Tohle je výsledek vaší otevřené náruče. https://creepingsharia.wordpress.com/2018/01/27/mohammed-invasion-germany/', 34);
INSERT INTO comments VALUES (8181, 'Jaromír Hlubek', 'Čo ste si navarili, to si aj zjesťe!', 34);
INSERT INTO comments VALUES (8182, 'Karel Horky', 'Koho se zeptala predem ? Tak at si to ted uzije.', 34);
INSERT INTO comments VALUES (8183, 'Frantisek Kofron', 'Ve finále jde navíc většina dotaci na různé projekty tlacene z EU o jejichž smysluplnost lze víc než pochybovat.', 34);
INSERT INTO comments VALUES (8184, 'Kateřina Vašáková', 'Protože jde o daleko podstatnější věci než jen o těch pár uprchlíků. Jde o princip fungování EU, o to, jakým způsobem se to nastaví, co všechno si ostatní státy nechají líbit, a jestli je vůbec to, kam to směřuje, ještě reformovatelné. Jistě znáte přísloví - podej prst a chytne celou ruku.', 34);
INSERT INTO comments VALUES (8185, 'Karel Novotny', 'Prisli bychom o par rozhleden v udoli a uprostred lesu, ze kterych nic neni videt, o hromady dalsich a predrazenych zakazek, atd., snizila by se silena urednicka masinerie nutna pro schvalovani a pridelovani tech dotaci, a predevsim by se snizila na to navazana korupce.', 34);
INSERT INTO comments VALUES (8186, 'Mirek Gajer', 'Je to paradox. Porad vas jeste cenzor nesmaznul.', 34);
INSERT INTO comments VALUES (8187, 'Tibor Sulo', 'Chcete levné montovny ? Nechte si uprchlíky.', 34);
INSERT INTO comments VALUES (8188, 'Karel Novotny', 'Milka jedna se zase ozvala! Před dvěma léty zvala všechny do Německa, tak ať si je tam nechá', 34);
INSERT INTO comments VALUES (8189, 'Karel Janyška', 'A ke zločinu.... Dzp', 34);
INSERT INTO comments VALUES (8190, 'Jirka Honců', 'Tak si, drahá paní Makrelová, strč svý peníze do Brusele a nech nás žít. My jsme neříkali, že je tady místo pro všechny. My jsme hnusní rasisti a xenofobové,..', 34);
INSERT INTO comments VALUES (8191, 'Libor Ulrich', 'Prosim vas jak souvisi EU s vyplatou dividend do zahranici? Firmy si vyplaceji dividendy, protoze mame otevrenou ekonomiku uzpusobenou k zahranicnimu obchodu. Pokud zamezite moznost firmam si vyplacet dividendy (a tedy zarazite podnikatelum moznost delat si co chce s nerozdelenym ziskem), tak tady nebude investovat uz vubec nikdo. Co se tyce predvalecneho rozvoje Ceskoslovenska, tak prachsproste lzete. Ceskoslovensko melo v roce 1938 per capita HDP vyjadrene v dolarech roku 1960 celkem 548 dolaru na hlavu. Rakousko melo 640, ale napriklad Nemecko melo 1126 a Britanie mela 1181 dolaru na hlavu, Francie 936. A to jsou polovicni rozdily. O Spojenych Statech, nejsilnejsi svetove ekonomice nekdy od roku 1880 ani nemluvim. Faktem je, ze jsme se NIKDY vyspelym zapadoevropskym zemim nepriblizili na tak malou vzdalenost jako po roce 2004, potazmo po roce 1989. Nejprve si zjistete fakta, pak blbe kecejte.', 34);
INSERT INTO comments VALUES (8192, 'Karel Janyška', 'Snad až na termín rudočerní. Od roku 1931 je církev svatá přesvědčena o správnosti socialismu, neboť přijala do své doktríny tzv. sociální spravedlnost, takto socialismu právě středobod. Od té doby se sice všemožné druhy socialistů perou navzájem, ale táhnou ve skutečnosti za jeden organizační provaz. Nutí je k tomu uznání řídící autority, coby podle nich jediné možné varianty uspořádání lidského světa a jedné možnosti, která lidstvo může přivést ke štěstí. Hluboce, ale opravdu hluboce, se mýlí. V případě náboženských fanatiků je to dokonce pohrdání dílem božím. Kdyby to tak Bůh chtěl, udělal by to tak a možnost zorganizovat lidský svět by lidem poskytl. On to však neudělal... Dzp.', 34);
INSERT INTO comments VALUES (8193, 'Karel Mazač', 'Falešnou solidaritou k totální záhubě a sebedestrukci. To se pak někdo diví, že lidé chtějí odchod z EU.', 34);
INSERT INTO comments VALUES (8194, 'Karel Janyška', 'Přičemž vzájemné vazby prvků spontánního řádu jsou výsostně omezující, tedy negativní, tedy zákazové. On jediný umožňuje lidem a jejich organizacím, včetně národních států, sledovat nekonečné množství účelů a cílů svých jednání tak, aby při tom nenarážely do ostatních, tedy, aby lidí se stejným neosobním sebezáchovným úkolem vůbec přežití. Tato neobkročitelná podstata musí každého, kdo se o zorganizování lidského světa snaží příkazy, varovat. Nejde to, není to možné, musel by nejprve umět zrušit vesmír a naučit se ho sestavovat, tedy nekonečně složité řády, příkazem (tedy lidským rozumem, který je ovšem produktem vývoje, takže to není možné už z tohoto důvodu), ne omezeními. To je každému, aspoň trochu málo zorientovanému člověku jasné. EU je tedy silou anticivilizační a může existovat jen na základě 3 předpokladů. 1.Všichni lidští obyvatelé a všechny národy Evropy se prohlásí dobrovolně, podtrhuji dobrovolně, za Němce a vznikne německý superstát, který budě řízen klasickými metodami. Bude mít i svoji velkou armádu a policii. 2.Momentální vývoj bude pokračovat navzdory názorům Evropanů na EU a bude postavena silná eunijní policie a armáda k potlačování odporu a nesouhlasu. Takový stav známe ze Sovětského svazu a každý ví, jak to s ním dopadlo. 3. Evropská unie bude stát na stejném principu, na kterém stojí spontánní řád lidského světa v Evropě, tedy na negativní multismlouvě mezi státy pro dosažení konsensu a garantování principu, já pán - ty pán. 3a. Žádná Evropská unie prostě nebude. Vráťme se teď k názvu eseje a zdůvodněme ho. Kdo má všech pět pohromadě, všechno našel unitř. Kdo ne tomu nemá žádný smysl cokoliv dalšího povídat.', 34);
INSERT INTO comments VALUES (8195, 'Kateřina Vašáková', 'Ono stačí, aby se nařídily ty "vyrovnané dávky", a už se jim sem bude chtít mnohem víc, a to i když budou vyrovnané "jen" podle životní úrovně. Takže bych říkala - zatím je V4 nezajímá, to se ale může velice rychle změnit. Jinak, taky jsem pro, aby se tedy zastavily granty a evropské dotace, beztak jen pokřivují trh - podporují jedny na úkor jiných. Co je potřeba, vrátit se k co největší regionální soběstačnosti, zachovat politiku všech azimutů v obchodu, naučit se vyvážet zboží s vysokou přidanou hodnotou a ne se stávat levnými montovnami. A hlavně, převzít odpovědnost a nečekat s nataženou rukou.', 34);
INSERT INTO comments VALUES (8196, 'Ivan Pap', 'trhni si nohou bábo', 34);
INSERT INTO comments VALUES (8197, 'Karel Novotny', 'Ovsem solidarne pak taky prestaneme do EU posilat penize sami. S ohledem na to, ze dotace jsou ze 40 % nase vlastni penize, a ze vetsina z tech dotaci se tak jako tak rozhazela na nesmyslne a predrazene projekty, pripadne je spolkla korupce a dalsi urednicka rezie na ty dotace, tak si troufnu tvrdit, ze pokud budem dobre hospodarit s temi nasimi 40 %, tak na tom opravdu budem vyrazne lip.', 34);
INSERT INTO comments VALUES (8198, 'Karel Janyška', 'Proč je EU socialistickým nesmyslem, zejména rudočerných socialistů, generujících zločin ? Jak známo, vývoj lidského světa je tažen civilizací jednotlivců obchodem, tedy jejich vyšším a vyšším osamostatňováním. Takoví lidé jsou pak už dlouhá tisíciletí tahouny dalšího jeho vybavování technickými prostředky, neboť svojí nekoordinovanou aktivitou vygenerovali neosobně a nezáměrně spontánní řád lidské interakce a lidského světa, který je svými evolučně vyvinutými pravidly řídí a nutí k efektivním a produktivním druhům jednání. A přinutil je založit evoluční cestou bez původního záměru vlastní správní jednotky, národní státy. Pomiňme tu eskapádu historických událostí, které vedly k dnešku a podívejme se na to, co je. Co vniklo a co dál vzniká. Jen za poslední 3 dekády vznikly desítky nových národních států a nikdo se jich, tedy každý toho svého, nechce vzdát. Jak to ? Vždyť EU je přece prý jakousi novou lepší formu lidského světa. Tak proč ne ? Jednoduše proto, že to není mírovými cestami možné. Historickými vazbami a vzájemnou všemožnou sounáležitostí osazené skupiny lidí, kteří si říkají národ, dokonce až paradoxně na tom trvají i v USA, kde je national skoro všechno, totiž se této vlastnosti ani vzdát nemohu. Je vrostena v jejich historii a v jejich myslích, tedy v systému tzv. pravidel jejich správného chování. Proto není znám případ, kdy by se někdo dobrovolně vzdal své národní identity. Národy a jejich národní státy, není zatím dáno, že co národní stát to jeden národ, se staly jedinými možnými správci zemského povrchu, a každý vidí, že skoro o každý metr svého území jsou ochotny bojovat. Ale co je nejpodstatnější pro existenci lidského světa, že se staly největšími prvky jeho spontánního řádu, který je samotnou podstatou jakékoliv možnosti, aby lidský svět vůbec existoval.', 34);
INSERT INTO comments VALUES (8199, 'Vlaďka Černajová', 'Můj příspěvek je nevhodný do slušné a kultivované diskuse.', 34);
INSERT INTO comments VALUES (8200, 'Petr Mikes', 'Tak já myslím, že asi takhle. https://youtu.be/ZA92eJczDow', 34);
INSERT INTO comments VALUES (8201, 'Jaroslav Krejčí', 'Tato osoba prohlásila, že to zvládnou a že islám do Německa patří. Následně si ji německé ovce znovu zvolili. Also liebe Deutsche, viel Glueck!', 34);
INSERT INTO comments VALUES (8202, 'Denis Huml', 'Juncker chce rozhodovat rychleji. Nejspíš podobně rychle jako Hitler. K Mnichovu se to docela hodí.', 34);
INSERT INTO comments VALUES (8203, 'Luděk Šišák', 'Co, že to musíme? Jako být solidární? A proč? Uprchlíky si pozvala k sobě Angela, tak ať si je pohostí. Ovšem před lety jsem slyšel, že přijdou kvalitní odborníci, inženýři a svým příchodem pozvednou jejich ekonomiku. Tak nechť se tak stane. Že přijdeme o peníze z EU? No vždyť my v ní ani nemusíme být. Možná by to asi z ekonomického pohledu nebyl zcela optimální krok, ale nechat si mluvit do toho, co máme dělat a co ne a když neuděláme, to co jiný stát vyžaduje budeme potrestáni. Co to sakra je?!!! Diktatura Berlína/Bruselu?!', 34);
INSERT INTO comments VALUES (8204, 'Antonín Mikulec', 'Tak aby bylo jasno. V roce 2017 jsme obdrželi od EU (po odečtení našich plateb) ubohých 55 miliard Kč. A tato částka po odchodu Britů výrazně klesne, protože oni byli čistí plátci! Když si vezmeme, kolik musíme živit darmožroutů při rozdělování této dotace, kolik je z toho úplatků apod., tak nám vychází, že postavíme pár km dálnic! Frau Merkel, běžte se svým vyhrožováním a s celýma dotacema už jednou provždy k šípku!', 34);
INSERT INTO comments VALUES (8205, 'Richard Tomeček', 'Snad je to donutí se před závažným rozhodnutím zeptat malých zemí na jejich názor a ne rozhodovat společné věci za ně.', 34);
INSERT INTO comments VALUES (8206, 'Petrik Blažek', 'OMG, tej birmovanej zväzáčke kompletne hrabe', 34);
INSERT INTO comments VALUES (8207, 'Lukáš Adamec', 'III. říše k nám poslal Wehrmacht , IV. EU říše k nám posílá barbary. Kdo byl pro nás a Evropu nebezpečnější necht si každý určí sám.', 34);
INSERT INTO comments VALUES (8208, 'Mirek Gajer', 'Vsimnete si, ze k natlaku a manipulaci s verejnym minenim (predevsim u Nemcu) pouziva pro ilegalni migranty opet slovo "uprchlici". To uz by snad melo byt v Nemecku trestne jako je tam trestne odpirani skutecnosti o holokaustu.', 34);
INSERT INTO comments VALUES (8209, 'Libor Ulrich', 'a to snad melo pripadnout vychodu? na cem jedete?', 34);
INSERT INTO comments VALUES (8210, 'David Chládek', 'K tomu, jak nám "matka" EU jen "dává"... ...jsme krmelec Evropy. Odliv kapitálu do ostatních zemí Evropské unie je jeden z vůbec nejvyšších. V roce 2016 odteklo na dividendách ze země 293 miliard korun. Zahraniční investoři tady vydělávají na levné práci. Peníze tady ale už zpátky neinvestují a odvezou je pryč (tedy zejména do jiných zemí EU a odkud se zpět vrací kosti, resp. řádově nižší peníze na "rozvoj", tedy reálně nás nikdo nedotuje, vydělávají na to zdejší lidé, lépe řečeno vydělají bohatším zemím EU mnohem víc). Systematicky se dojí česká ekonomika a politici tomu jen bezmocně přihlížejí, nebo přímo asistují. Na světě jsou čtyři země, které mezi lety 1937 a 1989 nejvíc upadly. Argentina, Uruguay, Česká republika a východní Německo. V té době jsme nebyli součástí globálního hospodářského prostoru. Když jsme se do něj po roce 1989 vrátili, více či méně bojujeme o to, abychom se dokázali dostat na pozici roku 1937. Zatím se nám to úplně nedaří. V roce 1937 jsme byli bohatší než Rakousko, Japonsko, Itálie. Byli jsme zhruba na úrovni Francie. Lehce pod úrovní Německa, Británie nebo Spojených států. Popravdě řečeno: naše cesta po roce 1989 se nedá charakterizovat jako hospodářský zázrak. Už mě fakt nebaví tyhle pokrytecké řečičky, jak mám jen nastavenou ruku a nejsme dost solidární. Paní Merkelové by měl někdo konečně nahlas vysvětlit, že dávno nejsme ničí protektorát.', 34);
INSERT INTO comments VALUES (8211, 'Jaroslav Rychtář', 'Merkelová si pozvala hosty. Merkelová má s hosty problém. Je to její problém. Pokud výběr zní "EU dotace + migranty nebo nic", pak mám jasno. Za dotace na golfová hřiště, akvaparky, rozhledny v údolí a potravu pro dotační odborníky... zaplatit ztrátou suverenity? Ani náhodou!', 34);
INSERT INTO comments VALUES (8212, 'Libor Ulrich', 'jasne, Nemci v cele s nemeckym kanclerem Gerhardem Bushem po utoku na zwillingstürme 11. zari 2001 zautocili na Irak a tim padem pripravili podhoubi pro nynejsi uprchlickou krizi... a nebo mam jen popletena historicka fakta', 34);
INSERT INTO comments VALUES (8213, 'Josef Babka', 'Německo vždy bylo a stále je hrozbou Evropy, bylo velkou chybou po II. sv. válce, že připadlo západu, to se již nesmí opakovat', 34);
INSERT INTO comments VALUES (8214, 'Vladimír Dlouhý', 'Bezpečí našich dětí,nebo Unie co nám bude diktovat co smíme a co musíme.Lehká otázka,czechexit', 34);
INSERT INTO comments VALUES (8215, 'Zdenek Hrdina', 'A nebudete mít opravený jediný chodník, jedinou cestu. A to budete pak řvát!', 34);
INSERT INTO comments VALUES (8216, 'Jiří Krejča', 'Genossin Reichfuehrerin hledá opět na východě Evropy nějakej ten Lebensraum... tentokrát pro německé mohamedány. Asi Merkel zapomněla jak to se skopčáky dopadlo, když ten životní prostor hledali na východě naposledy.', 34);
INSERT INTO comments VALUES (8217, 'Martin Bittner', 'Za mně jsem pro, abychom jim jako ČR navrhli, že nám tedy už nic posílat nebudou - ani peníze, ani migranty.', 34);
INSERT INTO comments VALUES (8218, 'Luděk Jaša', 'Jsem zvědavý m,jestli se najde mezi českými politiky takový s odvahou, že dokáže Merkelové do očí říct, že kdo si je pozval, ať se o ně stará a ať si to platí. navíc když do světa vykřikoval, že to zvládne! Najde se?', 34);
INSERT INTO comments VALUES (8220, 'Zdeněk Jeřábek', 'Mladý muži. Chápete vůbec ten princip příjmu „jenom těch 1500“ imigrantů. Když je přijmeme a i když utečou někam jinam, stejně na ně budeme pořád platit. A nejenom na ně, ale I na jejich příbuzné, kteří za nimi přijedou, ať už potom budou bydlet kdekoliv jinde ve světě. Zaregistrovaní budou u nás.', 34);
INSERT INTO comments VALUES (8221, 'Jirka Novák', 'to je zbytečné, to zvládnete, frau merkel.', 34);
INSERT INTO comments VALUES (8222, 'Jindřich Vlasák', 'Lžete, vítala a zvala všechny uprchlíky bez jakéhokoliv dalšího označení, tak ať si je teď má, nebo ať je naloží do letadla a odveze zpět.', 34);
INSERT INTO comments VALUES (8223, 'Miroslav Klimeš', 'Vy si je pozvete, a druzí se mají starat. Připálila jste si sedací část, a teď chcete, aby si i ostatní sedli na rozžhavenou plotnu. Rozhodla jste špatně, tak za to musíte nést následky. To je, jako kdybych způsobil dopravní nehodu, a chtěl, aby za to byl potrestán soused.', 34);
INSERT INTO comments VALUES (8224, 'Michal Wendl', 'Když se říká A. Musí se říct i B. Německé firmy velmi bohatnou na české práci, která ke velmi levná. Nebo mají dotace uvnitř země a mohou vyvážet zboží a potraviny a likvidovat místní. Tyto firmy pak odvadí daně doma a statní rozpočet Německa velmi bobtná. Pokud chtějí být solidarní ať platí české zaměstnance jako ty Německé !!!. A daně v ČR. Jen pro ukázku firmy, které na uzemí ČR vydělávají takové částky, že by se někdo divil. Škoda Auto, Kaufland ČR,Continental Automotive,Globus ČR,Penny Market,Lidl ČR,Bosch Diesel,Continental Barum,T-Mobile Czech Republic.', 34);
INSERT INTO comments VALUES (8225, 'Martin Kukol', 'bývalému *', 34);
INSERT INTO comments VALUES (8226, 'Lenka Pokorna', 'Jedine na co hodlam prispet je jeji leceni na nasich psychiatrickych klinikach.', 34);
INSERT INTO comments VALUES (8227, 'Stanislav Rosecký', 'Nemci se nikdy nezmění, po roce 45 už jim nemělo být nikdy dovoleno mít vlastní stát a vlastní vládu.', 34);
INSERT INTO comments VALUES (8228, 'Alois Slámečka', 'Anglie se to už netýká a Francie si bez Německa ani neuprdne. Lebensraum für Flüchtlinge!!! Heil! Heil! Heil!', 34);
INSERT INTO comments VALUES (8229, 'Milan Novotný', 'Pokud se nepletu tak ještě nedávno němečtí ekonomové jásali že jim migranti přinesou další ekonomický zázrak. Copak? Něco se od tě doby změnilo?', 34);
INSERT INTO comments VALUES (8230, 'Alena Koubová', 'Ať už ta baba zmlkne. Natahala si do Německa stovky tisíc netáhel a teď se diví, že nepracují? A tak je chce strčit nám. Ať si to Německo užije se vším všudy.', 34);
INSERT INTO comments VALUES (8231, 'Jindřich Zetsky', 'Ta babizna ještě nesedí v base?', 34);
INSERT INTO comments VALUES (8232, 'David Zajdák', 'Nemci maji na jedne strane mindraky z toho, kolik diky nim umrelo lidi. Ale na druhou stranu maji permanetni potrebu diktovat a delat kroky vedouci k destrukci.', 34);
INSERT INTO comments VALUES (8233, 'David Chládek', 'A co solidarita stran odměny za lidskou práci, kdy Němec bere za stejnou činnost a úsilí třikrát více než Čech? To není diskriminační?', 34);
INSERT INTO comments VALUES (8234, 'Ladislav Laker', 'Žádnou solidaritu, jedině nakopat do zadku. Je to jako za Hitlera: ten také požadoval od občanů okupovaných států solidaritu - vždyť němečtí vojáci za ně nasazují životy a krvácejí na bojištích celé Evropy.', 34);
INSERT INTO comments VALUES (8235, 'Jaroslava Kombercová', 'Tak evidentní to zase není. Nesledujete?', 34);
INSERT INTO comments VALUES (8236, 'Jiří Krejča', 'Všude kde mají s nimi dnes velké problémy jich bylo zpočátku jen pár...ale vždy se našel nějakej ignorant, který ostatním tvrdil, že to není žádný problém, že ano?', 34);
INSERT INTO comments VALUES (8237, 'Martin Tesař', 'Co si pozvala, tak ať si taky pěkně vyžere až do dna..', 34);
INSERT INTO comments VALUES (8238, 'Josef Ulman', 'Kolega nemluví o Africe nebo jo?', 34);
INSERT INTO comments VALUES (8239, 'Marek Kolman', 'Není náhodou solidarita spíše dobrovolný projev vůle? Jak někoho mohu nutit k solidaritě? Ta baba se snad už úplně zbláznila.', 34);
INSERT INTO comments VALUES (8240, 'Danuše Kramolišová', 'A samozřejmě tu chodí jen křesťané ze Sýrie...jen se cestou opálili...Nicméně volby v Itálii jsou za pár...a zas nám někdo zadlužený bude půl roku lomit rukama a skládat vládu...', 34);
INSERT INTO comments VALUES (8241, 'Petr Řihák', 'takze klasicky zase něco za něco? klidne si ty prachy nechte a přejmenujte to na nemecka unie. bud tenhle spolek projde hlubokou reformou a navratem ke korenum nebo se doufam zhrouti sam svoji vahou.', 34);
INSERT INTO comments VALUES (8242, 'Josef Babka', 'A větší životní prostor i pro své imigranty bude po Česku žádat Německo přímo nebo zase přes Francii a Anglii.', 34);
INSERT INTO comments VALUES (8243, 'Pavel Šimon', 'Pozor, pozor. Upozornění pro hosty madame Merkel. Z jednoho jejího prohlášení vyplývá, že část z vás, které pozvala do Německa s ujištěním, že se to zvládne, hodlá nátlakově vnutit do okolních rasistických a xenofobních zemí. Je to diskriminace. Nenechte se ovlivnit a setrvejte v místech, kam jste byli pozvání. Ve východní Evropě vás, tiché, unavené a vděčné nebudou lidé tak masově vítat na nádražích s dárky, kapesným a vřelým slovem jako v Německu. Pozor, pozor.', 34);
INSERT INTO comments VALUES (8244, 'Martin Kukol', '"... a varovala, že státy očekávající finanční podporu unie musejí v řešení migračního problému projevit solidaritu." Rád bych Frau připomněl, že EU nebyla založena na principu, že vyvolá-li stát X chaos a rozklad globálního charakteru, musí stát jiný stát na tento krok reagovat "solidaritou" a pokud tak neučiní, bude sankcionován + trestán upřením práv. Holt, čím dál více se utvrzuji v názoru, že EU už je opravdu nereformovatelná, a tato skutečnost mi, coby bývalého eurofederalistovi, vadí nejvíce.', 34);
INSERT INTO comments VALUES (8245, 'Josef Ulman', 'Tak hlavně ono vůbec nejde o nějakých 1500 lidí', 34);
INSERT INTO comments VALUES (8246, 'Petr Malík', 'Já už to nechci poslouchat !!! Czexit !!!', 34);
INSERT INTO comments VALUES (8247, 'Jiří Krejča', 'Kolik jich bylo zpočátku ve Švédsku? Také jen málo a také se našli ignoranti, kteří to brali na lehkou váhu, že ano?', 34);
INSERT INTO comments VALUES (8248, 'Ladislav Frydryšek', 'Merkelová žádá po státech EU solidaritu německého migračního procesu? Proč si to neřečí sama na území Německa?', 34);
INSERT INTO comments VALUES (8249, 'Michal Darmovzal', 'Myslím, že se měla zeptat ještě předtím, než se rozhodla, že je přijme....', 34);
INSERT INTO comments VALUES (8250, 'Eva Zavacka', 'Jo a to si paní Danuše nemůžete být jistá, čí pohřeb by byl první, jestli váš a nebo jejich. Při tom, jak se chovají..', 34);
INSERT INTO comments VALUES (8251, 'Slavomír Beneš', 'Zatímco Němci ve své německé velikosti si nevědí rady s problémem, který sami způsobili.', 34);
INSERT INTO comments VALUES (8252, 'Alois Slámečka', 'Touhle kreaturou a jejím nohsledem kazíte sváteční náladu (olympiáda)?', 34);
INSERT INTO comments VALUES (8253, 'Marek Kalina', 'Cizincem ve vlastní zemi? S těmi pár uprchlíky??? WTF?', 34);
INSERT INTO comments VALUES (8254, 'Slavomír Beneš', 'Proč bych měl být solidární s někým, kdo se se mnou až dosud nebavil o tom, co sám dělá? Proč bych měl cítil odpovědnost za nežádoucí těhotenství sousedovic Heleny, když jsem s ní buď nikdy nic neměl, nebo třeba i ano, ale naposledy před šestnácti měsíci a ona je teprve ve čtvrtém měsíci? A proč bych měl cítit odpovědnost za to, když si Merkel dělala selfíčka s migranty a neptala se mě, zda je to vhodné?', 34);
INSERT INTO comments VALUES (8255, 'František Podškubka', 'Merklová, jsme solidární, ať tě ti Němci nabijí sami, držíme palce.', 34);
INSERT INTO comments VALUES (8256, 'Danuše Kramolišová', 'Jako Jezinky...nejdříve Německo a pak dál či blíž...ven!!!!', 34);
INSERT INTO comments VALUES (8257, 'Petr Jurka', 'Solidarita ze své podstaty musí být vždy dobrovolná, jinak přestává být solidaritou a stává se diktátem. Pokud sama Merkelová říká, že spolupráce funguje při zajištění ochrany vnějších hranic a v boji s příčinami migrace, pak je vše v pořádku. Přijímání migrantů a uprchlíků je komplexní problematika spojená s národní bezpečnostní a budoucí státní integritou, kterou nelze brát na lehkou váhu (jako to dělají některé státy EU, které přijímaly s otevřenou náručí a teď pláčou) a pokud se některé státy rozhodnou, že už nikoho přijímat nechtějí (třeba protože přijímají dostatek migrantů z jiných programů), nesmí na ně být vyvíjen tlak. Nemáme povinnost řešit problémy celého světa, včetně neúnosných populačních nárůstů.', 34);
INSERT INTO comments VALUES (8258, 'Marek Kalina', 'Merkel nikoho z Afriky nezvala, jen řekla, že udělí azyl válečným uprchlíkům ze Sýrie, nikoho z Afriky nezvala...', 34);
INSERT INTO comments VALUES (8259, 'Roman Zágora', 'Lepší je trochu zchudnout (ale to není jisté),než být cizincem ve vlastní zemi. Ta bába v každém případě patří před soud pro velezradu.', 34);
INSERT INTO comments VALUES (8260, 'Karel Hynek', 'Tak si pěkně ve své české malosti zanadáváme....', 34);
INSERT INTO comments VALUES (8261, 'Eva Zavacka', 'Včera jsem šla stmívající se ulicí. Proti mne skupinka asi pěti chlapů do třicítky. Nevím jaké byli národnosti, jen vím, že to byli cizinci. Nebyli to ani naši milý Rómové ale přisnědlí byli. Jak tak schází z kopečka, před sebou měli asi pět zaparkovaných aut. Každé z nich se pokoušeli otestovat, jestli není otevřené.. Nevím, jestli by ujeli, nebo ho vykradli. Ale když procházeli kolem mne, tak jsem se opravdu bála.. Nechci se bát. Nelíbí se mi ten pocit. A už vůbec se nechci bát o své vnoučky!! To není nesolidarita, to je pud sebezáchovy.', 34);
INSERT INTO comments VALUES (8262, 'Vladimír Veteška', 'Pokud vím, tak si je tam pozvala. Tak ať se stará.', 34);
INSERT INTO comments VALUES (8263, 'Marek Kalina', 'Němci s ní jsou evidentně spokojený, zemi se daří jako nikdy...', 34);
INSERT INTO comments VALUES (8264, 'Danuše Kramolišová', 'Ano...1500 uprchlíků živit z našich daní....od kolébky do hrobu a i ten hrob je nejistý pokud se jedná o muslimy...čili pohřeb do 24 hodin a do země...zdá se Vám to málo? Tady skučí úřady, že musejí pochovávat bezdomovce a bylo zrušeno pohřebné. Tak si je vezměte domů, dejte jim střechu nad hlavou, postarejte se o školu, jazyk bez požadavku na náklady. Pak smeknu klobouk...', 34);
INSERT INTO comments VALUES (8265, 'Jiří Krejča', 'I ve Švédsku bylo zpočátku jen 1 500 mohamedánů a dokonce se našli ignoranzi, kteří tenkrát tvrdili, že to nebude žádnej problém.', 34);
INSERT INTO comments VALUES (8266, 'Jiří Horák', 'Čápi, čápi, čápi, čápi, ... furt si opakuju čápi, čápi, ...', 34);
INSERT INTO comments VALUES (8267, 'Lukáš Malvinsky', 'Dublin 4 má být trvalý přerozdělovací mechanismus. Čili na 1500 se to neomezí.', 34);
INSERT INTO comments VALUES (8268, 'Vlastimil Zastera', 'Mela by přestat vydírat a odejit konečně.', 34);
INSERT INTO comments VALUES (8269, 'Jan Macháč', 'Největší štěstí je,že nemá děti.To by byla tchyně jak z učebnice...', 34);
INSERT INTO comments VALUES (8270, 'Vlk Jaroslav', 'kolik konkrétné jich vy ubytujete u sebe a v sousedstvi vašeho domu ?', 34);
INSERT INTO comments VALUES (8271, 'Jiří Horák', 'Putin se vrací ... (a budou tam volby, taky.)', 34);
INSERT INTO comments VALUES (8272, 'Richard Tomeček', 'Milá Frau Merkel, je nám moc líto, že v tomto bodě nemůžeme vyhovět. Jistě při Vaši inteligenci pochopíte, že bez předchozí dohody je nezdvořilé si pozvat návštěvu a pak ji cpát k sousedům. To se prostě nedělá.', 34);
INSERT INTO comments VALUES (8273, 'Jiří Krejča', 'Čím více budou Genossin Reichsfuehrerin a jí podobní chtít protlačit takové hlouposti, tím více budou občané eurokolchoz milovat.', 34);
INSERT INTO comments VALUES (8274, 'Marek Kalina', 'Co to plácáte? Jde teď primárně o imigranty z afrických zemí... Ty žádná Merkel nezvala, tak jen řekla, že udělí azyl válečným uprchlíkům ze Sýrie, nikoho jiného nikam nezvala...', 34);
INSERT INTO comments VALUES (10111, 'Jiří Chocholáč', 'krásná ukázka jak funguje multikulturalismus', 49);
INSERT INTO comments VALUES (8275, 'Jarča Čacká', 'solidárně je poženeme zpátky , odkud přišli -- cpát je někam , kde nikdy nebudou doma , je nesmysl a sebevražda naší civilizace.', 34);
INSERT INTO comments VALUES (8276, 'Rudolf Svoboda', 'Stále kvoká, stále kvoká.', 34);
INSERT INTO comments VALUES (8277, 'Jakub Fišer', 'Premyslim jestli se mam do prispevku zacitat', 34);
INSERT INTO comments VALUES (8278, 'Jindřich Vlasák', 'K tomu snad nemá ani mandát.', 34);
INSERT INTO comments VALUES (8279, 'Marcel Vlna', 'Prestaňte si už klamať do zrkadla. Týchto ľudí nikto v Európe - ani v tej Západnej - nechce. Oni navyše chcú ísť len do niektorých krajín, V4 ich nezaujíma. Nie sú to žiadni vojnoví utečenci, sú to hľadači lepšieho života, ktorí prijímajúcej civilizácii nemajú čo ponúknuť. Možno s jednou výnimkou. Ak by sa kvôli nim súčasná forma pomúteného vedenia EÚ rozpadla, tak potom sa im všetkým ospravedlním.', 34);
INSERT INTO comments VALUES (8280, 'Marek Kalina', 'Jasně, těmi pár uprchlíky bychom spáchali sebevraždu...', 34);
INSERT INTO comments VALUES (8281, 'Petr Mostýn', 'jaký problém, tak snad už jsou všichni v německu,ne? nebo pořád sem někdo leze přes otevřené hranice?', 34);
INSERT INTO comments VALUES (8282, 'Jindřich Vlasák', 'Ta baba si je pozvala, tak ať se teď o ně i stará!', 34);
INSERT INTO comments VALUES (8283, 'Vlk Jaroslav', 'já mám obavy , že Babuš nakonec něco podepíše', 34);
INSERT INTO comments VALUES (8284, 'Marek Kalina', 'Kvóty nejsou funkční řešení, ale na český hysterie z 1500 uprchlíků, ze kterých by většina stejně šla jinam, to je až směšné... Takový počet je pro naši zemi zcela marginální a stavět se takhle na zadní je bláznivé...', 34);
INSERT INTO comments VALUES (8285, 'Pavel Schelle', 'Prchajici Nemce nikdy. Pak je zase budeme muset odsouvat.', 34);
INSERT INTO comments VALUES (8286, 'Milan Novotný', 'Pokud se většina vašich dobrých přátel rozhodne spáchat sebevraždu je nesolidární že vy se toho nechcete zůčastnit?', 34);
INSERT INTO comments VALUES (8287, 'Jiří Horák', 'Ano a zcela přesně!', 34);
INSERT INTO comments VALUES (8288, 'Jiří Hrbek', 'to ještě zatím nikdo pořádně nespočítal, protože hodně peněz nefiguruje v oficielním odevzdali do fondu, přijali z fondu', 34);
INSERT INTO comments VALUES (8289, 'Vladimír Šťastný', '', 34);
INSERT INTO comments VALUES (8290, 'Pavel Vosáhlo', 'jak to říkal Gross.....křištálově čisté.......a já dodávám VYDÍRÁNÍ', 34);
INSERT INTO comments VALUES (8291, 'Tomáš Kovanda', 'Kde ti skopčáci pořád berou tu drzost? 2x uvedli Evropu do totálního chaosu a zkouší to zase!', 34);
INSERT INTO comments VALUES (8292, 'Pavel Schelle', 'Merkelova je Putinuv agent. Nikdo v Evrope nedela pro rozpad EU vic nez tahle alternativne inteligentni vudkyne...', 34);
INSERT INTO comments VALUES (8293, 'Josef Babka', 'Je to s ohledem na neustálý příliv imigrantů do Německa legitimní požadavek, stejně jako související požadavek Německa na větší životní prostor', 34);
INSERT INTO comments VALUES (8294, 'Lukáš Malvinsky', 'Možná Merklová chce abychom z EU vystoupili, ale nechce nás k tomu vyzvat přímo. Tak to zkouší takto oklikou.', 34);
INSERT INTO comments VALUES (8295, 'Marcela Nečasová', 'A co konkrétně vlastně Merkel po nás chce? V Itálii budou na počátku března parlamemtní volby, ve kterých má šanci podle průzkumů s přehledem vyhrát pravicová koalice složená se stran Liga severu, Berlusconiho strany FI a dalších 2 menších stran. Tato pravicová koalice je velmi euroskeptická, proti-eurozónní a proti-migrantská. Berlusconi se chystá z Itálie vyhostit až 600.000 nelegálních migrantů, kteří nemají nárok na azyl. Pokud vím, tak naše země přece neodmítá žádosti o azyl a nikdy je neodmítala. Problém pro Merkel ale je, že ekonomičtí imigranti u nás o azyl žádat nechtějí.', 34);
INSERT INTO comments VALUES (8296, 'Jiří Huml', 'no jeden už tady byl před více jak 70ti lety který se snažil ošéfovat celou Evropu', 34);
INSERT INTO comments VALUES (8297, 'Jiří Krejča', 'To není pravda. Samozřejmě, že pár desítek tisíc pracantů z organizací napojených na bruselský penězovod bude na tom velice špatně, ale když přestaneme stavět lavičky za 100 000, akvaparky a rozhledny, tak na tom budeme lépe než dnes.', 34);
INSERT INTO comments VALUES (8298, 'Tomáš Netolický', 'Angelo, to zvládneš!', 34);
INSERT INTO comments VALUES (8299, 'Milan Volek', 'My bychom je rádi přijali, ale ti migranti šli na pozvání do Německa a jejich přesun do Česka by mohl být považován za ODSUN z Německa a my Češi jsme za něco podobného sklidili takovou kritiku, že se už na něčem podobném nechceme podílet.', 34);
INSERT INTO comments VALUES (8300, 'Jiří Zapletal', 'Já už jsem toho o dotacích EU přečetl a nadiskutoval tolik, že ten povýšený tón si skutečně můžete nechat od cesty.', 34);
INSERT INTO comments VALUES (8301, 'Martina Beránková', 'Co si dovoluje chtít? Vždyť tvrdila, že to zvládnou!', 34);
INSERT INTO comments VALUES (8302, 'Josef Ulman', 'Já bych naopak apeloval na racionální uvažování a rozum...nevím, jestli můj požadavek není nad síly těchto politiků.', 34);
INSERT INTO comments VALUES (8303, 'Luboš Zaoral', 'To vypada jak docasne umisteni vojsk SSSR, akorat s tim rozdilem, ze ti nakonec odejit museli.', 34);
INSERT INTO comments VALUES (8304, 'Alois Jindra', 'Neuvěřitelné, že to pořád vrací. Evidentně je to zaplevelení Evropy pro ni velmi důležité.', 34);
INSERT INTO comments VALUES (8305, 'Pavel Pokorný', 'Nebudeme řešit problémy Německa s migranty. Když někdo žádá o azyl, tak to má udělat v první bezpečné zemi, jinak porušuje zákony EU, a o lidi porušující naše zákony nemáme zájem. Pokud mají přímořské státy problémy s migranty, tak nás mohou požádat o pomoc a my zvážíme, jako pomoc jim poskytneme. Ale je potřeba brát v úvahu, že moře je zdroj peněz, tedy by se přímořské státy měly podělit nejen o zápory = ti migranti, ale i o klady, tedy zisk z turistů, tedy zisk z nás.', 34);
INSERT INTO comments VALUES (8306, 'Eva Zavacka', 'kamarád z klubu zahrádkářů si pozve na návštěvu bandu otrapů. Rozmlátí mu kvartýr, znásilní manželku a dceru a on pak po nás bude chtít, ať si jich pár vezmeme k sobě domů. Protože jsme přece v klubu? Tak co na tom, že on má vilu a my dvoupokoják. Že on má stotisíc příjem a my ani třetinu?', 34);
INSERT INTO comments VALUES (8307, 'Jiří Hrbek', 'Až ho budou chtít skutečně řešit, tak je V4 připravená pomáhat Zatím ho však jen připravili, eskalovali, udržovali a následky sypali pod koborec.', 34);
INSERT INTO comments VALUES (8308, 'Bruno Chojka', 'Stejně nebezpečná jako Juncker, ten by chtěl o všem rozhodovat v Bruselu .. „Není možné, že musíme vždy rozhodovat jednomyslně v otázkách bezpečnosti a zahraniční politiky,” řekl Junckerna mnichovské bezpečnostní konferenci. „Stále znovu zjišťujeme, že konsenzuálních jednomyslných rozhodnutí nejsme schopni,” zdůvodnil své přesvědčení. https://www.novinky.cz/zahranicni/463711-juncker-eu-se-musi-rozhodovat-rychleji-ne-vzdy-je-nutna-shoda.html', 34);
INSERT INTO comments VALUES (8309, 'Radovan Sýkora', 'Tak to budeme dost hodně v mínusu.', 34);
INSERT INTO comments VALUES (8310, 'Jachym Brzezina', 'Žádat může.', 34);
INSERT INTO comments VALUES (8311, 'Alois Slámečka', 'Asi takhle, Angelo: Už táhni i se svými nápady do brusele!', 34);
INSERT INTO comments VALUES (8312, 'Milan Novotný', 'Štědrá EU jim cpe nesmyslně vysoké sociální dávky a proto sem jedou. Stačilo by jim nic nedávat a "migrační problém" by byl hned vyřešený.', 34);
INSERT INTO comments VALUES (8313, 'Jaroslav Krejčí', 'To je politicky zoufalec. Doufám, že Visegrád neuhne. Strčte si dotace někam!', 34);
INSERT INTO comments VALUES (8314, 'Jaroslav Křížek', 'Merkel ale ne', 34);
INSERT INTO comments VALUES (8315, 'Ivan Marek', '"Welcome" a "to zvládneme" už neplatí ?', 34);
INSERT INTO comments VALUES (8316, 'Jan Skýpala', 'Hele, beru. Jako to, že žádné uprchlíky a taky konec dotací EU. Obojí nám jenom prospěje.', 34);
INSERT INTO comments VALUES (8317, 'Radovan Sýkora', 'Neovládáte zjevně kupecké počty, tak se raději nepouštějte do žádných rozborů situace. Migranty ať si Merkel nechá doma a ať se přestane zabývat jestli jich brát 100 tisíc nebo 200 tisíc ročně. Vy si ale uvědomte, žtráta dotací z EU by nás sakra dost bolela.', 34);
INSERT INTO comments VALUES (8318, 'Jiří Krejča', 'Propánakrále, tak nám žádné peníze z centrály eurokolchozu neposílejte. My tam také nic posílat nebudem a bude klid.', 34);
INSERT INTO comments VALUES (8319, 'Ondrej Patek', 'Dočetl jsem až k ,,dočasného domova''''...pak jsem se polil kávou!!! :-) Nein mutti, danke!', 34);
INSERT INTO comments VALUES (8320, 'Pavel Přibil', 'Tak já si pozvu návštěvu a pak budu obcházet sousedy, aby mi ji pomohli hostit... Trochu divné, ne?', 34);
INSERT INTO comments VALUES (8321, 'Arnold Lička', 'Merkelová žádá po státech EU solidaritu při řešení migračního problému -------------------------------------------------------------------------------------------------------------- Ne, to není žádný příkaz. Akorát, kdo této "žádosti" nevyhoví, tak se dostane do velkých problémů. Klidně se umínusujte, ale já jsem rád, že už tu naní Sobotka, ale Babiš, který (zatím) tlaku nepodlehl.', 34);
INSERT INTO comments VALUES (8322, 'Vlastimil Smoleja', 'Nebojte. Ta už to má promyšlené. Paraziiti dostanou domovské knížky a my budeme jejich dávky posílat za nimi do Němec. Pochopitelně v ve výši jako v Německu.', 34);
INSERT INTO comments VALUES (8323, 'Jiří Krejča', 'V čem pomáhat? V zaneřádění dalších oblastí Evropy mohamedány?', 34);
INSERT INTO comments VALUES (8324, 'Ludmila Bulvová', 'my jsem si nikoho nezvaly...než něco takového vyslovím musím vážit svova vážená paní státnice... jak dneska bylo napsáno v noviných.... z čr odchází v dividendách cca 400 mld kč.. nemluvím o nízkých mzdách.... (ale naše vina, že na ně přistupujem.. ).... ne němoecko je tak bohaté tak at si to vyřeší... nás se nikdo neptal.. a češi byli od samého počátku proti.... mámé své zkušenosti s návštěvama jedna přišla před 400 kety a neadaptovala se dote ́d... děkujeme nechceme..pošlete je od kud přišli....', 34);
INSERT INTO comments VALUES (8325, 'Adam Bílek', 'odpoená nelidská zrůda', 34);
INSERT INTO comments VALUES (8326, 'Aleš Adam', 'Solidárně bych ji poslal do prd3l3', 34);
INSERT INTO comments VALUES (8327, 'Petr Pavelka', 'Ta je drzá...', 34);
INSERT INTO comments VALUES (8328, 'Pavel Indrst', 'Jaká solidarita pořád? S kým nebo s cím mám být solidární?', 34);
INSERT INTO comments VALUES (8329, 'Lukáš Cvrček', 'Já jsem psal v přítomném čase, vy píšete o tom, co bude možná v budoucnosti Navíc velice pochybuji o tom, že paní Merkelová mluví za všechny státy, které jste vyjmenoval. Naopak se dostává do stále větší izolace, protože ve skutečnosti se nikomu nechce za ni jíst, co si nadrobila. Zejména Rakousko pod novou vládou zcela jistě nebude podporovat nějaké bezhlavé vítání a přizpůsobovat tomu dotační pravidla. I kdyby si však Merkelová prosadila svou, nás se to už tolik týkat nebude, protože do budoucna mají dotace klesat a občané ČR se mají přesunout do kategorie čistých plátců do unijního rozpočtu. Pokud to přijde dříve, než bylo původně plánováno, bude to škoda, ale určitě ne důvod odevzdat vlastní zem agresivním přistěhovalcům.', 34);
INSERT INTO comments VALUES (8330, 'Zdeněk Nutz', 'Vždyť ty projekty spotřebovávají hlavně sami sebe. Kdy už to někdo řekne na plnou hubu. A platí se z nich nesmyslné a zbytečné věci. Třeba různá školení pro školení. nebo okrasná zeleň ve Špindlerově Mlýně, nejbohatším městě ve státě Kdyby jsme peníze nedali do unie a zároveň nedostali to VÍC, o kterém tu ta paní mele a mohli o nich sami rozhodovat bez těch prosíků a administrace kolem a stavěli opravdu pořádná věci, nebylo by to lepší? O kolik VÍC vlastně dostáváme, víte to někdo????????', 34);
INSERT INTO comments VALUES (8331, 'Jan Dudek', 'Dotace nebo Uprchlíky? Lépe přežít bez dotací než dostat uprchlíky.', 34);
INSERT INTO comments VALUES (8332, 'Milan Novotný', 'Už se ani nesnaží lhát že to jsou uprchlíci ...', 34);
INSERT INTO comments VALUES (8333, 'Jiří Dvořáček', 'Co si Merklica navařila, to ať si taky sní...', 34);
INSERT INTO comments VALUES (8334, 'David Pospíšil', 'Já jsem pro. Navrhuji,že by naši dlouhodobě nezaměstnaní pomohli při budování plotu kolem hranic EU.', 34);
INSERT INTO comments VALUES (8335, 'Jan Mitlener', 'EI by měla přestat hlídat hranice u břehů Libye ...', 34);
INSERT INTO comments VALUES (8336, 'Vlastimil Smoleja', 'Auf der Stelle!', 34);
INSERT INTO comments VALUES (8337, 'Jan Vachuda', 'V chudém státě nikdo nebude,takže to bude hlavně Německá solidarita:D', 34);
INSERT INTO comments VALUES (8338, 'Jiří Zapletal', 'Už jí konečně někdo řekněte, že si ty nesmyslné dotace klidně může nechat, hlavně ať přestane prudit.', 34);
INSERT INTO comments VALUES (8339, 'Vlado Turček', 'Nech ide do frasa. Co si pozvala nech si chova. Basta', 34);
INSERT INTO comments VALUES (8340, 'Milan Novotný', 'Jaké řešení migračního problému? Co je prostě přestat vozit do EU?', 34);
INSERT INTO comments VALUES (8341, 'Karel Stehlík', 'Německo s Francií nikdy neopustí myšlenku reexporu migrantů do všech států EU. To bude důvod k odchodu některých států z EU a možná to nebude trvat ani moc dlouho.', 34);
INSERT INTO comments VALUES (8342, 'Vladimír Pupík', 'Merkelová si je pozvala si je pozvala tak ať se o ně stará', 34);
INSERT INTO comments VALUES (8343, 'Matěj Němeček', '"Merkelová žádá po státech EU solidaritu?!" = Ani neprosí? Tak ať si žádá...', 34);
INSERT INTO comments VALUES (8344, 'Honza Novák', 'Vás nepotěším pane Merkel, stále kvoká ,stále kvoká.', 34);
INSERT INTO comments VALUES (8345, 'Milan Hettner', 'podle studie při zachování stávající migrace do Evropy bude v roce 2050 žít ve Švédsku 4 450 000 muslimů. Dnes má Švédsko 9,9 milionů obyvatel. Chceto to? Dnes je ve Švédsku milion muslimů. Před 30 lety nebylo ve Švédsku více než 100 000 muslimů, no tak buďte solidární - https://zpravy.aktualne.cz/zahranici/muslimove/r~6198e76cd67e11e7b6a9ac1f6b220ee8/', 34);
INSERT INTO comments VALUES (8346, 'Lubomir Macho', 'Predtým štáty EU dlho žiadali Merkelovú, aby ten problém s imigráciou nepodnecovala', 34);
INSERT INTO comments VALUES (8347, 'Jiří Horák', 'Jenže tvrzení, že "tisíckrát opakovaná lež se stává pravdou" - vzešlo taky z německého území. Co tahle babizna nechtě potvrzuje.', 34);
INSERT INTO comments VALUES (8348, 'Pavel Sladovník', 'O tom se ale nějak v článku nepíše. Hraniční státy EU čerpají velmi pěkný příspěvek z fondů EU, aby hranice chránily. Že na to Řecko a Itálie kašlou a jen příspěvek prožírají je věc druhá. Hrozit by tak měla spíše těmto zemím, ne těm, které nechtějí přijímat lidi, o kterých jen tuší, že se chtějí přicucnout k jejich sociálnímu systému. Jiné země, jako třeba Španělsko dokazují, že to jde.', 34);
INSERT INTO comments VALUES (8349, 'Jan Sádlo', 'Vyděračko, co sis uvařila, to si spapej sama', 34);
INSERT INTO comments VALUES (8350, 'Jan Stehlík', 'Docasny domov pro pristehovalce? Asi jako docasna pomoc SSSR.', 34);
INSERT INTO comments VALUES (8351, 'Milan Novotný', 'V Africe jsou stovky milionů potenciálních migrantů do štědré EU. Je nutné je donutit pracovat doma v Africe a ne je držet na sociálních dávkách v EU!', 34);
INSERT INTO comments VALUES (8352, 'Jan Hlaváč', 'Je normální si pomáhat.', 34);
INSERT INTO comments VALUES (8353, 'Václav Mach', 'Myslím že bychom měli být solidární a zavázat se, že v budoucnu přijmeme prchající Němce, Švédy atd. - za předpokladu že projeví ochotu se integrovat.', 34);
INSERT INTO comments VALUES (8354, 'Pavel Houska', 'Jako ohraná deska.', 34);
INSERT INTO comments VALUES (8355, 'Martina Sedlická', 'Nejhorší je, že tahle bába zmanipuluje Němce k přesvědčení, že za všechny jejich potíže s migranty může nesolidární Východní Evropa.', 34);
INSERT INTO comments VALUES (8356, 'Miroslav Čapek', 'Ne ne ne ne! Ale když zase pěkně zatlačí tak jenom ještě zvětší odstředivé tlaky v EU a už aby to bylo! Pryč od nich, a klidně ty prachy z EU můžeme oželet, aspoň nebude co kmotry rozkrádat!', 34);
INSERT INTO comments VALUES (8357, 'Libor Mozis', 'kdo seje vítr... sklízí bouři...', 34);
INSERT INTO comments VALUES (8358, 'Slavomír Beneš', 'Jakápak solidarita ve druhé fázi problému? Dali nám právo veta při schvalování podmínek, které nabízejí migrantům a kvůli kterým ti tam chodí? Jsem ochoten být solidární, když se někomu něco stane bez jeho zavinění, nebo když třeba v tom jedeme spolu a mně to nepostihne, avšak jeho ano, ale klidně to mohlo být naopak. Spolu jsme udělali nějakou hloupost, ale já jsem si jen odřel koleno, avšak Dalibor si zlomil nohu. Tak mu dám pět tisíc a trochu zmírním tu ztrátu, která mohla být naopak. Ale my jsme v tom s Německem nejeli a od počátku jsme to kritizovali. Tak jakápak solidarita, když se ukázalo, že máme pravdu? Buď spolupráce v první fázi a solidarita ve druhé, nebo ani to, ani ono.', 34);
INSERT INTO comments VALUES (8359, 'Zdeněk Nutz', 'Tak dobře, tak my ty peníze nechceme a taky žádný nedáme! A co by se vlastně stalo? Přišli bychom o pár kruhových objezdů o nějaký ten most co se nezvedá a konečně bychom začali při investování myslet taky hlavou.', 34);
INSERT INTO comments VALUES (8360, 'Jozef Polák', 'Její žádost o solidaritu je spíš příkazem , ne prosba . Mutti roztáhla náruč , ne svou ale Evropskou .', 34);
INSERT INTO comments VALUES (8361, 'Vladimír Vítek', 'Solidárně uprchlíky nechceme, bude to pro nás lepší. Solidárně ani dotace nechceme, i to pro nás bude lepší.', 34);
INSERT INTO comments VALUES (8362, 'Jarek Jakubček', 'Coz o to, V4 by taky chtela aby EU byla solidarni a hlidala sve hranice.', 34);
INSERT INTO comments VALUES (8363, 'Ondrej Coufal', 'Ja jsem slapl do h***a tak buddte solidarni a slapnete taky !', 34);
INSERT INTO comments VALUES (8364, 'Milan Votava', 'Problém? My máme nějaký problém? My jsme způsobily nějaký problém?', 34);
INSERT INTO comments VALUES (8365, 'Jaroslav Reznik', 'Aha...zapadni koncerny prakticky vsechny zisky odvadi ve, jsme pro ne kolonie s nizkymi platy, kterou vyuzivaji...to je ta solidarita?', 34);
INSERT INTO comments VALUES (8366, 'Karel Pešek', 'Naprosto s timto pozadavkem souhlasím. Meli bychom Nemecku pomoci odvézt migranty zpet tam, odkud prisli.', 34);
INSERT INTO comments VALUES (8367, 'Bruno Chojka', 'Podle německé kancléřky dělba úkolů dobře funguje při ochraně vnějších hranic EU a v boji s příčinami migrace. Méně dobře ale v řešení otázky dočasného domova pro přistěhovalce ... škoda že nevysvětlila co si představuje pod pojmem dočasný domov, je přece jasné, že ty vyžírky už odsud nikdo nedostane ani heverem', 34);
INSERT INTO comments VALUES (8368, 'Milan Hettner', 'žádám solidární německé platy a výhody pro české důchodce a zaměstnance. Že je to nesmysl? Je, asi jako solidarita při migračním problému. https://www.thelocal.fr/20171201/how-frances-muslim-population-will-grow-in-the-future', 34);
INSERT INTO comments VALUES (8369, 'Pavel Sobotka', 'Velmi se mýlíte - co bude po roce 2020, i s ohledem na BRexit, se bude nyní schvalovat - a myslíte, že Francie, Německo, Rakousko, Nizozemsko,... neseženou pro schválení své verze dotačních pravidel dostatek hlasů?', 34);
INSERT INTO comments VALUES (8370, 'Vladimír Vítek', 'Pozvala jsem si návštěvu, ale ta mi dělá na zahradě i v domě bordel. Buďte solidární a vemte si ji domů.', 34);
INSERT INTO comments VALUES (8371, 'Milan Novotný', 'Takže už žádní nebozí uprchlíci a solidarita s nimi ale natvrdo migranti a povinná solidarita s nimi? A to jako proč?', 34);
INSERT INTO comments VALUES (8372, 'Tomáš Balvín', 'Ať si EU nechá své dotace i své uprchlíky. Nevymenit svobodu a státní samostatnost za eura musí být samozřejmým základem naší zahraniční politiky.', 34);
INSERT INTO comments VALUES (8373, 'Martin Kodat', 'Tak není nic lehčího než je nechat jít ne? :))', 34);
INSERT INTO comments VALUES (8374, 'Mojmir Peřina', 'Ale ona nechce solidaritu s Německem, ona chce solidaritu s Řeckem a Itálií.', 34);
INSERT INTO comments VALUES (8375, 'Zuzana Vass', 'Predsa by sa Nemecko nechcelo zbaviť toľkého obohatenia, prínosu, kvalifikovaných pracovníkov a v neposlednom rade - náboženstva mieru. Veď už tam musí byť pomaly pozemských raj, ktorý im budú všetci závidieť, až už konečne vymrú všetci nevzdelaní rasistickí vidiečania.', 34);
INSERT INTO comments VALUES (8376, 'Jiří Horák', '... a nedá pokoj, dokud jich pár neschytá!', 34);
INSERT INTO comments VALUES (8377, 'Vlastimil Smoleja', 'Co by chtěla, bosorka? Sama si je pozvala. A když to nezvládá, posílala by je ostatním? A jaké peníza nám posílá a kolik si od nás vycucají?', 34);
INSERT INTO comments VALUES (8378, 'Mojmir Peřina', 'Ale vždyť to je právě cíl těchto prohlášení. O tom, že byla chybou přijmout postkomunistické státy V3 už si cvrlikají i vrabci na EU střeše.', 34);
INSERT INTO comments VALUES (8379, 'Pavel Sladovník', 'Neříkala, že to zvládne? Tak ať se snaží. Jednání Merkel mi přijde stejné, jako když čerstvá šestnáctka pozve na Facebooku všechny na oslavný mejdan a pak chce, aby její pro její pařany uspořádala mejdan celá dlouhá čtvrť', 34);
INSERT INTO comments VALUES (8380, 'Tibor Zídek', 'Vy jste si je pozvala, paní kancléřko, tak si je živte sama.', 34);
INSERT INTO comments VALUES (8381, 'Jiří Horák', 'Ať si ta husa uvědomí, že její pitomá a nezákonná politika v oblasti migrace má podstatný podíl na výsledku britského referenda o brexitu. A pokud hodlá takto pokračovat, tak se ze mě stane příznivec odchodu z EU namísto toho, že jsem odpůrcem takového nápadu!!!', 34);
INSERT INTO comments VALUES (8382, 'Pavel Sobotka', 'jj, Češi do EU nepatří, to jsem tu psal už tolikrát....', 34);
INSERT INTO comments VALUES (8383, 'Mojmír Přívara', 'baba nedá pokoj', 34);
INSERT INTO comments VALUES (8384, 'Josef Vašata', 'Ta mr..a si nedá pokoj a nedá. Jak si to představuje, že všechny státy "musí" být solidární??? Sama si imigranty pozvala,, tak ať si je taky doma nechá.', 34);
INSERT INTO comments VALUES (8385, 'Martin Kodat', 'No a pak se někdo diví, že se zcela vážně zmiňuje Czexit. Ještě chvíli blbni paní kancléřko a EU do několika let skončí.', 34);
INSERT INTO comments VALUES (8386, 'Danuše Kramolišová', 'Milko..ty sis pozvala a mluvila za všechny, že to zvládneš...tak do toho...', 34);
INSERT INTO comments VALUES (8387, 'Josef Hřebíček', 'Scheren Sie sich zum Teufel, Frau Merkel.', 34);
INSERT INTO comments VALUES (8388, 'Tomáš Horák', 'Aneb když to Němcům samotným dle výsledků voleb nevadí, tak je snad vše OK nebo ne?', 34);
INSERT INTO comments VALUES (8389, 'Peter Biely', 'Pani Merkelova, nie-dakujeme. Co ste si napiekla-to si pekne spapajte.', 34);
INSERT INTO comments VALUES (8390, 'Lukáš Cvrček', 'Paní kancléřka se mýlí. Čerpání dotací a podpor není žádným způsobem vázáno na vstřícnost vůči její vítací politice. Pokud bude chtít pravidla změnit, může to zkusit, ale při vyhlášené pružnosti evropských struktur to zabere přinejmenším pár let', 34);
INSERT INTO comments VALUES (8391, 'Tomáš Horák', 'Ať jí Babiš hodí pětieurovku do klobouku, ať pořád nesomruje a bude klid.', 34);
INSERT INTO comments VALUES (8392, 'Michal Zelenka', 'Nikdo Vás Němci k ničemu nenutí, nenuťte tedy ani své sousedy.', 34);
INSERT INTO comments VALUES (8393, 'Evzen Soukup', 'To už tady nedávno bylo soudružko Merkel - solidarita', 34);
INSERT INTO comments VALUES (8394, 'Petr Tinka', 'A jak je na tom Merkelová se solidaritou při řešení důchodového problému?', 34);
INSERT INTO comments VALUES (8395, 'Ondrej Patek', 'Vy asi moc empatický nejste, že? A psa asi také nemáte... Ono vám to stáří ale vysvětoí samo!', 35);
INSERT INTO comments VALUES (8420, 'Vladimír Novák', 'Rád bych viděl tu vyhlášku domů se sociální službou. Četl jste ji někdy? To je něco jako domovní řád.', 35);
INSERT INTO comments VALUES (8396, 'Barbora Marešová', 'Je to "ústav sociální péče" a zdravotní péči tam zajišťují sestry, což se vzájemně nevylučuje. Jak píšete, doktor tam působí jen např. dva dny v týdnu, čili je externista => rozhodně to z toho nedělá zdravotnické zařízení. Záleží na tom, jak je zařízení zařazeno, jakými zákony se řídí, jak ho definuje zřizovatel. Pokud jako zdravotnické zařízení, nemůže to být "domov pro seniory" nebo "dům s pečovatelskou službou", ale např. "léčebna dlouhodobě nemocných", což je zpravidla součást nemocnice a lidé tam nebydlí.', 35);
INSERT INTO comments VALUES (8397, 'Vladimír Novák', 'No, jste jedna z mála rozumných lidí, s kterým se dá diskutovat. Tím Vám samozřejmě nebudu mazat med kolem huby, ale i když jste v protipólu, jednou jsme, pokud mně paměť ještě slouží, jsme si třeba své politické názory vyjasnili. Ovšem, opakuji jsem Váš protipól, sleduji Vaše reakce na mnohé příspěvky a články a musím uznat, že ve většině máte pravdu. Ale nechce se mi do všech diskusí zasahovat. I když přiznávám, těch pár debat s Vámi na různá temata, mě potěší. Jste rozumná ženská a uvažujete selským rozumem. Je škoda, že takových lidí jako jste Vy, na tomto webu neexistuje více.. . No, ale k věci. Nedělám to často (nemyslete, že to rozvažuji nějak velkopansky), ale rozhodl jsem se na Vaše jednotlivé body odpovědět. Tak ad 1) Ten článek o čoklech vlastně je (vidíte, už se vyjadřujete jako já), protože skýtá jakýsi precedens. Tzn., že to nebude jen o té staré paní. Jinak, zkuste dokázat té úřednici, že jí lhala, popř.podejte na ní žalobu. ad 2) Máte pravdu v tom, že jakýkoliv zřizovatel si do podmínek může dát co chce, krom nějakých blbostí, tzn.,že v tom místě můžou (ale i nesmí psi). ad 3)Ten Přerov jste správně pochopila a já musím kontrovat: Je všechno soukromé, správné? ad 4) Jsem rád, že Vaše rodina Vás do starobince nestrčí, zřejmě ji máte tak dobře vychovanou, tak jako je moje. A těší mě to, jsem rád, že se ještě dnes takové děti najdou. :Jen poznámka. Když manželka byla po třetí mrtvici a pak se náhle objevila rakovina (naštěstí dodatečně vyléčena), tak jsem jí nikam neposílal. A moje děti s vnučkami (mám 4) mi pomáhaly ve věcech, na které jsem nestačil. ad 5) I když to není vlastně bod. Na socialismus v tomto případě nenadávejte, děje se to tak i dnes.', 35);
INSERT INTO comments VALUES (8398, 'Jaroslav Stuchlík', 'Ta dáma by si měla vybrat. Buď čokl nebo dům pro seniory. Že by taky mohl její miláčekl vadit ostatním spolubydlícím jí samozřejmě nezajímá', 35);
INSERT INTO comments VALUES (8399, 'Sylva Šporková', 'Vy opravdu nedokážete chápat čtený commentText. Za prvé - ten článek není v prvé řadě o čoklech (to by tam bylo víc příkaldů, jak je to jinde apod.), Ten je o jednom jediném konkrétném případu, že jedna úřednice poničila život jedné staré paní, protože jí lhala. Za druhé - protože každý dům s pečovatelskou službou má svého zřizovatele, je na tom zřizovateli, co si do podmínek (vyhlášky) dá. Za třetí - i přes několikeré vysvětlování rozdílu mezi domovem důchodců (starbincem) a domem s pečovatelskou službou jste ten rozdíl nepochopil - protože ten Přerov, který uvádítě, žádný Dům s pečovatelskou službou nebyl (lidé tam žili nikoliv ve svých bytech, ale v pokojích a po několika lidech, člo většinou o nemohoucí). Za čtvrté - je mi zcela fuk, jestli v nějakom tom domě čoklové budou nebo ne, protože já mám takovou rodinu, u které nemusím mít obavy, že mě ať už do pečovateláku, nebo do starobince strčí A keKlausovi - to platí v každé době ve slušných rodinách, kde si rodiče dobře vychovali děti (a dnes daleko vícv měste než na vsi). Ti ostatní, co z nich vychovali sobce (leckdy vlastním příkladem, kdy za socíku do toho starobince strčili babičku či dědečka) - ti v tom starobinci nejspíš skončí', 35);
INSERT INTO comments VALUES (8400, 'Vladimír Novák', 'Jsem na rozpacích, jak Vám odpovědět, ale je vidět, že se dá s Vámi rozumně diskutovat, i když je zřejmé, že budu Váš protipól. Hlavní téma ale nám utíká, a to jsou ti čoklové, řadu minusů (které jsem získal, je za tu náhradu P.Řápka a za ten "starobinec". Tím jsem chtěl vyprokovat diskuse, což se mi snad podařilo. Anžto dnešní lidé, obzvláště ve městech podléhají jakési samolibosti. Když Vám napíšu, že mladí lidé (ale bylo to i za socialismu) nerozeznají noty třeba na piánu a mají modly ve zpěvácích, tak mě všichni sežerou. Ale to jsem jen odbočil,teď k tématu. Sama nabízíte dům s pečovatelskou službou ( i když jednotlivé byty v rámci družstevního baráku budou existovat také). Na tom, kdo ten dům provozuje, nezáleží (i když ta soukromá firma v Přerově byl otřes). Podmínky, za kterých ten člověk může ten byt získat, jste jmenovala, i když samozřejmě v příkladech, ale přesně, souhlasí.Ovšem chybí mi tam Vaše vyjádření o těch psech. Můžou tam být nebo ne? Má každé to zařízení jinou vyhlášku nebo je vyhláška celostátní? (V případě celostátní vyhlášky se pak tomu zákazu přítomnosti psa od úředníků nedivím, může to mít své důvody). Vrátím se ale ještě dále zpět. Protože skoro všichni nadávají na p.P.Řápka, aniž by se po několik týdnů projevil, řekl jsem si, že jej zastoupím, ovšem na rozdíl od něho, budu uvádět pragmatické problémy. To se týká i tohoto článku. Neznamená to, že bych neměl rád zvířata. 13 let jsem měl kocoura, na vesnici u zeťáka, když bylo sucho, tak jsem s dvěma mladšíma vnučkami přenášel ropuchy do naší studánky, kde ještě voda byla, skoro všechny kočky z vesnice chodí za mnou, poznají i naše auto a už běží za námi. Tak jim dávám mlíko, i když mnozí odborníci tvrdí, že je pro kočky nevhodné No, mlíko se přes noc u nás ztrácelo a zjistil jsem, že na něj chodí zmije. Tak jsem ji nechal, ona se dokonce vedle mě stočila do klubíčka, ale nesměl jsem odejít, jinak se odplazila. Ke Klausovi. To platilo dříve na vsi, ne dnešní době.', 35);
INSERT INTO comments VALUES (8401, 'Vladimír Novák', 'Netykáme si, Král. Ale divil byste se.', 35);
INSERT INTO comments VALUES (8402, 'Vladimír Novák', 'No, rád bych Vám odpověděl, ale nevím na co. V sociálních zařízeních finguje doktorka tak 1 - 2 týdně.Tzn., že když je to sociální ústav, že zdravotníci tam nesmějí? A uznávám, že služba v domově pro seniory je vyčerpávající (i když jsem viděl některé věci, které se mi tam nelíbily) . Jinak po 10 letech chápu, že jste odtamtud odešla.', 35);
INSERT INTO comments VALUES (8403, 'Frantisek Kral', 'Abys jednou nekoukal..podle toho jak se vyjadrujes te moc radi mit nemuzou Novak', 35);
INSERT INTO comments VALUES (8404, 'Frantisek Kral', 'Ja bych zase pral vsem Rapkum, Novakum a jejich rodinam.kdyby nekdu potrebovali pomoc psa pri zachrane Zivota aby ten pes zrovna nemel cas!!!', 35);
INSERT INTO comments VALUES (8405, 'Sylva Šporková', 'Ach jo - zjistěte si nejdřív základní pojmy, abyste tu ze sebe nedělala hlupce. Ano - dům se pečovatelskou službou je dům (celý dům- nikoliv nějaký byt v družstevním paneláku), ve kterém jsou jednotlivé bytové jednotky, za něž člověk platí nájem a nájemní smlouva se právně nijak neliší od nájemné mslouvy kdekoliv jinde. Provozuje ho ale město (stát, osukromá firmy), která si stanovení podmínky, za kterým dotyčný může tento nájemní byt získat. A mezi těmito podmínkami je věk, dzravotní stav (kde je řečena určitá míra soběstačnosti - takže ho např. nemůže získat člověk s pokročilým alzheimerem, invalida, který se neobejde bez celodenní pomoci - a naopak třeba zdravý a mladý člověk). V tomto domě nejsou žádné pokoje - jen samostatné byty. Může být organiztační součástí Domova seniorů )např. v přízemí je klasická domov seniorů s jendotlivými pokoji, v prvním patře jsou jednotlivé byty - ovšem pro každé z těchto zařízení platí jiné podmínky). A co se týká slov Klause - jako obvykle jste je bu ́d nepochopil, nebo zkomolil - a stím naprosto souhlasím. Klaus vždy říkal o sebe a svou rodinu - tedy že - v případě senior- nemají spoléhat na té, že se o stařečky bude starat nějaké státní zařízení (z daní nás všech), ale že by se o své staré rodiče měla postarat především jejich rodina. že by lidé měli své děti vychovávat tak, aby to brali jako samozřejmost...', 35);
INSERT INTO comments VALUES (8421, 'Sylva Šporková', 'Jste na omylu - státní úředník je tu proto, aby sloužil - proto se také říká zaměstnání placené státem "Státní služba" (v angličtině state service) a říkalo se tomu tak i u nás, než to komouši změnili, protože v socíku přece nikdo nokomu neslouží, že. A nic na tom nemění, že musí dodržovat zákony a předpisy - za prvé to přece dodržovat každý občan, za druhé tyto zákony a předpisy schválili lidé )poslanci, zastupitelé), které jsme si zvolili, tedy kteří za nás jako naši zástupci vykonávají naši vůli (proto se tomu říká zastupitelská demokracie)', 35);
INSERT INTO comments VALUES (8406, 'Vladimír Novák', 'Tzn.,že podle Vašeho názoru si Dům s pečovatelskou službou můžu vyložit tak, že v něm bydlí pouze lidé, kteří se mohou tedy částečně postarat o sebe. V tom případě, jak už jsem se částečně zmínil, se ti lidé musí řídit řádem pro takovýto dům. Jestli - že je tam zákaz pobytu psů, tak ta vyhláška platí. Pokud ale paní bude bydlet v BYTĚ s pečovatelskou službou a jinak ten dům je nájemní nebo patří družstvu nebo SVJ, tam ten domovní řád bude jiný a paní psa bude mít s sebou. S venčením psů máte sice pravdu, ale ne úplnou. Tak jsou třeba případy, že někdo psychicky nemocný nebo staroušem měl v bytě psa, možná jej krmil, ale ven s ním nechodil. Pak chudáci sousedi. Ještě poznámka. Pan Klaus tvrdil, že se má každý starat o sebe. A mnoho lidí si to vyložilo tak, že budou sobečtí a okolí se nebudou všímat. Důsledky, včetně těch čoklů, neseme dodnes, i když se mi zdá, že lidé se pomalu začínají sdružovat.', 35);
INSERT INTO comments VALUES (8407, 'Barbora Marešová', 'Myslím, že bych tam neměla být déle. Pracovala jsem v domově pro seniory cca 10 let jako brigádník i zaměstnanec a vím, o čem mluvím. Při svých praxích a studiu jsem navštívila i další. Spíš vy byste si měl ujasnit pojmy. A hádat se nemusíme. Sociální služby jsou sociální služby a zdravotní jsou zdravotní. Stačí, když si tyto rozdíly vyjasníte vy.', 35);
INSERT INTO comments VALUES (8408, 'Kateřina Vašáková', 'Nic takoveho, ze "neudela" v mem prispevku neni. Takze reagujete trochu mimo. A samozrejme zalezi na plemenu, civava vam pulku obliceje neukousne i kdyby se hodne snazila', 35);
INSERT INTO comments VALUES (8409, 'Pavel Ján Buvala', 'Přál bych vám všem, čoklů obhájcům, vstoupit do DPS ky, kde majitelky čoklic & čoklů nestihly "miláčky" vyvést ven vyčůrat a ty pak rozvonívají paty zdí kolem vstupních dveří!', 35);
INSERT INTO comments VALUES (8410, 'Jakub Korec', 'Vždyť o ničem jiném než o tom, jestli je činnost úředníků služba občanům nebo není, nepíšu? Nikde nepíšu že článek není o úředníkovi, pouze jsem vás opravil, že není o státním úředníkovi, což je pravda.', 35);
INSERT INTO comments VALUES (8411, 'Sylva Šporková', 'O čem tedy podle vás článek je - o Marťanech? Jak vidíte z diskuse - větší část lidí si vyložila, že článek, je o psech, menší část že o špatném jednání úředníka. A vy jste řekl, že článek není o úředníkovi - logicky z toho tedy pylývá, že se domníváte, že je o psovi (psech).', 35);
INSERT INTO comments VALUES (8412, 'Jakub Korec', 'Můžete označit, kde jsem psal něco o psech, nebo ze které části mých příspěvků vyplývá, že se něco takového domnívám?', 35);
INSERT INTO comments VALUES (8413, 'Sylva Šporková', 'kdybyste uměl chápat, čtený commentText, tak přesně to jsem napsala. že v zasutpitelské demokracii si volíme své zástupce, ti schvalují zákony a předpisy - a úředníci, které si platíme, nám mají přesně podle nich sloužit. A neschopnost chápat čtený commentText se projevila i v tom, že se domníváte, že článek je o psech.Nikoliv!! ČLánek je v první řadě o tom, že nějaký úředník nesplnil svou povinnost (tedy špatně vykonával svou službu) a řekl té paní lež. Že si tu diskutující začali povídat hlavě o psech - to už je zase jiné věc - ovšem zcela charakteristické pro českou společnost, která je zvyklá držet hubu a krok a špatně pracující úředník jim nevadí, zato jsou shcopni se do krve pohádat o blbostech', 35);
INSERT INTO comments VALUES (8414, 'Jakub Korec', 'Tak se důchodcům nepřizpůsobujte.', 35);
INSERT INTO comments VALUES (8415, 'Jakub Korec', 'V prvé řadě článek není o žádném státním úředníkovi. A v druhé řadě, úřady sice mají sloužit občanům ale jenom pouze způsobem, který stanoví zákon. Takže pokud přijdete na úřad že chcete stavět dům a ten vám to nepovolí, tak to sice můžete považovat za službu pro vás, ale s výsledkem asi spokojená nebudete.', 35);
INSERT INTO comments VALUES (8416, 'Sylva Šporková', 'Pojmy neznáte vy. Dům s pečovatelskou lsužbou je POUE takový dům, ve kterém jsou samostatném nájemní byty. Je určen pouze lidem, kteří se o sebe mohou částečně postarat sami, nikoliv nepohyblivým či slouhodobě nemocným lidem - což je dáno i v podmínkách takového zařízení. Mohou tam být samozřejmě i nějaké společné prostory (jídelna, dílna, společenská místnost...) - a pak už záleží na provozovateli, jestli umožní přístup zvír ́řatům i tam nebo. A co se týká venčení - pokud je dotyčný člověk svéprávný,je to pouze jeho věc, jak si zařídí venčení psa - do toho mu nemá kdo co kecat (stejně jako jiné staré paní, který bydlí o pár metrů dál v paneláku a bude měsíc nemocná - je to prostě její věc)', 35);
INSERT INTO comments VALUES (8417, 'Vladimír Novák', 'Měla byste si vyjasnit tedy domy s pečovatelskou službou, kde třeba ty jídelny jsou anebo je to opravdu soukromý byt. Pokud by se jednalo o soukromý byt, tak uvedu zatím jediný příklad. Může se stát, že paní bude imobilní, ale proč by v něm tedy podle soudu ostatních nemohla mít toho čoklíka? Pak ovšem nastává otázka, kdo jej bude ochoten venčit. Existují sice profesionální "venčiči", ale ta paní nezaplatí. A má se neustále v tom baráku někoho doprošovat? Sociálka, která rozváží ty obědy a přiváží nějaké ty léky to určitě nebude. Takže, co s tím?', 35);
INSERT INTO comments VALUES (8418, 'Vladimír Novák', 'S tím slovem jsem to, pravda, přehnal, ale zcela úmyslně, aby ten pejorativ se ujal. Takže se ujal. Mě starobinec (ale ani ta "pastouška") nečekají, protože mám ještě manželku, a ta by nedovolila, abych do takového zařízení přišel. Vždyť felčaři dnes nechávají třeba lidi s rakovinou umírat doma, aby nemocnice "ušetřila". A navíc, mám děti, které se postaraly o svého druhého dědu a nenechali jej umřít právě v tom starobinci . Jak si ty děti vychováte, takové je budete mít.', 35);
INSERT INTO comments VALUES (8419, 'Sylva Šporková', 'asi byste si měl nejdřív přečíst článek, pod kterým diskutujete. Paní šla do domu s pečovatelskou služnou, což jsou samostatné BYTY (jako v kterémkoliv jiném bytovém domě třeba na sídlišti), ovšem v tomto domě jsou poksytování navíc veškeré služby - od donášky obědů, nákupů, přes úklid až po pomoc s hygienou, reahabilitací apod. A tak není důvod, proč by v takovém nájemním bytě kdokoliv psa měl. Ovšem problém není pes - problém je úředník, kterého si platíme ze svým daní, aby pro ná pracoval - a který klidně té paní lže a omezujejí její práva. A proti tomu by se mělo protestovat, ne proti psům )které já mimochodem tak zrovna nemusím)', 35);
INSERT INTO comments VALUES (8458, 'Zdeněk Šmerda', 'pardon - pan Vomáčka', 35);
INSERT INTO comments VALUES (8422, 'Vladimír Novák', 'Když přijdu k někomu na návštěvu, tak pokud ten majitel má psa, tak ten psí odér je cítit, i když byt je v čistotě udržovaný a v pořádku. Netvrďte mi, že se v tom bytě ten pes neučůrne. To pozná jen člověk, který psa nevlastní. Starobinec se dnes rovná domov důchodců. Vzhledem, že jsem v některém z nich byl, viděl jsem jaké tam vládnou poměry, ale měla byste tam být déle. Můžeme se hádat, pod koho pečovatelská služba patří, buď pod sociálku nebo zdravotnictví, ale to je jedno. Pokud jde o byt s pečovatelskou službou, tak ať tam má paní třeba 10 čoklů, pak ovšem ostatní nájemníkům to nebude muset vyhovovat. Tímto poněkud poopravuji svůj předešlý článek, ale třeba ty knihovny, atd. nadále platí.', 35);
INSERT INTO comments VALUES (8423, 'Hana Malichova', 'Vladimír Novotný: Vaše schopnost empatie se rovná nule. A vaše vyjadřování taktéž. Jen to sebevědomí je zbytnělé.. Navíc babička je babičkou jenom pro svá vnoučata, jste jejím vnoučetem? Vzhledem k tomu, že používáte slovo starobinec asi těžko.', 35);
INSERT INTO comments VALUES (8424, 'Ondřej Zeman', 'Ale tam není problém mít psa. Vždyť to v tom článku je naprosto jasně uvedeno. Dokonce to není možné v tomto typu zařízení paušálně zakázat.', 35);
INSERT INTO comments VALUES (8425, 'Ondřej Zeman', 'Tyhle nesmysly ohledně lidí co mají psa máte odkud?', 35);
INSERT INTO comments VALUES (8426, 'Jiří Novotný', 'To je přesně ono. Nebojte se, on ten náš pejsek Vám a Vašemu dítěti nic neudělá, on je hodnej... A když pak náhodou někomu ukousne kus obličeje, tak za to nikdy nemůže pes, ale vždycky jen páníček nebo panička, kteří jej nedokázali uhlídat. Což je ale poněkud bizarní za situace, kdy samotná panička je v takovém stavu, že se již nedokáže v plné míře postarat ani sana o sebe a potřebuje proto pobývat v domě s pečovatelskou službou.', 35);
INSERT INTO comments VALUES (8427, 'Jiří Novotný', 'Paní Marešová, Vy asi patříte k lidem, kteří mají pocit, že si mohou dovolit všechno, bez ohledu na ostatní, pokud to nemají jednoznačně zakázáno černé na bílém, nejlépe s kulatým razítkem. Domy s pečovatelskou službou sice nejsou zdravotnickým zařízením, ale nejsou zároveň ani běžnými obytnými domy. Častým charakteristickým znakem domů s pečovatelskou službou jsou právě ona společná zařízení (tedy "veřejný prostor"), jako např. jídelny, společenské místnosti, často i společná sociální zařízení pro více "bytových jednotek", v nichž je pohyb domácích zvířat naprosto nevhodný, a to nejen z hygienických důvodů, ale i z hlediska bezpečnosti ostatních klientů. To, že se nejedná o zdravotnické zařízení v pravém slova smyslu, je v této souvislosti zcela nepodstatné. Jedná se totiž v každém případě o sociální zařízení, určené pro osoby se sníženou schopností postarat se o řádný chod vlastní domácnosti, nikoliv o psí nebo kočičí útulek.', 35);
INSERT INTO comments VALUES (8428, 'Petr Bačavčík', 'Doufám, že pro vás také jednou najdou něco jako STAROBINEC... nebo PASTOUŠKA.', 35);
INSERT INTO comments VALUES (8429, 'Petr Bačavčík', 'Někteří z nás nejsou schopni se přizpůsobit dnešní době a blbcům, kteří si hrají na bohy.', 35);
INSERT INTO comments VALUES (8430, 'Petr Hariprasad Hajič', 'Ano, přesně. Je to služba, která je vymezená judikaturou.', 35);
INSERT INTO comments VALUES (8431, 'Jakub Korec', 'Pokud vám někdo slouží, tak musí dělat to co chcete. Úředník ale nic takového dělat nemá. Je vázán právními předpisy, které musí dodržovat, bez ohledu na to, jestli se vám to líbí nebo ne.', 35);
INSERT INTO comments VALUES (8432, 'Hana Ulrychová', 'Vy budete podobně empatický jako ten úředník, viz ta poznámka o cicmání se se psem.', 35);
INSERT INTO comments VALUES (8433, 'Barbora Marešová', 'O důchoďáku se v článku nikde nepíše, měl byste procvičit čtení.', 35);
INSERT INTO comments VALUES (8434, 'Barbora Marešová', 'Zajímalo by mě, co si představujete pod slovem "starobinec". Ať už cokoliv, evidentně jsou vaše představy hodně vzdáleny realitě. Paní dostala byt v domě s pečovatelskou službou. To rozhodně není zdravotnické zařízení, v článku dokonce píšou, že se jedná o městské byty s pečovatelskou službou. Nejedná se tedy o žádný veřejný prostor. Pokud to není nikde v předpisech napsáno, myslíte si, že je v pořádku, aby bylo novému nájemníkovi zakázáno vzít si s sebou do bytu domácího mazlíčka? Další věc je, že psi "doma" nečůrají. Od toho je "venčení", o tom jste určitě slyšel.', 35);
INSERT INTO comments VALUES (8435, 'Tomáš Velčovský', 'Kdo ví, co to bylo za úředníka a pochybuji, že byl z toho zařízení. A když ne, nemohl mít motivaci zbavovat paní jejího psa. Ať tak či tak, asi dobře pro paní. Místo toho, aby se celé dny cicmala se psem, může opět navazovat vztahy s lidmi. Z článku mi totiž také vyplývá, že může mít problémy v běžné komunikaci.', 35);
INSERT INTO comments VALUES (8436, 'Tomáš Velčovský', 'Kdo ví, co to bylo za úředníka, pochybuji, že byl z toho zařízení. A když ne, nemohl mít motivaci zbavovat paní jejího psa. Ať tak či tak, asi dobře pro paní. Místo toho, aby se celé dny cicmala se psem, může opět navazovat vztahy s lidmi. Z článku mi totiž také vyplývá, že může mít problémy v běžné komunikaci.', 35);
INSERT INTO comments VALUES (8437, 'Barbora Marešová', 'To máte pravdu, vše je jen o vedení zařízení. Když je ředitel schopný, tak mají lidi určitý životní standard. Samostatnou kapitolou jsou soukromá zařízení, kdy majitele často zajímá prostý profit a ne blaho obyvatel. Zařízení, kde jsem pracovala (domov pro seniory), je budova z r. 1860, zřizovatelem je kraj a obyvatelé tam mají dost velký luxus - celá budova je po kompletní rekonstrukci, se špičkovým vybavením (polohovací postele pro každého klienta, zvedáky, výtahy, nouzová zařízení) dle nejlepších standardů. Ale, jak říkám, je to o lidech. Na spoustu ředitelských míst nastupují místo schopných manažerů "trafikanti" a jak má pak takové zařízení fungovat. Není to o tom, že by pro ně nebylo dost peněz, ale zodpovědné osoby je pro ně jen nezvládnou sehnat.', 35);
INSERT INTO comments VALUES (8438, 'Pavla Kozáková', 'Je jich většina, kazí to to pár neohleduplných sobeckých jedinců, kteří se najdou v každé sociální skupině.', 35);
INSERT INTO comments VALUES (8439, 'Tomáš Tatíček', 'To víš, pro věřící v nadpřirozeno, co si o sobě myslí, že jsou obrazem jakýhosi super-super-ducha, jsou zvířata věci, asi jako kameny. To oni tak mají s tou láskou, kterou oplývají.', 35);
INSERT INTO comments VALUES (8440, 'Kateřina Vašáková', 'Staří lidé prostě už nejsou takhle pružní v mysli, nebo mnoho z nich, ne všichni. Je jasné, že toto je to nejlepší, a asertivní člověk to v pohodě zvládne. Ale mnoho lidí je neinformovaných, nezná svoje práva - a úředníci, které si platíme, by měli spíš hledat uspokojivé řešení pro všechny, než naopak. Bohužel, často si nevidí ani na špičku nosu a sami se v pravidlech nevyznají.', 35);
INSERT INTO comments VALUES (8441, 'Kateřina Vašáková', 'Nechápu, proč by pes, který je v tašce nebo na podlaze a nic nedělá, neštěká, nekňučí, atd., nemohl do restaurace. Já malého psa mám, vždycky se zeptám předem, jestli je nebo není problém a v 90% není. Kde je, tam to respektuju. To stejné i na hradech, zámcích, někde už to umožňují, např. na Křivoklátu ho v tašce vzít s sebou můžete. Opět nevidím důvod, proč by to měl být problém - navolno to samozřejmě chápu, do potravin ho neberu nikdy, atd. Nelze prostě paušalizovat. Pro mnoho starých lidí je pes jediný společník, a pro mnoho lidí je zvíře člen rodiny, a nemyslím, že by si tím nahrazovali lidský vztah. Jen prostě mají místo i pro zvířata, a kdo nějaké měl, ví, že v mnoha směrech se od nich můžeme dost co učit.', 35);
INSERT INTO comments VALUES (8442, 'Jakub Kos', 'Jde hlavně o toho člověka, který má to zvíře rád. Ale chápu, že je na vás asi moc to probrat.', 35);
INSERT INTO comments VALUES (8443, 'Václav Kovář', 'To je otázka pro jeho blízké okolí, což většina diskutujících zřejmě nebude. Já osobně pána neznám, nicméně bych, na základě jeho výroků, na jeho "neškodnost" zrovna nesázel.', 35);
INSERT INTO comments VALUES (8444, 'Ondřej Zeman', 'Je kvůli tomu...', 35);
INSERT INTO comments VALUES (8445, 'Ondřej Zeman', 'A mimochodem, jak jsem už napsal, já psa nemám a nikdy jsem neměl, tak jak o mě můžete psát, že jsem pejskař?', 35);
INSERT INTO comments VALUES (8446, 'Ondřej Zeman', 'A to je důvod je nenávidět? Já chápu že to někomu může vadit protože se třeba psů bojí, co ale nechápu je až ta fanatická nenávist vůči nim.', 35);
INSERT INTO comments VALUES (8447, 'Ondřej Zeman', 'A chápete že byl vyšlechtěn k tomu aby se vůči lidem nechoval agresivně a poslouchali ho? Že z nich někteří jsou schopni udělat bestie co útočí na lidi, to je čistě problém té výchovy a dobrý majitel z něj takovou bestii nikdy neudělá, protože z něj takovou bestii mít nechce. V dobrých rukou to je fantastický domácí mazel kterému ani malé děti nevadí.', 35);
INSERT INTO comments VALUES (8448, 'Jiří Vomáčka', 'To je přesně to, co pejskaři nepochopí. Že existuje někdo, komu jsou psi ukradení a nehodlá se s nimi ani mazlit, ani očichávat.', 35);
INSERT INTO comments VALUES (8449, 'Ondřej Zeman', 'Tak já mám strach, přímo fóbii z vos, ale že bych k nim kvůli tomu nenáviděl? To tedy v žádném případě.', 35);
INSERT INTO comments VALUES (8450, 'Ondřej Zeman', 'Prostě jsem normální člověk který nevidí ve psech zrůdy...', 35);
INSERT INTO comments VALUES (8451, 'Vladimír Novák', 'Opět nahrazuji P.Řápka, ovšem opět v mírnějším tónu. Víte, dnešní lidé, a je jedno o jakou generaci se jedná, jsou buď dost natvrdlí anebo neví, co se kolem nich děje, i když malinko se začínají sdružovat, což se příčilo zejména p.V.Klausovi, který tvrdil, že každý z nás jsme invidualita a podle toho bychom se měli chovat. Ale zpět k psům. Už i nemocnice ty některé psy povolují z důvodů zdravotních asistencí. Ale jedinec si říká, sem tam, aniž bychom to viděli nebo máme tvář jak bůh Janus, že ti psi si některá místa v těch nemocnicí můžou i nemusí označkovat. Majitel třeba o tom ví, ale protože je imobilní, nemůže s tím něco dělat. To samé začíná být zvykem v knihovnách, školách i školkách. Skoro denně se s tím setkávám. Jen se ptám, je tohle normální a je to v pořádku? K tomu starobinci a psovi se vracím.Jeden z manželů může umřít a druhý, co se na něj navalí, nestačí zvládat. Ale snad v tomto případě existují bezplatné poradny, jen se nebát zjistit jejich adresu a využít tento úřad, pokud nemá děti nebo jsou sobecké. Ale zpět k tomu starobinci. Je to v podstatě zdravotnické zařízení a určitě tam existují určité hygienické předpisy. Takže pes by tam neměl, co dělat. A ještě dodávám, po teoretické stránce, co kdyby každý si na ten pokoj přivedl svého pejska? Co tedy s tím psem. Jsem tedy proti jeho utracení. Ovšem, mohl by se jej ujmout nějaký spolek, soused, příbuzný (to byl třeba můj případ, i když psy nemám rád). Babičce by pak mělo být dopřáno, aby se jednou za měsíc na toho svého čoklíka jet podívat.', 35);
INSERT INTO comments VALUES (8452, 'Vlada Musil', 'To je Vas solukromy nazor... Bydleli jsme / kdysi v Hnanicich u Znojmja/ a pes Wolf vytim v noci vadil kde komu', 35);
INSERT INTO comments VALUES (8453, 'Zdeněk Šmerda', 'Řápek na rozdíl od většiny čoklomilů a psů nikomu neškodí, jen se poněkud tvrději vyhraňuje.', 35);
INSERT INTO comments VALUES (8454, 'Michal Svoboda', 'Tohle vám nikdy nedokážu doložit, protože víte jaká dnešní doba je. Stačí se podívat na příspěvek pana Krychtálka nahoře. Z tohoto příspěvku si čtenář dovodí kolik je dneska ohleduplných lidí bez ohledu na fakt, jestli vlastní psa, či nikoli.', 35);
INSERT INTO comments VALUES (8455, 'Zdeněk Šmerda', 'Tak zkuste pochopit, že někomu to může být nepříjemné a může mít strach Je to tak těžké pochopit?', 35);
INSERT INTO comments VALUES (8456, 'Zdeněk Šmerda', 'Pitbull patří mezi nejnebezpečnější plemena psů. Původně byl tento americký psík určen pro štvanice býků a medvědů. Pes je nebojácný a má rád každého protivníka. Svou kořist drží v čelistech, dokud ji nezabije. Proto je dnes nejčastěji využíván jako bodyguard a na psí zápasy.', 35);
INSERT INTO comments VALUES (8457, 'Michal Svoboda', 'Z diskuze je patrné jak hluboká propast mezi pejskaři a nepejskaři je. Měli by jste si uvědomit, že všichni nejsou stejní, takže není důvod je házet do jednoho pytle. Spousta arogantních sobeckých nicek se pohybuje v obou táborech. Někdo preferuje svého miláčka a tahá ho vždy a všude a někdo zase narcisticky preferuje sebe a podle toho se taky chová. Pořád je ale spousta normálních soudných lidí, kteří ví, že musí respektovat pravidla odsouhlasená demokratickým většinovým hlasováním. Pokud jsou ta pravidla v provozním řádu domova pro seniory, tak se paní musí přizpůsobit. Pokud žádné omezení ve vlastnictví psa v provozním řádu není, nikdo nemá právo nařizovat, aby se klient před ubytováním zbavoval psa.', 35);
INSERT INTO comments VALUES (8459, 'Zdeněk Šmerda', 'Možná to tak vyznělo, že háže všechny pejskaře do jednoho pytle, což možná neměl v úmyslu, ale řekněte kolik procent je těch ohleduplných pejskařů, kteří nehledí jenom na sebe a svého psa?', 35);
INSERT INTO comments VALUES (8460, 'Michal Svoboda', 'Chudáčku, závidíte, že se nikdo nepostaral o vás, že.', 35);
INSERT INTO comments VALUES (8461, 'Zdeněk Šmerda', 'A co napsal p. Zeman tak nelidského? Přečtěte si to ještě jednou.', 35);
INSERT INTO comments VALUES (8462, 'Michal Svoboda', 'To je jednoduché pan Zeman vidí věci v širších souvislostech. V tomto případě neexistuje žádný zákaz, pouze si ho úředníci vymysleli a nakrmili paní. Vy totiž opravdu nechápete, protože všechny pejskaře házíte do jednoho pytle. Všichni pejskaři nejsou bezohlední sobci, kteří všude preferují své miláčky. Spousta z nich si uvědomuje omezení, ale pokud žádné předem dané omezení v zařízení pro seniory není, nikdo nemá právo paní psa brát ani nařizovat, aby se ho zbavila. Tak jednoduché to je.', 35);
INSERT INTO comments VALUES (8463, 'Tomáš Velčovský', 'Když to bylo tak důležité, proč si to teda neověřila přímo v tom zařízení? Když někam jdu, chci znát podmínky, provozní řád apod.. Ona se spokojila s jakousi pofidérní informací z telefonu. Připomíná mi to jinou hundemutru, která mi pustila psa pod zadní kolo auta asi v třicetikilometrové rychlosti a pak si po obličeji rozmazávala jeho mozek se slovy, že je to její dítě a chtěla po mně 10 000, přitom ona na psa kašlala a pustila ho do cesty....', 35);
INSERT INTO comments VALUES (8464, 'Tomáš Velčovský', 'Hrozné žvásty...', 35);
INSERT INTO comments VALUES (8465, 'Vladimír Krychtálek', 'Kdyby se celý život nestarala o čokly a sebe, ale o děti, neskončila by v důchoďáku.', 35);
INSERT INTO comments VALUES (8466, 'Sylva Šporková', 'Proboha lidi, nehádejte se o psa - o toho tady vůbec nejde.- Jde o to, že úředníci, které si platíme ze svých daní, aby nás sloužili, se domnívají, že nám můžou vládnout, buzerovat nás a dělat si, co chtějí - právě jako v tomto případě, kdy té paní úředník (úřednice) prostě lhala, třeba jen proto, že sama nemá ráda psy. Začněme se tomu bránit - ti lidé jsou tu opravdu proto, aby nám sloužili, kdyby nebylo nás a našich daní, tak polovina z nich pomře hlady. Ale pokud si na jejich špatnou práci nebudeme stěžovat, ale jen nadávat, budete stále horší!', 35);
INSERT INTO comments VALUES (8467, 'Jiří Vomáčka', 'Pak netuším, kde jste přišel k takovým názorům.', 35);
INSERT INTO comments VALUES (8468, 'Ondřej Zeman', 'Hele, když hromadně mímuskujte, přemýšlíte u toho, nebo je to vedeno jen vaší nenávistí ke psům?', 35);
INSERT INTO comments VALUES (8469, 'Ondřej Zeman', 'A je tu další kdo tu předvádí jak lidský je...', 35);
INSERT INTO comments VALUES (8470, 'Michal Steblo', 'Proboha jde jen o čokla', 35);
INSERT INTO comments VALUES (8471, 'Ondřej Zeman', 'Pitbull v dobrých rukou tohle nikdy neudělá, tedy pokud neonemocní nějakou infekcí mozku nebo něčím takovým, ale to se dá včas poznat. Je vidět že vaše příspěvky jsou vedeny jen a pouze vaší nenávistí k těmto tvorům, to se potom rozum někde pase a píšou se takové hlouposti.', 35);
INSERT INTO comments VALUES (8472, 'Ondřej Zeman', 'A čemu to vadí? To vám snad ta noha potom upadne nebo co?', 35);
INSERT INTO comments VALUES (8473, 'Ondřej Zeman', 'Já psa nemám a nikdy jsem neměl, co teď?', 35);
INSERT INTO comments VALUES (8474, 'Jiří Vomáčka', 'Můžete mi vysvětlit, proč bych se měl nechat očichávat cizím psem?', 35);
INSERT INTO comments VALUES (8475, 'Ondřej Zeman', 'No jo, ale klidně si ji mohla vzít zpátky, ten komu ho dala by to pochopil. Zkrátka není jasné zda ten pes s ní tam nakonec je nebo není.', 35);
INSERT INTO comments VALUES (8476, 'Jiří Vomáčka', 'To nic nemění na tom, že žádná čivava ještě nikoho neukousala k smrti. Kdežto pitbull v dobrých rukou docela často.', 35);
INSERT INTO comments VALUES (8477, 'Jiří Vomáčka', 'Jen do mě, pejskaři... Vaše mínusy pochopím, vy žijete v jiném světě.', 35);
INSERT INTO comments VALUES (8478, 'Ondřej Zeman', 'A přitom je zcela jasně napsáno že to možné je a že v tom není žádný problém. Jo, tohle je problém se zaslepenci...', 35);
INSERT INTO comments VALUES (8479, 'Jan Čermák', 'tady to zase je plné zvířatomilů, kteří nechápou, že něco prostě možné není... Ano, většinou zvíře nebývá problém, ale zvíře v hodné do malé garsonky, což většinou pečováky bývají... Ostatně tam píšou, že kočka problém nebývá, se psem je to horší... A je to i pochopitelné... Pes potřebuje mnohem větší péči a venčení, což ne vždy ten člověk zvládá... Navíc krom štěkání a vytí kterým dům ruší je nutno, aby k dotyčnému chodili další lidé, kteří mohou mít za psa strach... A často oprávněně... Tedy má člověk na výběr. Buď pečovák bez psa a nebo domácí charitu se psem. Je-li to tedy možné, neboť i tam chodí pečovatelka a i tam jsou sousedé... Holt, pokud někde bydlím musím dodržovat pravidla toho místa, obzvlášť, pokud jsem se tam nově přistěhoval... To je základní podmínka dobrého soužití se sousedy případně života v daném zařízení...', 35);
INSERT INTO comments VALUES (8480, 'Libor Foltýnek', 'Vždyť píšou, že se psa zbavila a našla pro něho jiné majitele, teď v tom vrtá ještě zpětně, že to asi nebylo nutné.', 35);
INSERT INTO comments VALUES (8481, 'Ondřej Zeman', 'To byste chtěla vědět moc, i právě proto lidem co takto píšou neříkám novináři, ale pisálkové.', 35);
INSERT INTO comments VALUES (8482, 'Marie Čermáková', 'No tak jak to tedy dopadlo? Nechala si ta paní toho psa nebo ne?', 35);
INSERT INTO comments VALUES (8483, 'Petr Schön', 'To ne, ten by toho psa rovnou skartoval a ještě by paní rozstřihnul řidičák a sešrotoval auto ;-)', 35);
INSERT INTO comments VALUES (8484, 'Ondřej Zeman', 'Ikdyby toho pitbulla měla, a co? Jen na základě té rasy jde rozhodnout zda je hodně tam vhodné mít? Mě mnohem více ruší různí kapesní Napoleoni co na vás okamžitě začnou štěkat než takový pitbull který si mě nevšímá, nanejvýš si mě chce očichat. Tihle velcí psi jsou obvykle velmi dobře vychováni na rozdíl od těch malých.', 35);
INSERT INTO comments VALUES (8485, 'Jiří Brabec', 'Nechápete a vůbec nevíte, o čem píšete. Před dvanácti lety bych možná napsal to samé co Vy. Nyní už ne.', 35);
INSERT INTO comments VALUES (8486, 'Richard Hladík', 'Kdybyste paní chápal, nikdy nenapíšete to, co jste napsal. Přeji Vám,ať jste zdráv a nezůstanete sám', 35);
INSERT INTO comments VALUES (8487, 'Ondřej Zeman', 'Za to vy tu předvádíte jak lidský jste...', 35);
INSERT INTO comments VALUES (8488, 'Jaroslav Křížek', 'Aha, jsem zapomněl na otazník .', 35);
INSERT INTO comments VALUES (8489, 'Ondřej Zeman', 'Někdo tu nepochopil kam že to ta paní jde...', 35);
INSERT INTO comments VALUES (8490, 'Kristýna Benešová', 'Standard vami popisovany (..."dnes jiz tedy standardne..") u domova pro seniory neni vsude standardem. Vim o domove pro seniory, kde maji vetsi cast triluzkovych pokoju a socialni zarizeni vzdy pro kridlo na patre. Jedna se o historickou budovu, slouzici kdysi jako nemocnice. Od pani, ktera tam dlouho pracovala, jsem slysela, ze je to domov duchodcu spis pro "lezaky",, ale i tak si myslim, ze triluzkove pokoje by mely byt prezitkem. Z vlastni zkusenosti a ted nedavno ze zkusenosti me maminky z nemocnice vim, jak se nemocni lide mnohdy v pokoji rusi pri spanku, je tu otazka soukromi...', 35);
INSERT INTO comments VALUES (8491, 'Ondřej Zeman', 'Zaprvé, odkud víte jakého má psa? A za druhé, tito psi patří mezi nejvíce společenská, nějtrpělivější a nejhodnější plemena. Že si s nimi pár jedinců honí ego a udělají z něj bestii, to na tom nic nemění.', 35);
INSERT INTO comments VALUES (8492, 'Věra Rúčková', 'zapomente na zdravy selsky rozum. Na uradech nejlepe vi co je pro vas vhodne a co muzete a co ne. V domove pro seniory je nejlepsi takovy co lezi, nechodi, neotravuje a se kterym si muzou delat co chteji.A nejlepe a alzheimrem aby rodine nerekl co se tam s nim deje kdyz jsou jen s pecovateli. Kontroly se ani nepodivaji co se deje s klienty, ty zajimaji jen aby byly papiry v poradku a pri kontrole se ani na lidi nepodivaji. Na druhou stranu, ze tam nema byt se psem je logicke, umravnovat ciziho psa kdyz mate s necim pomoci seniorovi je slozite a nekdy i nemozne, ten vas neposlechne.A vynaseni vykalu po psovi neni v tabulkach na socialni peci.', 35);
INSERT INTO comments VALUES (8493, 'Jaroslav Křížek', 'Paní má pittbulla', 35);
INSERT INTO comments VALUES (8494, 'Jaroslav Křížek', 'Tam na úřadě pracuje p.Řapek...', 35);
INSERT INTO comments VALUES (8495, 'Jiří Vomáčka', 'Chápu, že ona má toho psa ráda. Ale ti pejskaři absolutně nemají soudnost, že někdo psy nemusí. Pejskaři mají pocit, že pes může kamkoliv a považují to za normální, aniž by pochopili, že někoho soužití se psem může obtěžovat, že pes nepatří do společných prostor, na sedačku v dopravních prostředcích, na zámky, do restaurací apod. Prostě kdo má psa, měl by akceptovat, že musí strpět jistá omezení.', 35);
INSERT INTO comments VALUES (8496, 'Dana Warkentin', 'No to snad ani není možné - nařizovat starším lidem, aby se zbavili často toho posledního partaka, kterého na světě maji. Mě ovšem spis prijde, že se pořád jede podle starých kolejí, ve kterých jsem byla vychovaná i já "co není povoleno, je zakazano" a ne tak, jak by to podle mě melo byt "co není zakazano, je povoleno". Úřednice na úřadě asi nevidela, že pes je povolen a tak holt byl automaticky zakazan. Místo pochopeni pro paní a její city se jede podle "narizeni". Smutne.', 35);
INSERT INTO comments VALUES (8497, 'Evzen Soukup', 'Tak to je tedy ZPRÁVA....', 35);
INSERT INTO comments VALUES (8498, 'Barbora Marešová', 'To mě taky pobaví, když do sebe autoři míchají dva naprosto odlišné typy zařízení - dům s pečovatelskou službou a domov pro seniory. První má samostatné byty a žijí tam lidé, kteří potřebují jednou za týden pomoct s úklidem, donést oběd, donést nákup... kdežto DpS je zařízení pro lidi, kteří pobírají příspěvek na péči = nedokážou se sami vůbec obstarat, mají zpravidla pro sebe (či ve dvojici) jeden pokoj (dnes již tedy standardně s koupelnou) a stará se o ně nepřetržitě pečovatelka. Míra soběstačnosti je navíc potvrzena přiznáním příspěvku na péči, který zpravidla lidé v domě s pečovatelskou službou nedostávají, protože na něj nemají nárok (jsou mnohem víc "schopní").', 35);
INSERT INTO comments VALUES (8499, 'Ondřej Zeman', 'Ten nesmysl s bojovými plemeny se drží, drží a drží a je jedno, absolutně jedno kolikrát jsou ti pisálkové upozorněni že nic takového není, je to marné, je tom marné, je to marné.', 35);
INSERT INTO comments VALUES (8500, 'Pavel Jeřábek', 'Ale jaro přichází i v únoru je blíž, než v listopadu.', 36);
INSERT INTO comments VALUES (8501, 'Václav Svoboda', 'To jsou ti jarní lobbisté.', 36);
INSERT INTO comments VALUES (8502, 'František Schlichts', 'Eestli si eště pamatuju, eště tak převčírem mělo bejt v sobotu a v neděli +8°C a skorem jaro.', 36);
INSERT INTO comments VALUES (8503, 'Marek Flekač', 'Proč by mělo v polovině unora přicházet jaro proboha??', 36);
INSERT INTO comments VALUES (8504, 'Petr Skála', 'tak v Brdech už chumelí a minus dva', 36);
INSERT INTO comments VALUES (8505, 'Richard Tomeček', 'Z úsporných důvodů se jaro vynechává a na přelomu dubna a května zima přejde přes noc v léto.', 36);
INSERT INTO comments VALUES (8506, 'Evzen Soukup', 'Výstraha, že bude v zimě sněžit .....', 36);
INSERT INTO comments VALUES (8507, 'Ilona Prokopcova', 'U nas taky nic', 36);
INSERT INTO comments VALUES (8508, 'Václav Svoboda', 'Jihozápad sněžení,severozápad zase nic!', 36);
INSERT INTO comments VALUES (8509, 'Lenka Matoušková', 'Tak schválně', 36);
INSERT INTO comments VALUES (8510, 'Martin Janda', 'koukám, že 16 měsíců vyšetřování přineslo své ovoce...slavná FBI a Mueller prokázali existenci ruské trollí farmy o jejíž existenci s cílem "nahlodávat" demokracii na západě už se tady psalo před 2 lety. Tak byli hoši zabráni do úkolu vyštrachat něco na Trumpa, že ani nezaregistrovali, že pod okny FBI vyhrožoval ozbrojený maniak, že vystřílí školu.', 37);
INSERT INTO comments VALUES (8631, 'Bohumil Petras', 'To ten loupeznik Prigozin byl vlastne takovym ruskym Masinem, tedy hrdinou, co rozvracel predchozi komunisticky rezim.', 37);
INSERT INTO comments VALUES (8511, 'Daniel Novák', 'Ta nenávist či spíše snaha podmanit si Rusko tu je již od dob jejich agenta jistého Uljanova, známého pod přezdívkou Lenin. I když první pokus nezdařený v r.1905. Lenin byl úspěšný, Bohu žel přišel Stalin a bylo po srandě. "Vodka" Jelcin - téměř úspěch, Bohu žel přišel Putin.', 37);
INSERT INTO comments VALUES (8512, 'Daniel Novák', 'A na ty informace musí mít vláda USA zvláštní rozhlasovou stanici kdesi v Evropě. Nestačí jim veřejnoprávní stanice Čt a nebo BBC?', 37);
INSERT INTO comments VALUES (8513, 'Michal Grešlik', 'Je pondelok.', 37);
INSERT INTO comments VALUES (8514, 'Michal Grešlik', 'Ale zakaždým tej deravej.', 37);
INSERT INTO comments VALUES (8515, 'Martin Janda', '"podle tebe" je jako vždy mimo...skvělou zábavu, Grešlik', 37);
INSERT INTO comments VALUES (8516, 'Martin Janda', 'tebe jsem vždycky strčil do kapsy', 37);
INSERT INTO comments VALUES (8517, 'Michal Grešlik', 'Podľa mňa už víkend skončil, ale chápem, že "socky" to neriešia.', 37);
INSERT INTO comments VALUES (8518, 'Michal Grešlik', 'U Vás je obava zrejme rovnakého druhu, pretože na argumentovanie nemáte bunky.', 37);
INSERT INTO comments VALUES (8519, 'Martin Janda', 'super víkendová zábava, Grešlik', 37);
INSERT INTO comments VALUES (8520, 'Martin Janda', 'vy tady dlouhodobě zesměšňujete jen sám sebe', 37);
INSERT INTO comments VALUES (8521, 'Jiri Bartunek', 'Odkud přišla ta nenavist Ruska která se posledni dobou tak rozmohla po celem západním světe? Co asi Rusko provedlo,že je prohlášeno hlavním nepřítelem Spojených státu.Je to ropaganda která stejně nikam nevede.''', 37);
INSERT INTO comments VALUES (8522, 'Jiri Tichy', 'Takze kdyz nejakaruska agentura vysila nepravdive zpravy, je mozno ji zazalovat s ovlivnovanim voleb. A co tedy denni lzi vysilane CNN a dalsimi americkymi stanicemi (v prezidentskych volbach zlotrile proti Trumpovi a plne pro Clintonovo)? Kdy je budou zalovat za nezakonne ovlivnovani voleb?', 37);
INSERT INTO comments VALUES (8523, 'Miloš Setík', 'Pokud nechápete ten rozdíl, pak je zbytečné s vámi diskutovat. O tom, že jaderné zbraně v Iráku byl výmysl dnes nikdo nepochybuje. Proč tomu tak bylo je dodnes ne úplně jasné.', 37);
INSERT INTO comments VALUES (8524, 'Jiří Bušek', 'Informace správně orientované a vyselektované.', 37);
INSERT INTO comments VALUES (8525, 'Jiřina Simajchlová', 'Doporucuji ruskym hackerum at se jeste vice pusti do USA, vubec by neskodilo, kdyby jim rozlozily CIA, jen jim hosi pridejte prace, bude na svete sranda. Mozna pak budou mit us tajne sluzby mene casu na zmeny rezimu ve svete a budou se muset zabyvat vlastnim rozkladem.', 37);
INSERT INTO comments VALUES (8526, 'Luboš Rychna', 'To je ten rozdíl co oni nepoznají', 37);
INSERT INTO comments VALUES (8527, 'Luboš Rychna', 'Ta kazarkinová není to darja.', 37);
