INSERT INTO comments VALUES (8840, 'Eva Zavacka', 'Pane Pokorný i to je podnikání již od posvěcených polistopadových tunelů.. Bohužel', 37);
INSERT INTO comments VALUES (8841, 'Robert Raz', 'Ono se to da pouzit na obe zeme, ale ja pisu o USA a jejich valecnych zlocinech v Iraku a v Syrii. Americka vlada by mela komplet skoncit pred tribunalem v Hague!', 37);
INSERT INTO comments VALUES (8842, 'Eva Zavacka', 'U nás je to jiné, pane Dušánku? Putin své působení v KGB nikdy nepopíral. Za to zde mnozí bývalí agenti skartovali své materiály a bezostyšně vlezli jak do politiky, tak do podnikání. Čistí, jak lilie. Co my máme právo soudit?', 37);
