INSERT INTO comments VALUES (9162, 'Jara Rank', 'Tak ona tady za velkou louzi neni zadna legrace, konecne se vysetrovani pohnulo a uz "povidaji", Donikovi uz je krusno, navic se Melanie dozvedela, ze byl se Stormy a musel zaplatit 130.000 za mlceni.', 42);
