INSERT INTO comments VALUES (9256, 'Ladislav Frydryšek', 'Sobotkovou chybou nebylo, že podcenil estébáka, že si ho do vlády přibral, byla superchyba a já jsem rád tomu, že se tím odepsal bohouš.', 43);
INSERT INTO comments VALUES (9208, 'Marek Kalina', 'Jaká arogance? Není arogantní spíše český postoj, když např. ostentativně nedodržujeme něco, co bylo řádně schváleno podle předepsaných procedur? To je seriózníá přístup? Nemusím se vším souhlasit, ale když se jednou hlasuje, tak je třeba přijmout výsledek a ne se chovat aroigantně a ignorovat to...', 42);
