INSERT INTO comments VALUES (9210, 'Jiří Nosek', 'na svém názoru trvám....', 42);
INSERT INTO comments VALUES (9211, 'Ivan Vašek', 'Nikdy nebylo vaše.Bylo Germánů...Jak za Římanů,tak po nich.Musíme s nimi žít a můžeme s nimi žít.Ale nesmí se zapomínat,kdo jsou.Prostě brát historii a fakta,jak jsou.', 42);
INSERT INTO comments VALUES (9212, 'Marek Kalina', 'To je věc těch křesťanů, když ten kostel chtěli, tak ho neměli prodávat... NOvý majitel si s objektem může dělat co chce...', 42);
INSERT INTO comments VALUES (9213, 'Marek Kalina', 'Německo pod Merkel už 16 let velmi prosperuje, i přes to, že nemají lehkou situaci a jiný stát by takto dobře takovou migrační vlnu rozhodně nezvládl...', 42);
INSERT INTO comments VALUES (9214, 'Denis Huml', 'Tohle napsal přesně před 5 lety Luboš Palata: Ve středověku museli muslimové dobýt křesťanská území, aby tamní kostely mohly přestavět na mešity. Dnes to jde v Německu, uprostřed křesťanské Evropy, daleko snadněji. Stačí nastrčený kupec a oznámení stavebnímu úřadu. https://zpravy.idnes.cz/nemecko-kostel-mesita-muslimove-doj-/zahranicni.aspx?c=A130218_133627_zahranicni_pul', 42);
INSERT INTO comments VALUES (9215, 'Marek Kalina', 'Co to plácáte? V Německu je svoboda, jen nechtějí nechat veřejně kázat nenávist, rasismus, nesnášenlivost... s tím já problém nemám... JInak demokracie funguje vzorně...', 42);
INSERT INTO comments VALUES (9216, 'Ivan Vašek', 'Arogance,pane.', 42);
INSERT INTO comments VALUES (9217, 'Ivan Vašek', 'Vadí vám.A lidem,kteří mají rádi zavřené oči a sní.Nevidí problémy,nechtějí je vidět,protože potom je po klidu.Trump má určitě vadu ve vztahu k Sýrii a SA.', 42);
INSERT INTO comments VALUES (9218, 'Jiří Nosek', 'myslím, že problémem je spíše Německo s Merkel, než USA s Trumpem', 42);
